INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(1, 'Vivo V15 128GB', 1, 'Điện thoại', '<p>Được xem l&agrave; bản r&uacute;t gọn của flagship Vivo V15 Pro sắp ra mắt, chiếc điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/vivo-v15\" target=\"_blank\" title=\"Xem điện thoại Vivo V15 tại thegioididong.com\" type=\"Xem điện thoại Vivo V15 tại thegioididong.com\">Vivo V15</a>&nbsp;vẫn mang trong m&igrave;nh nhiều n&eacute;t đ&aacute;ng gi&aacute; với thiết kế cao cấp c&ugrave;ng camera sefie pop-up 32 MP lạ mắt.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/199041/Slider/-vivo-v15-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Sạc, Tai nghe, S&aacute;ch hướng dẫn, C&aacute;p, C&acirc;y lấy sim, Miếng d&aacute;n m&agrave;n h&igrave;nh, Ốp lưng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Được xem l&agrave; bản r&uacute;t gọn của flagship Vivo V15 Pro sắp ra mắt, chiếc điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/vivo-v15\" target=\"_blank\" title=\"Xem điện thoại Vivo V15 tại thegioididong.com\" type=\"Xem điện thoại Vivo V15 tại thegioididong.com\">Vivo V15</a>&nbsp;vẫn mang trong m&igrave;nh nhiều n&eacute;t đ&aacute;ng gi&aacute; với thiết kế cao cấp c&ugrave;ng camera sefie pop-up 32 MP lạ mắt.</h2>\r\n\r\n<h3>Thiết kế tr&agrave;n viền ho&agrave;n hảo</h3>\r\n\r\n<p>Vivo V15 sẽ cho bạn cảm nhận m&agrave;n h&igrave;nh đ&uacute;ng nghĩa tr&agrave;n viền bởi tr&ecirc;n mặt trước của m&aacute;y kh&ocirc;ng hề c&oacute; vị tr&iacute; n&agrave;o cho camera selfie m&agrave; thay v&agrave;o đ&oacute; camera sẽ được giấu k&iacute;n với cơ chế pop-up (khi sử dụng th&igrave; dịch chuyển ra).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/199041/vivo-v15-008.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế màn hình của Vivo 15\" src=\"https://cdn.tgdd.vn/Products/Images/42/199041/vivo-v15-008.jpg\" title=\"Thiết kế màn hình của Vivo 15\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh của m&aacute;y c&oacute; k&iacute;ch thước l&ecirc;n đến 6.53 inch với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Tìm hiểu về tấm nền IPS LCD\" type=\"Tìm hiểu về tấm nền IPS LCD\">tấm nền IPS LCD</a>,&nbsp;độ ph&acirc;n giải Full HD+ cộng th&ecirc;m với thiết kế tr&agrave;n viền như tr&ecirc;n, mang lại kh&ocirc;ng gian trải nghiệm rộng r&atilde;i v&agrave; sinh động cho bạn.&nbsp;</p>\r\n\r\n<p>M&aacute;y được phủ tấm k&iacute;nh cường lực chắn chắn&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/gorilla-glass-5-ra-mat-smartphone-roi-tu-do-cao-1-6-met-vo-tu-860409\" target=\"_blank\" title=\"Tìm hiểu thêm về kính cường lực Gorilla Glass 5 \" type=\"Tìm hiểu thêm về kính cường lực Gorilla Glass 5 \">Gorilla Glass 5</a>&nbsp;gi&uacute;p bạn an t&acirc;m với c&aacute;c va chạm th&ocirc;ng thường khi sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/199041/vivo-v15-0012-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế mặt sau của Vivo V15\" src=\"https://cdn.tgdd.vn/Products/Images/42/199041/vivo-v15-0012-1.jpg\" title=\"Thiết kế mặt sau của Vivo V15\" /></a></p>\r\n\r\n<p>Điểm kh&aacute;c biệt giữa Vivo V15 v&agrave; V15 Pro l&agrave; ở chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các loại điện thoại đang kinh doanh tại thegioididong.com\" type=\"Tham khảo các loại điện thoại đang kinh doanh tại thegioididong.com\">smartphone</a>&nbsp;V15 sử dụng cảm biến v&acirc;n tay ở mặt sau kh&aacute;c với cảm biến v&acirc;n tay tr&ecirc;n m&agrave;n h&igrave;nh của V15 Pro.</p>\r\n\r\n<p>Tuy m&aacute;y c&oacute; k&iacute;ch thước lớn như c&aacute;c cạnh v&agrave; g&oacute;c m&aacute;y đều được v&aacute;t cong tạo sự uyển chuyển v&agrave; &ecirc;m &aacute;i để thao t&aacute;c cầm nắm được thoải m&aacute;i nhất.</p>\r\n\r\n<h3>Camera sefie pop-up 32 MP</h3>\r\n\r\n<p>Bạn sẽ rất ngạc nhi&ecirc;n với chiếc camera sefie c&oacute; cơ chế &quot;ẩn - hiện&quot; n&agrave;y c&oacute; độ ph&acirc;n giải khủng l&ecirc;n đến 32 MP hơn cả độ ph&acirc;n giải của camera sau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/199041/vivo-v15-003.jpg\" onclick=\"return false;\"><img alt=\"Camera sefie của điện thoại Vivo V15\" src=\"https://cdn.tgdd.vn/Products/Images/42/199041/vivo-v15-003.jpg\" title=\"Camera sefie của điện thoại Vivo V15\" /></a></p>\r\n\r\n<p>Ở mặt sau của m&aacute;y được trang bị bộ 3 camera với một ống k&iacute;nh ch&iacute;nh độ ph&acirc;n giải 12 MP + hai ống k&iacute;nh 8 MP v&agrave; 5 MP để zoom v&agrave; đo chiều s&acirc;u để hỗ trợ chụp ảnh x&oacute;a ph&ocirc;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/199041/vivo-v15-005.jpg\" onclick=\"return false;\"><img alt=\"Bộ ba camera sau của điện thoại Vivo V15\" src=\"https://cdn.tgdd.vn/Products/Images/42/199041/vivo-v15-005.jpg\" title=\"Bộ ba camera sau của điện thoại Vivo V15\" /></a></p>\r\n\r\n<p>Bộ camera n&agrave;y c&oacute; thể cho bạn trải nghiệm chụp h&igrave;nh vui nhộn với sự t&iacute;ch hợp t&iacute;nh năng Sticker AR, hay chụp h&igrave;nh chuy&ecirc;n nghiệp tự tay chỉnh c&aacute;c th&ocirc;ng số m&aacute;y ảnh để cho ra tấm ảnh ưng &yacute; nhất.</p>\r\n\r\n<p>Hoặc nếu bạn kh&ocirc;ng r&agrave;nh về m&aacute;y ảnh th&igrave; AI camera sẽ hỗ trợ bạn nhận biết th&ocirc;ng minh nhất.</p>\r\n\r\n<h3>Hiệu năng ổn định với chip MediaTek Helio P70</h3>\r\n\r\n<p>Kh&ocirc;ng giống như phi&ecirc;n bản V15 Pro, chiếc điện thoại Vivo V15 được trang bị con ch&iacute;p xử l&iacute;&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/mediatek-helio-p70-ra-mat-hieu-suat-tot-hon-13--cai-tien-ai-1126674\" target=\"_blank\" title=\"Tìm hiểu về chip MediaTek Helio P70\" type=\"Tìm hiểu về chip MediaTek Helio P70\">MediaTek Helio P70</a>&nbsp;8 nh&acirc;n đi c&ugrave;ng với RAM 6 GB v&agrave; bộ nhớ trong 128 GB tạo kh&ocirc;ng gian bộ nhớ rộng r&atilde;i để thiết bị chạy c&aacute;c ứng dụng trơn tru mượt m&agrave; hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/199041/vivo-v15-006.jpg\" onclick=\"return false;\"><img alt=\"Giao điện điện thoại Vivo V15\" src=\"https://cdn.tgdd.vn/Products/Images/42/199041/vivo-v15-006.jpg\" title=\"Giao điện điện thoại Vivo V15\" /></a></p>\r\n\r\n<p>Vivo V15 chạy tr&ecirc;n hệ điều h&agrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/top-5-tinh-nang-moi-duoc-yeu-thich-nhat-tren-and-1107119\" target=\"_blank\" title=\"Tham khảo thông tin về hệ điều hành Android 9.0\" type=\"Tham khảo thông tin về hệ điều hành Android 9.0\">Android 9.0 (Pie)</a>&nbsp;với giao diện đặc sản của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-vivo\" target=\"_blank\" title=\"Xem thêm các loại điện thoại Vivo khác tại thegioididong.com\" type=\"Xem thêm các loại điện thoại Vivo khác tại thegioididong.com\">Vivo</a>&nbsp;l&agrave; Funtouch OS 9 c&oacute; hiển thị dễ nh&igrave;n, dễ sử dụng v&agrave; thao t&aacute;c nhanh.</p>\r\n\r\n<p>Với mức cấu h&igrave;nh n&agrave;y m&aacute;y c&oacute; thể đ&aacute;p ứng tốt c&aacute;c nhu cầu xem phim, chơi game, lướt web giải tr&iacute; của bạn v&agrave; với kh&ocirc;ng gian hiển thị cực rộng n&agrave;y chắc chắn sẽ đem lại cảm gi&aacute;c th&iacute;ch th&uacute; khi trải nghiệm.</p>\r\n\r\n<h3>Năng lượng k&eacute;o d&agrave;i</h3>\r\n\r\n<p>Cung cấp năng lượng cho Vivo V15 l&agrave; vi&ecirc;n pin c&oacute; dung lượng 4000 mAh gi&uacute;p k&eacute;o d&agrave;i thời gian sử dụng của thiết bị để trải nghiệm được trọn vẹn hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/199041/vivo-v15-5.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế đáy của điện thoại Vivo V15\" src=\"https://cdn.tgdd.vn/Products/Images/42/199041/vivo-v15-5.jpg\" title=\"Thiết kế đáy của điện thoại Vivo V15\" /></a></p>\r\n\r\n<p>Sản phẩm vẫn d&ugrave;ng cổng sạc MicroUSB dễ kiếm hiện nay n&ecirc;n gi&uacute;p việc lấy lại năng lượng cho m&aacute;y thuận tiện</p>\r\n', 1, 0, 'vivo-v15-quanghai-400x400.jpg', 7490000),
(2, 'OPPO F11 Pro 128GB', 1, 'Điện thoại', '<p>L&acirc;u nay, những chiếc điện thoại của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại OPPO chính hãng\">OPPO</a>&nbsp;thường được đ&aacute;nh gi&aacute; cao ở camera selfie v&agrave; với&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-f11-pro-128gb\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO F11 Pro 128GB chính hãng\">OPPO F11 Pro 128GB</a>&nbsp;th&igrave; ngo&agrave;i thế mạnh về camera trước với hệ thống trượt, OPPO l&agrave;m người ta phải ấn tượng th&ecirc;m cả về camera sau.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/202703/Slider/vi-vn-oppo-f11-pro-128gb-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Sạc, Tai nghe, S&aacute;ch hướng dẫn, C&aacute;p, C&acirc;y lấy sim, Ốp lưng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>L&acirc;u nay, những chiếc điện thoại của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại OPPO chính hãng\">OPPO</a>&nbsp;thường được đ&aacute;nh gi&aacute; cao ở camera selfie v&agrave; với&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-f11-pro-128gb\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO F11 Pro 128GB chính hãng\">OPPO F11 Pro 128GB</a>&nbsp;th&igrave; ngo&agrave;i thế mạnh về camera trước với hệ thống trượt, OPPO l&agrave;m người ta phải ấn tượng th&ecirc;m cả về camera sau.</h2>\r\n\r\n<h3>M&agrave;n h&igrave;nh tr&agrave;n viền kh&ocirc;ng khuyết điểm</h3>\r\n\r\n<p>Kh&ocirc;ng &ldquo;tai thỏ&rdquo;, kh&ocirc;ng &ldquo;nốt ruồi&rdquo; gi&uacute;p cho OPPO F11 Pro 128GB kh&aacute;c biệt ho&agrave;n to&agrave;n với nhiều&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>&nbsp;tr&ecirc;n thị trường hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-13.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-13.jpg\" title=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Camera selfie nằm ẩn b&ecirc;n trong cơ chế trượt, từ đ&oacute; ch&uacute;ng ta c&oacute; một&nbsp;<a href=\"https://www.thegioididong.com/dtdd?f=man-hinh-tran-vien\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có thiết kế màn hình tràn viền\">m&agrave;n h&igrave;nh tr&agrave;n viền</a>&nbsp;cực kỳ ấn tượng gi&uacute;p người d&ugrave;ng c&oacute; một diện t&iacute;ch trải nghiệm tuyệt vời.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-7.jpg\" title=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>OPPO F11 Pro 128GB được trang bị m&agrave;n h&igrave;nh 6.5 inch, độ ph&acirc;n giải Full HD+, tỉ lệ 19.5:9, cộng với viền mỏng, n&oacute; gi&uacute;p chiếc m&aacute;y dễ d&agrave;ng nằm gọn trong l&ograve;ng b&agrave;n tay người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-18.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-18.jpg\" title=\"Màn hình của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Đồng thời, g&oacute;c nh&igrave;n v&agrave; độ s&aacute;ng của m&aacute;y cũng được đ&aacute;nh gi&aacute; l&agrave; kh&aacute; tốt khi người d&ugrave;ng trải nghiệm ngo&agrave;i trời.</p>\r\n\r\n<h3>Camera selfie trượt ấn tượng</h3>\r\n\r\n<p>F11 Pro 128GB l&agrave; m&aacute;y sở hữu camera selfie 16 MP c&oacute; cơ chế &ldquo; th&ograve; thụt&rdquo; kh&aacute; độc đ&aacute;o v&agrave; tiện lợi cho người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-12.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-12.jpg\" title=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>OPPO gọi đ&acirc;y l&agrave; cơ chế pop-up (bật l&ecirc;n), l&uacute;c n&agrave;o cần th&igrave; camera sẽ tự động được bật l&ecirc;n v&agrave; sẽ ẩn đi khi kh&ocirc;ng sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-11.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-11.jpg\" title=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Camera selfie được đặt tại vị tr&iacute; ch&iacute;nh giữa gi&uacute;p c&aacute;c bức ảnh selfie dễ canh h&igrave;nh hơn, kh&ocirc;ng bị lệch v&agrave; bạn chỉ cần mất v&agrave;i gi&acirc;y để bật camera l&ecirc;n để tự sướng th&ocirc;i.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ thế, camera selfie hỗ trợ tr&iacute; tuệ nh&acirc;n tạo AI với c&ocirc;ng nghệ Beautification được OPPO đ&aacute;nh gi&aacute; cao, cho ph&eacute;p mang đến những bức ảnh selfie tốt nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-20.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-20.jpg\" title=\"Camera trước selfie của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Phần mềm m&aacute;y ảnh cũng sẽ c&oacute; nhiều hiệu ứng kh&aacute;c nhau để l&agrave;m cho ảnh selfie của bạn tr&ocirc;ng tuyệt vời hơn nữa.</p>\r\n\r\n<h3>Camera sau l&ecirc;n đến 48 MP</h3>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; camera trước m&agrave;&nbsp;OPPO F11 Pro 128 GB c&ograve;n sở hữu camera ch&iacute;nh với độ ph&acirc;n giải l&ecirc;n đến 48 MP v&agrave; nhiều t&iacute;nh năng chụp h&igrave;nh th&uacute; vị kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-15.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-15.jpg\" title=\"Camera sau của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; F11 Pro c&ograve;n được trang bị cảm biến độ s&acirc;u 5 MP với khẩu độ f/2.4, sử dụng cảm biến của Sony gi&uacute;p camera nhận được nhiều &aacute;nh s&aacute;ng hơn v&agrave; thu được nhiều chi tiết hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-6.jpg\" title=\"Camera sau của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Ngo&agrave;i độ ph&acirc;n giải cao, OPPO F11 Pro c&ograve;n trang bị qu&aacute; nhiều c&ocirc;ng nghệ ti&ecirc;n tiến tr&ecirc;n một chiếc camera như: AI Ultra-clear Engine gi&uacute;p người d&ugrave;ng lu&ocirc;n c&oacute; được những bức ảnh tuyệt vời d&ugrave; chụp đủ s&aacute;ng hay thiếu s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-5.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-5.jpg\" title=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ Tetracell xuất hiện tr&ecirc;n m&aacute;y cũng sẽ cải thiện khả năng chụp ảnh ch&acirc;n dung v&agrave;o ban đ&ecirc;m.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-4.jpg\" title=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Chế độ Ultra Night c&oacute; chức năng mang đến những bức ảnh tuyệt vời trong nhiều cảnh đ&ecirc;m kh&aacute;c nhau v&agrave; k&egrave;m theo t&iacute;nh năng Color Mapping nhằm kiểm so&aacute;t m&agrave;u da của con người.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-3.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-3.jpg\" title=\"Ảnh chụp từ camera của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Tất cả những g&igrave; bạn cần l&agrave;m l&agrave; cầm chiếc&nbsp;OPPO F11 Pro l&ecirc;n v&agrave; chụp, mọi việc c&ograve;n lại đ&atilde; c&oacute; m&aacute;y xử l&yacute; tự động cho bạn.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd?f=pin-khung-3000-mah\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có dung lượng pin \">Pin &quot;si&ecirc;u khủng&quot;</a>&nbsp;k&egrave;m&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"Tìm hiểu công nghệ sạc nhanh VOOC\" type=\"Tìm hiểu công nghệ sạc nhanh VOOC\">c&ocirc;ng nghệ VOOC</a></h3>\r\n\r\n<p>OPPO F11 Pro được n&acirc;ng cấp c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd?f=sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có tính năng sạc pin nhanh\">sạc nhanh</a>&nbsp;VOOC 3.0 gi&uacute;p m&aacute;y c&oacute; thể được sạc đầy chỉ trong v&ograve;ng 80 ph&uacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-10.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-10.jpg\" title=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Với sự n&acirc;ng cấp n&agrave;y sẽ cho m&aacute;y sạc nhanh hơn 20% so với &quot;đ&agrave;n em&quot; OPPO F9 đ&atilde; ra mắt trước đ&oacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-9.jpg\" title=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Vi&ecirc;n pin dung lượng l&ecirc;n tới 4000 mAh hứa hẹn cũng sẽ cho bạn thoải m&aacute;i sử dụng cả ng&agrave;y m&agrave; kh&ocirc;ng cần lo lắng về việc phải sạc pin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/202703/oppo-f11-pro-128gb-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/202703/oppo-f11-pro-128gb-tgdd-8.jpg\" title=\"Thời lượng pin của điện thoại OPPO F11 Pro 128GB chính hãng\" /></a></p>\r\n\r\n<p>Đồng thời, với 5 lớp bảo vệ trong c&ocirc;ng nghệ VOOC, c&ocirc;ng nghệ sạc nhanh của OPPO đảm bảo sự an to&agrave;n cho người d&ugrave;ng.</p>\r\n', 1, 0, '1563866621oppo-f11-pro-128gb-avatar-1-600x600.jpg', 8490000),
(3, 'Huawei P30 Lite', 1, 'Điện thoại', '<p>Mới đ&acirc;y Huawei đ&atilde; ch&iacute;nh thức giới thiệu chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd/huawei-p30-lite\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei P30 Lite chính hãng\">Huawei P30 Lite</a>&nbsp;b&ecirc;n cạnh c&aacute;c sản phẩm kh&aacute;c trong d&ograve;ng P30 series v&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>&nbsp;được định hướng tới ph&acirc;n kh&uacute;c tầm trung.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/198985/Slider/-huawei-p30-lite-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Sạc, Tai nghe, S&aacute;ch hướng dẫn, C&aacute;p, C&acirc;y lấy sim, Ốp lưng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Mới đ&acirc;y Huawei đ&atilde; ch&iacute;nh thức giới thiệu chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd/huawei-p30-lite\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei P30 Lite chính hãng\">Huawei P30 Lite</a>&nbsp;b&ecirc;n cạnh c&aacute;c sản phẩm kh&aacute;c trong d&ograve;ng P30 series v&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>&nbsp;được định hướng tới ph&acirc;n kh&uacute;c tầm trung.</h2>\r\n\r\n<h3>Camera vẫn rất nổi bật trong ph&acirc;n kh&uacute;c</h3>\r\n\r\n<p>Điểm nhấn đ&aacute;ng ch&uacute; &yacute; nhất tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Tham khảo điện thoại Huawei chính hãng\">điện thoại Huawei</a>&nbsp;l&agrave; hệ thống 3 camera sau bao gồm cảm biến ch&iacute;nh 24 MP (khẩu độ f/.8), cảm biến độ s&acirc;u 2 MP v&agrave; cảm biến 8 MP hỗ trợ chụp g&oacute;c rộng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite6.jpg\" title=\"Camera sau của điện thoại Huawei P30 Lite chính hãng\" /></a></p>\r\n\r\n<p>Với việc hỗ trợ th&ecirc;m một camera g&oacute;c rộng th&igrave; bạn c&oacute; thể c&oacute; được cho m&igrave;nh những bức ảnh với phong c&aacute;ch rộng tuyệt đẹp, kh&ocirc;ng bỏ s&oacute;t bất cứ thứ g&igrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite1.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite1.jpg\" title=\"Camera sau của điện thoại Huawei P30 Lite chính hãng\" /></a></p>\r\n\r\n<p>Đồng thời, camera của m&aacute;y c&ograve;n c&oacute; chế độ chụp ảnh đ&ecirc;m mới v&agrave; t&iacute;ch hợp AI để tối ưu chụp ảnh, tạo nh&atilde;n d&aacute;n AR hay t&igrave;m kiếm th&ocirc;ng tin tr&ecirc;n ảnh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite9.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite9.jpg\" title=\"Ảnh chụp từ camera của điện thoại Huawei P30 Lite chính hãng\" /></a></p>\r\n\r\n<p>Đ&acirc;y cũng l&agrave; chiếc điện thoại đầu ti&ecirc;n của Huawei được trang bị camera selfie l&ecirc;n đến 32MP, cao nhất thế giới hiện nay, hứa hẹn đem đến cho người d&ugrave;ng những bức ảnh tự sướng c&oacute; chất lượng cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite8.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite8.jpg\" title=\"Camera trước selfie của điện thoại Huawei P30 Lite chính hãng\" /></a></p>\r\n\r\n<p>Tất nhi&ecirc;n cũng kh&ocirc;ng thể thiếu t&iacute;nh năng l&agrave;m đẹp bằng AI cho camera trước, m&aacute;y sẽ l&agrave;m đẹp dựa tr&ecirc;n cấu tr&uacute;c v&agrave; h&igrave;nh dạng khu&ocirc;n mặt của mỗi người sẽ đảm bảo bạn lu&ocirc;n c&oacute; được những bức h&igrave;nh rạng rỡ nhất.</p>\r\n\r\n<h3>Thoải m&aacute;i lưu trữ với 128 GB bộ nhớ trong</h3>\r\n\r\n<p>Huawei P30 Lite cải thiện RAM v&agrave; dung lượng bộ nhớ tạo ra nhiều kh&ocirc;ng gian lưu giữ hơn cho những thứ bạn y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite5.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite5.jpg\" title=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" /></a></p>\r\n\r\n<p>Bạn c&oacute; thể thoải m&aacute;i lưu trữ b&agrave;i h&aacute;t, h&igrave;nh ảnh hay video trong thời gian d&agrave;i m&agrave; kh&ocirc;ng phải x&oacute;a đi bất cứ thứ g&igrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite3.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite3.jpg\" title=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; GPU Turbo đ&atilde; được n&acirc;ng cấp th&ecirc;m để hỗ trợ nhiều game hơn, cho ph&eacute;p HUAWEI P30 Lite mang đến người d&ugrave;ng trải nghiệm tốt hơn với mọi tựa game h&agrave;ng đầu hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite4.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite4.jpg\" title=\"Cấu hình của điện thoại Huawei P30 Lite chính hãng\" /></a></p>\r\n\r\n<p>Với P30 Lite, người d&ugrave;ng c&ograve;n c&oacute; thể tải clip tr&ecirc;n c&aacute;c website hoặc nền tảng mạng x&atilde; hội để sử dụng t&iacute;nh năng hỗ trợ c&agrave;i đặt nhạc chu&ocirc;ng bằng video.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh rực rỡ, pin x&agrave;i cả ng&agrave;y</h3>\r\n\r\n<p>Huawei P30 Lite sở hữu&nbsp;m&agrave;n h&igrave;nh &quot;giọt nước&quot; 6.13 inch với độ ph&acirc;n giải Full HD+ với tấm nền IPS cho m&agrave;u sắc đẹp mắt, h&igrave;nh ảnh sắc n&eacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite2.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite2.jpg\" title=\"Màn hình của điện thoại Huawei P30 Lite chính hãng\" /></a></p>\r\n\r\n<p>Người d&ugrave;ng cũng sẽ c&oacute; t&iacute;nh năng ẩn đi tai thỏ&nbsp;với cạnh viền bo cong thay v&igrave; đường thẳng, điều n&agrave;y tạo n&ecirc;n n&eacute;t h&agrave;i h&ograve;a cho m&aacute;y khi nh&igrave;n v&agrave;o tổng thể.</p>\r\n\r\n<p>Dung lượng pin 3340 mAh của m&aacute;y gi&uacute;p bạn thoải m&aacute;i sử dụng cả ng&agrave;y m&agrave; kh&ocirc;ng lo m&aacute;y hết pin giữa chừng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/198985/huawei-p30-lite7.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại Huawei P30 Lite chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/198985/huawei-p30-lite7.jpg\" title=\"Thời lượng pin của điện thoại Huawei P30 Lite chính hãng\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ sạc nhanh gi&uacute;p tối ưu thời gian sạc đồng thời gi&uacute;p bạn vẫn c&oacute; được thời gian sử dụng thoải m&aacute;i khi kh&ocirc;ng c&oacute; nhiều thời gian sạc pin</p>\r\n', 1, 0, 'huawei-p30-lite-1-400x400.jpg', 6690000),
(4, 'OPPO A7', 1, 'Điện thoại', '<p><a href=\"https://www.thegioididong.com/dtdd/oppo-a7\" target=\"_blank\" title=\"Tham khảo chi tiết điện thoại OPPO A7 chính hãng\" type=\"Tham khảo chi tiết điện thoại OPPO A7 chính hãng\">OPPO A7</a>, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng, giá tốt tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại chính hãng, giá tốt tại Thegioididong.com\">điện thoại</a>&nbsp;được xem l&agrave; sự hiện th&acirc;n của c&aacute;i đẹp, c&aacute;i s&aacute;ng tạo m&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo\" target=\"_blank\" title=\"Tham khảo các đòng diện thoai OPPO chính hãng\" type=\"Tham khảo các đòng diện thoai OPPO chính hãng\">OPPO</a>&nbsp;mang đến cho người d&ugrave;ng với mặt lưng được t&ocirc; điểm bởi những họa tiết 3D th&uacute; vị c&ugrave;ng chiếc tai thỏ h&igrave;nh giọt nước đ&aacute;ng y&ecirc;u.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/193212/Slider/vi-vn-oppo-a7-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Sạc, S&aacute;ch hướng dẫn, C&acirc;y lấy sim, Ốp lưng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dtdd/oppo-a7\" target=\"_blank\" title=\"Tham khảo chi tiết điện thoại OPPO A7 chính hãng\" type=\"Tham khảo chi tiết điện thoại OPPO A7 chính hãng\">OPPO A7</a>, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng, giá tốt tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại chính hãng, giá tốt tại Thegioididong.com\">điện thoại</a>&nbsp;được xem l&agrave; sự hiện th&acirc;n của c&aacute;i đẹp, c&aacute;i s&aacute;ng tạo m&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo\" target=\"_blank\" title=\"Tham khảo các đòng diện thoai OPPO chính hãng\" type=\"Tham khảo các đòng diện thoai OPPO chính hãng\">OPPO</a>&nbsp;mang đến cho người d&ugrave;ng với mặt lưng được t&ocirc; điểm bởi những họa tiết 3D th&uacute; vị c&ugrave;ng chiếc tai thỏ h&igrave;nh giọt nước đ&aacute;ng y&ecirc;u.</h2>\r\n\r\n<h3>Thiết kế cực chất với mặt lưng 3D dạng lưới</h3>\r\n\r\n<p>OPPO A7 được tạo n&ecirc;n nhờ với một ng&ocirc;n ngữ thiết kế mới lạ, bắt mắt khi c&oacute; phần mặt lưng phản chiếu 3D v&acirc;n lưới &aacute;nh s&aacute;ng cực đẹp v&agrave; thu h&uacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-2.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế mặt lưng điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-2.jpg\" title=\"Thiết kế mặt lưng điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<p>Th&acirc;n h&igrave;nh mảnh mai, quyến rũ với c&aacute;c đường cong nhẹ nh&agrave;ng bao quanh th&acirc;n m&aacute;y tạo cho bạn cảm gi&aacute;c cầm nắm thoải m&aacute;i v&agrave; chắc chắn hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-12.jpg\" onclick=\"return false;\"><img alt=\"Khả năng cầm nắm thoải mái, chắc chắn trên điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-12.jpg\" title=\"Khả năng cầm nắm thoải mái, chắc chắn trên điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<p>Đập v&agrave;o mắt bạn sẽ l&agrave; kh&ocirc;ng gian hiển thị rộng r&atilde;i tr&ecirc;n k&iacute;ch thước 6.2 inch được c&aacute;ch điệu bởi chiếc tai thỏ h&igrave;nh giọt nước tr&ocirc;ng v&ocirc; c&ugrave;ng tinh tế.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-5.jpg\" onclick=\"return false;\"><img alt=\"Màn hình rộng rãi trên điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-5.jpg\" title=\"Màn hình rộng rãi trên điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh c&oacute;&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178\" target=\"_blank\" title=\"Tìm hiểu độ phân giải HD+\" type=\"Tìm hiểu độ phân giải HD+\">độ ph&acirc;n giải HD+</a>&nbsp;n&ecirc;n OPPO A7 khiến bạn cảm thấy h&agrave;i l&ograve;ng với những khung h&igrave;nh sống động, rực rỡ mỗi khi trải nghiệm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-4.jpg\" onclick=\"return false;\"><img alt=\"Màn hình rộng rãi trên điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-4.jpg\" title=\"Màn hình rộng rãi trên điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<h3>Hiệu năng mạnh mẽ với 4 GB RAM đi k&egrave;m</h3>\r\n\r\n<p>Sức mạnh cung cấp cho&nbsp;OPPO A7 đến từ con chip&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/qualcomm-snapdragon-450-ra-mat-sieu-tiet-kiem-pin-ho-tro-camera-kep-quick-charge-3-0-997437\" target=\"_blank\" title=\"Tìm hiểu chip xử lý Snapdragon 450\" type=\"Tìm hiểu chip xử lý Snapdragon 450\">Qualcomm Snapdragon 450</a>&nbsp;c&oacute; hiệu năng ổn định trong việc xử l&yacute; c&aacute;c t&aacute;c vụ cơ bản thường ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-11.jpg\" onclick=\"return false;\"><img alt=\"Khả năng chơi game mạnh mẽ trên điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-11.jpg\" title=\"Khả năng chơi game mạnh mẽ trên điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<p>Nhờ được trang bị 4 GB RAM c&ugrave;ng 64 GB bộ nhớ trong gi&uacute;p m&aacute;y chạy đa nhiệm mượt m&agrave; hơn cũng như lưu trữ thoải m&aacute;i c&aacute;c ứng dụng hay v&agrave; c&aacute;c tựa game hot.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-4-1.jpg\" onclick=\"return false;\"><img alt=\"Chạy đa nhiệm mượt mà, lưu trữ thoải mái trên điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-4-1.jpg\" title=\"Chạy đa nhiệm mượt mà, lưu trữ thoải mái trên điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<p>Chưa hết, bạn c&ograve;n được tận hưởng nhiều điều th&uacute; vị trong giao diện&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/he-dieu-hanh-coloros-tren-oppo-la-ma-quen-1073718\" target=\"_blank\" title=\"Tìm hiểu giao diện ColorOS 5.2\" type=\"Tìm hiểu giao diện ColorOS 5.2\">ColorOS 5.2</a>&nbsp;tuyệt đẹp được OPPO t&ugrave;y biến th&ocirc;ng minh v&agrave; bổ sung nhiều t&iacute;nh năng hấp dẫn.</p>\r\n\r\n<h3>Ấn tượng về khả năng chụp ảnh selfie si&ecirc;u đẹp</h3>\r\n\r\n<p>Điểm mạnh tr&ecirc;n&nbsp;OPPO A7 kh&ocirc;ng thể kh&ocirc;ng kể đến khả năng chụp ảnh selfie tuyệt đẹp với nhiều chế độ chụp ảnh chuy&ecirc;n nghiệp đi k&egrave;m.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-10.jpg\" onclick=\"return false;\"><img alt=\"Camera  trước trên điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-10.jpg\" title=\"Camera  trước trên điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<p>Cụ thể hơn, cụm camera trước c&oacute; độ ph&acirc;n giải 16 MP hỗ trợ c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-selfie-ai-beauty-la-gi-1049958\" target=\"_blank\" title=\"Tìm hiểu  công nghệ Selfie A.I Beauty\" type=\"Tìm hiểu  công nghệ Selfie A.I Beauty\">Selfie A.I Beauty</a>,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/ar-stickers-la-gi-vi-sao-hang-nao-cung-ap-dung-ar-1096228\" target=\"_blank\" title=\"Tìm hiểu tính năng Sticker AR\" type=\"Tìm hiểu tính năng Sticker AR\">Sticker AR</a>&nbsp;hứa hẹn sẽ cho ra những bức ảnh tự sướng đẹp tự nhi&ecirc;n m&agrave; kh&ocirc;ng cần chỉnh sửa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-3-1.jpg\" onclick=\"return false;\"><img alt=\"Nhiều chế độ chụp ảnh selfie chuyên nghiệp trên điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-3-1.jpg\" title=\"Nhiều chế độ chụp ảnh selfie chuyên nghiệp trên điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<p>Về cụm camera k&eacute;p ph&iacute;a sau c&oacute; 13 MP d&agrave;nh cho cảm biến ch&iacute;nh v&agrave; 2 MP d&agrave;nh cho cảm biến phụ sẽ đ&aacute;p ứng cho bạn trong việc&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-xoa-phong-huong-dan-xoa-phong-tren-moi-smartphone-892585\" target=\"_blank\" title=\"Tìm hiểu tính năng chụp ảnh xóa phông\" type=\"Tìm hiểu tính năng chụp ảnh xóa phông\">chụp ảnh x&oacute;a ph&ocirc;ng</a>&nbsp;một c&aacute;ch nhanh ch&oacute;ng v&agrave; thuận tiện nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-6-1.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera sau trên điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-6-1.jpg\" title=\"Giao diện camera sau trên điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng những thế bạn c&ograve;n c&oacute; thể ghi lại những khoảnh khắc đ&aacute;ng y&ecirc;u trong cuộc sống th&ocirc;ng qua khả năng quay phim&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/chuan-quay-phim-tren-dien-thoai-596472#fullhd\" target=\"_blank\" title=\"Tìm hiểu khả năng quay phim FullHD 1080p@30fps\" type=\"Tìm hiểu khả năng quay phim FullHD 1080p@30fps\">FullHD 1080p@30fps</a>&nbsp;được hỗ trợ tr&ecirc;n m&aacute;y.</p>\r\n\r\n<h3>Sử dụng bền bỉ với dung lượng pin cực khủng</h3>\r\n\r\n<p>OPPO A7 sở hữu vi&ecirc;n pin 4230 mAh v&agrave; được t&iacute;ch hợp chế độ quản l&yacute; pin th&ocirc;ng minh, hứa hẹn sẽ cho bạn một thời gian trải nghiệm thoải m&aacute;i cũng như tự động tiết kiệm pin mỗi khi cần thiết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/193212/oppo-a7-8.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin mạnh mẽ trên điện thoại OPPO A7 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/193212/oppo-a7-8.jpg\" title=\"Dung lượng pin mạnh mẽ trên điện thoại OPPO A7 chính hãng\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung, với những g&igrave; m&agrave; OPPO A7 mang lại trong một mức gi&aacute; hấp dẫn th&igrave; quả thực đ&acirc;y l&agrave; một thiết bị đ&aacute;ng để bạn lựa chọn trong ph&acirc;n kh&uacute;c điện thoại tầm trung.</p>\r\n', 1, 0, 'oppo-a7-green-400x400.jpg', 5090000),
(5, 'Xiaomi Redmi Go 16GB', 1, 'Điện thoại', '<p><a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-go-16gb\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Redmi Go 16GB chính hãng\">Xiaomi Redmi Go 16GB</a>&nbsp;l&agrave; một trong những chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>&nbsp;gi&aacute; rẻ nhất tới từ Xiaomi nhưng vẫn mang lại cho bạn một trải nghiệm mượt m&agrave; đến &quot;kh&oacute; tin&quot;.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/200603/Slider/vi-vn-xiaomi-redmi-go-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Dock sạc, C&aacute;p</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-go-16gb\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Redmi Go 16GB chính hãng\">Xiaomi Redmi Go 16GB</a>&nbsp;l&agrave; một trong những chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>&nbsp;gi&aacute; rẻ nhất tới từ Xiaomi nhưng vẫn mang lại cho bạn một trải nghiệm mượt m&agrave; đến &quot;kh&oacute; tin&quot;.</h2>\r\n\r\n<h3>Cấu h&igrave;nh l&agrave; điểm s&aacute;ng của m&aacute;y</h3>\r\n\r\n<p>Trong tầm gi&aacute; như&nbsp;Xiaomi Redmi Go nhiều người sẽ nghĩ tới việc mua một chiếc m&aacute;y để trải nghiệm Android l&agrave; ch&iacute;nh tuy nhi&ecirc;n những g&igrave; m&agrave; m&aacute;y mang lại cho bạn thực sự ngo&agrave;i sức mong đợi.</p>\r\n\r\n<p>M&aacute;y sở hữu con chip&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/qualcomm-cong-bo-loat-chip-snapdragon-moi-785447\" target=\"_blank\" title=\"Tìm hiểu chip Qualcomm Snapdragon 425 4 nhân 64-bit\">Qualcomm Snapdragon 425 4 nh&acirc;n 64-bit</a>&nbsp;&quot;xịn x&ograve;&quot; hiếm c&oacute; trong ph&acirc;n kh&uacute;c c&ugrave;ng 1 GB RAM.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/200603/xiaomi-redmi-go-16gb-black-6.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Android Go 16GB chính hãng | Giao diện máy\" src=\"https://cdn.tgdd.vn/Products/Images/42/200603/xiaomi-redmi-go-16gb-black-6.jpg\" title=\"Xiaomi Android Go 16GB chính hãng | Giao diện máy\" /></a></p>\r\n\r\n<p>Tất nhi&ecirc;n c&aacute;c t&aacute;c vụ thường ng&agrave;y như lướt Facebook hay xem đọc b&aacute;o th&igrave; m&aacute;y đ&aacute;p ứng một c&aacute;ch kh&aacute; đơn giản.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/200603/xiaomi-redmi-go-16gb-game.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Go chính hãng | Trải nghiệm game nhẹ\" src=\"https://cdn.tgdd.vn/Products/Images/42/200603/xiaomi-redmi-go-16gb-game.jpg\" title=\"Xiaomi Redmi Go chính hãng | Trải nghiệm game nhẹ\" /></a></p>\r\n\r\n<p>Thậm ch&iacute; bạn c&oacute; thể sử dụng m&aacute;y để chơi một số tựa game nhẹ đến trung b&igrave;nh tuy nhi&ecirc;n thiết lập đồ họa sẽ chỉ dừng lại ở mức thấp m&agrave; th&ocirc;i.</p>\r\n\r\n<h3>Hệ điều h&agrave;nh nhẹ nh&agrave;ng tối ưu hiệu quả</h3>\r\n\r\n<p>Đ&uacute;ng như t&ecirc;n gọi th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Xiaomi chính hãng\">điện thoại Xiaomi</a>&nbsp;n&agrave;y&nbsp;chạy tr&ecirc;n nền tảng&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/android-go-la-gi-co-gi-khac-voi-phien-ban-android-thuong-khong--1048255\" target=\"_blank\" title=\"Tìm hiểu Android 8 Oreo (Go Edition)\">Android 8 Oreo (Go Edition)</a>&nbsp;được Google tối ưu h&oacute;a d&agrave;nh ri&ecirc;ng cho những chiếc m&aacute;y gi&aacute; rẻ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/200603/xiaomi-redmi-go-16gb-black-2.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Android Go 16GB chính hãng | Giao diện Android Go\" src=\"https://cdn.tgdd.vn/Products/Images/42/200603/xiaomi-redmi-go-16gb-black-2.jpg\" title=\"Xiaomi Android Go 16GB chính hãng | Giao diện Android Go\" /></a></p>\r\n\r\n<p>Phi&ecirc;n bản Android n&agrave;y rất đơn giản để sử dụng, kh&ocirc;ng c&oacute; t&iacute;nh năng thừa th&atilde;i gi&uacute;p m&aacute;y hoạt động kh&aacute; mượt m&agrave; so với c&aacute;c đối thủ trong tầm gi&aacute;.</p>\r\n\r\n<h3>Bộ đ&ocirc;i camera kh&ocirc;ng phải &quot;chỉ cho c&oacute;&quot;</h3>\r\n\r\n<p>Với những chiếc m&aacute;y gi&aacute; rẻ th&igrave; thường camera sẽ kh&ocirc;ng được c&aacute;c h&atilde;ng ch&uacute; trọng nhiều tuy nhi&ecirc;n tr&ecirc;n&nbsp;Xiaomi Redmi Go chất lượng camera kh&aacute; ấn tượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/200603/xiaomi-redmi-go-16gb-black-9.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Android Go 16GB chính hãng | Ảnh chụp từ camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/200603/xiaomi-redmi-go-16gb-black-9.jpg\" title=\"Xiaomi Android Go 16GB chính hãng | Ảnh chụp từ camera\" /></a></p>\r\n\r\n<p>Giao diện camera đơn giản, dễ sử dụng v&agrave; với khả năng lấy n&eacute;t nhanh gi&uacute;p m&aacute;y đ&aacute;p ứng tốt nhu cầu chụp ảnh bất cứ l&uacute;c n&agrave;o của người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/200603/xiaomi-redmi-go-16gb5.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Xiaomi Redmi Go 16GB chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/200603/xiaomi-redmi-go-16gb5.jpg\" title=\"Ảnh chụp từ camera của điện thoại Xiaomi Redmi Go 16GB chính hãng\" /></a></p>\r\n\r\n<p>Camera trước của Redmi Go cũng c&oacute; khả năng cung cấp c&aacute;c h&igrave;nh ảnh tự sướng tốt trong điều kiện &aacute;nh s&aacute;ng đầy đủ.</p>\r\n\r\n<h3>Pin tốt, hỗ trợ cả 4G tốc độ cao</h3>\r\n\r\n<p>Pin cũng l&agrave; một điểm cộng tr&ecirc;n Redmi Go khi m&aacute;y sở hữu vi&ecirc;n pin c&oacute; dung lượng 3000 mAh trong khi m&agrave;n h&igrave;nh chỉ 5 inch c&ugrave;ng độ ph&acirc;n giải HD hứa hẹn sẽ cho thời gian sử dụng kh&aacute; thoải m&aacute;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/200603/xiaomi-redmi-go-16gb-black-3.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Android Go 16GB chính hãng | Cổng sạc trên máy\" src=\"https://cdn.tgdd.vn/Products/Images/42/200603/xiaomi-redmi-go-16gb-black-3.jpg\" title=\"Xiaomi Android Go 16GB chính hãng | Cổng sạc trên máy\" /></a></p>\r\n\r\n<p>Với chế độ tiết kiệm pin t&iacute;ch hợp, m&aacute;y đảm bảo được thời gian sử dụng l&acirc;u d&agrave;i l&ecirc;n tới 2 ng&agrave;y với mức độ b&igrave;nh thường.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/200603/xiaomi-redmi-go-16gb-black-5.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Android Go 16GB chính hãng | Khe SIM thẻ nhớ\" src=\"https://cdn.tgdd.vn/Products/Images/42/200603/xiaomi-redmi-go-16gb-black-5.jpg\" title=\"Xiaomi Android Go 16GB chính hãng | Khe SIM thẻ nhớ\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; m&aacute;y cũng hỗ trợ 2 SIM 2 s&oacute;ng v&agrave; kết nối 4G gi&uacute;p c&aacute;c bạn trẻ c&oacute; thể sử dụng 1 SIM ch&iacute;nh v&agrave; 1 SIM 4G tha hồ online mọi l&uacute;c mọi nơi.</p>\r\n', 1, 0, 'xiaomi-redmi-go-16gb-black-400x400.jpg', 1990000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(6, 'Samsung Galaxy S10+ 128GB Bạc Đa Sắc', 1, 'Điện thoại', '<p><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S10+ chính hãng\" type=\"Tham khảo điện thoại Samsung Galaxy S10+ chính hãng\">Samsung Galaxy S10+</a>&nbsp;đ&atilde; ra mắt v&agrave; thực sự người d&ugrave;ng kh&oacute; c&oacute; thể cường lại sức h&uacute;t kỳ lạ tới từ si&ecirc;u phẩm n&agrave;y của Samsung v&agrave; giờ đ&acirc;y h&atilde;ng sản xuất smartphone tới từ H&agrave;n Quốc tiếp tục tung ra phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus-128gb-bac-da-sac\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\">Samsung Galaxy S10+ 128GB Bạc Đa Sắc</a>&nbsp;thậm ch&iacute; c&ograve;n thu h&uacute;t hơn c&aacute;c phi&ecirc;n bản cũ.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/203207/Slider/vi-vn-samsung-galaxy-s10-plus-128gb-bac-da-sac-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Sạc,Tai nghe,S&aacute;ch hướng dẫn,C&aacute;p OTG,C&acirc;y lấy sim,Ốp lưng,Hộp,C&aacute;p</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S10+ chính hãng\" type=\"Tham khảo điện thoại Samsung Galaxy S10+ chính hãng\">Samsung Galaxy S10+</a>&nbsp;đ&atilde; ra mắt v&agrave; thực sự người d&ugrave;ng kh&oacute; c&oacute; thể cường lại sức h&uacute;t kỳ lạ tới từ si&ecirc;u phẩm n&agrave;y của Samsung v&agrave; giờ đ&acirc;y h&atilde;ng sản xuất smartphone tới từ H&agrave;n Quốc tiếp tục tung ra phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus-128gb-bac-da-sac\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\">Samsung Galaxy S10+ 128GB Bạc Đa Sắc</a>&nbsp;thậm ch&iacute; c&ograve;n thu h&uacute;t hơn c&aacute;c phi&ecirc;n bản cũ.</h2>\r\n\r\n<h3>Phi&ecirc;n bản giới hạn với bộ phụ kiện cao cấp</h3>\r\n\r\n<p>Kh&aacute;c biệt ho&agrave;n to&agrave;n với phi&ecirc;n bản S10+ th&ocirc;ng thường, chiếc điện thoại S10+ Park Hang Seo (Bạc Đa Sắc) n&agrave;y sở hữu bộ phụ kiện độc đ&aacute;o kh&ocirc;ng m&aacute;y n&agrave;o c&oacute; được.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-24.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-24.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\" /></a></p>\r\n\r\n<p>Điểm nhấn của phi&ecirc;n bản n&agrave;y l&agrave; chiếc ốp lưng m&agrave;u đỏ, in h&igrave;nh người thầy đ&aacute;ng k&iacute;nh của đội tuyển Việt Nam.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-26.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-26.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Phụ kiện theo máy\" /></a></p>\r\n\r\n<p>Hơn nữa, m&aacute;y c&ograve;n độc đ&aacute;o ở chỗ &quot;m&agrave;u bạc đa sắc&quot; sẽ cho từng m&agrave;u kh&aacute;c nhau khi chuyển đổi g&oacute;c m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-25.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-25.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\" /></a></p>\r\n\r\n<p>Từng m&agrave;u v&agrave;ng, bạc, hay đỏ,... sẽ xuất hiện gi&uacute;p người d&ugrave;ng th&ecirc;m độc đ&aacute;o v&agrave; kh&ocirc;ng bị &quot;đụng h&agrave;ng&quot;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-5.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-5.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế mặt lưng\" /></a></p>\r\n\r\n<h3>Thiết kế g&acirc;y ấn tượng từ c&aacute;i nh&igrave;n đầu ti&ecirc;n</h3>\r\n\r\n<p>Samsung Galaxy S10+ sở hữu m&agrave;n h&igrave;nh Infinity-O tr&agrave;n viền với m&agrave;u sắc v&ocirc; c&ugrave;ng sống động chắc chắn sẽ l&agrave;m tan chảy bất k&igrave; con tim n&agrave;o.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-27.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-27.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\" /></a></p>\r\n\r\n<p>Nhờ thiết kế đục lỗ, Galaxy S10+ c&oacute; m&agrave;n h&igrave;nh tr&agrave;n viền thực sự, nh&igrave;n đ&atilde; mắt v&agrave; tỷ lệ m&agrave;n h&igrave;nh/mặt trước của m&aacute;y l&agrave; 89%, cao hơn nhiều so với thế hệ Galaxy S9 cũ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-1.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/man-hinh-dynamic-amoled-la-gi-no-co-cong-dung-gi-noi-bat-1153075\" target=\"_blank\" title=\"Tìm hiểu về tấm nền Dynamic AMOLED\" type=\"Tìm hiểu về tấm nền Dynamic AMOLED\">Dynamic AMOLED</a>, tỷ lệ m&agrave;n h&igrave;nh 19.5:9, viền bezel cực mỏng, c&oacute; thể n&oacute;i l&agrave; mỏng nhất từ trước đến nay từng thấy, trải nghiệm h&igrave;nh ảnh tr&ecirc;n S10+ l&agrave; cực kỳ đẳng cấp v&agrave; đ&aacute;ng gi&aacute;.</p>\r\n\r\n<p>Một n&acirc;ng cấp tuy nhỏ nhưng kh&aacute; quan trọng kh&aacute;c l&agrave; m&agrave;n h&igrave;nh tr&ecirc;n Galaxy S10+ hỗ trợ chuẩn h&igrave;nh ảnh ti&ecirc;n tiến nhất hiện nay&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/hdr10-la-gi-vi-sao-no-lam-cho-man-hinh-galaxy-s10-dep-hon--1151189\" target=\"_blank\" title=\"Tìm hiểu về công nghệ HDR10+\" type=\"Tìm hiểu về công nghệ HDR10+\">c&ocirc;ng nghệ HDR10+</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-7.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Thiết kế màn hình\" /></a></p>\r\n\r\n<p>Điều n&agrave;y sẽ gi&uacute;p m&agrave;u sắc t&aacute;i hiện sống động hơn v&agrave; đặc biệt độ s&acirc;u của m&agrave;u đen nh&igrave;n đ&atilde; hơn, sướng mắt hơn.</p>\r\n\r\n<h3>Camera được n&acirc;ng cấp mạnh mẽ</h3>\r\n\r\n<p>Samsung đ&atilde; &quot;bắt trend&quot; rất nhanh ở tr&agrave;o lưu 3 camera khi Galaxy S10+ được trang bị th&ecirc;m một camera g&oacute;c rộng si&ecirc;u rộng nữa b&ecirc;n cạnh camera tele v&agrave; camera ti&ecirc;u chuẩn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Giao diện camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-8.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Giao diện camera\" /></a></p>\r\n\r\n<p>Camera g&oacute;c si&ecirc;u rộng 16 MP tr&ecirc;n S10+ c&oacute; g&oacute;c chụp tới 123 độ, ti&ecirc;u cự 12mm, khẩu độ f/2.2, l&agrave; một trong những camera g&oacute;c rộng nhất hiện nay tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-13.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Ảnh chụp góc rộng\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-13.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Ảnh chụp góc rộng\" /></a></p>\r\n\r\n<p>Với cụm ba m&aacute;y ảnh, Nếu bạn muốn lấy nhiều cảnh vật hơn, bạn c&oacute; thể chuyển qua ống k&iacute;nh si&ecirc;u rộng, nếu bạn muốn chụp ch&acirc;n dung, bạn chuyển qua ống k&iacute;nh tele.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd9.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd9.jpg\" title=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\" /></a></p>\r\n\r\n<p>Thậm ch&iacute; nếu bạn muốn điều khiển to&agrave;n bộ th&ocirc;ng số m&aacute;y ảnh, bạn c&oacute; thể chọn chế độ chuy&ecirc;n nghiệp tr&ecirc;n phần mềm chụp ảnh. S10+ c&oacute; thể phơi s&aacute;ng tối đa 10 gi&acirc;y, đủ để bạn c&oacute; được những cảnh chụp đ&ecirc;m hay phong cảnh huyền b&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd10.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd10.jpg\" title=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\" /></a></p>\r\n\r\n<p>Với camera selfie, S10+ c&oacute; đến 2 camera gồm 1 camera ch&iacute;nh 10 MP, khẩu độ f/1.9, ti&ecirc;u cự 26mm, điểm ảnh 1.22&micro;m, hỗ trợ lấy n&eacute;t Dual Pixel PDAF.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; l&agrave; 1 camera 8 MP, khẩu độ f/2.2, ti&ecirc;u cự 22mm (wide), điểm ảnh 1.12&micro;m, chuy&ecirc;n để thu độ s&acirc;u phục vụ việc x&oacute;a ph&ocirc;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd11.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd11.jpg\" title=\"Ảnh chụp từ camera của điện thoại Samsung Galaxy S10+ 128GB Bạc Đa Sắc chính hãng\" /></a></p>\r\n\r\n<p>C&aacute;c chế độ l&agrave;m đẹp hay x&oacute;a ph&ocirc;ng đều l&agrave;m việc hiệu quả, tạo ra những bức ảnh selfie &quot;lung linh&quot; hơn nhưng kh&ocirc;ng c&agrave; mặt qu&aacute; đ&agrave; như trước nữa.</p>\r\n\r\n<h3>Hiệu năng h&agrave;ng đầu thế giới smartphone</h3>\r\n\r\n<p>Cung cấp sức mạnh cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Samsung chính hãng\">Samsung</a>&nbsp;Galaxy S10+ 128GB Bạc Đa Sắc l&agrave; con chip h&agrave;ng đầu tới từ Samsung Exynos 9820 8 nh&acirc;n 64-bit&nbsp;c&ugrave;ng 8 GB RAM v&agrave; 128 GB bộ nhớ trong.</p>\r\n\r\n<p>Trong sử dụng thực tế, kh&ocirc;ng bất ngờ khi hiệu năng của S10+ đ&aacute;p ứng rất nhanh v&agrave; mượt m&agrave;, c&aacute;c ứng dụng mở gần như ngay tức th&igrave;, cuộn trang v&agrave; c&aacute;c hiệu ứng chuyển động tr&ecirc;n giao diện phần mềm diễn ra trơn tru.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-28.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Trải nghiệm chơi game\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-28.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Trải nghiệm chơi game\" /></a></p>\r\n\r\n<p>Nếu kết hợp với đế Galaxy DeX, thậm ch&iacute; bạn ho&agrave;n to&agrave;n c&oacute; thể để laptop ở nh&agrave; m&agrave; vẫn c&oacute; thể đảm bảo c&ocirc;ng việc văn ph&ograve;ng chạy trơn tru như b&igrave;nh thường.</p>\r\n\r\n<p>Ngo&agrave;i ra m&aacute;y c&ograve;n sở hữu chuẩn kết nối Wi-Fi 6 ho&agrave;n to&agrave;n mới đảm bảo chiếc smartphone của bạn c&oacute; thể kết nối internet nhanh nhất c&oacute; thể.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-14.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Trên tay và trải nghiệm\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-14.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Trên tay và trải nghiệm\" /></a></p>\r\n\r\n<p>Thời lượng pin 4100mAh sẽ đủ để bạn d&ugrave;ng từ s&aacute;ng đến tối m&agrave; kh&ocirc;ng cần phải sạc giữa chừng, thậm ch&iacute; c&ograve;n dư để c&oacute; thể sạc kh&ocirc;ng d&acirc;y cho c&aacute;c thiết bị kh&aacute;c khi cần.</p>\r\n\r\n<h3>Nhiều trang bị cao cấp kh&aacute;c nữa</h3>\r\n\r\n<p>Samsung l&agrave; một h&atilde;ng smartphone hiếm hoi vẫn giữ được những chất ri&ecirc;ng cho chiếc smartphone của m&igrave;nh.</p>\r\n\r\n<p>Bạn sẽ vẫn c&oacute; jack cắm tai nghe 3.5 mm để c&oacute; thể sử dụng với c&aacute;c tai nghe phổ biến hiện nay m&agrave; kh&ocirc;ng cần th&ocirc;ng qua jack chuyển.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-3.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Jack 3.5 mm\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-3.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Jack 3.5 mm\" /></a></p>\r\n\r\n<p>Bạn vẫn sẽ c&oacute; ti&ecirc;u chuẩn chống nước v&agrave; bụi bẩn cao cấp nhất hiện nay IP68 gi&uacute;p m&aacute;y c&oacute; thể chịu đựng được trong c&aacute;c m&ocirc;i trường khắc nghiệt.</p>\r\n\r\n<p>V&agrave; k&egrave;m theo đ&oacute; l&agrave; một bộ giải m&atilde; &acirc;m thanh cực kỳ đ&aacute;ng gi&aacute; hỗ trợ chuẩn Dolby Atmos/AKG để phục vụ c&aacute;c t&iacute;n đồ y&ecirc;u &acirc;m nhạc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-9.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Vân tay siêu âm\" src=\"https://cdn.tgdd.vn/Products/Images/42/203207/samsung-galaxy-s10-plus-128gb-bac-da-sac-tgdd-9.jpg\" title=\"Samsung Galaxy S10+ 128GB Bạc Đa Sắc | Vân tay siêu âm\" /></a></p>\r\n\r\n<p>Samsung Galaxy S10+ 128GB Bạc Đa Sắc cũng l&agrave; một trong những smartphone đầu ti&ecirc;n sở hữu c&ocirc;ng nghệ cảm biến&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-quet-van-tay-song-sieu-am-la-gi-912419\" target=\"_blank\" title=\"Tìm hiểu công nghệ vân tay siêu âm\" type=\"Tìm hiểu công nghệ vân tay siêu âm\">v&acirc;n tay si&ecirc;u &acirc;m</a>&nbsp;b&ecirc;n trong m&agrave;n h&igrave;nh với độ ch&iacute;nh x&aacute;c rất cao.</p>\r\n', 0, 0, '1563866383samsung-galaxy-s10-plus-128gb-bac-da-sac-600x600.jpg', 23990000),
(7, 'iPhone Xs Max 64GB', 1, 'Điện thoại', '<p>Ho&agrave;n to&agrave;n xứng đ&aacute;ng với những g&igrave; được mong chờ, phi&ecirc;n bản cao cấp nhất&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-xs-max\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone XS Max\" type=\"Chi tiết điện thoại iPhone XS Max\">iPhone Xs Max</a>&nbsp;của Apple năm nay nổi bật với chip A12 Bionic mạnh mẽ, m&agrave;n h&igrave;nh rộng đến 6.5 inch, c&ugrave;ng camera k&eacute;p tr&iacute; tuệ nh&acirc;n tạo v&agrave; Face ID được n&acirc;ng cấp</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/190321/Slider/-iphone-xs-max-thiet-ke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Sạc, Tai nghe, S&aacute;ch hướng dẫn, C&aacute;p, C&acirc;y lấy sim</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Ho&agrave;n to&agrave;n xứng đ&aacute;ng với những g&igrave; được mong chờ, phi&ecirc;n bản cao cấp nhất&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-xs-max\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone XS Max\" type=\"Chi tiết điện thoại iPhone XS Max\">iPhone Xs Max</a>&nbsp;của Apple năm nay nổi bật với chip A12 Bionic mạnh mẽ, m&agrave;n h&igrave;nh rộng đến 6.5 inch, c&ugrave;ng camera k&eacute;p tr&iacute; tuệ nh&acirc;n tạo v&agrave; Face ID được n&acirc;ng cấp.</h2>\r\n\r\n<h3>Thiết kế cao cấp với viền th&eacute;p kh&ocirc;ng gỉ v&agrave; mặt k&iacute;nh cường lực</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">Điện thoại</a>&nbsp;iPhone Xs Max sở hữu lối thiết kế v&ocirc; c&ugrave;ng đẹp mắt với những đường cong mềm mại được thừa hưởng từ chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại iPhone\" type=\"Tham khảo các dòng điện thoại iPhone\">iPhone</a>&nbsp;đời trước đ&oacute;.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-5.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-5.jpg\" title=\"Thiết kế điện thoại iPhone Xs Max chính hãng\" /></a></h3>\r\n\r\n<p>Tuy nhi&ecirc;n,&nbsp;iPhone Xs Max lại c&oacute; một th&acirc;n h&igrave;nh to bản ngang bằng với k&iacute;ch thước d&ograve;ng Plus nhưng chứa đựng một m&agrave;n h&igrave;nh rộng lớn l&ecirc;n đến 6.5 inch.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-10.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-10.jpg\" title=\"Màn hình điện thoại iPhone Xs Max chính hãng\" /></a></p>\r\n\r\n<p>Nhờ thế m&agrave; bạn sẽ c&oacute; một kh&ocirc;ng gian trải nghiệm v&ocirc; c&ugrave;ng rộng r&atilde;i để thưởng thức những bộ phim chất lượng cao được trở n&ecirc;n trọn vẹn.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-1.jpg\" title=\"Thiết kế điện thoại iPhone Xs Max chính hãng\" /></a></h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/tin-tuc/man-hinh-oled-la-gi-smartphone-nao-co-man-hinh-oled-1048951\" target=\"_blank\" title=\"Tìm hiểu về màn hình OLED\" type=\"Tìm hiểu về màn hình OLED\">M&agrave;n h&igrave;nh OLED</a>&nbsp;chất lượng cao rộng 6.5 inch đầu ti&ecirc;n của Apple</h3>\r\n\r\n<p>Với c&ocirc;ng nghệ Super Retina kết hợp tấm nền OLED tr&ecirc;n&nbsp;iPhone Xs Max đem lại dải m&agrave;u sắc cực k&igrave; sống động v&agrave; sắc n&eacute;t đến từng chi tiết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-8.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-8.jpg\" title=\"Màn hình điện thoại iPhone Xs Max chính hãng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Apple c&ograve;n t&iacute;ch hợp th&ecirc;m c&ocirc;ng nghệ HDR10 gi&uacute;p chất lượng h&igrave;nh ảnh được n&acirc;ng cao v&agrave; mượt m&agrave; hơn đ&aacute;ng kể.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-4.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-4.jpg\" title=\"Màn hình điện thoại iPhone Xs Max chính hãng\" /></a></p>\r\n\r\n<p>Việc sở hữu m&agrave;n h&igrave;nh lớn đem đến cho bạn kh&aacute; nhiều tiện &iacute;ch như dễ d&agrave;ng chỉnh sửa ảnh, xem phim, lướt web nhưng sẽ kh&oacute; khăn hơn trong việc di chuyển.</p>\r\n\r\n<h3>Hiệu năng đỉnh của đỉnh với&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-chip-apple-a12-bionic-ben-trong-iphone-xs-xs-max-1116982\" target=\"_blank\" title=\"Tìm hiểu chip Apple A12\" type=\"Tìm hiểu chip Apple A12\">chip Apple A12</a></h3>\r\n\r\n<p>L&agrave; một flagship cao cấp,&nbsp;iPhone Xs Max được Apple trang bị cho con chip mới toanh h&agrave;ng đầu của h&atilde;ng mang t&ecirc;n Apple A12 bionic.</p>\r\n\r\n<p>Chip A12 bionic được x&acirc;y dựng tr&ecirc;n tiến tr&igrave;nh 7nm đầu ti&ecirc;n m&agrave; h&atilde;ng sản xuất với 6 nh&acirc;n đ&aacute;p ứng vượt trội trong việc xử l&yacute; c&aacute;c t&aacute;c vụ v&agrave; khả năng tiết kiệm năng lượng tối ưu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-12.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-12.jpg\" title=\"Chơi game trên điện thoại iPhone Xs Max chính hãng\" /></a></p>\r\n\r\n<p>Hơn nữa,&nbsp;iPhone Xs Max c&ograve;n c&oacute; bộ xử l&yacute; đồ họa mạnh mẽ được Apple thiết kế ri&ecirc;ng gi&uacute;p hiệu năng được cải thiện rất lớn về mặt đồ họa của m&aacute;y.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-6.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-6.jpg\" title=\"Trải nghiệm điện thoại iPhone Xs Max chính hãng\" /></a></h2>\r\n\r\n<p>Chưa dừng lại ở đ&oacute;, m&aacute;y c&ograve;n được t&iacute;ch hợp tr&iacute; th&ocirc;ng minh nh&acirc;n tạo gi&uacute;p phần cứng tối ưu hiệu suất, nhờ đ&oacute; m&agrave; c&aacute;c thao t&aacute;c của bạn được xử l&yacute; một c&aacute;ch nhanh ch&oacute;ng hơn.</p>\r\n\r\n<h3>Ấn tượng về&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/camera-kep-la-gi-co-phai-la-xu-huong-tren-smartphone-2017-953888\" target=\"_blank\" title=\"Tìm hiểu về cẩm kép trên điện thoại\" type=\"Tìm hiểu về cẩm kép trên điện thoại\">camera k&eacute;p</a>&nbsp;t&iacute;ch hợp tr&iacute; tuệ nh&acirc;n tạo</h3>\r\n\r\n<p>D&ugrave; kh&ocirc;ng sở hữu th&ocirc;ng số camera khủng nhưng&nbsp;iPhone XS Max lu&ocirc;n cho thấy sự đẳng cấp của m&igrave;nh về khả năng nhiếp ảnh với cụm camera k&eacute;p c&oacute; c&ugrave;ng độ ph&acirc;n giải 12 MP.</p>\r\n\r\n<p>M&aacute;y được trang bị hệ thống xử l&yacute; h&igrave;nh ảnh chất lượng&nbsp;c&acirc;n bằng s&aacute;ng, giảm nhiễu, tăng cường độ phơi s&aacute;ng, m&agrave;u da sao cho ph&ugrave; hợp v&agrave; tự nhi&ecirc;n nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-3.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-3.jpg\" title=\"Camera sau điện thoại iPhone Xs Max chính hãng\" /></a></p>\r\n\r\n<p>C&ugrave;ng với đ&oacute; l&agrave; khả năng điều chỉnh khẩu độ ấn tượng từ f/1.4 đến f/16 ngay tr&ecirc;n bức ảnh sau khi chụp ảnh với chế độ ch&acirc;n dung.</p>\r\n\r\n<p>Chưa dừng lại ở đ&oacute;, m&aacute;y c&ograve;n được t&iacute;ch hợp th&ecirc;m c&ocirc;ng nghệ&nbsp;Smart HDR gi&uacute;p t&aacute;i tạo h&igrave;nh ảnh v&agrave; cho ra một bức h&igrave;nh với độ s&aacute;ng tốt nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-2.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-2.jpg\" title=\"Camera sau điện thoại iPhone Xs Max chính hãng\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;iPhone XS Max c&ograve;n được hỗ trợ bởi tr&iacute; th&ocirc;ng minh nh&acirc;n tạo đem đến khả năng tự động điều chỉnh m&agrave;u sắc, độ s&aacute;ng v&agrave; độ tương phản sao cho ph&ugrave; hợp với từng vật thể kh&aacute;c nhau.</p>\r\n\r\n<h3>Một số t&iacute;nh năng cao cấp được cập nhật v&agrave; bổ sung</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/hoi-dap/face-id-la-gi-va-cach-hoat-dong-cua-face-id-1021871\" target=\"_blank\" title=\"Tìm hiểu về tính năng Face ID trên điện thoại iPhone\" type=\"Tìm hiểu về tính năng Face ID trên điện thoại iPhone\">Face ID</a>&nbsp;đ&atilde; được Apple cải tiến về khả năng bảo mật cũng như cho tốc độ mở kh&oacute;a được nhanh hơn nhờ c&aacute;c thuật to&aacute;n mới..</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-7.jpg\" onclick=\"return false;\"><img alt=\"Mở khoá điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-7.jpg\" title=\"Mở khoá điện thoại iPhone Xs Max chính hãng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/giai-thich-su-khac-nhau-giua-ar-emoji-cua-samsung-va-animoji-cua-apple-1070052\" target=\"_blank\" title=\"Tìm hiểu về Animoji trên điện thoại Apple\" type=\"Tìm hiểu về Animoji trên điện thoại Apple\">Animoji</a>&nbsp;cũng được cập nhật th&ecirc;m một số biểu tượng mới tr&ocirc;ng kh&aacute; ngộ nghĩnh v&agrave; đ&aacute;ng y&ecirc;u.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-11.jpg\" onclick=\"return false;\"><img alt=\"Animojis trên điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-11.jpg\" title=\"Animojis trên điện thoại iPhone Xs Max chính hãng\" /></a></p>\r\n\r\n<p>Với&nbsp;hệ thống camera&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/mot-su-that-bat-ngo-ve-camera-truedepth-tren-iphone-x-1021510\" target=\"_blank\" title=\"Tìm hiểu về công nghệ TrueDepth\" type=\"Tìm hiểu về công nghệ TrueDepth\">TrueDepth</a>&nbsp;nay bạn c&oacute; thể tự tạo cho bản th&acirc;n những bức ảnh ấn tượng với c&ocirc;ng nghệ thực tế ảo tăng cường AR.</p>\r\n\r\n<p>Ngo&agrave;i ra, hệ thống &acirc;m thanh 2 chiều tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/sieu-pham-moi\" target=\"_blank\" title=\"Đặt gạch siêu phẩm mới iPhone 2018 \" type=\"Đặt gạch siêu phẩm mới iPhone 2018 \">si&ecirc;u phẩm mới</a>&nbsp;được Apple tinh chỉnh lại cho dải &acirc;m rộng, &acirc;m thanh sống động hơn hay khả năng kh&aacute;ng nước v&agrave; bụi cũng được n&acirc;ng cấp l&ecirc;n th&agrave;nh IP 68 đảm bảo an to&agrave;n hơn cho m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190321/iphone-xs-max-gold-9.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại iPhone Xs Max chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-9.jpg\" title=\"Đánh giá điện thoại iPhone Xs Max chính hãng\" /></a></p>\r\n', 0, 0, 'iphone-xs-max-gray-400x400.jpg', 29990000),
(8, 'iPad Wifi 32GB (2018)', 2, 'Máy tính bảng', '<p><strong><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-6th-wifi-32gb\" target=\"_blank\" title=\"iPad 6th Wifi 32GB\">iPad 6th Wifi 32GB</a>&nbsp;với nhiều n&acirc;ng cấp về phần cứng, đặc biệt hơn gi&aacute; của sản phẩm n&agrave;y được định h&igrave;nh ở ph&acirc;n kh&uacute;c gi&aacute; rẻ, sinh vi&ecirc;n&nbsp;sẽ l&agrave; sự lựa chọn &ldquo;kh&ocirc;ng qu&aacute; xa tầm tay&rdquo; người d&ugrave;ng.</strong></p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/163645/Slider/-ipad-6th-wifi-32gb-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Adapter, S&aacute;ch hướng dẫn, C&aacute;p Lightning, Hộp m&aacute;y</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-6th-wifi-32gb\" target=\"_blank\" title=\"iPad 6th Wifi 32GB\">iPad 6th Wifi 32GB</a>&nbsp;với nhiều n&acirc;ng cấp về phần cứng, đặc biệt hơn gi&aacute; của sản phẩm n&agrave;y được định h&igrave;nh ở ph&acirc;n kh&uacute;c gi&aacute; rẻ, sinh vi&ecirc;n&nbsp;sẽ l&agrave; sự lựa chọn &ldquo;kh&ocirc;ng qu&aacute; xa tầm tay&rdquo; người d&ugrave;ng.</strong></h2>\r\n\r\n<h3><strong>Thiết kế ho&agrave;n mỹ</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Sản phẩm của Apple\">Apple</a>&nbsp;vẫn giữ ng&ocirc;n ngữ thi&ecirc;́t k&ecirc;́ từ xa xưa tới nay, n&ecirc;n phi&ecirc;n bản iPad 6th cũng kh&ocirc;ng có gì khác lắm với những người ti&ecirc;̀n nhi&ecirc;̣m còn lại.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-6th-wifi-32gb-22.jpg\" onclick=\"return false;\"><img alt=\"Không có nhiều sự khác biết nhưng vẫn rất sang trọng và tinh tế\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-6th-wifi-32gb-22.jpg\" title=\"Không có nhiều sự khác biết nhưng vẫn rất sang trọng và tinh tế\" /></a></p>\r\n\r\n<p><em>Kh&ocirc;ng c&oacute; nhiều sự kh&aacute;c biệt nhưng vẫn rất sang trọng v&agrave; tinh tế</em></p>\r\n\r\n<p>C&aacute;c chi tiết được l&agrave;m rất tỉ mỉ, độ mỏng ấn tượng, mặt lưng được l&agrave;m cong để hạn chế bị cấn tay khi cầm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-wifi-32gb-2018-mau-xam-2-2.jpg\" onclick=\"return false;\"><img alt=\"Không có nhiều sự khác biết nhưng vẫn rất sang trọng và tinh tế\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-wifi-32gb-2018-mau-xam-2-2.jpg\" title=\"Không có nhiều sự khác biết nhưng vẫn rất sang trọng và tinh tế\" /></a></p>\r\n\r\n<p><em>C&aacute;c g&oacute;c cạnh bo cong mềm mại</em></p>\r\n\r\n<p>Ph&iacute;m Home của m&aacute;y vẫn được t&iacute;ch hợp khả năng nhận diện v&acirc;n tay gi&uacute;p bạn mở kh&oacute;a thiết bị nhanh ch&oacute;ng.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh sắc n&eacute;t</strong></h3>\r\n\r\n<p>Với k&iacute;ch thước 9.7 inch kết hợp với tấm nền&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cac-cong-nghe-hien-thi-tren-man-hinh-laptop-597377#ledbacklit\" target=\"_blank\" title=\"Tìm hiểu LED backlit LCD\">LED backlit LCD</a>&nbsp;độ ph&acirc;n giải&nbsp;1536 x 2048 pixels&nbsp;iPad 6th vẫn mang lại cho bạn khả năng hiển thị h&igrave;nh ảnh trung thực c&ugrave;ng g&oacute;c nh&igrave;n rộng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-6th-wifi-32gb-11.jpg\" onclick=\"return false;\"><img alt=\"Màn hình tốt, đáp ứng mọi nhu cầu\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-6th-wifi-32gb-11.jpg\" title=\"Màn hình tốt, đáp ứng mọi nhu cầu\" /></a></p>\r\n\r\n<p><em>M&agrave;n h&igrave;nh tốt, đ&aacute;p ứng mọi nhu cầu</em></p>\r\n\r\n<p>M&agrave;u sắc tr&ecirc;n m&agrave;n h&igrave;nh tươi s&aacute;ng, hiển thị sắc n&eacute;t, th&iacute;ch hợp để đọc b&aacute;o, xem phim cũng như phục vụ những t&iacute;nh năng giải tr&iacute; kh&aacute;c của người d&ugrave;ng.</p>\r\n\r\n<h3><strong>Hiệu năng mượt m&agrave;</strong></h3>\r\n\r\n<p>Kh&ocirc;ng c&oacute; g&igrave; phải ph&agrave;n n&agrave;n về hiệu năng của&nbsp;iPad 6th.&nbsp;Chiếc m&aacute;y t&iacute;nh bảng n&agrave;y hoạt động rất mượt m&agrave; v&agrave; xử l&yacute; nhanh ch&oacute;ng tất cả c&aacute;c t&aacute;c vụ, ứng dụng tải từ App Store.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-6th-wifi-32gb-14.jpg\" onclick=\"return false;\"><img alt=\"Giải trí hay làm việc với những chiếc iPad chưa bao giờ khiến bạn thất vọng\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-6th-wifi-32gb-14.jpg\" title=\"Giải trí hay làm việc với những chiếc iPad chưa bao giờ khiến bạn thất vọng\" /></a></p>\r\n\r\n<p><em>Giải tr&iacute; hay l&agrave;m việc với những chiếc iPad chưa bao giờ khiến bạn thất vọng</em></p>\r\n\r\n<p>Con chip&nbsp;Apple A10 Fusion kết hợp với 2 GB RAM sẽ khiến hiện tượng giật lag &iacute;t khi xảy ra khi sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-6th-wifi-32gb-24.jpg\" onclick=\"return false;\"><img alt=\"AR sẽ là công cụ đắc lực cho việc học tập của học sinh sinh viên\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-6th-wifi-32gb-24.jpg\" title=\"AR sẽ là công cụ đắc lực cho việc học tập của học sinh sinh viên\" /></a></p>\r\n\r\n<p><em>AR sẽ l&agrave; c&ocirc;ng cụ đắc lực cho việc học tập của học sinh sinh vi&ecirc;n</em></p>\r\n\r\n<p>iPad 9.7 inch cũng được cung cấp đầy đủ c&aacute;c c&ocirc;ng cụ AR cần thiết để người d&ugrave;ng, đặc biệt l&agrave; học sinh, sinh vi&ecirc;n c&oacute; thể thỏa sức s&aacute;ng tạo.</p>\r\n\r\n<h3><strong>Các k&ecirc;́t n&ocirc;́i kh&ocirc;ng d&acirc;y</strong></h3>\r\n\r\n<p>iPad 6th mới cũng sẽ h&ocirc;̃ trợ Apple Pencil gi&uacute;p bạn c&oacute; thể thoải m&aacute;i s&aacute;ng tạo nghệ thuật.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-6th-wifi-32gb-12.jpg\" onclick=\"return false;\"><img alt=\"Bạn có thể kết nối với chuột và bàn phím để biến máy thành một chiếc laptop thực thụ\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-6th-wifi-32gb-12.jpg\" title=\"Bạn có thể kết nối với chuột và bàn phím để biến máy thành một chiếc laptop thực thụ\" /></a></p>\r\n\r\n<p><em>Bạn c&oacute; thể kết nối với chuột v&agrave; b&agrave;n ph&iacute;m để biến m&aacute;y th&agrave;nh một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop các thương hiệu\">laptop</a>&nbsp;thực thụ</em></p>\r\n\r\n<h3><strong>Dung lượng pin lớn</strong></h3>\r\n\r\n<p>M&aacute;y c&oacute; thể hoạt động li&ecirc;n tục 10 tiếng gi&uacute;p bạn thoải m&aacute;i l&agrave;m việc hay giải tr&iacute; cả ng&agrave;y d&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-6th-wifi-32gb-23-1.jpg\" onclick=\"return false;\"><img alt=\"Pin là lợi thế lớn của iPad 6th\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-6th-wifi-32gb-23-1.jpg\" title=\"Pin là lợi thế lớn của iPad 6th\" /></a></p>\r\n\r\n<p><em>Pin l&agrave; lợi thế lớn của iPad 6th</em></p>\r\n\r\n<p>Đ&aacute;nh đổi th&igrave; bạn cũng phải mất một thời gian kh&aacute; d&agrave;i để c&oacute; thể sạc đầy pin thiết bị.</p>\r\n\r\n<h3><strong>Hỗ trợ kết nối với&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/apple-pencil\" target=\"_blank\" title=\"Tham khảo giá bút cảm ứng Apple Pencil\" type=\"Tham khảo giá bút cảm ứng Apple Pencil\">Apple Pencil</a></strong></h3>\r\n\r\n<p>Nếu cảm thấy với c&aacute;c thao t&aacute;c cảm ứng bằng tay chưa thể đ&aacute;p ứng bạn th&igrave; h&atilde;y thử trải nghiệm mới với c&acirc;y b&uacute;t Apple Pencil thần th&aacute;nh&nbsp;<strong>(b&uacute;t cảm ứng l&agrave; sản phẩm mua ri&ecirc;ng, kh&ocirc;ng k&egrave;m với m&aacute;y).</strong></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163645/ipad-wifi-32gb-2018-with-apple-pencil-tgdd.jpg\" onclick=\"return false;\"><img alt=\"iPad wifi 32GB 2018 hỗ trợ kết nối với Apple Pencil\" src=\"https://cdn.tgdd.vn/Products/Images/522/163645/ipad-wifi-32gb-2018-with-apple-pencil-tgdd.jpg\" title=\"iPad wifi 32GB 2018 hỗ trợ kết nối với Apple Pencil\" /></a></p>\r\n\r\n<p>C&acirc;y b&uacute;t cảm ứng n&agrave;y sẽ gi&uacute;p bạn c&oacute; những thao t&aacute;c nhanh hơn th&ocirc;ng qua độ lực mạnh, nhẹ hay vẽ vời, ghi ch&uacute; v&agrave; s&aacute;ng tạo.</p>\r\n', 0, 0, 'ipad-wifi-32gb-2018-thumb-33397-123-123321-600x600-400x400.jpg', 7990000),
(9, 'iPad Wifi 128 GB (2018)', 2, 'Máy tính bảng', '<p><strong><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-6th-wifi-128-gb\" target=\"_blank\" title=\"iPad 6th Wifi 128 GB\">iPad 6th Wifi 128 GB</a>&nbsp;l&agrave; chiếc iPad mới được Apple ra mắt với mức gi&aacute; phải chăng hơn hứa hẹn sẽ mang c&aacute;c thiết bị iPad đến được với đ&ocirc;ng đảo người d&ugrave;ng hơn.</strong></p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/163791/Slider/-ipad-6th-wifi-128-gb-1-copy.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Adapter, S&aacute;ch hướng dẫn, C&aacute;p Lightning, Hộp m&aacute;y</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-6th-wifi-128-gb\" target=\"_blank\" title=\"iPad 6th Wifi 128 GB\">iPad 6th Wifi 128 GB</a>&nbsp;l&agrave; chiếc iPad mới được Apple ra mắt với mức gi&aacute; phải chăng hơn hứa hẹn sẽ mang c&aacute;c thiết bị iPad đến được với đ&ocirc;ng đảo người d&ugrave;ng hơn.</strong></h2>\r\n\r\n<h3><strong>Thiết kế chuẩn mực</strong></h3>\r\n\r\n<p>iPad 6th vẫn sử dụng chất liệu nh&ocirc;m nguy&ecirc;n khối, được ho&agrave;n thiện tỉ mỉ cho cảm gi&aacute;c cầm chắc chắn v&agrave; sang trọng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163791/ipad-6th-wifi-128-gb-13.jpg\" onclick=\"return false;\"><img alt=\"Vẫn là kiểu thiết kế quen thuộc của Apple\" src=\"https://cdn.tgdd.vn/Products/Images/522/163791/ipad-6th-wifi-128-gb-13.jpg\" title=\"Vẫn là kiểu thiết kế quen thuộc của Apple\" /></a></p>\r\n\r\n<p><em>Vẫn l&agrave; kiểu thiết kế quen thuộc của Apple</em></p>\r\n\r\n<p>M&aacute;y cũng như t&iacute;ch hợp cảm biến v&acirc;n tay v&agrave;o n&uacute;t home ở mặt trước gi&uacute;p bạn mở kh&oacute;a thiết bị dễ d&agrave;ng.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh hiển thị đẹp</strong></h3>\r\n\r\n<p>Ch&uacute;ng ta vẫn sẽ c&oacute; một m&agrave;n h&igrave;nh k&iacute;ch thước 9.7 inch kết hợp với tấm nền&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cac-cong-nghe-hien-thi-tren-man-hinh-laptop-597377#ledbacklit\" target=\"_blank\" title=\"Tìm hiểu LED backlit LCD\">LED backlit LCD</a>&nbsp;độ ph&acirc;n giải&nbsp;1536 x 2048 pixels cho khả năng hiển thị h&igrave;nh ảnh trung thực, sắc n&eacute;t c&ugrave;ng g&oacute;c nh&igrave;n rộng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163791/ipad-6th-wifi-128-gb-17.jpg\" onclick=\"return false;\"><img alt=\"Không gian lớn cho làm việc và giải trí\" src=\"https://cdn.tgdd.vn/Products/Images/522/163791/ipad-6th-wifi-128-gb-17.jpg\" title=\"Không gian lớn cho làm việc và giải trí\" /></a></p>\r\n\r\n<p><em>Kh&ocirc;ng gian lớn cho l&agrave;m việc v&agrave; giải tr&iacute;</em></p>\r\n\r\n<p>Đ&aacute;ng tiếc l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Máy tính bảng Apple\">Apple</a>&nbsp;đ&atilde; loại bỏ đi c&ocirc;ng nghệ TrueTone gi&uacute;p m&aacute;y c&oacute; thể tự động điều chỉnh m&agrave;u sắc để ph&ugrave; hợp với điều kiện m&ocirc;i trường.</p>\r\n\r\n<h3><strong>Hiệu năng vẫn rất ấn tượng</strong></h3>\r\n\r\n<p>iPad 9.7 (2018) được trang bị vi xử l&yacute; Apple A10 (c&oacute; tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Điện thoại iPhone\">iPhone</a>&nbsp;7/ 7 Plus) cải thiện đ&aacute;ng kể hiệu suất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163791/ipad-6th-wifi-128-gb-15.jpg\" onclick=\"return false;\"><img alt=\"Giật lag là điều bạn ít khi gặp phải với iPad 6th\" src=\"https://cdn.tgdd.vn/Products/Images/522/163791/ipad-6th-wifi-128-gb-15.jpg\" title=\"Giật lag là điều bạn ít khi gặp phải với iPad 6th\" /></a></p>\r\n\r\n<p><em>Giật lag l&agrave; điều bạn &iacute;t khi gặp phải với iPad 6th</em></p>\r\n\r\n<p>Điểm đ&aacute;ng ch&uacute; &yacute; nhất được Apple nhấn mạnh nhiều lần tr&ecirc;n sản phẩm n&agrave;y ch&iacute;nh l&agrave; khả năng hỗ trợ v&agrave; tương t&aacute;c mượt m&agrave; với c&ocirc;ng nghệ thực tế ảo tăng cường (AR).</p>\r\n\r\n<h3><strong>Camera chất lượng</strong></h3>\r\n\r\n<p>Apple giữ nguy&ecirc;n camera c&oacute; độ ph&acirc;n giải 8 MP v&agrave; khả năng quay phim Full HD.&nbsp;M&aacute;y cho&nbsp;chất lượng ảnh &ndash; video vượt trội hầu hết laptop v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"máy tính bảng\" type=\"máy tính bảng\">m&aacute;y t&iacute;nh bảng</a>&nbsp;tr&ecirc;n thị trường.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163791/ipad-6th-wifi-128-gb-24.jpg\" onclick=\"return false;\"><img alt=\"Camera đủ dùng cho mọi nhu cầu\" src=\"https://cdn.tgdd.vn/Products/Images/522/163791/ipad-6th-wifi-128-gb-24.jpg\" title=\"Camera đủ dùng cho mọi nhu cầu\" /></a></p>\r\n\r\n<p><em>Camera đủ d&ugrave;ng cho mọi nhu cầu</em></p>\r\n\r\n<h3><strong>Thời lượng pin thoải m&aacute;i</strong></h3>\r\n\r\n<p>&ldquo;10 tiếng&rdquo; gần như đ&atilde; trở th&agrave;nh ti&ecirc;u chuẩn về thời lượng pin cho iPad v&agrave; c&oacute; vẻ như phi&ecirc;n bản 9.7 inch (2018) cũng kh&ocirc;ng phải l&agrave; một ngoại lệ.</p>\r\n\r\n<p>Với 10 tiếng xem video hoặc lướt web li&ecirc;n tục, chiếc iPad mới của Apple dư sức g&aacute;nh v&aacute;c mọi hoạt động học tập, l&agrave;m việc v&agrave; giải tr&iacute; gần như trọn vẹn một ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/163791/ipad-6th-wifi-128-gb-11.jpg\" onclick=\"return false;\"><img alt=\"Thoải mái sử dụng iPad 6th Wifi 128 GB cả ngày dài\" src=\"https://cdn.tgdd.vn/Products/Images/522/163791/ipad-6th-wifi-128-gb-11.jpg\" title=\"Thoải mái sử dụng iPad 6th Wifi 128 GB cả ngày dài\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra iPad Wifi 2018 n&agrave;y c&ograve;n hỗ trợ kết nối v&agrave; sử dụng được với&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/apple-pencil\" target=\"_blank\" title=\"Tham khảo giá bút cảm ứng Apple Pencil\" type=\"Tham khảo giá bút cảm ứng Apple Pencil\">c&acirc;y b&uacute;t cảm ứng Apple Pencil</a>. Đ&acirc;y l&agrave; điều rất thuận tiện cho những bạn sinh vi&ecirc;n, văn ph&ograve;ng thường xuy&ecirc;n ghi ch&eacute;p nhiều, thao t&aacute;c nhanh hay vẽ vời.</p>\r\n', 0, 0, 'ipad-wifi-128-gb-2018-thumb-400x400.jpg', 10490000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(10, 'Samsung Galaxy Tab A 10.1 T515 (2019)', 2, 'Máy tính bảng', '<p><a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-a-101-t515-2019\" target=\"_blank\" title=\"Tham khảo máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) chính hãng\">Samsung Galaxy Tab A 10.1 T515 (2019)</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo các dòng máy tính bảng chính hãng chính hãng\">m&aacute;y t&iacute;nh bảng</a>&nbsp;c&oacute; m&agrave;n h&igrave;nh lớn c&ugrave;ng cấu h&igrave;nh vừa phải đ&aacute;p ứng tốt cho bạn trong hầu hết c&aacute;c nhu cầu giải tr&iacute; hằng ng&agrave;y.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/200963/Slider/vi-vn-samsung-galaxy-tab-a-101-t515-2019-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Adapter, S&aacute;ch hướng dẫn, Hộp m&aacute;y</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-a-101-t515-2019\" target=\"_blank\" title=\"Tham khảo máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) chính hãng\">Samsung Galaxy Tab A 10.1 T515 (2019)</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo các dòng máy tính bảng chính hãng chính hãng\">m&aacute;y t&iacute;nh bảng</a>&nbsp;c&oacute; m&agrave;n h&igrave;nh lớn c&ugrave;ng cấu h&igrave;nh vừa phải đ&aacute;p ứng tốt cho bạn trong hầu hết c&aacute;c nhu cầu giải tr&iacute; hằng ng&agrave;y.</h2>\r\n\r\n<h3>M&agrave;n h&igrave;nh k&iacute;ch thước lớn, sử dụng thoải m&aacute;i</h3>\r\n\r\n<p>Với m&agrave;n h&igrave;nh k&iacute;ch thước lớn l&ecirc;n tới 10.1 inch th&igrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-samsung\" target=\"_blank\" title=\"Tham khảo các dòng máy tính bảng Samsung chính hãng chính hãng\">m&aacute;y t&iacute;nh bảng Samsung</a>&nbsp;gi&uacute;p bạn c&oacute; một kh&ocirc;ng gian lớn hơn để c&oacute; thể l&agrave;m việc hay giải tr&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-1.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Màn hình\" /></a></p>\r\n\r\n<p>M&aacute;y c&ograve;n sở hữu ri&ecirc;ng chế độ ngo&agrave;i trời gi&uacute;p bạn c&oacute; thể theo d&otilde;i nội dung hiển thị một c&aacute;ch r&otilde; r&agrave;ng nhất m&agrave; kh&ocirc;ng sợ hiện tượng l&oacute;a hay in b&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-10.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Hiển thị màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-10.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Hiển thị màn hình\" /></a></p>\r\n\r\n<p>Một điểm cộng kh&aacute;c rất đ&aacute;ng gi&aacute; tr&ecirc;n thiết kế của m&aacute;y l&agrave; với phi&ecirc;n bản 2019 th&igrave; Samsung đ&atilde; mang l&ecirc;n m&aacute;y th&acirc;n h&igrave;nh kim loại cứng c&aacute;p v&agrave; chắc chắn hơn thay v&igrave; nhựa như những phi&ecirc;n bản trước đ&oacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-5.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-5.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Thiết kế\" /></a></p>\r\n\r\n<p>Để đảm bảo rằng m&aacute;y t&iacute;nh bảng cũng c&oacute; thời gian sử dụng tốt, Samsung t&iacute;ch hợp vi&ecirc;n pin dung lượng 6.150 mAh cho Galaxy Tab A 10.1 (2019).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-4.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Cổng sạc Type-C\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-4.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Cổng sạc Type-C\" /></a></p>\r\n\r\n<p>M&aacute;y c&oacute; thể được sạc qua&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/nhung-dieu-can-biet-ve-chuan-ket-noi-moi-usb-type--619776\" target=\"_blank\" title=\"Tìm hiểu về cổng kết nối Type-C\" type=\"Tìm hiểu về cổng kết nối Type-C\">cổng USB-C</a>&nbsp;mới gi&uacute;p bạn kh&ocirc;ng phải bận t&acirc;m cắm chiều n&agrave;o th&igrave; mới c&oacute; thể sạc được pin cho m&aacute;y.</p>\r\n\r\n<h3>Giao diện mới với nhiều t&iacute;nh năng</h3>\r\n\r\n<p>Samsung đ&atilde; ph&aacute;t h&agrave;nh giao diện OneUI mới cho Galaxy Tab A 10.1 (2019), đ&acirc;y l&agrave; phi&ecirc;n bản được điều chỉnh ri&ecirc;ng cho những chiếc m&aacute;y t&iacute;nh bảng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-8.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Giao diện Android 9 Pie\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-8.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Giao diện Android 9 Pie\" /></a></p>\r\n\r\n<p>Kết hợp với đ&oacute; l&agrave; con chip do ch&iacute;nh Samsung sản xuất&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/samsung-ra-mat-exynos-7904-chip-tam-trung-danh-cho-an-do-1145911\" target=\"_blank\" title=\"Tìm hiểu về dòng chip Exynos 7904\" type=\"Tìm hiểu về dòng chip Exynos 7904\">Exynos 7904</a>&nbsp;c&ugrave;ng 3 GB RAM v&agrave; 32 GB bộ nhớ trong.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-2.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Trên tay và trải nghiệm\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-2.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Trên tay và trải nghiệm\" /></a></p>\r\n\r\n<p>Hiệu năng tr&ecirc;n m&aacute;y được tối ưu kh&aacute; tốt, mọi thao t&aacute;c của người d&ugrave;ng đều đ&aacute;p ứng rất mượt m&agrave; v&agrave; &iacute;t c&oacute; độ trễ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>&Acirc;m thanh chất lượng, giải tr&iacute; cực đ&atilde;</h3>\r\n\r\n<p>Samsung Galaxy Tab A 10.1 (2019) sở hữu 2 loa ở mặt dưới với c&ocirc;ng suất kh&aacute; lớn v&agrave; đặc biệt l&agrave; hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-dolby-atmos-tren-smartphone-772074\" target=\"_blank\" title=\"Tìm hiểu về công nghệ âm thanh Dolby Atmos\" type=\"Tìm hiểu về công nghệ âm thanh Dolby Atmos\">c&ocirc;ng nghệ &acirc;m thanh v&ograve;m Dolby Atmos</a>.</p>\r\n\r\n<p>Những &acirc;m thanh khi bạn chơi game hay xem phim sẽ được t&aacute;i tạo một c&aacute;ch ch&acirc;n thực nhất v&agrave; chắc chắn sẽ kh&ocirc;ng l&agrave;m bạn thất vọng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-6.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Tích hợp jack 3.5 mm\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-6.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Tích hợp jack 3.5 mm\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra th&igrave; Samsung vẫn giữ cho người d&ugrave;ng jack cắm tai nghe 3.5 mm để bạn c&oacute; thể nghe nhạc trong những kh&ocirc;ng gian đ&ocirc;ng người.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Khe sim và thẻ nhớ\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-7.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Khe sim và thẻ nhớ\" /></a></p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng kh&aacute;c hỗ trợ giải tr&iacute; vẫn sẽ c&oacute; mặt tr&ecirc;n m&aacute;y như hỗ trợ khe SIM 4G, bộ đ&ocirc;i camera trước sau chất lượng cho c&aacute;c cuộc gọi video call,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-12.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Tính năng sạc cho thiết bị khác\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-12.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Tính năng sạc cho thiết bị khác\" /></a></p>\r\n\r\n<p>V&agrave; điều đặc biệt xuất hiện tr&ecirc;n chiếc m&aacute;y t&iacute;nh bảng n&agrave;y l&agrave; c&oacute; hỗ trợ t&iacute;nh năng sạc cho thiết bị kh&aacute;c th&ocirc;ng qua c&aacute;p, ho&agrave;n tiện tiện &iacute;ch để chia sẻ năng lượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-13.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Tính năng sạc cho thiết bị khác\" src=\"https://cdn.tgdd.vn/Products/Images/522/200963/samsung-galaxy-tab-a-101-t515-2019-tgdd-13.jpg\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.1 T515 (2019) | Tính năng sạc cho thiết bị khác\" /></a></p>\r\n', 0, 0, 'samsung-galaxy-tab-a-101-t515-2019-gold-400x400.jpg', 7490000),
(11, 'iPad Mini 7.9 inch Wifi 64GB (2019)', 2, 'Máy tính bảng', '<p>Nhiều người đồn đo&aacute;n rằng Apple đ&atilde; khai tử d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo máy tính bảng Apple chính hãng\">iPad</a>&nbsp;Mini của m&igrave;nh khi đ&atilde; 4 năm kể từ thế hệ mới nhất kh&ocirc;ng c&oacute; th&ecirc;m bất cứ n&acirc;ng cấp n&agrave;o. Tuy nhi&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-mini-79-inch-wifi-2019\" target=\"_blank\" title=\"Tham khảo máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\">iPad Mini 7.9 inch Wifi 2019</a>&nbsp;ra mắt v&agrave; đ&aacute;nh dấu sự trở lại của một chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng chính hãng\">m&aacute;y t&iacute;nh bảng</a>&nbsp;nhỏ gọn như ng&agrave;y n&agrave;o.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/202706/Slider/vi-vn-ipad-mini-79-inch-wifi-2019-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Nhiều người đồn đo&aacute;n rằng Apple đ&atilde; khai tử d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo máy tính bảng Apple chính hãng\">iPad</a>&nbsp;Mini của m&igrave;nh khi đ&atilde; 4 năm kể từ thế hệ mới nhất kh&ocirc;ng c&oacute; th&ecirc;m bất cứ n&acirc;ng cấp n&agrave;o. Tuy nhi&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-mini-79-inch-wifi-2019\" target=\"_blank\" title=\"Tham khảo máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\">iPad Mini 7.9 inch Wifi 2019</a>&nbsp;ra mắt v&agrave; đ&aacute;nh dấu sự trở lại của một chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng chính hãng\">m&aacute;y t&iacute;nh bảng</a>&nbsp;nhỏ gọn như ng&agrave;y n&agrave;o.</h2>\r\n\r\n<h3>Tất cả n&acirc;ng cấp đều ở b&ecirc;n trong</h3>\r\n\r\n<p>IPad mini 4 v&agrave; iPad Mini 7.9 inch Wifi 2019 l&agrave; bước nhảy vọt về hiệu năng từ bộ xử l&yacute; A8 sang A12 Bionic mới nhất mang lại &quot;hiệu năng gấp 3 lần v&agrave; đồ họa nhanh hơn 9 lần&quot;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-20191.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-20191.jpg\" title=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Đồng thời, dung lượng RAM đ&atilde; c&oacute; sự n&acirc;ng cấp nhẹ so với thế hệ thứ 4, tăng từ 2 GB l&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang?g=3-gb\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng có RAM 3 GB\" type=\"Tham khảo giá máy tính bảng có RAM 3 GB\">RAM 3 GB</a>&nbsp;gi&uacute;p bạn chạy đa nhiệm nhiều ứng dụng tốt hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-20199.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-20199.jpg\" title=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Chip A12 Bionic được trang bị tr&ecirc;n iPad Mini 7.9 inch Wifi 2019 l&agrave; một trong những bộ xử l&yacute; di động tốt nhất trong việc chơi game hiện nay, đồng thời gi&uacute;p xử l&iacute; c&aacute;c t&aacute;c vụ kh&aacute;c một c&aacute;ch mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-20194.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-20194.jpg\" title=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Với nhu cầu l&agrave;m việc, lướt web, xem video, chơi tr&ograve; chơi v&agrave; thậm ch&iacute; chỉnh sửa video 4K tr&ecirc;n thiết bị n&agrave;y đều được xử l&iacute; trơn tru v&agrave; kh&ocirc;ng bị trục trặc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-20198.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-20198.jpg\" title=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Bạn sẽ kh&ocirc;ng ngạc nhi&ecirc;n khi thấy iOS 12 mới nhất của Apple l&agrave; hệ điều h&agrave;nh đang được sử dụng tr&ecirc;n iPad Mini 7.9 inch Wifi 2019.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-20193.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-20193.jpg\" title=\"Cấu hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, iPad Mini 7.9 inch Wifi 2019 cũng sẽ hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/apple-pencil\" target=\"_blank\" title=\"Tham khảo giá máy tính bảng Apple Pencil\" type=\"Tham khảo giá máy tính bảng Apple Pencil\">Apple Pencil</a>&nbsp;mang tới nhiều trải nghiệm th&uacute; vị hơn tr&ecirc;n m&agrave;n h&igrave;nh như viết, vẽ, s&aacute;ng t&aacute;c nghệ thuật...</p>\r\n\r\n<h3>Một thiết bị nhỏ gọn c&oacute; thể dễ d&agrave;ng mang theo</h3>\r\n\r\n<p>L&yacute; do để người d&ugrave;ng chọn chiếc iPad Mini 7.9 inch Wifi 2019 chắc chắn l&agrave; v&igrave; k&iacute;ch thước nhỏ gọn của m&aacute;y so với c&aacute;c d&ograve;ng iPad kh&aacute;c của Apple.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-20196.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-20196.jpg\" title=\"Thiết kế của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh của iPad Mini 7.9 inch Wifi 2019 đạt ti&ecirc;u chuẩn Retina với k&iacute;ch thước 7.9 inch, độ ph&acirc;n giải 2224 x 1668 pixel v&agrave; mật độ điểm ảnh 326 ppi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-20197.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-20197.jpg\" title=\"Màn hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>Chiếc iPad mini mới nhất n&agrave;y c&ograve;n hỗ trợ c&ocirc;ng nghệ hiển thị True Tone của Apple, gi&uacute;p &iacute;ch trong việc hiển thị nội dung v&agrave; mang lại khả năng điều chỉnh m&agrave;n h&igrave;nh với c&aacute;c t&igrave;nh huống &aacute;nh s&aacute;ng kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-20195.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-20195.jpg\" title=\"Màn hình của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, thiết bị n&agrave;y cũng c&oacute; n&uacute;t Home truyền thống, cho ph&eacute;p người d&ugrave;ng nhấn một lần để về m&agrave;n h&igrave;nh ch&iacute;nh v&agrave; mở đa nhiệm bằng hai c&uacute; nhấp.</p>\r\n\r\n<h3>Thời lượng pin thoải m&aacute;i d&ugrave;ng 2 ng&agrave;y</h3>\r\n\r\n<p>Với&nbsp;iPad Mini 7.9 inch Wifi 2019 th&igrave; bạn sẽ c&oacute; được 10 tiếng sử dụng li&ecirc;n tục với c&aacute;c t&aacute;c vụ như xem video hay lướt web, một con số m&agrave; nhiều người hằng mơ ước.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-20192.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-20192.jpg\" title=\"Thời lượng pin của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>iPad Mini mới sở hữu loa k&eacute;p hướng xuống dưới c&oacute; &acirc;m lượng lớn cho trải nghiệm &acirc;m thanh kh&aacute; tốt, đặc biệt l&agrave; &acirc;m trầm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/202706/ipad-mini-79-inch-wifi-201911.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/522/202706/ipad-mini-79-inch-wifi-201911.jpg\" title=\"Thời lượng pin của máy tính bảng iPad Mini 7.9 inch Wifi 2019 chính hãng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; m&aacute;y vẫn giữ lại cho ch&uacute;ng ta jack cắm tai nghe 3.5mm quen thuộc gi&uacute;p bạn dễ d&agrave;ng vừa c&oacute; thể nghe nhạc v&agrave; vừa c&oacute; thể sạc pin.</p>\r\n', 0, 0, 'ipad-mini-79-inch-wifi-2019-1-400x400.jpg', 10990000),
(12, 'iPad Pro 11 inch Wifi 64GB', 2, 'Máy tính bảng', '<p>M&aacute;y t&iacute;nh bảng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-11-inch-2018-64gb-wifi\" target=\"_blank\" title=\"Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018) chính hãng đang kinh doanh tại Thegioididong.com\">iPad Pro 11 inch 64GB Wifi (2018)</a>&nbsp;được ra mắt v&agrave;o cuối năm 2018, thu h&uacute;t nh&acirc;n vi&ecirc;n văn ph&ograve;ng, doanh nh&acirc;n bởi thiết kế hiện đại, đầy sức đột ph&aacute; v&agrave; một cấu h&igrave;nh mạnh mẽ đ&aacute;p ứng tốt tất cả c&aacute;c nhu cầu từ giải tr&iacute; đến l&agrave;m việc</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/195067/Slider/-pad-pro-11-inch-2018-64gb-wifi-33397-12-thietke.gif\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>M&aacute;y t&iacute;nh bảng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-11-inch-2018-64gb-wifi\" target=\"_blank\" title=\"Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018) chính hãng đang kinh doanh tại Thegioididong.com\">iPad Pro 11 inch 64GB Wifi (2018)</a>&nbsp;được ra mắt v&agrave;o cuối năm 2018, thu h&uacute;t nh&acirc;n vi&ecirc;n văn ph&ograve;ng, doanh nh&acirc;n bởi thiết kế hiện đại, đầy sức đột ph&aacute; v&agrave; một cấu h&igrave;nh mạnh mẽ đ&aacute;p ứng tốt tất cả c&aacute;c nhu cầu từ giải tr&iacute; đến l&agrave;m việc.</h2>\r\n\r\n<h3>Thiết kế đẳng cấp</h3>\r\n\r\n<p>iPad Pro 11 inch 64GB Wifi (2018)&nbsp;sở hữu ngoại h&igrave;nh ho&agrave;n to&agrave;n mới, viền m&agrave;n h&igrave;nh được thu hẹp hơn, 4 cạnh được v&aacute;t mạnh&nbsp;đ&atilde; khiến m&aacute;y&nbsp;trở th&agrave;nh một trong những chiếc m&aacute;y t&iacute;nh bảng đẹp nhất, đẳng cấp nhất hiện tại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&uacute;t Home tr&ecirc;n thế hệ mới đ&atilde; được loại bỏ, c&aacute;c thao t&aacute;c về lại m&agrave;n h&igrave;nh ch&iacute;nh, mở thanh th&ocirc;ng b&aacute;o, mở đa nhiệm, bảng điều khiển,... đều c&oacute; thể sử dụng dễ d&agrave;ng th&ocirc;ng qua c&aacute;c thao t&aacute;c k&eacute;o vuốt thả tr&ecirc;n m&agrave;n h&igrave;nh ch&iacute;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-thietke.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế tinh tế trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-thietke.jpg\" title=\"Thiết kế tinh tế trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh si&ecirc;u sắc n&eacute;t</h3>\r\n\r\n<p>C&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;Liquid&nbsp;Retina với độ ph&acirc;n giải 2388 x 1668 pixels tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng chính hãng đang kinh doanh tại Thegioididong.com\">m&aacute;y t&iacute;nh bảng</a>&nbsp;11 inch c&oacute; khả&nbsp;năng hiển thị sắc n&eacute;t đến từng chi tiết nhỏ, m&agrave;u sắc c&oacute; độ s&acirc;u v&agrave; ch&acirc;n thật.&nbsp;Xem phim, chơi game,... đều cho trải nghiệm tuyệt vời nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-manhinh.jpg\" onclick=\"return false;\"><img alt=\"Màn hình sắc nét trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-manhinh.jpg\" title=\"Màn hình sắc nét trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<h3>Vi xử l&yacute; mạnh mẽ</h3>\r\n\r\n<p>Vi xử l&yacute;&nbsp;<strong>Apple A12X Bionic</strong>&nbsp;mạnh mẽ kết hợp c&ugrave;ng&nbsp;<strong>4GB RAM&nbsp;</strong>v&agrave;&nbsp;<strong>hệ điều h&agrave;nh IOS 12</strong>&nbsp;gi&uacute;p<strong>&nbsp;iPad Pro 11 inch 64GB Wifi (2018)</strong>&nbsp;c&oacute; thể chiến mượt m&agrave; c&aacute;c tựa game nặng nhất hiện tại v&agrave; chạy trơn tru c&aacute;c ứng dụng đồ hoạ. Với&nbsp;Apple A12X Bionic l&agrave; trung t&acirc;m xử l&yacute;,&nbsp;iPad Pro 11 inch 64GB Wifi (2018)&nbsp;ngo&agrave;i giải tr&iacute; c&ograve;n đ&aacute;p ứng tốt nhu cầu xử l&yacute; c&ocirc;ng việc hằng ng&agrave;y của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-dmx-cauhinh.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình mạnh mẽ trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-dmx-cauhinh.jpg\" title=\"Cấu hình mạnh mẽ trên Cổng kết nối USB C đa chức năng trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<h3>FaceID&nbsp;đầu ti&ecirc;n tr&ecirc;n&nbsp;Ipad</h3>\r\n\r\n<p>C&ocirc;ng nghệ bảo mật bằng khu&ocirc;n mặt ti&ecirc;n tiến lần đầu ti&ecirc;n xuất hiện tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Ipad chính hãng đang kinh doanh tại Thegioididong.com\">iPad</a>&nbsp;Pro 11 inch 64GB Wifi (2018)&nbsp;với cụm&nbsp;cảm biến&nbsp;<strong>FaceID&nbsp;th&ocirc;ng minh&nbsp;</strong>c&oacute; thể nhận diện khi bạn xoay ngang hay xoay dọc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-faceid.jpg\" onclick=\"return false;\"><img alt=\"Công nghệ Face ID trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-faceid.jpg\" title=\"Công nghệ Face ID trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<h3>Camera TrueDepth&nbsp;sắc n&eacute;t</h3>\r\n\r\n<p><strong>C&ocirc;ng nghệ Smart HDR</strong>&nbsp;gi&uacute;p n&acirc;ng cao chất lượng ảnh chụp c&ugrave;ng nhiều c&ocirc;ng nghệ kh&aacute;c như chụp ch&acirc;n dung x&oacute;a ph&ocirc;ng, tạo Animoji, Memoji, quay video 4K,... đều được trang bị tr&ecirc;n cả&nbsp;<strong>camera trước 7MP</strong>&nbsp;v&agrave;&nbsp;<strong>camera sau 12MP</strong>&nbsp;tr&ecirc;n Ipad Pro 2018 cho chất lượng ảnh tươi s&aacute;ng v&agrave; sắc n&eacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-camera.jpg\" onclick=\"return false;\"><img alt=\"Camera trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-camera.jpg\" title=\"Camera trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<h3>Apple Pencil s&aacute;ng tạo mọi l&uacute;c, mọi nơi&nbsp;</h3>\r\n\r\n<p>Apple Pencil&nbsp;thế hệ 2 đ&atilde; được cải tiến lớn v&agrave; liền mạch hơn cho cảm gi&aacute;c cầm nắm thật sự tốt. Đặc biệt,&nbsp;<strong>Apple pencil thế hệ 2</strong>&nbsp;đ&atilde; c&oacute; thể sạc kh&ocirc;ng d&acirc;y với&nbsp;iPad Pro 2018&nbsp;(kh&ocirc;ng cần phải sạc qua cổng lightning như thế hệ trước). Chỉ cần để h&iacute;t v&agrave;o cạnh iPad l&agrave; qu&aacute; tr&igrave;nh sạc được k&iacute;ch hoạt. (sản phẩm kh&ocirc;ng tặng k&egrave;m b&uacute;t).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-applepen.jpg\" onclick=\"return false;\"><img alt=\"Apple pen thế 2 trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-applepen.jpg\" title=\"Apple pen thế 2 trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Kết nối USB-C đa năng</h3>\r\n\r\n<p>Cổng kết nối&nbsp;<strong>USB Type C hiện đại</strong>, ngo&agrave;i sử dụng để sạc cho m&aacute;y c&ograve;n c&oacute; khả sạc cho iPhone, kết nối với c&aacute;c thiết bị ngoại vi như m&aacute;y in, m&aacute;y ảnh, ổ cứng,... v&agrave; tốc độ truyền dữ liệu cũng được cải thiện đ&aacute;ng kể so với cổng Lightning thế hệ trước. Ngo&agrave;i ra, b&ecirc;n trong&nbsp;iPad Pro&nbsp;2018&nbsp;cũng được trang bị nhiều nam ch&acirc;m để kết nối với b&agrave;n ph&iacute;m Smart Keyboard, tăng khả năng nhập liệu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-usb.jpg\" onclick=\"return false;\"><img alt=\"Cổng kết nối USB C đa chức năng trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-usb.jpg\" title=\"Cổng kết nối USB C đa chức năng trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n\r\n<h3>&nbsp;Thời lượng pin 10 giờ</h3>\r\n\r\n<p>Với dung lượng pin&nbsp;30.4 Wh bạn c&oacute; thể thoải m&aacute;i sử dụng l&agrave;m việc, xem phim,...&nbsp;l&ecirc;n đến 10 giờ, nhưng nếu bạn chỉ thỉnh thoảng xử l&yacute; c&ocirc;ng việc hay xem đoạn clip th&igrave; thời gian sử dụng l&ecirc;n đến gần 2 ng&agrave;y. Thật tiện lợi để bạn c&oacute; thể lu&ocirc;n lu&ocirc;n mang theo b&ecirc;n m&igrave;nh đi du lịch hay l&agrave;m việc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-pin.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/195067/ipad-pro-11-inch-2018-64gb-wifi-content-33397-tgdd-pin.jpg\" title=\"Dung lượng pin trên Máy tính bảng iPad Pro 11 inch 64GB Wifi (2018)\" /></a></p>\r\n', 0, 0, 'ipad-pro-11-inch-2018-64gb-wifi-33397-thumb-400x400.jpg', 21490000),
(13, 'Laptop HP 15 da0055TU i3', 3, 'Laptop', '<p><a href=\"https://www.thegioididong.com/laptop-hp-compaq\" target=\"_blank\" title=\"Máy tính HP chính hãng, giá tốt, có trả góp tại Thế Giới Di Động\">Laptop&nbsp;</a><a href=\"https://www.thegioididong.com/laptop/hp-15-da0055tu-4na89pa\" target=\"_blank\" title=\"Máy tính HP 15 da0055TU chính hãng, giá rẻ tại Thế Giới Di Động\">HP 15 da0055TU</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Máy tính chính hãng, giá tốt, có trả góp đang bán tại Thế Giới Di Động\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;sở hữu Core i3, 4 GB RAM, ổ cứng lưu trữ HDD 1 TB, Windows bản quyền được c&agrave;i đặt sẵn tr&ecirc;n m&aacute;y cho hiệu năng ổn định c&ugrave;ng card đồ họa t&iacute;ch hợp&nbsp;Intel&reg; HD Graphics 620 cho khả năng hiển thị h&igrave;nh ảnh r&otilde; n&eacute;t, mang đến kh&ocirc;ng gian&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Máy tính dành cho học tâp - làm việc chính hãng tại Thế Giới Di Động\">l&agrave;m việc - học tập</a>&nbsp;năng động, thoải m&aacute;i.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/204064/Slider/vi-vn-hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa--(1).jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, D&acirc;y nguồn, Adapter sạc, S&aacute;ch hướng dẫn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Laptop&nbsp;<a href=\"https://www.thegioididong.com/laptop/hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa\" target=\"_blank\" title=\"Laptop HP 15 da0359TU N4417/4GB/500GB/Win10 (6KD00PA) chính hãng đang kinh doanh tại Thegioididong\" type=\"Laptop HP 15 da0359TU N4417/4GB/500GB/Win10 (6KD00PA) chính hãng đang kinh doanh tại Thegioididong\">HP 15 da0359TU N4417 (6KD00PA)</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Các loại laptop văn phòng chính hãng, giá rẻ khác tại Thế Giới Di Động\" type=\"Các loại laptop văn phòng chính hãng, giá rẻ khác tại Thế Giới Di Động\">laptop văn ph&ograve;ng gi&aacute; rẻ</a>&nbsp;được trang bị cấu h&igrave;nh vừa đủ sử dụng t&iacute;nh năng cơ bản v&agrave; c&agrave;i sẵn hệ điều h&agrave;nh Windows 10 bản quyền gi&uacute;p sử dụng ổn định, tiện lợi hơn. Đ&acirc;y sẽ l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\" type=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">laptop</a>&nbsp;ph&ugrave; hợp cho học, sinh vi&ecirc;n, hay d&acirc;n văn ph&ograve;ng c&oacute; điều kiện kinh tế thấp.</h2>\r\n\r\n<h3>Thiết kế thanh lịch, mỏng nhẹ</h3>\r\n\r\n<p>Sản phẩm c&oacute; thiết kế đơn giản nhưng kh&ocirc;ng k&eacute;m phần thanh lịch với logo HP s&aacute;ng b&oacute;ng. C&aacute;c g&oacute;c cạnh của&nbsp;<a href=\"https://www.thegioididong.com/laptop-hp-compaq\" target=\"_blank\" title=\"Laptop HP chính hãng đang được kinh doanh tại Thegioididong.com\" type=\"Laptop HP chính hãng đang được kinh doanh tại Thegioididong.com\">laptop HP</a>&nbsp;được bo cong nhẹ nh&agrave;ng để tạo một cảm gi&aacute;c thoải m&aacute;i nhất cho người d&ugrave;ng trong suốt qu&aacute; tr&igrave;nh sử dụng. Đặc biệt sản phẩm chỉ nặng&nbsp;<strong>1.77 kg</strong>n&ecirc;n dễ d&agrave;ng để bạn c&oacute; thể bỏ v&agrave;o trong t&uacute;i x&aacute;ch, ba l&ocirc; để mang laptop đi bất cứ đ&acirc;u bạn muốn.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/44/204064/hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa-kg.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP 15 da0359TU N4417 (6KD00PA) có thiết kế đơn giản\" src=\"https://cdn.tgdd.vn/Products/Images/44/204064/hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa-kg.jpg\" title=\"Laptop HP 15 da0359TU N4417 (6KD00PA) có thiết kế đơn giản\" /></a></h2>\r\n\r\n<h3>M&agrave;n h&igrave;nh lớn, xem phim thoải m&aacute;i</h3>\r\n\r\n<p>Điểm nổi bật nhất tr&ecirc;n m&aacute;y ch&iacute;nh&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=tren-15\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop chính hãng 15 inch bán tại Thế Giới Di Động\" type=\"Xem thêm các mẫu Laptop chính hãng 15 inch bán tại Thế Giới Di Động\">m&agrave;n h&igrave;nh laptop 15.6 inch</a>, gi&uacute;p laptop c&oacute; kh&ocirc;ng gian hiển thị lớn, ph&ugrave; hợp với nhiều nhu cầu sử dụng. C&ocirc;ng nghệ hiển thị&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cac-chuan-man-hinh-su-dung-tren-laptop-747028#hdbledb\" target=\"_blank\" title=\"Tìm hiểu về công nghệ hiển thị HD BrightView LED Backlit trên laptop\" type=\"Tìm hiểu về công nghệ hiển thị HD BrightView LED Backlit trên laptop\">HD BrightView LED Backlit</a>&nbsp;đem đến cho bạn một trải nghiệm xem tối ưu v&agrave; đ&aacute;p ứng tốt nhu cầu giải tr&iacute; cơ bản tr&ecirc;n chiếc laptop n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Hiệu năng tốt cho c&ocirc;ng việc học tập cơ bản</h3>\r\n\r\n<p>Mang tr&ecirc;n m&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-vi-xu-ly-may-tinh-cpu-intel-596066#intelpentinum\" target=\"_blank\" title=\"Tìm hiểu về vi xử lý Intel Pentium trên laptop\" type=\"Tìm hiểu về vi xử lý Intel Pentium trên laptop\">bộ vi xử l&yacute;&nbsp;Intel Pentium</a>&nbsp;N4417U, bạn c&oacute; thể xử l&yacute; c&aacute;c t&aacute;c vụ hằng ng&agrave;y như c&ocirc;ng việc văn ph&ograve;ng, soạn thảo văn bản, xem phim, lướt web... một c&aacute;ch dễ d&agrave;ng. B&ecirc;n cạnh đ&oacute;, m&aacute;y t&iacute;nh c&oacute;<strong>&nbsp;4 GB RAM&nbsp;</strong>v&agrave;<strong>&nbsp;500 GB&nbsp;</strong>dung lượng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/hdd-la-gi-922791\" target=\"_blank\" title=\"Ổ cứng HDD là gì?\" type=\"Ổ cứng HDD là gì?\">ổ cứng HDD</a>&nbsp;để cung cấp cho người d&ugrave;ng trải nghiệm đa nhiệm mượt m&agrave; v&agrave; lưu trữ dữ liệu thoải m&aacute;i.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/44/204064/hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa-hdd.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP 15 da0359TU N4417 (6KD00PA) có thể xử lý các tác vụ hằng ngày như công việc văn phòng, soạn thảo văn bản, xem phim, lướt web... \" src=\"https://cdn.tgdd.vn/Products/Images/44/204064/hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa-hdd.jpg\" title=\"Laptop HP 15 da0359TU N4417 (6KD00PA) có thể xử lý các tác vụ hằng ngày như công việc văn phòng, soạn thảo văn bản, xem phim, lướt web... \" /></a></h2>\r\n\r\n<h3>Hệ điều h&agrave;nh&nbsp;<a href=\"https://www.thegioididong.com/phan-mem/windows-10-home-32-bit-64-bit-all-languages-kw9-0\" target=\"_blank\" title=\"Phần mềm Windows 10 chính hãng bán tại Thế Giới Di Động\" type=\"Phần mềm Windows 10 chính hãng bán tại Thế Giới Di Động\">Windows 10</a>&nbsp;được c&agrave;i đặt sẵn</h3>\r\n\r\n<p><strong>Windows 10&nbsp;</strong>c&oacute; bản quyền gi&uacute;p m&aacute;y hoạt động ổn định hơn, nhận được c&aacute;c bản cập nhật đều đặn nhanh ch&oacute;ng từ Microsoft gi&uacute;p sửa lỗi v&agrave; tăng t&iacute;nh bảo mật cho m&aacute;y, hạn chế virus x&acirc;m nhập. Ngo&agrave;i ra m&aacute;y cũng sẽ nhận được c&aacute;c thay đổi về giao diện mới nhất khi sử dụng hệ điều h&agrave;nh c&oacute; bản quyền.&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/44/204064/hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa-win10.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP 15 da0359TU N4417 (6KD00PA) với Hệ điều hành Windows 10 được cài đặt sẵn\" src=\"https://cdn.tgdd.vn/Products/Images/44/204064/hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa-win10.jpg\" title=\"Laptop HP 15 da0359TU N4417 (6KD00PA) với Hệ điều hành Windows 10 được cài đặt sẵn\" /></a></h2>\r\n\r\n<h3>Hỗ trợ cảm ứng đa chạm&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/multi-touchpad-la-gi-920569\" target=\"_blank\" title=\"Tìm hiểu về Multi TouchPad trên laptop\" type=\"Tìm hiểu về Multi TouchPad trên laptop\">Multi TouchPad</a>&nbsp;tr&ecirc;n chuột của laptop</h3>\r\n\r\n<p>Multi TouchPad nhận diện nhiều t&aacute;c động c&ugrave;ng l&uacute;c l&ecirc;n TouchPad v&agrave; m&aacute;y t&iacute;nh c&oacute; thể hiểu được c&aacute;c t&aacute;c động n&agrave;y để đưa ra c&aacute;c xử l&yacute; đ&uacute;ng y&ecirc;u cầu.​ C&ocirc;ng nghệ n&agrave;y sẽ gi&uacute;p bạn đơn giản hơn trong cuộc sống khi kh&ocirc;ng phải l&uacute;c n&agrave;o cũng phải k&egrave; k&egrave; con chuột b&ecirc;n m&igrave;nh.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/44/204064/hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa-multitouchpad.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP 15 da0359TU N4417 (6KD00PA) nhận diện nhiều tác động cùng lúc lên TouchPad \" src=\"https://cdn.tgdd.vn/Products/Images/44/204064/hp-15-da0359tu-n4417-4gb-500gb-win10-6kd00pa-multitouchpad.jpg\" title=\"Laptop HP 15 da0359TU N4417 (6KD00PA) nhận diện nhiều tác động cùng lúc lên TouchPad \" /></a></h2>\r\n\r\n<h2>&nbsp;</h2>\r\n', 0, 0, '1563866859hp-15-da0055tu-4na89pa-33397-thietke-3-600x600.jpg', 10290000),
(14, 'Laptop Apple Macbook Air 2017 ', 3, 'Laptop', '<p><a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-mqd32sa-a-i5-5350u\" target=\"_blank\" title=\"Xem chi tiết về sản phẩm MacBook Air 2017 i5 128 GB\" type=\"Xem chi tiết về sản phẩm MacBook Air 2017 i5 128 GB\">MacBook Air 2017 i5 128GB</a>&nbsp;l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Xem thêm các mẫu laptop văn phòng đang kinh doanh\" type=\"Xem thêm các mẫu laptop văn phòng đang kinh doanh\">laptop văn ph&ograve;ng</a>, c&oacute; thiết kế si&ecirc;u mỏng v&agrave; nhẹ, vỏ nh&ocirc;m nguy&ecirc;n khối sang trọng. M&aacute;y c&oacute; hiệu năng ổn định, thời lượng pin cực l&acirc;u 12 giờ, ph&ugrave; hợp cho hầu hết c&aacute;c nhu cầu l&agrave;m việc v&agrave; giải tr&iacute;.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/106875/Slider/-apple-macbook-air-mqd32sa-a-i5-5350u-tk.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: D&acirc;y nguồn,Adapter sạc,Th&ugrave;ng m&aacute;y,S&aacute;ch hướng dẫn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-mqd32sa-a-i5-5350u\" target=\"_blank\" title=\"Xem chi tiết về sản phẩm MacBook Air 2017 i5 128 GB\" type=\"Xem chi tiết về sản phẩm MacBook Air 2017 i5 128 GB\">MacBook Air 2017 i5 128GB</a>&nbsp;l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Xem thêm các mẫu laptop văn phòng đang kinh doanh\" type=\"Xem thêm các mẫu laptop văn phòng đang kinh doanh\">laptop văn ph&ograve;ng</a>, c&oacute; thiết kế si&ecirc;u mỏng v&agrave; nhẹ, vỏ nh&ocirc;m nguy&ecirc;n khối sang trọng. M&aacute;y c&oacute; hiệu năng ổn định, thời lượng pin cực l&acirc;u 12 giờ, ph&ugrave; hợp cho hầu hết c&aacute;c nhu cầu l&agrave;m việc v&agrave; giải tr&iacute;.&nbsp;</h2>\r\n\r\n<h3>Thiết kế si&ecirc;u mỏng v&agrave; nhẹ&nbsp;</h3>\r\n\r\n<p>Macbook Air 2017 mang những đặc trưng thiết kế của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-air\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook Air khác\" type=\"Xem thêm các mẫu MacBook Air khác\">MacBook Air</a>&nbsp;với trọng lượng v&agrave; độ d&agrave;y của&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Tìm hiểu các dòng laptop khác tại Thegioididong\" type=\"Tìm hiểu các dòng laptop khác tại Thegioididong\">laptop&nbsp;</a>lần lượt l&agrave;&nbsp;<strong>1.7 cm</strong>&nbsp;v&agrave;&nbsp;<strong>1.35 kg</strong>&nbsp;rất tiện lợi v&agrave; dễ d&agrave;ng gi&uacute;p người d&ugrave;ng kh&ocirc;ng cảm thấy bất tiện khi mang tr&ecirc;n vai thường xuy&ecirc;n khi đi học hay đi l&agrave;m.&nbsp;</p>\r\n\r\n<p>Đ&acirc;y cũng l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem những mẫu MacBook khác\" type=\"Xem những mẫu MacBook khác\">MacBook ch&iacute;nh h&atilde;ng</a>&nbsp;c&oacute; gi&aacute; rẻ nhất hiện tại, ph&ugrave; hợp với mọi người ti&ecirc;u d&ugrave;ng.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-4.jpg\" onclick=\"return false;\"><img alt=\"Hình mô tả thiết kế Laptop MacBook Air 2017 i5 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-4.jpg\" title=\"Hình mô tả thiết kế Laptop MacBook Air 2017 i5 128GB\" /></a></p>\r\n\r\n<h3>Hiệu năng mượt m&agrave;</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U c&oacute; bộ xử l&yacute; Intel&nbsp;<strong>Core i5</strong>&nbsp;Broadwell hai nh&acirc;n xung nhịp 1.8 GHz, bộ nhớ&nbsp;<strong>RAM 8 GB</strong>&nbsp;thoải m&aacute;i trong việc duyệt web, chơi game, sử dụng mượt m&agrave; cac thao t&aacute;c trong phần mềm Photoshop, AI&hellip;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-1-1.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mượt mà của laptop Macbook Air MQD32SA/A i5 5350U\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-1-1.jpg\" title=\"Hiệu năng mượt mà của laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n\r\n<p>Ổ cứng lưu trữ&nbsp;<strong>SSD 128 GB</strong>&nbsp;cho tốc độ cực nhanh, thời gian để bạn khởi động m&aacute;y chỉ khoảng 10-15 gi&acirc;y, dung lượng 128 GB kh&ocirc;ng qu&aacute; dư giả, nhưng vẫn đủ để lưu trữ c&aacute;c t&agrave;i liệu, h&igrave;nh ảnh, ứng dụng thường d&ugrave;ng.</p>\r\n\r\n<h3>Thời lượng pin cực l&acirc;u</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U cho thời lượng sử dụng l&ecirc;n đến&nbsp;<strong>12 tiếng</strong>&nbsp;sau một lần sạc đầy khi sử dụng soạn thảo v&agrave; lướt web th&ocirc;ng thường.&nbsp;</p>\r\n\r\n<p>Với c&aacute;c t&aacute;c vụ nặng hơn như Photoshop, lướt web từ 10-20 tab, sử dụng c&aacute;c ứng dụng nhắn tin, gọi video khi l&agrave;m việc th&igrave; chiếc m&aacute;y vẫn trụ được khoảng&nbsp;<strong>5-7 tiếng li&ecirc;n tục</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u5.jpg\" onclick=\"return false;\"><img alt=\"Laptop Macbook Air MQD32SA/A i5 5350U\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u5.jpg\" title=\"Laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh đ&aacute;p ứng nhu cầu sử dụng h&agrave;ng ng&agrave;y</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U c&oacute; m&agrave;n h&igrave;nh rộng&nbsp;<strong>13.3 inch</strong>, độ ph&acirc;n giải l&agrave;&nbsp;<strong>1440x900px</strong>.&nbsp;</p>\r\n\r\n<p>Mặc d&ugrave; chưa được n&acirc;ng cấp l&ecirc;n m&agrave;n h&igrave;nh Retina si&ecirc;u n&eacute;t như c&aacute;c d&ograve;ng kh&aacute;c, m&agrave;n h&igrave;nh MacBook Air 2017 vẫn cho ra&nbsp;<strong>m&agrave;u sắc trung thực</strong>, v&agrave; độ&nbsp;<strong>tương phản kh&aacute; tốt</strong>&nbsp;nhờ tấm nền LED Backlit chất lượng cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-manhinh.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của laptop Macbook Air MQD32SA/A i5 5350U\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-manhinh.jpg\" title=\"Màn hình của laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n\r\n<h3>Vẫn giữ đầy đủ c&aacute;c cổng kết nối cần thiết</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U l&agrave; d&ograve;ng MacBook duy nhất ở hiện tại c&ograve;n trang bị 2 cổng&nbsp;<strong>USB 3.0</strong>,&nbsp;<strong>SD card</strong>&nbsp;gi&uacute;p kh&aacute;ch h&agrave;ng thuận tiện hơn trong việc gắn k&egrave;m phụ kiện như chuột, b&agrave;n ph&iacute;m hay chia sẻ dữ liệu qua USB, thẻ nhớ m&aacute;y ảnh&hellip;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-cong-ket-noi.jpg\" onclick=\"return false;\"><img alt=\"Khe cắm của laptop Macbook Air MQD32SA/A i5 5350U\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-cong-ket-noi.jpg\" title=\"Khe cắm của laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n\r\n<h3>B&agrave;n r&ecirc; cảm ứng mượt m&agrave;, b&agrave;n ph&iacute;m tối ưu sử dụng</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U với b&agrave;n r&ecirc; c&oacute; k&iacute;ch thước&nbsp;<strong>lớn&nbsp;</strong>v&agrave; bề mặt&nbsp;<strong>trơn&nbsp;</strong>nhẵn dễ thao t&aacute;c. Trackpad n&agrave;y c&ograve;n hỗ trợ nhiều thao t&aacute;c sử dụng tiện lợi như: Cuộn, thu ph&oacute;ng, chuyển qua lại giữa c&aacute;c ứng dụng, mở khay ứng dụng nhanh ch&oacute;ng...</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cu-chi-thong-minh-voi-trackpad-tren-mac-os-848490\" target=\"_blank\" title=\"Tổng hợp các cử chỉ thông minh với Trackpad trên MacBook\" type=\"Tổng hợp các cử chỉ thông minh với Trackpad trên MacBook\">Tổng hợp c&aacute;c cử chỉ th&ocirc;ng minh với Trackpad tr&ecirc;n MacBook</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-track-pad.jpg\" onclick=\"return false;\"><img alt=\"Bàn di chuột lớn trên MacBook Air 2017\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-track-pad.jpg\" title=\"Bàn di chuột lớn trên MacBook Air 2017\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m tr&ecirc;n MacBook Air 2017 cũng được trang bị&nbsp;<strong>đ&egrave;n nền</strong>&nbsp;gi&uacute;p bạn dễ d&agrave;ng sử dụng m&aacute;y trong b&oacute;ng tối.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-bac-7-2-org.jpg\" onclick=\"return false;\"><img alt=\"Bàn phím của laptop Macbook Air MQD32SA/A i5 5350U\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-bac-7-2-org.jpg\" title=\"Bàn phím của laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n', 0, 0, 'apple-macbook-air-mqd32sa-a-i5-5350u-400-1-450x300-400x400.jpg', 22490000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(15, 'Laptop Dell Inspiron ', 3, 'Laptop', '<p>Thiết kế thanh lịch, trọng lượng kh&aacute; nhẹ ph&ugrave; hợp cho việc di chuyển hằng ng&agrave;y đến lớp học, c&ocirc;ng sở -&nbsp;<a href=\"https://www.thegioididong.com/laptop/dell-inspiron-3576-p63f002n76f\" target=\"_blank\" title=\"Laptop Dell Inspiron 3576 i5 8250U chính hãng đang kinh doanh tại Thegioididong.com\">Laptop Dell Inspiron 3576 i5 8250U&nbsp;</a>được trang bị cấu h&igrave;nh đủ mạnh để chạy tốt c&aacute;c ứng dụng văn ph&ograve;ng, cho phản hồi c&aacute;c thao t&aacute;c k&eacute;o thả trong photoshop, AI kh&aacute; nhanh rất ph&ugrave; hợp cho&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Laptop chính hãng phù hợp cho sinh viên - nhân viên văn phòng đang kinh doanh chính hãng tại Thegioididong.com\">sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng.</a></p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/166521/Slider/-dell-inspiron-3576-p63f002n76f-slider-33397-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Pin,D&acirc;y nguồn,Adapter sạc,Th&ugrave;ng m&aacute;y,S&aacute;ch hướng dẫn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Thiết kế thanh lịch, trọng lượng kh&aacute; nhẹ ph&ugrave; hợp cho việc di chuyển hằng ng&agrave;y đến lớp học, c&ocirc;ng sở -&nbsp;<a href=\"https://www.thegioididong.com/laptop/dell-inspiron-3576-p63f002n76f\" target=\"_blank\" title=\"Laptop Dell Inspiron 3576 i5 8250U chính hãng đang kinh doanh tại Thegioididong.com\">Laptop Dell Inspiron 3576 i5 8250U&nbsp;</a>được trang bị cấu h&igrave;nh đủ mạnh để chạy tốt c&aacute;c ứng dụng văn ph&ograve;ng, cho phản hồi c&aacute;c thao t&aacute;c k&eacute;o thả trong photoshop, AI kh&aacute; nhanh rất ph&ugrave; hợp cho&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Laptop chính hãng phù hợp cho sinh viên - nhân viên văn phòng đang kinh doanh chính hãng tại Thegioididong.com\">sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng.</a></h2>\r\n\r\n<h3>Thiết kế đơn giản, thuận tiện di chuyển</h3>\r\n\r\n<p>Vỏ được l&agrave;m bằng nhựa nh&aacute;m bao bọc gi&uacute;p cho m&aacute;y hạn chế được bụi bẩn, trầy xước cũng như chịu va chạm tốt trong qu&aacute; tr&igrave;nh sử dụng. Chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-dell\" target=\"_blank\" title=\"Máy tính xách tay Dell chính hãng đang được kinh doanh tại Thegioididong.com\" type=\"Máy tính xách tay Dell chính hãng đang được kinh doanh tại Thegioididong.com\">m&aacute;y t&iacute;nh x&aacute;ch tay Dell</a>&nbsp;c&oacute; trọng lượng vừa phải chỉ 2.3 kg, v&igrave; vậy ch&uacute;ng ta c&oacute; thể mang theo b&ecirc;n m&igrave;nh khi l&agrave;m việc ngo&agrave;i c&aacute;c kh&ocirc;ng gian kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/166521/laptop-dell-inspiron-3576-p63f002n76f-9.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế truyền thống trên máy tính xách tay Dell Inspiron 3576\" src=\"https://cdn.tgdd.vn/Products/Images/44/166521/laptop-dell-inspiron-3576-p63f002n76f-9.jpg\" title=\"Thiết kế truyền thống trên máy tính xách tay Dell Inspiron 3576\" /></a></p>\r\n\r\n<h3>Xử l&yacute; tốt đồ hoạ cơ bản</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop chính hãng giá tốt, đang kinh doanh tại Thegioididong.com\">Laptop</a>&nbsp;Dell Inspiron 3576&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=core-i5\" target=\"_blank\" title=\"Laptop chính hãng trang bị chip xử lý core i5 đang kinh doanh chính hãng tại Thegioidiong.com\">core i5&nbsp;thế hệ 8</a>, xung nhịp&nbsp;1.6 Ghz kết hợp c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Laptop chính hãng trang bị 4GB RAM đang kinh doanh tại Thegioididong.com\">4GB RAM</a>&nbsp;đ&aacute;p ứng đầy đủ nhu cầu xử l&yacute; đồ hoạ cơ bản như photoshop, Ai. Chiến c&aacute;c game như li&ecirc;n minh huyền thoại, fifa online 3 kh&aacute; mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/166521/dell-inspiron-3576-p63f002n76f-33397-cauhinh.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình mạnh mẽ trên máy tính xách tay Dell Inspiron 3576 i5 8250U\" src=\"https://cdn.tgdd.vn/Products/Images/44/166521/dell-inspiron-3576-p63f002n76f-33397-cauhinh.jpg\" title=\"Máy tính xách tay Dell Inspiron 3576 i5 8250U có cấu hình mạnh mẽ\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh rộng trải nghiệm giải tr&iacute; ho&agrave;n hảo</h3>\r\n\r\n<p>Sở hữu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=tren-15\" target=\"_blank\" title=\"Laptop màn hình rông 15.6 inch đang kinh doanh chính hãng tại Thegioididong.com\">m&agrave;n h&igrave;nh rộng&nbsp;15.6 inch</a>&nbsp;c&ugrave;ng với đ&oacute; l&agrave; độ ph&acirc;n giải HD, với chất lượng ảnh tươi s&aacute;ng, kh&ocirc;ng gian l&agrave;m việc rộng r&atilde;i, dễ nh&igrave;n.</p>\r\n\r\n<h3>B&agrave;n ph&iacute;m to, r&otilde; độ nảy tốt thuận tiện đ&aacute;nh m&aacute;y</h3>\r\n\r\n<p>B&agrave;n ph&iacute;m fullsize tr&ecirc;n m&aacute;y t&iacute;nh x&aacute;ch tay Dell Inspiron 3576 ph&ugrave; hợp cho kế to&aacute;n, người thường sử dụng b&agrave;n ph&iacute;m số để nhập liệu. Độ nảy ph&iacute;m tốt sẽ gi&uacute;p g&otilde; ph&iacute;m ch&iacute;nh x&aacute;c, hạn chế sai lỗi ch&iacute;nh tả kh&ocirc;ng cần thiết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/166521/dell-inspiron-3576-p63f002n76f-33397-banphim.jpg\" onclick=\"return false;\"><img alt=\"Bàn phím của máy tính xách tay Dell Inspiron 3576 i5 8250U\" src=\"https://cdn.tgdd.vn/Products/Images/44/166521/dell-inspiron-3576-p63f002n76f-33397-banphim.jpg\" title=\"Bàn phím của máy tính xách tay Dell Inspiron 3576 i5 8250U\" /></a></p>\r\n\r\n<h3>Ổ đĩa quang thuận tiện học tập,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-30-la-gi-926737\" target=\"_blank\" title=\"USB 3.0 là gì? | Thegioididong.com\">USB 3.0&nbsp;</a>truyền dữ liệu nhanh ch&oacute;ng</h3>\r\n\r\n<p>M&aacute;y c&oacute; ổ đĩa quang gi&uacute;p bạn xem phim hay học tiếng Anh với đĩa DVD dễ d&agrave;ng hơn. Một điểm cộng tiếp theo m&aacute;y vẫn trang bị cổng mạng LAN gi&uacute;p bạn kết nối tiện lợi với internet khi Wi-Fi yếu.&nbsp;K&egrave;m theo cổng USB 2.0 l&agrave;&nbsp;2 x USB 3.0&nbsp;gi&uacute;p truyền tải dữ liệu nhanh hơn. Th&ecirc;m nữa, cổng HDMI cho ph&eacute;p kết nối với tivi hoặc m&aacute;y chiếu tiện lợi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/166521/dell-inspiron-3576-p63f002n76f-33397-congketnoi.jpg\" onclick=\"return false;\"><img alt=\"Cổng kết nối của máy tính xách tay Dell Inspiron 3576 i5 8250U\" src=\"https://cdn.tgdd.vn/Products/Images/44/166521/dell-inspiron-3576-p63f002n76f-33397-congketnoi.jpg\" title=\"Đầy đủ các cổng kết nối trên Dell Inspiron 3576 i5 8250U\" /></a></p>\r\n', 0, 0, '1563785416dell-inspiron-3576-p63f002n76f-450-2-600x600.jpg', 14390000),
(16, 'Laptop Asus VivoBook A411UA ', 3, 'Laptop', '<p><a href=\"https://www.thegioididong.com/laptop/asus-a411ua-i3-8130u-eb688t\" target=\"_blank\" title=\"Asus A411UA (EB688T) | Thegioididong\">Asus A411UA (EB688T)</a>&nbsp;l&agrave; chiếc laptop sở hữu cấu h&igrave;nh cho hiệu năng hoạt động ổn định với chip Intel Core i3 thế hệ thứ 8, 4 GB RAM, ổ cứng lưu trữ HDD 1 TB, c&ugrave;ng hệ điều h&agrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/windows-10-co-tot-khong-953411\" target=\"_blank\" title=\"Tìm hiểu về hệ điều hành Windows 10\">Windows bản quyền</a>&nbsp;được c&agrave;i sẵn tr&ecirc;n m&aacute;y.&nbsp;Asus A411UA ph&ugrave; hợp với những ai đang t&igrave;m một mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Máy tính văn phòng, học tập giá tốt\" type=\"Máy tính văn phòng, học tập giá tốt\">m&aacute;y t&iacute;nh văn ph&ograve;ng</a>, hay giải tr&iacute;, học tập, sinh vi&ecirc;n.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/177932/Slider/vi-vn-thiet-ke.jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: D&acirc;y nguồn,Adapter sạc,Th&ugrave;ng m&aacute;y,S&aacute;ch hướng dẫn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/laptop/asus-a411ua-i3-8130u-eb688t\" target=\"_blank\" title=\"Asus A411UA (EB688T) | Thegioididong\">Asus A411UA (EB688T)</a>&nbsp;l&agrave; chiếc laptop sở hữu cấu h&igrave;nh cho hiệu năng hoạt động ổn định với chip Intel Core i3 thế hệ thứ 8, 4 GB RAM, ổ cứng lưu trữ HDD 1 TB, c&ugrave;ng hệ điều h&agrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/windows-10-co-tot-khong-953411\" target=\"_blank\" title=\"Tìm hiểu về hệ điều hành Windows 10\">Windows bản quyền</a>&nbsp;được c&agrave;i sẵn tr&ecirc;n m&aacute;y.&nbsp;Asus A411UA ph&ugrave; hợp với những ai đang t&igrave;m một mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Máy tính văn phòng, học tập giá tốt\" type=\"Máy tính văn phòng, học tập giá tốt\">m&aacute;y t&iacute;nh văn ph&ograve;ng</a>, hay giải tr&iacute;, học tập, sinh vi&ecirc;n.</h2>\r\n\r\n<h3>Thiết kế truyền thống đơn giản</h3>\r\n\r\n<p>M&aacute;y với độ d&agrave;y chỉ 18.8 mm c&ugrave;ng với trọng lượng chỉ&nbsp;<strong>1.43 kg,</strong><a href=\"https://www.thegioididong.com/laptop?g=mong-nhe-thoi-trang\" target=\"_blank\" title=\"Xem thêm các mẫu laptop nhỏ gọn, mỏng nhẹ khác\" type=\"Xem thêm các mẫu laptop nhỏ gọn, mỏng nhẹ khác\">&nbsp;laptop nhỏ gọn</a><strong>&nbsp;</strong>tiện lợi cho người d&ugrave;ng mang theo sử dụng mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-tk.jpg\" onclick=\"return false;\"><img alt=\"Máy tính văn phòng Asus A411UA (EB688T) có thiết kế truyền thống đơn giản\" src=\"https://cdn.tgdd.vn/Products/Images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-tk.jpg\" title=\"Máy tính văn phòng Asus A411UA (EB688T) có thiết kế truyền thống đơn giản\" /></a></p>\r\n\r\n<h3>Hiệu năng ổn định với Core i3 thế hệ thứ 8</h3>\r\n\r\n<p>Asus A411UA (EB688T) được trang bị chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cpu-intel-kabylake-refresh-la-gi-1017640\" target=\"_blank\" title=\"CPU Intel Kabylake Refresh là gì?\">Intel Core i3</a>&nbsp;với thế hệ thứ 8 c&ugrave;ng 4 GB RAM cho c&aacute;c t&aacute;c vụ cơ bản mượt m&agrave; khi thao t&aacute;c như soạn thảo, nhập liệu văn bản hay giải tr&iacute; nhẹ nh&agrave;ng. B&ecirc;n cạnh đ&oacute;, ổ cứng HDD 1 TB đem lại khả năng lưu trữ dữ liệu thoải m&aacute;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-ch.jpg\" onclick=\"return false;\"><img alt=\"Máy tính laptop Asus A411UA (EB688T) xử lý tốt các tác vụ văn phòng\" src=\"https://cdn.tgdd.vn/Products/Images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-ch.jpg\" title=\"Máy tính laptop Asus A411UA (EB688T) xử lý tốt các tác vụ văn phòng\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Full HD 14 inch</h3>\r\n\r\n<p>Với k&iacute;ch thước 14 inch đồng sở hữu viền m&agrave;n h&igrave;nh mỏng kết hợp c&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cac-cong-nghe-hien-thi-tren-man-hinh-laptop-597377#splendvideo\" target=\"_blank\" title=\"Công nghệ màn hình Splendid Video Intelligent\">ASUS Splendid Video</a>&nbsp;mang lại chất lượng h&igrave;nh ảnh&nbsp;sắc n&eacute;t, rực rỡ v&agrave; ch&acirc;n thực hơn, thế n&ecirc;n m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\" title=\"Laptop Asus giá tốt | Thegioididong\">laptop Asus</a>&nbsp;d&ugrave; nhỏ nhưng vẫn đủ d&ugrave;ng khi l&agrave;m việc cũng như học tập v&agrave; giải tr&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-mh.jpg\" onclick=\"return false;\"><img alt=\"Máy tính văn phòng Asus A411UA (EB688T) có Màn hình Full HD 14 inch\" src=\"https://cdn.tgdd.vn/Products/Images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-mh.jpg\" title=\"Máy tính văn phòng Asus A411UA (EB688T) có Màn hình Full HD 14 inch\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/hoi-dap/windows-hello-la-gi-cach-cai-dat-windows-hello-1013813\" target=\"_blank\" title=\"Cách cài đặt bảo mật vân tay trên Windows Hello\">Bảo mật v&acirc;n tay</a>&nbsp;tối ưu</h3>\r\n\r\n<p>Chỉ với một c&aacute;i chạm bằng v&acirc;n tay tr&ecirc;n TouchPad cho khả năng mở m&aacute;y nhanh ch&oacute;ng, tiện lợi. Đồng thời dữ liệu tr&ecirc;n m&aacute;y được bảo vệ tối ưu hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-bnvt.jpg\" onclick=\"return false;\"><img alt=\"Bảo mật vân tay trên máy tính văn phòng Asus A411UA (EB688T)\" src=\"https://cdn.tgdd.vn/Products/Images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-bnvt.jpg\" title=\"Bảo mật vân tay trên máy tính văn phòng Asus A411UA (EB688T)\" /></a></p>\r\n\r\n<h3>Cổng kết nối đa dạng</h3>\r\n\r\n<p>B&ecirc;n cạnh cổng USB 2.0, USB 3.0 truyền thống,&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">laptop</a>&nbsp;Asus A411UA (EB688T) c&ograve;n được trang bị th&ecirc;m cổng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-type-c-chua-n-mu-c-co-ng-ke-t-no-i-mo-i-723760\" target=\"_blank\" title=\"Tìm hiểu Cổng USB Type-C\">USB Type-C</a>&nbsp;cho tốc độ sao ch&eacute;p dữ liệu nhanh hơn.</p>\r\n\r\n<p>Việc truy xuất h&igrave;nh ảnh, &acirc;m thanh từ m&aacute;y t&iacute;nh sang m&agrave;n h&igrave;nh lớn như m&aacute;y chiếu, tivi sẽ dễ d&agrave;ng v&agrave; tiện lợi hơn với cổng&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/hdmi-la-gi--590061\" target=\"_blank\" title=\"Tìm hiểu cổng HDMI\">HDMI</a>&nbsp;được trang bị tr&ecirc;n m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-ckn.jpg\" onclick=\"return false;\"><img alt=\"laptop Asus A411UA (EB688T) - máy ính văn phòng với Cổng kết nối đa dạng\" src=\"https://cdn.tgdd.vn/Products/Images/44/177932/asus-a411ua-i3-8130u-eb688t-tgdd-ckn.jpg\" title=\"laptop Asus A411UA (EB688T) - Cổng kết nối đa dạng | Thegioididong\" /></a></p>\r\n', 0, 0, 'asus-a411ua-i3-8130u-eb688t-ava-400x400.jpg', 11290000),
(17, 'Laptop Acer Aspire E5 476 ', 3, 'Laptop', '<p><a href=\"https://www.thegioididong.com/laptop/acer-aspire-e5-476-i3-8130u-nxgwtsv002\" target=\"_blank\" title=\"Máy tính Acer aspire e5 476 chính hãng, giá tốt, có trả góp tại Thế Giới Di Động\">Acer Aspire E5 476</a>&nbsp;i3 8130U l&agrave; phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Máy tính xách tay chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;với cấu h&igrave;nh cao, sử dụng vi xử l&yacute; mạnh mẽ trong ph&acirc;n kh&uacute;c&nbsp;nhưng vẫn rất tiết kiệm pin do sử dụng kiến tr&uacute;c chip mới từ Intel.&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer\" target=\"_blank\" title=\"Máy tính xách tay Acer chính hãng, giá tốt, có trả góp tại Thế Giới Di Động\">Laptop Acer</a>&nbsp;với mức gi&aacute; th&agrave;nh hợp l&yacute; c&ugrave;ng cấu h&igrave;nh cực k&igrave; mạnh mẽ, Aspire E5 476 c&oacute; thể đ&aacute;p ứng tốt cho người d&ugrave;ng phổ th&ocirc;ng cần một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Máy tính văn phòng, học tập, giải trí\" type=\"Máy tính văn phòng, học tập, giải trí\">m&aacute;y t&iacute;nh văn ph&ograve;ng</a>, giải tr&iacute;.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/160296/Slider/-acer-aspire-e5-476-i3-8130u-nxgwtsv002-slider-33397-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: D&acirc;y nguồn,Adapter sạc,Th&ugrave;ng m&aacute;y,S&aacute;ch hướng dẫn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/laptop/acer-aspire-e5-476-i3-8130u-nxgwtsv002\" target=\"_blank\" title=\"Máy tính Acer aspire e5 476 chính hãng, giá tốt, có trả góp tại Thế Giới Di Động\">Acer Aspire E5 476</a>&nbsp;i3 8130U l&agrave; phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Máy tính xách tay chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;với cấu h&igrave;nh cao, sử dụng vi xử l&yacute; mạnh mẽ trong ph&acirc;n kh&uacute;c&nbsp;nhưng vẫn rất tiết kiệm pin do sử dụng kiến tr&uacute;c chip mới từ Intel.&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer\" target=\"_blank\" title=\"Máy tính xách tay Acer chính hãng, giá tốt, có trả góp tại Thế Giới Di Động\">Laptop Acer</a>&nbsp;với mức gi&aacute; th&agrave;nh hợp l&yacute; c&ugrave;ng cấu h&igrave;nh cực k&igrave; mạnh mẽ, Aspire E5 476 c&oacute; thể đ&aacute;p ứng tốt cho người d&ugrave;ng phổ th&ocirc;ng cần một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Máy tính văn phòng, học tập, giải trí\" type=\"Máy tính văn phòng, học tập, giải trí\">m&aacute;y t&iacute;nh văn ph&ograve;ng</a>, giải tr&iacute;.</h2>\r\n\r\n<h3>Thiết kế</h3>\r\n\r\n<p>M&aacute;y mang thiết kế đơn giản quen thuộc của đa số&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"các dòng laptop hiện đang bán tại thế giới di động\">c&aacute;c d&ograve;ng laptop hiện giờ</a>&nbsp;với bộ khung cứng c&aacute;p, chắc chắn, ngoại h&igrave;nh gọn g&agrave;ng. Th&acirc;n m&aacute;y được l&agrave;m thiết kế hoa văn v&acirc;n nổi tăng th&ecirc;m phần cao cấp, độc đ&aacute;o cho vẻ ngo&agrave;i của m&aacute;y. Vỏ nhựa gi&uacute;p m&aacute;y bền bỉ hơn chịu được va chạm tốt hơn v&agrave; giảm thiểu trọng lượng nhằm tăng t&iacute;nh di động gi&uacute;p người d&ugrave;ng dễ d&agrave;ng mang theo.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-thietke.jpg\" onclick=\"return false;\"><img alt=\"Thiế kế đơn giản thanh lịch trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" src=\"https://cdn.tgdd.vn/Products/Images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-thietke.jpg\" title=\"Thiế kế đơn giản thanh lịch trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh</h3>\r\n\r\n<p>Sở hữu một trong những vi xử l&yacute; mới nhất từ Intel, cho hiệu năng cao, ổn định v&agrave; tiết kiệm pin.&nbsp;<strong>Chip Core i3 thế hệ mới</strong>&nbsp;cho xung nhịp đến 3.4 GHz kết hợp c&ugrave;ng&nbsp;<strong>4 GB</strong>&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/ram-ddr4-la-gi-882173\" target=\"_blank\" title=\"ddr 4 là gì\">DDR4</a>&nbsp;tốc độ cao cho khả năng chơi tốt một số game nhẹ như game di động, web game, hay những game giải tr&iacute; nhẹ nh&agrave;ng.</p>\r\n\r\n<p>Về khả năng xử l&yacute; c&ocirc;ng việc, m&aacute;y c&oacute; thể xử l&yacute; mượt m&agrave; c&aacute;c t&aacute;c vụ phổ th&ocirc;ng như lướt web, xem phim, l&agrave;m c&ocirc;ng việc văn ph&ograve;ng một c&aacute;ch dễ d&agrave;ng, ngo&agrave;i ra m&aacute;y c&oacute; thể l&agrave;m đồ hoạ nhẹ, ph&ugrave; hợp với người d&ugrave;ng cần một chiếc m&aacute;y đa năng, mạnh mẽ về cấu h&igrave;nh v&agrave; gọn nhẹ.</p>\r\n\r\n<p>Nhờ c&oacute;&nbsp;<strong><a href=\"https://www.thegioididong.com/hoi-dap/card-do-hoa-tich-hop-la-gi-950047\" target=\"_blank\" title=\"Card đồ hoạ tích hợp là gì\">vi xử l&yacute; đồ hoạ t&iacute;ch hợp</a>&nbsp;thế hệ mới UHD 620</strong>&nbsp;từ Intel cho khả năng xuất h&igrave;nh ảnh l&ecirc;n đến độ ph&acirc;n giải 4K mạnh mẽ hơn thế hệ cũ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-cauhinh.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình ổn định trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" src=\"https://cdn.tgdd.vn/Products/Images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-cauhinh.jpg\" title=\"Cấu hình ổn định trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh</h3>\r\n\r\n<p>Trang bị m&agrave;n h&igrave;nh 14 inch c&ugrave;ng độ ph&acirc;n giải HD, m&aacute;y c&oacute; khả năng hiển thị kh&aacute; tốt c&aacute;c h&igrave;nh ảnh, nội dung. M&agrave;u sắc h&agrave;i ho&agrave; đ&aacute;p ứng tốt c&aacute;c nhu cầu cơ bản về chất lượng h&igrave;nh ảnh tr&ecirc;n laptop. K&iacute;ch thước m&agrave;n h&igrave;nh ở mức 14 inch cũng gi&uacute;p m&aacute;y c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh vừa phải, dễ nh&igrave;n, h&igrave;nh ảnh kh&ocirc;ng bị qu&aacute; nhỏ g&acirc;y hại mắt khi l&agrave;m việc trong thời gian d&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-manhinh.jpg\" onclick=\"return false;\"><img alt=\"Màn hình tươi sáng trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" src=\"https://cdn.tgdd.vn/Products/Images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-manhinh.jpg\" title=\"Màn hình tươi sáng trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>&Acirc;m thanh</h3>\r\n\r\n<p>Nằm ờ ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://www.thegioididong.com/laptop?p=duoi-10-trieu\" target=\"_blank\" title=\"Laptop giá rẻ dưới 10 triệu đang được kinh doanh tại Thegioididong.com\">laptop gi&aacute; rẻ</a>&nbsp;nhưng m&aacute;y vẫn được trang bị&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-am-thanh-trueharmony-co-gi-dac-biet-1017616\" target=\"_blank\" title=\"công nghệ trueharmony có gì đặc biệt\">c&ocirc;ng nghệ TrueHarmony</a>&nbsp;cho &acirc;m thanh sống động, ch&acirc;n thực. Sử dụng loa k&eacute;p stereo cho &acirc;m thanh hai chiều gi&uacute;p trải nghiệm &acirc;m thanh khi chơi game, xem phim hay hơn. C&ocirc;ng nghệ TrueHarmony c&ograve;n gi&uacute;p t&aacute;i tạo &acirc;m thanh v&ograve;m, tăng cường bass mang lại &acirc;m thanh mạnh mẽ. Ngo&agrave;i ra, m&aacute;y c&ograve;n t&iacute;ch hợp microphone gi&uacute;p gọi điện video tr&ecirc;n laptop m&agrave; kh&ocirc;ng cần tai nghe.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-amthanh1.jpg\" onclick=\"return false;\"><img alt=\"Âm thanh to, rõ trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" src=\"https://cdn.tgdd.vn/Products/Images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-amthanh1.jpg\" title=\"Âm thanh to, rõ trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Hệ điều h&agrave;nh v&agrave; c&aacute;c kết nối</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer\" target=\"_blank\" title=\"Laptop acer đang bán tại thế giới di động\">laptop đến từ Acer</a>&nbsp;sử dụng Windows 10 c&oacute; bản quyền gi&uacute;p m&aacute;y hoạt động ổn định hơn, nhận được c&aacute;c bản cập nhật đều đặn nhanh ch&oacute;ng từ Microsoft gi&uacute;p sửa lỗi v&agrave; tăng t&iacute;nh bảo mật cho m&aacute;y, hạn chế virus x&acirc;m nhập. Ngo&agrave;i ra m&aacute;y cũng sẽ nhận được c&aacute;c thay đổi về giao diện mới nhất khi sử dụng hệ điều h&agrave;nh c&oacute; bản quyền.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&aacute;y c&oacute; 3&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cong-giao-tiep-usb-la-gi--590168\" target=\"_blank\" title=\"Cổng usb là gì\">cổng USB</a>&nbsp;tiện lợi, 1&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/hdmi-la-gi--590061\" target=\"_blank\" title=\"Cổng HDMI là gì\">cổng HDMI</a>&nbsp;v&agrave; 1&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/vga-la-gi-920575\" target=\"_blank\" title=\"Cổng VGA là gì\">cổng VGA</a>&nbsp;gi&uacute;p kết nối dễ d&agrave;ng để ph&aacute;t h&igrave;nh l&ecirc;n c&aacute;c loại m&aacute;y chiếu hoặc tivi dễ d&agrave;ng, 1&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cho-minh-hoi-thong-so-laptop-co-nhung-cai-nay-10-743872\" target=\"_blank\" title=\"Cổng cắm mạng LAN là gì\">cổng LAN</a>&nbsp;gi&uacute;p kết nối mạng c&oacute; d&acirc;y cho đường truyền nhanh v&agrave; ổn định hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-congketnoi.jpg\" onclick=\"return false;\"><img alt=\"Đa dạng các cổng kêt nối trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" src=\"https://cdn.tgdd.vn/Products/Images/44/160296/acer-aspire-e5-476-i3-8130u-nxgwtsv002-33397-congketnoi.jpg\" title=\"Đa dạng các cổng kêt nối trên máy tính văn phòng Acer Aspire E5 476 i3 8130U\" /></a></p>\r\n', 0, 0, 'acer-aspire-e5-476-i3-8130u-nxgwtsv002-ava-400x400.jpg', 9490000),
(18, 'Laptop Dell Inspiron 3576 ', 3, 'Laptop', '<p><a href=\"https://www.thegioididong.com/laptop/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi\" target=\"_blank\" title=\"Laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W) chính hãng đang được kinh doanh tại Thegioididong.com\">Laptop Dell Inspiron 3576&nbsp;(C5I3133W)&nbsp;</a>vừa được cho ra mắt với thiết kế mạnh mẽ nhưng cũng kh&ocirc;ng k&eacute;m phần thanh lịch. C&ugrave;ng với&nbsp;đ&oacute; l&agrave; một cấu h&igrave;nh mạnh mẽ,&nbsp;đ&aacute;p&nbsp;ứng kh&aacute; mượt m&agrave; c&aacute;c&nbsp;ứng dụng văn ph&ograve;ng cũng như xử l&yacute; tốt c&aacute;c thao t&aacute;c&nbsp;đồ họa th&igrave; chắc chắn,&nbsp;đấy sẽ l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu máy tính xách tay đang kinh doanh tại Thegioididong.com\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;đ&aacute;ng&nbsp;để c&acirc;n nhắc d&agrave;nh cho kh&aacute;ch h&agrave;ng l&agrave; nh&acirc;n vi&ecirc;n văn ph&ograve;ng hoặc học sinh, sinh vi&ecirc;n trong c&ugrave;ng ph&acirc;n kh&uacute;c.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/198907/Slider/-dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-slider-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Pin,Hộp,D&acirc;y nguồn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/laptop/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi\" target=\"_blank\" title=\"Laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W) chính hãng đang được kinh doanh tại Thegioididong.com\">Laptop Dell Inspiron 3576&nbsp;(C5I3133W)&nbsp;</a>vừa được cho ra mắt với thiết kế mạnh mẽ nhưng cũng kh&ocirc;ng k&eacute;m phần thanh lịch. C&ugrave;ng với&nbsp;đ&oacute; l&agrave; một cấu h&igrave;nh mạnh mẽ,&nbsp;đ&aacute;p&nbsp;ứng kh&aacute; mượt m&agrave; c&aacute;c&nbsp;ứng dụng văn ph&ograve;ng cũng như xử l&yacute; tốt c&aacute;c thao t&aacute;c&nbsp;đồ họa th&igrave; chắc chắn,&nbsp;đấy sẽ l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu máy tính xách tay đang kinh doanh tại Thegioididong.com\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;đ&aacute;ng&nbsp;để c&acirc;n nhắc d&agrave;nh cho kh&aacute;ch h&agrave;ng l&agrave; nh&acirc;n vi&ecirc;n văn ph&ograve;ng hoặc học sinh, sinh vi&ecirc;n trong c&ugrave;ng ph&acirc;n kh&uacute;c.</h2>\r\n\r\n<h3>&nbsp;Thiết kế mạnh mẽ nhưng cũng kh&ocirc;ng k&eacute;m phần thanh lịch</h3>\r\n\r\n<p><strong>Laptop Dell Inspiron 3576 (C5I3133W)</strong>&nbsp;được trau chuốt tỉ mỉ v&agrave; ho&agrave;n thiện từ chất liệu nhựa với t&ocirc;ng m&agrave;u đen l&agrave;m chủ đạo, c&ugrave;ng với đ&oacute; l&agrave; c&aacute;c g&oacute;c cạnh được bo tr&ograve;n nhẹ tạo n&ecirc;n cho sản phẩm một sự mạnh mẽ nhưng kh&ocirc;ng k&eacute;m phần thanh lịch.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-thietketgdd.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế hài hoà trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" src=\"https://cdn.tgdd.vn/Products/Images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-thietketgdd.jpg\" title=\"Thiết kế hài hoà trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" /></a></h2>\r\n\r\n<h3>&nbsp;H&igrave;nh&nbsp;ảnh ch&acirc;n thật, sắc n&eacute;t&nbsp;đến từng chi tiết</h3>\r\n\r\n<p>Nh&agrave; sản xuất Dell đ&atilde; trang bị c&ocirc;ng nghệ&nbsp;<strong>chống ch&oacute;i, chống l&oacute;a AntiGlare</strong>&nbsp;tr&ecirc;n chiếc m&agrave;n h&igrave;nh&nbsp;<strong>15.6 inch</strong>&nbsp;c&oacute; độ ph&acirc;n giải&nbsp;<strong>Full HD</strong>&nbsp;của chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-dell\" target=\"_blank\" title=\"Xem thêm các mẫu laptop Dell chính hãng đang kinh doanh tại Thegioididong.com\">laptop Dell</a>&nbsp;<strong>Inspiron 3576 (C5I3133W)</strong>&nbsp;gi&uacute;p mang đến cho người d&ugrave;ng những trải nghiệm h&igrave;nh ảnh tuyệt vời đến từng chi tiết ngay cả khi sử dụng trong điều kiện ngược s&aacute;ng.<a href=\"https://www.thegioididong.com/images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-manhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Màn hình sắc nét trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" src=\"https://cdn.tgdd.vn/Products/Images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-manhinhtgdd.jpg\" title=\"Màn hình sắc nét trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh mạnh mẽ</h3>\r\n\r\n<p><strong>Laptop Dell Inspiron 3576 (C5I3133W)</strong>&nbsp;được trang bị chip xử l&yacute; mạnh mẽ thế hệ thứ 7 Intel core i3-7020U, b&ecirc;n cạnh đ&oacute; l&agrave; bộ nhớ Ram DDR4 4GB (c&oacute; thể n&acirc;ng cấp l&ecirc;n đến 32GB)&nbsp;&nbsp;mang đến khả năng xử l&yacute; mượt m&agrave; c&aacute;c ứng dụng văn ph&ograve;ng cơ bản.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-cauhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình mạnh mẽ trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" src=\"https://cdn.tgdd.vn/Products/Images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-cauhinhtgdd.jpg\" title=\"Cấu hình mạnh mẽ trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" /></a></h2>\r\n\r\n<h3>Xử l&yacute;&nbsp;đồ họa mượt m&agrave; hơn c&ugrave;ng&nbsp;<strong>AMD Radeon 520</strong></h3>\r\n\r\n<p>Cấu h&igrave;nh mạnh mẽ của Dell Inspiron 3576 (C5I3133W) sẽ được tiếp th&ecirc;m sức mạnh nhờ trợ thủ đắc lực l&agrave; chiếc<strong>&nbsp;card đồ họa AMD Radeon 520</strong>, gi&uacute;p cho việc xử l&yacute; c&aacute;c ứng dụng đồ họa như photoshop, ai,... sẽ trở n&ecirc;n mượt m&agrave; hơn.</p>\r\n\r\n<h3>&nbsp;<a href=\"https://www.thegioididong.com/images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-card-roi.jpg\" onclick=\"return false;\"><img alt=\"Card đồ hoạ rời chiếc game thoả thích trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" src=\"https://cdn.tgdd.vn/Products/Images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-card-roi.jpg\" title=\"Card đồ hoạ rời chiếc game thoả thích trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" /></a></h3>\r\n\r\n<h3>Nghe nhạc trầm ấm</h3>\r\n\r\n<p>Laptop Dell Inspiron 3576 (C5I3133W) được trang bị&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cong-nghe-am-thanh-tren-laptop-598521#maxxaudio\" target=\"_blank\" title=\"Tìm hiểu về công nghệ âm thanh trên laptop cùng Thegioididong.com\">c&ocirc;ng nghệ &acirc;m thanh Waves MaxxAudio</a>&nbsp;gi&uacute;p tăng cường v&agrave; đưa chất lượng &acirc;m thanh ph&ograve;ng thu đến gần hơn với người sử dụng cũng như tạo n&ecirc;n những trải nghiệm &acirc;m thanh tuyệt vời nhất cho người d&ugrave;ng.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-amthanh.jpg\" onclick=\"return false;\"><img alt=\"Âm thanh to, rõ trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" src=\"https://cdn.tgdd.vn/Products/Images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-amthanh.jpg\" title=\"Âm thanh to, rõ trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" /></a></h2>\r\n\r\n<h3>&nbsp;Cổng kết nối USB 3.1 truyền dữ liệu nhanh ch&oacute;ng</h3>\r\n\r\n<p>Ngo&agrave;i những cổng kết nối căn bản th&igrave;&nbsp;&nbsp;laptop Dell Inspiron 3576 (C5I3133W) c&ograve;n được t&iacute;ch hợp sẵn 2 cổng kết nối&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-31-la-gi-926746\" target=\"_blank\" title=\"USB 3.1 là gì? | Thegioididong.com\">USB 3.1</a>&nbsp;với tốc độ truyền tải dữ liệu l&ecirc;n đến 600MB/s gi&uacute;p bạn c&oacute; thể truyền dữ liệu một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-congkenoi.jpg\" onclick=\"return false;\"><img alt=\"Đầy đủ các cổng kết nối trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" src=\"https://cdn.tgdd.vn/Products/Images/44/198907/dell-inspiron-3576-i3-7020u-4gb-1tb-2gb-amd-520-wi-33397-content-congkenoi.jpg\" title=\"Đầy đủ các cổng kết nối trên laptop văn phòng Dell Inspiron 3576 i3 (C5I3133W)\" /></a></p>\r\n', 0, 0, 'dell-inspiron-3576-p63f002n76b-450-400x400.png', 12990000),
(19, 'Laptop Lenovo Ideapad 330S 14IKBR ', 3, 'Laptop', '<p><a href=\"https://www.thegioididong.com/laptop/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10#danhgia\" target=\"_blank\" title=\"Laptop Lenovo Ideapad 330S 14IKBR  chính hãng đang kinh doanh tại Thegioididong.com\">Laptop Ideapad 330S 14IKBR (81F400NLVN)</a>&nbsp;vừa được Lenovo cho ra mắt nhằm hướng tới kh&aacute;ch h&agrave;ng l&agrave; sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng, người thường xuy&ecirc;n di chuyển với trọng lượng m&aacute;y nhẹ v&agrave; thiết kế hiện đại. Cấu h&igrave;nh m&aacute;y chạy tốt c&aacute;c ứng dụng văn ph&ograve;ng, cho phản hồi c&aacute;c thao t&aacute;c k&eacute;o thả trong ứng dụng đồ hoạ nhanh ch&oacute;ng.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/195078/Slider/-lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-slider-33397-thieke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, D&acirc;y nguồn, S&aacute;ch hướng dẫn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/laptop/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10#danhgia\" target=\"_blank\" title=\"Laptop Lenovo Ideapad 330S 14IKBR  chính hãng đang kinh doanh tại Thegioididong.com\">Laptop Ideapad 330S 14IKBR (81F400NLVN)</a>&nbsp;vừa được Lenovo cho ra mắt nhằm hướng tới kh&aacute;ch h&agrave;ng l&agrave; sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng, người thường xuy&ecirc;n di chuyển với trọng lượng m&aacute;y nhẹ v&agrave; thiết kế hiện đại. Cấu h&igrave;nh m&aacute;y chạy tốt c&aacute;c ứng dụng văn ph&ograve;ng, cho phản hồi c&aacute;c thao t&aacute;c k&eacute;o thả trong ứng dụng đồ hoạ nhanh ch&oacute;ng.</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thiết kế thời trang, tiện lợi di chuyển</h3>\r\n\r\n<p>Được ho&agrave;n thiện từ chất liệu nhựa, trọng lượng m&aacute;y chỉ 1.67kg v&agrave; mỏng 18.9 mm ph&ugrave; hợp cho việc di chuyển hằng ng&agrave;y của bạn c&ugrave;ng chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Máy tính xách tay chính hãng đang kinh doanh chính hãng tại Thegioididong.com\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>. C&aacute;c khớp nối m&agrave;n h&igrave;nh v&agrave; th&acirc;n m&aacute;y cứng c&aacute;p, bo cong nhẹ ở c&aacute;c cạnh tạo sự thanh tho&aacute;t, hiện đại trong thiết kế.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-thietketgdd.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế thanh lịch trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" src=\"https://cdn.tgdd.vn/Products/Images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-thietketgdd.jpg\" title=\"Thiết kế thanh lịch trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh xem phim tươi s&aacute;ng, sắc n&eacute;t</h3>\r\n\r\n<p>K&iacute;ch thước&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=khoang-14\" target=\"_blank\" title=\"Laptop màn hình 14 inch đang kinh doanh chính hãng tại Thegioididong.com\">m&agrave;n h&igrave;nh 14 inch</a>&nbsp;tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop chính hãng đang được kinh doanh tại Thegioididong.com\">Laptop</a>&nbsp;Lenovo Ideapad 330S 14IKBR (81F400NLVN) với độ ph&acirc;n giải Full HD (1920 x 1080) cho chất lượng ảnh sắc n&eacute;t ở từng chi tiết.&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/ips-la-gi-807724\" target=\"_blank\" title=\"Tấm nền IPS là gì? Thegioididong.com\">Tấm nền IPS</a>&nbsp;gi&uacute;p bạn mở rộng g&oacute;c nh&igrave;n, xem phim c&ugrave;ng bạn b&egrave; ở nhiều vị tr&iacute; kh&aacute;c nhau nhưng kh&ocirc;ng bị mờ hay nho&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-manhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Màn hình sắc nét trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" src=\"https://cdn.tgdd.vn/Products/Images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-manhinhtgdd.jpg\" title=\"Màn hình sắc nét trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" /></a></p>\r\n\r\n<h3>Hiệu năng xử l&yacute; tốt ứng dụng văn ph&ograve;ng</h3>\r\n\r\n<p>Với chip xử l&yacute;&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=core-i5\" target=\"_blank\" title=\"Laptop core i5 đang được kinh doanh chính hãng tại Thegioididong.com\">core i5 thế hệ 8</a>, c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Laptop trang bị 4GB RAM đang kinh doanh chính hãng tại Thegioididong.com\">4GB RAM DDR4</a>&nbsp;gi&uacute;p chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-lenovo\" target=\"_blank\" title=\"Laptop Lenovo chính hãng đang được kinh doanh tại Thegioididong.com\">laptop Lenovo</a>&nbsp;chạy tốt c&aacute;c ứng dụng văn ph&ograve;ng như word, excel, powerpoint. Cho phản hồi c&aacute;c thao t&aacute;c k&eacute;o thả trong photoshop nhanh ch&oacute;ng.</p>\r\n\r\n<p>Việc hỗ trợ khe cắm&nbsp;SSD M.2 PCIe sẽ cho ph&eacute;p bạn n&acirc;ng cấp ổ cứng SSD gi&uacute;p bạn khởi động m&aacute;y, v&agrave;o ứng dụng nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-cauhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng ổ định trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" src=\"https://cdn.tgdd.vn/Products/Images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-cauhinhtgdd.jpg\" title=\"Hiệu năng ổ định trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" /></a></p>\r\n\r\n<h3>Nghe nhạc to, r&otilde;</h3>\r\n\r\n<p>C&ocirc;ng nghệ &acirc;m thanh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/am-thanh-dolby-audio-premium-co-gi-dac-biet-1017812\" target=\"_blank\" title=\"Âm thanh Dolby Audio Premium có gì đặc biệt?\">Dolby Audio Premium&nbsp;</a>gi&uacute;p bạn nghe nhạc to, r&otilde;. &Acirc;m thanh được khuếch t&aacute;n nhiều hướng, bạn c&oacute; thể giải tr&iacute; tại nh&agrave; sau những giờ l&agrave;m việc mệt mỏi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-amthanhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Âm thanh to rõ trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" src=\"https://cdn.tgdd.vn/Products/Images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-amthanhtgdd.jpg\" title=\"Âm thanh to rõ trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" /></a></p>\r\n\r\n<h3>Cổng USB 3.1, USB Type C truyền dữ liệu nhanh ch&oacute;ng</h3>\r\n\r\n<p>B&ecirc;n cạnh c&aacute;c cổng kết nối cơ bản, chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Laptop phù hơp cho nhân viên văn phòng đang kinh doanh tại thegioididong.com\">laptop văn ph&ograve;ng</a>&nbsp;được trang bị 2 cổng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-31-la-gi-926746\" target=\"_blank\" title=\"USB 3.1 là gì? \">USB 3.1</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-typec-chuan-muc-cong-ket-noi-moi-723760\" target=\"_blank\" title=\"USB Type C là gì?\">USB Type C</a>gi&uacute;p bạn truyền dữ liệu nhanh ch&oacute;ng với tốc độ tối đa m&agrave; cổng n&agrave;y hỗ trợ l&ecirc;n đến 600MB/s.&nbsp;<a href=\"https://www.thegioididong.com/images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-congketnoi.jpg\" onclick=\"return false;\"><img alt=\"Cổng kết nối hiện đại trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" src=\"https://cdn.tgdd.vn/Products/Images/44/195078/lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-content-congketnoi.jpg\" title=\"Cổng kết nối hiện đại trên laptop Lenovo Ideapad 330S 14IKBR i5 (81F400NLVN)\" /></a></p>\r\n', 0, 0, 'lenovo-ideapad-330s-14ikbr-i5-8250u-4gb-1tb-win10-33397-thumb-400x400.jpg', 12990000),
(20, 'Dây cáp Micro USB 1 m eSaver DS118-TB', 4, 'Phụ kiện', '<ul>\r\n	<li>Chất lượng tương đương với h&agrave;ng ch&iacute;nh h&atilde;ng.</li>\r\n	<li>D&ugrave;ng để ch&eacute;p dữ liệu với m&aacute;y t&iacute;nh hay sạc pin (d&ugrave;ng với adapter ri&ecirc;ng).</li>\r\n	<li>D&acirc;y d&agrave;i 1 m thoải m&aacute;i cho bạn vừa sạc vừa sử dụng với pin sạc dự ph&ograve;ng.</li>\r\n	<li>D&acirc;y c&aacute;p được l&agrave;m to v&agrave; trơn b&oacute;ng gi&uacute;p kh&ocirc;ng bị rối.</li>\r\n</ul>\r\n', '<h2><strong>D&acirc;y c&aacute;p Micro USB 1 m eSaver DS118-TB c&oacute; m&agrave;u xanh đẹp mắt, thiết kế quen thuộc gi&uacute;p người d&ugrave;ng dễ sử dụng ngay trong lần đầu ti&ecirc;n.</strong></h2>\r\n\r\n<h3><strong>Thiết kế đơn giản, chiều d&agrave;i d&acirc;y th&iacute;ch hợp</strong></h3>\r\n\r\n<p>Với chiều d&agrave;i d&acirc;y 1 m c&oacute; thế tối ưu h&oacute;a khả năng c&ocirc;ng việc bằng c&aacute;ch vừa kết nối với m&aacute;y t&iacute;nh để truyền dữ liệu, vừa sử dụng điện thoại c&ugrave;ng 1 l&uacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-2-1.jpg\" onclick=\"return false;\"><img alt=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Thiết kế đơn giản, chiều dài dây thích hợp\" src=\"https://cdn.tgdd.vn/Products/Images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-2-1.jpg\" title=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Thiết kế đơn giản, chiều dài dây thích hợp\" /></a></p>\r\n\r\n<h3><strong>Kiểu d&aacute;ng quen thuộc, dễ sử dụng</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\" title=\"Phụ kiện sạc, cáp các loại\">C&aacute;p</a>&nbsp;c&oacute; phần đầu micro USB kết nối với hầu hết cổng điện thoại hiện nay; đầu c&aacute;p USB tương th&iacute;ch với mọi adapter hay cổng tr&ecirc;n tivi, laptop, pin sạc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-2-4.jpg\" onclick=\"return false;\"><img alt=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Đầu cáp micro USB thông dụng trên các thiết bị\" src=\"https://cdn.tgdd.vn/Products/Images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-2-4.jpg\" title=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Đầu cáp micro USB thông dụng trên các thiết bị\" /></a></p>\r\n\r\n<p><em>Đầu c&aacute;p micro USB th&ocirc;ng dụng tr&ecirc;n c&aacute;c thiết bị</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-2-3.jpg\" onclick=\"return false;\"><img alt=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Đầu cáp USB cắm vào cổng USB trên laptop, pin sạc, adapter…\" src=\"https://cdn.tgdd.vn/Products/Images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-2-3.jpg\" title=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Đầu cáp USB cắm vào cổng USB trên laptop, pin sạc, adapter…\" /></a></p>\r\n\r\n<p><em>Đầu c&aacute;p USB cắm v&agrave;o cổng USB tr&ecirc;n laptop, pin sạc, adapter&hellip;</em></p>\r\n\r\n<h3><strong>Sạc pin, truyền tải dữ liệu cho thiết bị (<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại các thương hiệu\">điện thoại</a></strong><strong>,&nbsp;</strong><strong><a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các thương hiệu\">m&aacute;y t&iacute;nh bảng</a></strong><strong>&hellip;)</strong></h3>\r\n\r\n<p>C&aacute;p tương th&iacute;ch với hầu hết cổng tr&ecirc;n smartphone hiện nay như Samsung, HTC, LG, Lumia, Oppo, Sony...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-2-5.jpg\" onclick=\"return false;\"><img alt=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Sạc pin cho thiết bị từ pin sạc dự phòng\" src=\"https://cdn.tgdd.vn/Products/Images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-2-5.jpg\" title=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Sạc pin cho thiết bị từ pin sạc dự phòng\" /></a></p>\r\n\r\n<p><em>Sạc pin cho thiết bị từ pin sạc dự ph&ograve;ng</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-1-7.jpg\" onclick=\"return false;\"><img alt=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Đảm bảo kết nối ổn định giữa laptop với thiết bị\" src=\"https://cdn.tgdd.vn/Products/Images/58/86507/cap-micro-usb-1m-esaver-ds118-tb-xanh-duong-1-7.jpg\" title=\"Dây cáp Micro USB 1 m eSaver DS118-TB - Đảm bảo kết nối ổn định giữa laptop với thiết bị\" /></a></p>\r\n\r\n<p><em>Đảm bảo kết nối ổn định giữa laptop với thiết bị</em></p>\r\n', 0, 0, 'cap-micro-1m-esaver-ds118br-tb-avatar-1-600x600.jpg', 40000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(21, 'Dây cáp Type C 1 m Xmobile MU09-1000', 4, 'Phụ kiện', '<ul>\r\n	<li>Tương th&iacute;ch với tất cả d&ograve;ng m&aacute;y c&oacute; cổng kết nối Type C.</li>\r\n	<li>D&ugrave;ng để ch&eacute;p dữ liệu với m&aacute;y t&iacute;nh hay sạc pin (d&ugrave;ng với adapter ri&ecirc;ng).</li>\r\n	<li>D&acirc;y 1 m thoải m&aacute;i vừa sạc vừa d&ugrave;ng điện thoại.</li>\r\n</ul>\r\n', '<h2><strong>C&aacute;p Type C 1m X Mobile MU09-1000 l&agrave; loại kết nối mới, nhỏ v&agrave; có tốc độ si&ecirc;u nhanh, tốc độ tối đa 10 Gbps, mức năng lượng đầu ra cao v&agrave; tương th&iacute;ch cho c&aacute;c thiết bị sử dụng cổng Type C.</strong></h2>\r\n\r\n<h3><strong>Thiết kế đơn giản, dễ sử dụng</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/89057/cap-type-c-1m-x-mobile-mu09-1000-2-3.jpg\" onclick=\"return false;\"><img alt=\"Cáp Type C 1m X Mobile MU09-1000 - Dây cáp được thiết kế với đầu cáp USB tương thích với mọi adapter hay cổng trên pin sạc, laptop…\" src=\"https://cdn.tgdd.vn/Products/Images/58/89057/cap-type-c-1m-x-mobile-mu09-1000-2-3.jpg\" title=\"Cáp Type C 1m X Mobile MU09-1000 - Dây cáp được thiết kế với đầu cáp USB tương thích với mọi adapter hay cổng trên pin sạc, laptop…\" /></a></p>\r\n\r\n<p><em>D&acirc;y&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\" title=\"Phụ kiện cáp sạc \">c&aacute;p</a>&nbsp;được thiết kế với đầu c&aacute;p USB tương th&iacute;ch với mọi adapter hay cổng tr&ecirc;n pin sạc, laptop&hellip;</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/89057/cap-type-c-1m-x-mobile-mu09-1000-2-4.jpg\" onclick=\"return false;\"><img alt=\"Cáp Type C 1m X Mobile MU09-1000 - Đầu cáp Type C có cùng kích cỡ với đầu cáp micro USB, thiết kế “thuận nghịch” cho phép kết nối đảo chiều tránh làm trầy xước cổng có hỗ trợ Type C trên thiết bị như laptop, Macbook…\" src=\"https://cdn.tgdd.vn/Products/Images/58/89057/cap-type-c-1m-x-mobile-mu09-1000-2-4.jpg\" title=\"Cáp Type C 1m X Mobile MU09-1000 - Đầu cáp Type C có cùng kích cỡ với đầu cáp micro USB, thiết kế “thuận nghịch” cho phép kết nối đảo chiều tránh làm trầy xước cổng có hỗ trợ Type C trên thiết bị như laptop, Macbook…\" /></a></p>\r\n\r\n<p><em>Đầu c&aacute;p Type C c&oacute; c&ugrave;ng k&iacute;ch cỡ với đầu c&aacute;p micro USB, thiết kế &ldquo;thuận nghịch&rdquo; cho ph&eacute;p kết nối đảo chiều tr&aacute;nh l&agrave;m trầy xước cổng c&oacute; hỗ trợ Type C tr&ecirc;n thiết bị như laptop, Macbook&hellip;</em></p>\r\n\r\n<h3><strong>Sạc pin, truyền tải dữ liệu qua lại giữa 2 thiết bị (<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại các thương hiệu\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop các thương hiệu\">laptop</a>&hellip;)</strong></h3>\r\n\r\n<p>Sản phẩm tiện dụng gi&uacute;p bạn sạc pin cho thiết bị từ nguồn điện hay pin sạc dự ph&ograve;ng, kết nối truyền dữ liệu dễ d&agrave;ng với thiết bị c&oacute; chuẩn kết nối Type C.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/89057/cap-type-c-1m-x-mobile-mu09-1000-2-5.jpg\" onclick=\"return false;\"><img alt=\"Cáp Type C 1m X Mobile MU09-1000 - Sạc pin, truyền tải dữ liệu qua lại giữa 2 thiết bị \" src=\"https://cdn.tgdd.vn/Products/Images/58/89057/cap-type-c-1m-x-mobile-mu09-1000-2-5.jpg\" title=\"Cáp Type C 1m X Mobile MU09-1000 - Sạc pin, truyền tải dữ liệu qua lại giữa 2 thiết bị \" /></a></p>\r\n\r\n<p>C&aacute;p truyền tải dữ liệu nhanh hơn gấp 2 lần USB 3.0; tương th&iacute;ch với c&aacute;c thiết bị như Lumia 950, Xiaomi Mi 5,&nbsp;Xperia Z5, Chromebook Pixel 2, New Macbook&hellip;</p>\r\n\r\n<h3><strong>Gọn g&agrave;ng dễ mang theo khi đi học, du lịch&hellip;</strong></h3>\r\n\r\n<p>Chiều d&agrave;i d&acirc;y 100 cm kết nối 2 thiết bị với nhau thuận lợi, đồng thời d&acirc;y c&oacute; thể cuộn lại dễ d&agrave;ng sau khi d&ugrave;ng m&agrave; kh&ocirc;ng sợ bị g&atilde;y hay hư hại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/89057/cap-type-c-1m-x-mobile-mu09-1000-3-1.jpg\" onclick=\"return false;\"><img alt=\"Cáp Type C 1m X Mobile MU09-1000 - Gọn gàng dễ mang theo khi đi học, du lịch…\" src=\"https://cdn.tgdd.vn/Products/Images/58/89057/cap-type-c-1m-x-mobile-mu09-1000-3-1.jpg\" title=\"Cáp Type C 1m X Mobile MU09-1000 - Gọn gàng dễ mang theo khi đi học, du lịch…\" /></a></p>\r\n', 0, 0, 'cap-type-c-1m-x-mobile-mu09-1000-avatar-1-600x600.jpg', 55000),
(22, 'Tai nghe nhét trong Kanen IP-218', 4, 'Phụ kiện', '<ul>\r\n	<li>Tương th&iacute;ch tốt với nhiều d&ograve;ng điện thoại.</li>\r\n	<li>Tai nghe dạng n&uacute;t gi&uacute;p c&aacute;ch &acirc;m tốt với b&ecirc;n ngo&agrave;i.</li>\r\n	<li>C&oacute; n&uacute;t ấn nhận cuộc gọi, ngừng/chơi nhạc, tăng/giảm &acirc;m lượng.</li>\r\n	<li>D&acirc;y d&agrave;i 120 cm thoải m&aacute;i để vừa d&ugrave;ng m&aacute;y vừa nghe nhạc.\r\n	<h2>&nbsp;</h2>\r\n	</li>\r\n</ul>\r\n', '<h2><a href=\"https://www.thegioididong.com/images/54/72775/tai-nghe-ep-kanen-ip-218-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe EP Kanen IP-218\" src=\"https://cdn.tgdd.vn/Products/Images/54/72775/tai-nghe-ep-kanen-ip-218-1.jpg\" title=\"Tai nghe EP Kanen IP-218\" /></a></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/72775/tai-nghe-ep-kanen-ip-218-4.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế dây dẹt thông minh chống rối\" src=\"https://cdn.tgdd.vn/Products/Images/54/72775/tai-nghe-ep-kanen-ip-218-4.jpg\" title=\"Thiết kế dây dẹt thông minh chống rối\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/72775/tai-nghe-ep-kanen-ip-218-5.jpg\" onclick=\"return false;\"><img alt=\"Phụ kiện kèm theo tai nghe\" src=\"https://cdn.tgdd.vn/Products/Images/54/72775/tai-nghe-ep-kanen-ip-218-5.jpg\" title=\"Phụ kiện kèm theo tai nghe\" /></a></p>\r\n\r\n<h3><strong>Sử dụng được cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại các hãng\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các hãng\">m&aacute;y t&iacute;nh bảng</a>,&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop các hãng\">laptop</a>, m&aacute;y nghe nhạc...</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/72775/tai-nghe-ep-kanen-ip-218-3.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng được cho điện thoại, máy tính bảng, laptop, máy nghe nhạc...\" src=\"https://cdn.tgdd.vn/Products/Images/54/72775/tai-nghe-ep-kanen-ip-218-3.jpg\" title=\"Sử dụng được cho điện thoại, máy tính bảng, laptop, máy nghe nhạc...\" /></a></p>\r\n\r\n<h3><strong>N&uacute;t ấn tương t&aacute;c tốt</strong></h3>\r\n\r\n<p>- Đối với ứng dụng nghe nhạc mặc định, bạn c&oacute; thể tăng giảm &acirc;m lượng, ngừng/chơi nhạc, nhận cuộc gọi, ấn n&uacute;t 2 lần để chuyển b&agrave;i.</p>\r\n\r\n<p>- Đối với ứng dụng nghe nhạc online, bạn kh&ocirc;ng thể ấn để chuyển b&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/72775/tai-nghe-ep-kanen-ip-218-2-3.jpg\" onclick=\"return false;\"><img alt=\"Nút ấn tương tác tốt\" src=\"https://cdn.tgdd.vn/Products/Images/54/72775/tai-nghe-ep-kanen-ip-218-2-3.jpg\" title=\"Nút ấn tương tác tốt\" /></a></p>\r\n', 0, 0, 'tai-nghe-ep-kanen-ip-218-ava-600x600.jpg', 90000),
(23, 'Pin sạc dự phòng 7.500 mAh eSaver LA Y323S', 4, 'Phụ kiện', '<ul>\r\n	<li>Thiết kế gọn g&agrave;ng, m&agrave;u sắc đẹp mắt.</li>\r\n	<li>Sạc 3.5 lần thiết bị pin dưới 1.500 mAh, 1.5 lần dưới 3.500 mAh.</li>\r\n	<li>Dễ d&agrave;ng kiểm tra lại được dung lượng pin c&ograve;n lại trong sạc.</li>\r\n	<li>Sử dụng l&otilde;i pin Li-Ion an to&agrave;n.</li>\r\n	<li>Sạc được cho mọi điện thoại v&agrave; m&aacute;y t&iacute;nh bảng.</li>\r\n	<li>Bộ sản phẩm gồm: pin sạc.</li>\r\n</ul>\r\n', '<h3><strong><a href=\"https://www.thegioididong.com/sac-dtdd/pin-sac-du-phong-7500mah-esaver-la-y323s\" target=\"_blank\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S giá rẻ bán tại Thế Giới Di Động\" type=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S giá rẻ bán tại Thế Giới Di Động\">Pin sạc dự ph&ograve;ng eSaver LA Y323S</a>&nbsp;c&oacute; nhiều m&agrave;u sắc trẻ trung cho bạn lựa chọn</strong></h3>\r\n\r\n<p>K&iacute;ch thước kh&ocirc;ng qu&aacute; to, c&oacute; thể cho v&agrave;o balo hay t&uacute;i x&aacute;ch để đi học, đi l&agrave;m hay du lịch.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-11.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S\" src=\"https://cdn.tgdd.vn/Products/Images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-11.jpg\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-17.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S\" src=\"https://cdn.tgdd.vn/Products/Images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-17.jpg\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S\" /></a></p>\r\n\r\n<h3><strong>C&aacute;c chi tiết tr&ecirc;n pin sạc</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-12.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Các chi tiết trên pin sạc\" src=\"https://cdn.tgdd.vn/Products/Images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-12.jpg\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Các chi tiết trên pin sạc\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-13.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Các chi tiết trên pin sạc\" src=\"https://cdn.tgdd.vn/Products/Images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-13.jpg\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Các chi tiết trên pin sạc\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-18.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Các chi tiết trên pin sạc\" src=\"https://cdn.tgdd.vn/Products/Images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-18.jpg\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Các chi tiết trên pin sạc\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/sac-dtdd?g=7500-mah\" target=\"_blank\" title=\"Pin sạc dự phòng dung lượng pin 7.500 mAh giá rẻ bán tại Thế Giới Di Động\" type=\"Pin sạc dự phòng dung lượng pin 7.500 mAh giá rẻ bán tại Thế Giới Di Động\"><strong>Dung lượng pin 7.500 mAh</strong></a></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/sac-dtdd\" target=\"_blank\" title=\"Pin sạc dự phòng giá rẻ bán tại Thế Giới Di Động\" type=\"Pin sạc dự phòng giá rẻ bán tại Thế Giới Di Động\">Pin sạc dự ph&ograve;ng</a>&nbsp;c&oacute; thể đầy pin cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động các hãng\">điện thoại</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các hãng\">m&aacute;y t&iacute;nh bảng</a>&nbsp;dung lượng dưới 6.000 mAh.</p>\r\n\r\n<p><strong>Mẹo:</strong>&nbsp;Bạn c&oacute; thể sạc nhanh cho thiết bị bằng c&aacute;ch cắm sạc cổng 2.1A.</p>\r\n\r\n<p><strong>*Lưu &yacute;:&nbsp;</strong>Trong qu&aacute; tr&igrave;nh sạc pin sẽ bị hao hụt khoảng 35% dung lượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-15.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Dung lượng pin 7.500 mAh\" src=\"https://cdn.tgdd.vn/Products/Images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-15.jpg\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Dung lượng pin 7.500 mAh\" /></a></p>\r\n\r\n<h3><strong>Sạc song song c&ugrave;ng l&uacute;c 2 điện thoại</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-16.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Sạc song song cùng lúc 2 điện thoại\" src=\"https://cdn.tgdd.vn/Products/Images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-16.jpg\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Sạc song song cùng lúc 2 điện thoại\" /></a></p>\r\n\r\n<h3><strong>C&oacute; thể d&ugrave;ng cho quạt mini, đ&egrave;n, tai nghe, loa bluetooth...</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-19.gif\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Có thể dùng cho quạt mini, đèn, tai nghe, loa bluetooth...\" src=\"https://cdn.tgdd.vn/Products/Images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-19.gif\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Có thể dùng cho quạt mini, đèn, tai nghe, loa bluetooth...\" /></a></p>\r\n\r\n<h3><strong>Thời gian đầy pin từ 4 đến 5 giờ với nguồn điện sạc 2.1A</strong></h3>\r\n\r\n<p>Bạn c&oacute; thể d&ugrave;ng adapter sạc hay d&acirc;y c&aacute;p nối với&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Các mẫu laptop nhiều thương hiệu\">laptop</a>&nbsp;để sạc cho pin.</p>\r\n\r\n<p>=&gt;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/chon-cap-sac-phu-hop-cho-dien-thoai-1020035\" target=\"_blank\" title=\"Chọn cáp sạc phù hợp\">Tham khảo th&ecirc;m c&aacute;ch chọn d&acirc;y c&aacute;p sạc ph&ugrave; hợp cho bạn</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-14.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Thời gian đầy pin từ 4 đến 5 giờ với nguồn điện sạc 2.1A\" src=\"https://cdn.tgdd.vn/Products/Images/57/145556/pin-sac-du-phong-7500mah-esaver-la-y323s-14.jpg\" title=\"Pin sạc dự phòng 7.500 mAh eSaver LA Y323S - Thời gian đầy pin từ 4 đến 5 giờ với nguồn điện sạc 2.1A\" /></a></p>\r\n', 0, 0, 'pin-sac-du-phong-7500mah-esaver-la-y323s-anh-dai-dien-600x600.jpg', 240000),
(24, 'Pin sạc dự phòng 10.000 mAh eSaver LA Y325S', 4, 'Phụ kiện', '<ul>\r\n	<li>Sản phẩm được cập nhật v&agrave; n&acirc;ng cấp bo mạch, chip set mới.</li>\r\n	<li>Gi&aacute; th&agrave;nh hấp dẫn nhưng chất lượng vẫn đảm bảo tốt.</li>\r\n	<li>Sạc 4 lần thiết bị pin dưới 1.500 mAh, 2 lần dưới 3.500 mAh.</li>\r\n	<li>Sử dụng l&otilde;i pin Li-Ion an to&agrave;n.</li>\r\n	<li>Sạc được cho mọi điện thoại v&agrave; m&aacute;y t&iacute;nh bảng.</li>\r\n	<li>Bộ sản phẩm gồm: pin sạc.</li>\r\n</ul>\r\n', '<h3>&nbsp;</h3>\r\n\r\n<h3><strong><a href=\"https://www.thegioididong.com/sac-dtdd/pin-sac-du-phong-10000mah-esaver-la-y325s\" target=\"_blank\" title=\"Pin sạc dự phòng 10.000 mAh eSaver LA Y325S giá rẻ bán tại Thế Giới Di Động\" type=\"Pin sạc dự phòng 10.000 mAh eSaver LA Y325S giá rẻ bán tại Thế Giới Di Động\">Pin sạc dự ph&ograve;ng eSaver LA Y325S</a>&nbsp;c&oacute; m&agrave;u sắc tươi s&aacute;ng</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/sac-dtdd\" target=\"_blank\" title=\"Pin sạc dự phòng giá rẻ bán tại Thế Giới Di Động\" type=\"Pin sạc dự phòng giá rẻ bán tại Thế Giới Di Động\">Pin sạc dự ph&ograve;ng</a>&nbsp;c&oacute; vỏ nhựa nh&aacute;m kh&ocirc;ng b&aacute;m v&acirc;n tay, c&aacute;ch điện tốt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-5.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S\" src=\"https://cdn.tgdd.vn/Products/Images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-5.jpg\" title=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S\" /></a></p>\r\n\r\n<h3><strong>C&aacute;c chi tiết tr&ecirc;n pin sạc</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-4.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Các chi tiết trên pin sạc\" src=\"https://cdn.tgdd.vn/Products/Images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-4.jpg\" title=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Các chi tiết trên pin sạc\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-6.gif\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Các chi tiết trên pin sạc\" src=\"https://cdn.tgdd.vn/Products/Images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-6.gif\" title=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Các chi tiết trên pin sạc\" /></a></p>\r\n\r\n<h3><strong><a href=\"https://www.thegioididong.com/sac-dtdd?g=10000-mah\" target=\"_blank\" title=\"Pin sạc dự phòng dung lượng pin 10.000 mAh giá rẻ bán tại Thế Giới Di Động\" type=\"Pin sạc dự phòng dung lượng pin 10.000 mAh giá rẻ bán tại Thế Giới Di Động\">Dung lượng pin 10000</a>&nbsp;lớn nhiều c&aacute;ch sử dụng</strong></h3>\r\n\r\n<p>Sạc được đầy pin cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động các hãng\">điện thoại</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các hãng\">m&aacute;y t&iacute;nh bảng</a>&nbsp;c&oacute; dung lượng dưới 7.500 mAh.</p>\r\n\r\n<p>Khi bạn sạc c&ugrave;ng l&uacute;c điện thoại v&agrave; m&aacute;y t&iacute;nh bảng th&igrave; h&atilde;y cắm thiết bị lớn hơn v&agrave;o cổng 2.1A.</p>\r\n\r\n<p><strong>*Lưu &yacute;:</strong>&nbsp;Trong qu&aacute; tr&igrave;nh sạc pin sẽ bị hao hụt khoảng 35% dung lượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-2.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Dung lượng pin lớn nhiều cách sử dụng\" src=\"https://cdn.tgdd.vn/Products/Images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-2.jpg\" title=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Dung lượng pin lớn nhiều cách sử dụng\" /></a></p>\r\n\r\n<h3><strong>Sạc song song 2 thiết bị</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-3.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Sạc song song 2 thiết bị\" src=\"https://cdn.tgdd.vn/Products/Images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-3.jpg\" title=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Sạc song song 2 thiết bị\" /></a></p>\r\n\r\n<h3><strong>Thời gian nạp đầy pin từ 5 đến 6 giờ</strong></h3>\r\n\r\n<p>Bạn c&oacute; thể d&ugrave;ng adapter sạc hay d&acirc;y c&aacute;p nối với&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Các mẫu laptop nhiều thương hiệu\">laptop</a>&nbsp;để sạc cho pin.</p>\r\n\r\n<p>=&gt;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/chon-cap-sac-phu-hop-cho-dien-thoai-1020035\" target=\"_blank\" title=\"Chọn cáp sạc phù hợp\">Tham khảo th&ecirc;m c&aacute;ch chọn d&acirc;y c&aacute;p sạc ph&ugrave; hợp cho bạn</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-1.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Thời gian nạp đầy pin từ 5 đến 6 giờ\" src=\"https://cdn.tgdd.vn/Products/Images/57/145557/pin-sac-du-phong-10000mah-esaver-la-y325s-1.jpg\" title=\"Pin sạc dự phòng 10000mAh eSaver LA Y325S - Thời gian nạp đầy pin từ 5 đến 6 giờ\" /></a></p>\r\n', 0, 0, 'pin-sac-du-phong-10000mah-esaver-la-y325s-anh-dai-dien-600x600.jpg', 325000),
(25, 'Samsung Galaxy Watch 42mm ', 5, 'Đồng hồ', '<p>L&agrave; sự kết hợp giữa cổ điển v&agrave; hiện đại với t&iacute;nh ứng dụng th&ocirc;ng minh c&ugrave;ng phong c&aacute;ch thời trang.&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-watch-42mm\" target=\"_blank\" title=\"Samsung Galaxy Watch 42mm chính hãng, giá tốt, bán tại Thế Giới Di Động\">Samsung Galaxy Watch 42mm</a>&nbsp;sở hữu kiểu d&aacute;ng mặt tr&ograve;n truyền thống kết hợp c&ugrave;ng nhiều mặt đồng hồ được trang bị dễ d&agrave;ng thay đổi.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/Slider/-mat-dong-ho.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Samsung Galaxy Watch - C&aacute; t&iacute;nh ri&ecirc;ng của bạn</h3>\r\n\r\n<p>L&agrave; sự kết hợp giữa cổ điển v&agrave; hiện đại với t&iacute;nh ứng dụng th&ocirc;ng minh c&ugrave;ng phong c&aacute;ch thời trang.&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-watch-42mm\" target=\"_blank\" title=\"Samsung Galaxy Watch 42mm chính hãng, giá tốt, bán tại Thế Giới Di Động\">Samsung Galaxy Watch 42mm</a>&nbsp;sở hữu kiểu d&aacute;ng mặt tr&ograve;n truyền thống kết hợp c&ugrave;ng nhiều mặt đồng hồ được trang bị dễ d&agrave;ng thay đổi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-kieudang.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm có kiểu dáng phong cách\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-kieudang.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm có kiểu dáng phong cách\" /></a></p>\r\n\r\n<h3>D&acirc;y đeo chất liệu cao su</h3>\r\n\r\n<p>Chất liệu cao su được sử dụng hạn chế tối đa việc bong tr&oacute;c khi d&acirc;y tiếp x&uacute;c với mồ h&ocirc;i l&uacute;c vận động trong thời gian d&agrave;i hoặc d&ugrave;ng trong m&ocirc;i trường nước. Th&ecirc;m v&agrave;o đ&oacute;, d&acirc;y với khả năng th&aacute;o rời cho bạn tạo n&ecirc;n phong c&aacute;ch ri&ecirc;ng với c&aacute;c loại d&acirc;y đeo m&agrave;u sắc kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-2.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm với dây đeo silicone tháo rời\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-2.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm với dây đeo silicone tháo rời\" /></a></p>\r\n\r\n<h3>Tấm nền Super Amoled</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh được Samsung trang bị mặt k&iacute;nh Gorilla DX+ kết hợp c&ocirc;ng nghệ m&agrave;n h&igrave;nh Super Amoled, độ ph&acirc;n giải 360x360 pixels mật độ điểm ảnh khoảng 390 (ppi) cho h&igrave;nh ảnh sắc n&eacute;t, chi tiết từng điểm ảnh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/dong-ho-thong-minh-samsung-galaxy-watch-42mm-20-1.jpg\" onclick=\"return false;\"><img alt=\"Công nghệ màn hình Super Amoled trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/dong-ho-thong-minh-samsung-galaxy-watch-42mm-20-1.jpg\" title=\"Công nghệ màn hình Super Amoled trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" /></a></p>\r\n\r\n<h3>Kết nối lu&ocirc;n lu&ocirc;n trong tầm tay</h3>\r\n\r\n<p>Ngo&agrave;i khả năng hiển thị chi tiết cuộc gọi đến, tin nhắn, nay Samsung Galaxy Watch cho khả năng nhận/từ chối/ nhắn tin trả lời cuộc gọi trực tiếp ngay tr&ecirc;n mặt đồng hồ tiện lợi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-4.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm cho kết nối luôn trong tầm tay\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-4.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm cho kết nối luôn trong tầm tay\" /></a></p>\r\n\r\n<h3>Kho ứng dụng đa dạng</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-samsung\" target=\"_blank\" title=\"Smartwatch Samsung chính hãng, giá tốt, bán tại Thế Giới Di Động\">Samsung</a>&nbsp;t&iacute;ch hợp rất nhiều ứng dụng cho bạn c&oacute; nhiều sự lựa chọn hơn sao cho ph&ugrave; hợp nhất với c&aacute; nh&acirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/dong-ho-thong-minh-samsung-galaxy-watch-42mm-20-2.jpg\" onclick=\"return false;\"><img alt=\"Kho ứng dụng đa dạng trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/dong-ho-thong-minh-samsung-galaxy-watch-42mm-20-2.jpg\" title=\"Kho ứng dụng đa dạng trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" /></a></p>\r\n\r\n<h3>Trợ l&iacute; huấn luyện vi&ecirc;n - B&aacute;c sĩ tại gia</h3>\r\n\r\n<p>[info]</p>\r\n\r\n<p>Samsung Galaxy Watch 42mm được trang bị những t&iacute;nh năng đa dạng thi&ecirc;n về vận động v&agrave; chăm s&oacute;c sức khỏe:</p>\r\n\r\n<p>- Nhắc nhở tập&nbsp;thể dục.</p>\r\n\r\n<p>- T&iacute;ch hợp sẵn&nbsp;21 b&agrave;i tập&nbsp;trong nh&agrave;, c&oacute; thể lưu trữ tận&nbsp;39 b&agrave;i tập theo d&otilde;i.</p>\r\n\r\n<p>- Định vị GPS, con quay hồi chuyển.</p>\r\n\r\n<p>- Theo d&otilde;i nhịp tim&nbsp;để x&acirc;y dựng biểu đồ theo d&otilde;i sức khỏe.</p>\r\n\r\n<p>- Khả năng&nbsp;theo d&otilde;i giấc ngủ.</p>\r\n\r\n<p>- Ứng dụng&nbsp;Connected Health&nbsp;gi&uacute;p cải thiện stress.[/info]</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-6.jpg\" onclick=\"return false;\"><img alt=\"Các tính năng thông minh trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-6.jpg\" title=\"Các tính năng thông minh trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" /></a></p>\r\n\r\n<h3>Theo d&otilde;i mức độ căng thẳng</h3>\r\n\r\n<p>B&ecirc;n cạnh c&aacute;c cảm biến thường thấy tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Smartwatch chính hãng, giá tốt, bán tại Thế Giới Di Động\">smartwatch</a>, Samsung đ&atilde; th&ecirc;m cho Galaxy Watch t&iacute;nh năng theo d&otilde;i v&agrave; b&aacute;o động mức độ stress kh&aacute; hiện đại. Dựa v&agrave;o kết quả đ&oacute;, bạn c&oacute; thể chủ động điều chỉnh cường độ l&agrave;m việc, tập luyện... một c&aacute;ch hợp l&iacute; hơn để cải thiện mức độ căng thẳng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-7.jpg\" onclick=\"return false;\"><img alt=\"Tính năng theo dõi căng thẳng trên trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-7.jpg\" title=\"Tính năng theo dõi căng thẳng trên trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" /></a></p>\r\n\r\n<h3>Một số t&iacute;nh năng th&ocirc;ng minh của Samsung Galaxy Watch</h3>\r\n\r\n<p>- T&iacute;nh năng phản s&aacute;ng tối ưu cho ph&eacute;p bạn lu&ocirc;n nh&igrave;n r&otilde; c&aacute;c th&ocirc;ng tin. Th&ecirc;m v&agrave;o đ&oacute;, t&iacute;nh năng&nbsp;<strong>Always On</strong>&nbsp;cho ph&eacute;p xem giờ, ng&agrave;y th&aacute;ng v&agrave; c&aacute;c th&ocirc;ng tin cần thiết bất cứ khi n&agrave;o m&agrave; kh&ocirc;ng cần t&aacute;c động v&agrave;o đồng hồ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-8.jpg\" onclick=\"return false;\"><img alt=\"Màn hình luôn hiển thị trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-8.jpg\" title=\"Màn hình luôn hiển thị trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" /></a></p>\r\n\r\n<p>-&nbsp;<strong>Trợ l&yacute; ảo th&ocirc;ng minh Bixby:</strong>&nbsp;Sẽ ph&acirc;n t&iacute;ch v&agrave; đ&aacute;nh gi&aacute; lại to&agrave;n bộ hoạt động trong một ng&agrave;y của bạn như: Lịch tr&igrave;nh mỗi ng&agrave;y, thời tiết, nhắc nhở, tổng hợp th&ocirc;ng số về sức khỏe&hellip;</p>\r\n\r\n<p>-&nbsp;<strong>Khả năng sạc đơn giản:</strong>&nbsp;Bạn chỉ cần đặt đồng hồ xuống đế sạc sạc nam ch&acirc;m v&agrave; đồng hồ sẽ tự động sạc.</p>\r\n\r\n<h3>Tương th&iacute;ch với nhiều điện thoại</h3>\r\n\r\n<p>Galaxy Watch tương th&iacute;ch với hầu hết smartphone chạy Android 5.0 v&agrave; iOS 9 trở l&ecirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-10.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm tương thích nhiều điện thoại\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-10.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm tương thích nhiều điện thoại\" /></a></p>\r\n\r\n<h3>Khả năng ph&aacute;t nhạc độc lập tr&ecirc;n đồng hồ</h3>\r\n\r\n<p>Với 4 GB bộ nhớ trong, bạn c&oacute; thể copy nhạc v&agrave;o đồng hồ v&agrave; thưởng thức trực tiếp tr&ecirc;n đồng hồ hoặc qua kết nối tai nghe Bluetooth cho trải nghiệm tuyệt vời hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-11.jpg\" onclick=\"return false;\"><img alt=\"Khả năng phát nhạc trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-11.jpg\" title=\"Khả năng phát nhạc trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" /></a></p>\r\n\r\n<h3>Thời gian sử dụng</h3>\r\n\r\n<p>Đối với phi&ecirc;n bản 42mm, vi&ecirc;n pin c&oacute; dung lượng 270 mAh, theo c&ocirc;ng bố th&igrave; thời lượng c&oacute; thể l&ecirc;n tới 2 ng&agrave;y tuy nhi&ecirc;n c&ograve;n t&ugrave;y v&agrave;o nhu cầu sử dụng kh&aacute;c nhau n&ecirc;n thời gian sử dụng sẽ c&oacute; thay đổi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-12.jpg\" onclick=\"return false;\"><img alt=\"Thời gian sử dụng trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-12.jpg\" title=\"Thời gian sử dụng trên Đồng hồ thông minh Samsung Galaxy Watch 42mm\" /></a></p>\r\n\r\n<h3>Chỉ số chống nước 5ATM</h3>\r\n\r\n<p>Bạn c&oacute; thể sử dụng đồng hồ Samsung Galaxy Watch trong c&aacute;c điều kiện thời tiết kh&aacute;c nhau. Tuy nhi&ecirc;n bạn kh&ocirc;ng n&ecirc;n để đồng hồ tiếp x&uacute;c với nước (đặc biệt l&agrave; nước mặn) qu&aacute; l&acirc;u.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/197085/samsung-galaxy-watch-42mm-khangnuoctgdd.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm kháng nước bảo vệ đồng hồ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/197085/samsung-galaxy-watch-42mm-khangnuoctgdd.jpg\" title=\"Đồng hồ thông minh Samsung Galaxy Watch 42mm kháng nước bảo vệ đồng hồ\" /></a></p>\r\n', 0, 0, '1563784554samsung-galaxy-watch-42mm-nt-600x600.jpg', 6990000),
(26, 'Samsung Galaxy Watch Active R500', 5, 'Đồng hồ', '<p>Với thiết kế tối giản nhưng kh&ocirc;ng k&eacute;m phần thanh lịch,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm các mẫu đồng hồ thông minh đang kinh doanh\" type=\"Xem thêm các mẫu đồng hồ thông minh đang kinh doanh\">đồng hồ th&ocirc;ng minh</a>mới nhất của Samsung - Galaxy Watch Active, sẽ mang đến cho bạn trải nghiệm c&ocirc;ng nghệ v&agrave; t&iacute;nh năng theo d&otilde;i sức khỏe tuyệt vời.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/200965/Slider/-slide-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Với thiết kế tối giản nhưng kh&ocirc;ng k&eacute;m phần thanh lịch,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm các mẫu đồng hồ thông minh đang kinh doanh\" type=\"Xem thêm các mẫu đồng hồ thông minh đang kinh doanh\">đồng hồ th&ocirc;ng minh</a>mới nhất của Samsung - Galaxy Watch Active, sẽ mang đến cho bạn trải nghiệm c&ocirc;ng nghệ v&agrave; t&iacute;nh năng theo d&otilde;i sức khỏe tuyệt vời.</h2>\r\n\r\n<h3>Thiết kế tối giản, thanh lịch v&agrave; gọn nhẹ</h3>\r\n\r\n<p>Sự thanh lịch của&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-watch-active-r500\" target=\"_blank\" title=\"Xem chi tiết về Đồng hồ Samsung Galaxy Watch Active R500\" type=\"Xem chi tiết về Đồng hồ Samsung Galaxy Watch Active R500\">Samsung Galaxy Watch Active</a>&nbsp;đến từ việc kết hợp chất liệu kim loại v&agrave; k&iacute;nh cao cấp. B&ecirc;n cạnh đ&oacute;, việc lược bỏ v&ograve;ng bezel xoay v&agrave; xu hướng gọn nhẹ gi&uacute;p bạn thoải m&aacute;i đeo đồng hồ th&ocirc;ng minh Galaxy Watch Active tr&ecirc;n tay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200965/samsung-galaxy-watch-active-r500-8.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Watch Active siêu mỏng và nhẹ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200965/samsung-galaxy-watch-active-r500-8.jpg\" title=\"Samsung Galaxy Watch Active siêu mỏng và nhẹ\" /></a></p>\r\n\r\n<h3>Hoạt động nhiều hơn với Daily Activities</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-samsung\" target=\"_blank\" title=\"Xem thêm các mẫu smartwatch Samsung khác\" type=\"Xem thêm các mẫu smartwatch Samsung khác\">Smartwatch Samsung&nbsp;</a>Galaxy Watch Active gi&uacute;p bạn hoạt động nhiều hơn để n&acirc;ng cao sức khỏe của m&igrave;nh. T&iacute;nh năng Daily Activities sẽ nhắc nhở khi n&agrave;o bạn cần bổ sung nước hay đứng dậy đi v&agrave;i v&ograve;ng khi ngồi qu&aacute; l&acirc;u.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200965/samsung-galaxy-watch-active-r500-6.jpg\" onclick=\"return false;\"><img alt=\"Tính năng nhắc nhở vận động Samsung Galaxy Watch Active\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200965/samsung-galaxy-watch-active-r500-6.jpg\" title=\"Tính năng nhắc nhở vận động Samsung Galaxy Watch Active\" /></a></p>\r\n\r\n<h3>T&iacute;nh năng cảnh b&aacute;o stress</h3>\r\n\r\n<p>Nhờ v&agrave;o cảm biến nhịp tim v&agrave; phần mềm đặc biệt, Galaxy Watch Active c&oacute; được khả năng theo d&otilde;i mức độ căng thẳng của bạn v&agrave; cảnh b&aacute;o khi n&oacute; đạt ngưỡng tối đa. Khi đ&oacute;, bạn sẽ được gợi &yacute; c&aacute;c phương ph&aacute;p điều tiết hơi thở để giảm mức căng thẳng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200965/samsung-galaxy-watch-active-r500-7.jpg\" onclick=\"return false;\"><img alt=\"Tính năng theo dõi stress Samsung Galaxy Watch Active\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200965/samsung-galaxy-watch-active-r500-7.jpg\" title=\"Tính năng theo dõi stress Samsung Galaxy Watch Active\" /></a></p>\r\n\r\n<h3>Hỗ trợ luyện tập hiệu quả</h3>\r\n\r\n<p>Được trang bị nhiều cảm biến ti&ecirc;n tiến, GPS v&agrave; khả năng chống nước 5ATM* v&agrave; IP68, Samsung Galaxy Watch Active c&oacute; thể đưa ra hoặc theo d&otilde;i c&aacute;c tiến tr&igrave;nh luyện tập kh&aacute;c nhau.</p>\r\n\r\n<p>*5ATM: chịu được 50 m dưới nước, khi đi bơi, đi tắm.</p>\r\n\r\n<p>Th&ecirc;m v&agrave;o đ&oacute;, với c&aacute;c ứng dụng hỗ trợ sức khỏe đến từ Samsung, Strava hay Under Armour, bạn c&oacute; thể n&acirc;ng cao trải nghiệm luyện tập của m&igrave;nh. Ứng dụng Spotify cũng hoạt động mượt m&agrave; tr&ecirc;n Galaxy Watch Active.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200965/samsung-galaxy-watch-active-r500-5.jpg\" onclick=\"return false;\"><img alt=\"Tập luyện cùng Samsung Galaxy Watch Active\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200965/samsung-galaxy-watch-active-r500-5.jpg\" title=\"Tập luyện cùng Samsung Galaxy Watch Active\" /></a></p>\r\n\r\n<h3>Nhận v&agrave; trả lời tin nhắn, th&ocirc;ng b&aacute;o từ điện thoại</h3>\r\n\r\n<p>Đồng hồ th&ocirc;ng minh Galaxy Watch Active c&oacute; khả năng kết nối với cả iOS v&agrave; Android. Nhờ vậy, bạn sẽ lu&ocirc;n lu&ocirc;n nhận được c&aacute;c cuộc gọi, th&ocirc;ng b&aacute;o, nhắc nhở,.. từ chiếc điện thoại của m&igrave;nh. Tr&ecirc;n hết, khi sử dụng k&egrave;m điện thoại Samsung, chiếc đồng hồ th&ocirc;ng minh n&agrave;y thể hiện khả năng tối đa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200965/samsung-galaxy-watch-active-r500-4.jpg\" onclick=\"return false;\"><img alt=\"Nhận thông báo và trả lời trên  Samsung Galaxy Watch Active\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200965/samsung-galaxy-watch-active-r500-4.jpg\" title=\"Nhận thông báo và trả lời trên  Samsung Galaxy Watch Active\" /></a></p>\r\n\r\n<h3>Theo d&otilde;i chi tiết giấc ngủ</h3>\r\n\r\n<p>T&iacute;nh năng theo d&otilde;i giấc ngủ của Samsung Galaxy Watch Active rất tuyệt vời khi c&oacute; thể ghi ch&eacute;p lại chi tiết 4 giai đoạn của giấc ngủ. Biết được qu&aacute; tr&igrave;nh ngủ của m&igrave;nh, bạn c&oacute; thể điều chỉnh kịp thời để khỏe mạnh hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200965/samsung-galaxy-watch-active-r500-3.jpg\" onclick=\"return false;\"><img alt=\"Theo dõi giấc ngủ với Samsung Galaxy Watch Active\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200965/samsung-galaxy-watch-active-r500-3.jpg\" title=\"Theo dõi giấc ngủ với Samsung Galaxy Watch Active\" /></a></p>\r\n\r\n<h3>Sạc kh&ocirc;ng d&acirc;y tiện lợi</h3>\r\n\r\n<p>Samsung Galaxy Watch Active c&oacute; thời lượng pin 45 giờ chỉ với 1 lần sạc. B&ecirc;n cạnh đ&oacute;, bộ pin dự ph&ograve;ng sạc kh&ocirc;ng d&acirc;y (kh&ocirc;ng k&egrave;m đồng hồ) tiện lợi gi&uacute;p sạc đầy bất cứ đ&acirc;u. Đặc biệt, bạn ho&agrave;n to&agrave;n c&oacute; thể d&ugrave;ng điện thoại flagship Samsung Galaxy S10 để nạp đầy năng lượng cho chiếc đồng hồ n&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200965/samsung-galaxy-watch-active-r500-1.jpg\" onclick=\"return false;\"><img alt=\"Sạc không dây trên Samsung Galaxy Watch Active\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200965/samsung-galaxy-watch-active-r500-1.jpg\" title=\"Sạc không dây trên Samsung Galaxy Watch Active\" /></a></p>\r\n', 0, 0, 'samsung-galaxy-watch-active-r500-10-400x400.jpg', 5090000),
(27, 'Apple Watch S3 GPS 42mm ', 5, 'Đồng hồ', '<p>Về mặt tổng thể&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s3-gps-42mm-vien-nhom-day-cao-su-den\" target=\"_blank\" title=\"Apple Watch S3 GPS 42mm viền nhôm dây cao su màu đen chính hãng, giá tốt, bán tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh&nbsp;</a><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s3-gps-42mm-vien-nhom-day-cao-su-den\" target=\"_blank\" title=\"Apple Watch S3 GPS 42mm viền nhôm dây cao su màu đen chính hãng, giá tốt, bán tại Thế Giới Di Động\">Apple Watch S3 GPS 42mm</a>&nbsp;c&oacute; k&iacute;ch thước tương tự như Apple Watch Series 2.&nbsp;Sản phẩm ch&uacute; trọng th&ecirc;m về mặt thẩm mỹ với thiết kế sang trọng, năng động, d&acirc;y đeo c&oacute; nhiều m&agrave;u sắc kh&aacute;c nhau.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/194536/Slider/vi-vn-apple-watch-s3-gps-42mm-vien-nhom-day-cao-su-mau-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thiết kế hiện đại năng động</h3>\r\n\r\n<p>Về mặt tổng thể&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s3-gps-42mm-vien-nhom-day-cao-su-den\" target=\"_blank\" title=\"Apple Watch S3 GPS 42mm viền nhôm dây cao su màu đen chính hãng, giá tốt, bán tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh&nbsp;</a><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s3-gps-42mm-vien-nhom-day-cao-su-den\" target=\"_blank\" title=\"Apple Watch S3 GPS 42mm viền nhôm dây cao su màu đen chính hãng, giá tốt, bán tại Thế Giới Di Động\">Apple Watch S3 GPS 42mm</a>&nbsp;c&oacute; k&iacute;ch thước tương tự như Apple Watch Series 2.&nbsp;Sản phẩm ch&uacute; trọng th&ecirc;m về mặt thẩm mỹ với thiết kế sang trọng, năng động, d&acirc;y đeo c&oacute; nhiều m&agrave;u sắc kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-thietke.gif\" onclick=\"return false;\"><img alt=\"Thiết kể Apple Watch S3 GPS, 42mm viền nhôm, dây cao su\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-thietke.gif\" title=\"Thiết kể Apple Watch S3 GPS, 42mm viền nhôm, dây cao su\" /></a></p>\r\n\r\n<h3>D&acirc;y đeo th&aacute;o rời tiện lợi</h3>\r\n\r\n<p>D&acirc;y đeo của&nbsp;Apple Watch S3 GPS, 42mm viền nh&ocirc;m được thiết kế với khả năng dễ d&agrave;ng th&aacute;o rời, cho ph&eacute;p người d&ugrave;ng linh hoạt thay đổi bằng c&aacute;c d&acirc;y đeo m&agrave;u sắc kh&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-thaoroidaydeo.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen có thể tháo rời dây đeo\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-thaoroidaydeo.jpg\" title=\"Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen có thể tháo rời dây đeo\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh 1.65 inch lớn hơn</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\" title=\"Đồng hồ thông minh Apple Watch chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh Apple Watch</a>&nbsp;phi&ecirc;n bản 42 mm lớn 2.1 inch hiển thị chi tiết ảnh nhiều hơn, mặt đồng hồ cảm ứng trang bị mặt k&iacute;nh cường lực&nbsp;Ion-X gi&uacute;p bảo vệ mặt k&iacute;nh chống trầy xước tốt v&agrave; hiển thị tốt d&ugrave; l&agrave; dưới &aacute;nh mặt trời.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-manhinh.jpg\" onclick=\"return false;\"><img alt=\"Màn hình trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-manhinh.jpg\" title=\"Màn hình trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" /></a></p>\r\n\r\n<h3>N&uacute;t tiện &iacute;ch</h3>\r\n\r\n<p><strong>- N&uacute;t xoay Digital Crown ( h&igrave;nh tr&ograve;n )</strong>: Nhấn n&uacute;t gi&uacute;p trở về m&agrave;n h&igrave;nh Home, nhấn giữ để Siri xuất hiện. Xoay để&nbsp;cuộn l&ecirc;n/xuống để đọc tin nhắn, v&agrave; zoom để xem h&igrave;nh ảnh.</p>\r\n\r\n<p><strong>- N&uacute;t bấm nguồn ( h&igrave;nh bầu dục )</strong>: Bấm để tắt/mở nguồn đồng hồ, thao t&aacute;c để mở những app sử dụng gần nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-nutbam.jpg\" onclick=\"return false;\"><img alt=\"Nút tiện ích trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-nutbam.jpg\" title=\"Nút tiện ích trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" /></a></p>\r\n\r\n<h3>Chip W2 cho hiệu năng mạnh hơn</h3>\r\n\r\n<p>Kh&ocirc;ng cần tới iPhone khi Apple Watch Series 3 được trang bị chip vi xử l&yacute; dual-core ho&agrave;n to&agrave;n mới W2 gi&uacute;p truy cập Wifi nhanh hơn 85%.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-chip.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng chip trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-chip.jpg\" title=\"Hiệu năng chip trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" /></a></p>\r\n\r\n<h3>Trải nghiệm kh&ocirc;ng gian &acirc;m nhạc</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Đồng hồ thông minh chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh</a>&nbsp;Apple Watch thế hệ mới hoạt động gần giống với iPhone, c&oacute; thể sử dụng Apple Music cho ph&eacute;p chứa hơn 40 triệu b&agrave;i h&aacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-amnhac.jpg\" onclick=\"return false;\"><img alt=\"Nghe nhạc trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-amnhac.jpg\" title=\"Nghe nhạc trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" /></a></p>\r\n\r\n<h3>T&iacute;nh năng sức khỏe</h3>\r\n\r\n<p>Chăm s&oacute;c, gi&aacute;m s&aacute;t sức khỏe của bạn với chức năng đo nhịp tim, đếm bước ch&acirc;n, đếm giờ tập&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-donhiptim.jpg\" onclick=\"return false;\"><img alt=\"Tính năng đo nhịp tim trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-donhiptim.jpg\" title=\"Tính năng đo nhịp tim trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" /></a><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-suckhoe.jpg\" onclick=\"return false;\"><img alt=\"Các chế độ theo dõi sức khỏe trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-suckhoe.jpg\" title=\"Các chế độ theo dõi sức khỏe trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" /></a></p>\r\n\r\n<h3>Trợ l&iacute; ảo Siri</h3>\r\n\r\n<p>Siri đ&atilde; c&oacute; thể &quot;n&oacute;i chuyện&quot; ngay tr&ecirc;n Apple Watch, hoạt động nhanh v&agrave; th&ocirc;ng minh hơn, bạn c&oacute; thể y&ecirc;u cầu Siri thiết lập lời nhắc, gửi lời mời hoặc đưa ra hướng dẫn (c&acirc;u hỏi bằng tiếng anh).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-siri.jpg\" onclick=\"return false;\"><img alt=\"Trợ lí ảo Siri trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-siri.jpg\" title=\"Trợ lí ảo Siri trên Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" /></a></p>\r\n\r\n<h3>Hướng dẫn kết nối với điện thoại v&agrave; sử dụng chi tiết&nbsp;Apple Watch S3 GPS 42mm</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Khả năng kh&aacute;ng nước l&ecirc;n đến độ s&acirc;u 50m</h3>\r\n\r\n<p>Apple Watch 3 cũng trang bị khả năng chống nước tốt v&agrave; đ&atilde; được n&acirc;ng cấp, lu&ocirc;n sẵn s&agrave;ng khi bạn bơi trong bể bơi hay lặn dưới biển.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-chongnuoc.jpg\" onclick=\"return false;\"><img alt=\"Khả năng chống nước của Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" src=\"https://cdn.tgdd.vn/Products/Images/1882/117878/dong-ho-apple-watch-3-phien-ban-42-mm-chongnuoc.jpg\" title=\"Khả năng chống nước của Apple Watch S3 GPS, 42mm viền nhôm, dây cao su màu đen\" /></a></p>\r\n', 0, 0, 'apple-watch-s3-gps-42mm-vien-nhom-day-cao-su-den-nt-400x400.jpg', 8490000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(28, 'Huawei Watch GT Active Edition', 5, 'Đồng hồ', '<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/huawei-watch-gt-xanh-la-cay\" target=\"_blank\" title=\"Xem thêm về Huawei Watch GT màu xanh lá\" type=\"Xem thêm về Huawei Watch GT màu xanh lá\">Huawei Watch GT</a>&nbsp;là m&ocirc;̣t sản ph&acirc;̉m được thi&ecirc;́t k&ecirc;́ với vẻ đẹp c&ocirc;̉ đi&ecirc;̉n, đ&ocirc;̣ mỏng &acirc;́n tượng. Thời lượng pin dài l&ecirc;n đ&ecirc;́n 2 tu&acirc;̀n cùng nhi&ecirc;̀u tính năng theo dõi và rèn luy&ecirc;̣n sức khỏe.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/200594/Slider/-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/huawei-watch-gt-xanh-la-cay\" target=\"_blank\" title=\"Xem thêm về Huawei Watch GT màu xanh lá\" type=\"Xem thêm về Huawei Watch GT màu xanh lá\">Huawei Watch GT</a>&nbsp;là m&ocirc;̣t sản ph&acirc;̉m được thi&ecirc;́t k&ecirc;́ với vẻ đẹp c&ocirc;̉ đi&ecirc;̉n, đ&ocirc;̣ mỏng &acirc;́n tượng. Thời lượng pin dài l&ecirc;n đ&ecirc;́n 2 tu&acirc;̀n cùng nhi&ecirc;̀u tính năng theo dõi và rèn luy&ecirc;̣n sức khỏe.</h2>\r\n\r\n<h3>Thi&ecirc;́t k&ecirc;́ c&ocirc;̉ đi&ecirc;̉n, mỏng và đẹp mắt</h3>\r\n\r\n<p>Vỏ của&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-huawei\" target=\"_blank\" title=\"Xem thêm các mẫu smartwtach Huawei đang kinh doanh\" type=\"Xem thêm các mẫu smartwtach Huawei đang kinh doanh\">smartwatch Huawei</a>&nbsp;được thi&ecirc;́t k&ecirc;́ đẹp mắt với vi&ecirc;̀n g&ocirc;́m, vỏ thép. Mặt đ&ocirc;̀ng h&ocirc;̀ mỏng 10.6mm. T&acirc;́m n&ecirc;̀n AMOLED 1.39&rdquo; cho ch&acirc;́t lượng hi&ecirc;̉n thị r&acirc;́t t&ocirc;́t ngay cả khi sử dụng ngoài trời.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200594/huawei-watch-gt-xanh-la-cay-1-2.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế Huawei Watch GT\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200594/huawei-watch-gt-xanh-la-cay-1-2.jpg\" title=\"Thiết kế Huawei Watch GT\" /></a></p>\r\n\r\n<p>Mặt&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm các mẫu đồng hồ thông minh đang kinh doanh\" type=\"Xem thêm các mẫu đồng hồ thông minh đang kinh doanh\">đ&ocirc;̀ng h&ocirc;̀ th&ocirc;ng minh</a>&nbsp;có th&ecirc;̉ d&ecirc;̃ dàng thay đ&ocirc;̉i nhi&ecirc;̀u giao di&ecirc;̣n khác nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200594/huawei-watch-gt-xanh-la-cay-12.gif\" onclick=\"return false;\"><img alt=\"Nhiều mặt đồng hồ của  Huawei Watch GT\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200594/huawei-watch-gt-xanh-la-cay-12.gif\" title=\"Nhiều mặt đồng hồ của  Huawei Watch GT\" /></a></p>\r\n\r\n<h3>Pin khủng- sử dụng li&ecirc;n tục l&ecirc;n đ&ecirc;́n 14 ngày</h3>\r\n\r\n<p>Huawei mang đ&ecirc;́n cho bạn m&ocirc;̣t thi&ecirc;́t bị có tu&ocirc;̉i thọ pin cực t&ocirc;́t. Bạn có th&ecirc;̉ sử dụng đ&ocirc;̀ng h&ocirc;̀ trong hai tu&acirc;̀n li&ecirc;̀n với ch&ecirc;́ đ&ocirc;̣ đo nhịp tim lu&ocirc;n b&acirc;̣t và theo dõi t&acirc;̣p luy&ecirc;̣n 90 phút m&ocirc;̃i tu&acirc;̀n. N&ecirc;́u bạn b&acirc;̣t tính năng theo dõi luy&ecirc;̣n t&acirc;̣p, định vị và theo dõi nhịp tim thì con s&ocirc;́ này là 22 giờ. Pin sạc nam ch&acirc;m kh&ocirc;ng d&acirc;y giúp ti&ecirc;̣n lợi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200594/huawei-watch-gt-xanh-la-cay-111.jpg\" onclick=\"return false;\"><img alt=\"thời lượng pin của Huawei Watch GT\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200594/huawei-watch-gt-xanh-la-cay-111.jpg\" title=\"thời lượng pin của Huawei Watch GT\" /></a></p>\r\n\r\n<h3>C&ocirc;ng ngh&ecirc;̣ theo dõi và rèn luy&ecirc;̣n sức khỏe&nbsp;</h3>\r\n\r\n<p>&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm về đồng hồ thông minh khác\" type=\"Xem thêm về đồng hồ thông minh khác\">Đ&ocirc;̀ng h&ocirc;̀ th&ocirc;ng minh</a>&nbsp;có tính năng theo dõi nhịp tim li&ecirc;n tục. Đ&ocirc;̀ng thời là tính năng theo dõi và nhắc nhở người dùng vào khu&ocirc;n kh&ocirc;̉ rèn luy&ecirc;̣n th&ecirc;̉ thao, mang lại sức khỏe t&ocirc;́t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200594/huawei-watch-gt-xanh-la-cay-4-1.jpg\" onclick=\"return false;\"><img alt=\"Theo dõi nhịp tim Huawei Watch GT\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200594/huawei-watch-gt-xanh-la-cay-4-1.jpg\" title=\"Theo dõi nhịp tim Huawei Watch GT\" /></a></p>\r\n\r\n<h3>Chạy b&ocirc;̣, đạp xe, leo núi</h3>\r\n\r\n<p>Với đ&ocirc;̣ cảm bi&ecirc;́n cao và khả năng định vị, Huawei Watch GT đ&ocirc;̀ng hành cùng bạn tr&ecirc;n những cung đường và những ngọn núi. Ghi nh&acirc;̣n lại giúp bạn những thử thách đã chinh phục.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200594/huawei-watch-gt-xanh-la-cay-7-1.jpg\" onclick=\"return false;\"><img alt=\"Tính năng theo dõi vị trí Huawei Watch GT\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200594/huawei-watch-gt-xanh-la-cay-7-1.jpg\" title=\"Tính năng theo dõi vị trí Huawei Watch GT\" /></a></p>\r\n\r\n<h3>Theo dõi gi&acirc;́c ngủ &ndash; mang lại gi&acirc;́c ngủ s&acirc;u</h3>\r\n\r\n<p>Được cài đặt tính năng đặc bi&ecirc;̣t giúp theo dõi những gi&acirc;́c ngủ của bạn. Th&ocirc;ng báo những v&acirc;́n đ&ecirc;̀ ph&ocirc;̉ bi&ecirc;́n v&ecirc;̀ gi&acirc;́c ngủ và đưa ra hơn 200 giải pháp khắc phục. Giúp bạn có được những gi&acirc;́c ngủ s&acirc;u, cho ngày làm vi&ecirc;̣c năng đ&ocirc;̣ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200594/huawei-watch-gt-xanh-la-cay-11-1.jpg\" onclick=\"return false;\"><img alt=\"Huawei Watch GT theo dõi giấc ngủ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200594/huawei-watch-gt-xanh-la-cay-11-1.jpg\" title=\"Huawei Watch GT theo dõi giấc ngủ\" /></a></p>\r\n\r\n<h3>Khả năng ch&ocirc;́ng nước&nbsp;</h3>\r\n\r\n<p>Huawei mang lại 1 chi&ecirc;́c đ&ocirc;̀ng h&ocirc;̀ vời khả năng ch&ocirc;́ng nước ở đ&ocirc;̣ s&acirc;u 50m. Cho phép bạn có th&ecirc;̉ mang đ&ocirc;̀ng h&ocirc;̀ khi rửa tay, đi mưa hay đi tắm mà kh&ocirc;ng c&acirc;̀n lo ngại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200594/huawei-watch-gt-xanh-la-cay-8.jpg\" onclick=\"return false;\"><img alt=\"Chống nước Huawei Watch GT\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200594/huawei-watch-gt-xanh-la-cay-8.jpg\" title=\"Chống nước Huawei Watch GT\" /></a></p>\r\n\r\n<h3>Khả năng k&ecirc;́t n&ocirc;́i hi&ecirc;̣n đại</h3>\r\n\r\n<p>Huawei Watch GT có th&ecirc;̉ k&ecirc;́t n&ocirc;́i với cả Android và iOS. K&ecirc;́t n&ocirc;́i đơn giản qua bluetooth giúp bạn nh&acirc;̣n những cu&ocirc;̣c gọi, tin nhắn mọi lúc, mọi nơi m&ocirc;̣t cách d&ecirc;̃ dàng và ti&ecirc;̣n lợi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/200594/huawei-watch-gt-xanh-la-cay-11.jpg\" onclick=\"return false;\"><img alt=\"kết nối trên Huawei Watch GT\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200594/huawei-watch-gt-xanh-la-cay-11.jpg\" title=\"kết nối trên Huawei Watch GT\" /></a></p>\r\n', 0, 0, 'huawei-watch-gt-xanh-la-cay-400x400.jpg', 3990000),
(29, 'Apple Watch S4 GPS 44mm viền nhôm dây cao su', 5, 'Đồng hồ', '<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s4-gps-44mm-vien-nhom-day-cao-su-den\" target=\"_blank\" title=\"Apple Watch S4 GPS 44mm viền nhôm dây cao su màu đen (MU6D2VN/A) chính hãng, giá tốt, bán tại Thế Giới Di Động\">Apple Watch S4 GPS 44mm viền nh&ocirc;m d&acirc;y cao su</a>. M&agrave;n h&igrave;nh 44m gi&uacute;p hiển thị th&ocirc;ng tin dễ nh&igrave;n, r&otilde; r&agrave;ng hơn cho người d&ugrave;ng.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/194392/Slider/-n1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s4-gps-44mm-vien-nhom-day-cao-su-den\" target=\"_blank\" title=\"Apple Watch S4 GPS 44mm viền nhôm dây cao su màu đen (MU6D2VN/A) chính hãng, giá tốt, bán tại Thế Giới Di Động\">Apple Watch S4 GPS 44mm viền nh&ocirc;m d&acirc;y cao su</a>. M&agrave;n h&igrave;nh 44m gi&uacute;p hiển thị th&ocirc;ng tin dễ nh&igrave;n, r&otilde; r&agrave;ng hơn cho người d&ugrave;ng.</h2>\r\n\r\n<h3>M&agrave;n h&igrave;nh lớn</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh l&ecirc;n đến 44m c&oacute; viền m&agrave;n h&igrave;nh mỏng, hiển thị th&ocirc;ng tin r&otilde; r&agrave;ng hơn kh&aacute; nhiều so với thế hệ trước. Ngo&agrave;i ra phần th&acirc;n đồng hồ cũng mỏng hơn đ&aacute;ng kể, cho kiểu d&aacute;ng thời trang, nhẹ nh&agrave;ng hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-6.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-6.jpg\" title=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - màn hình\" /></a></p>\r\n\r\n<h3>Mặt đồng hồ thay được</h3>\r\n\r\n<p>Mặt đồng hồ điện tử hiển thị ngo&agrave;i thời gian c&ograve;n c&oacute; rất nhiều th&ocirc;ng tin hữu &iacute;ch, như thời tiết, qu&aacute; tr&igrave;nh vận động trong ng&agrave;y, gi&uacute;p bạn dễ d&agrave;ng theo d&otilde;i c&aacute;c th&ocirc;ng tin trong ng&agrave;y chỉ bằng việc nh&igrave;n v&agrave;o đồng hồ. Ngo&agrave;i ra, đồng hồ c&ograve;n c&oacute; nhiều hiệu ứng đẹp mắt để bạn thay thế theo &yacute; th&iacute;ch.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-5.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - mặt đồng hồ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-5.jpg\" title=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - mặt đồng hồ\" /></a></p>\r\n\r\n<h3>Điện t&acirc;m đồ</h3>\r\n\r\n<p>Điện t&acirc;m đồ l&agrave; chức năng kh&aacute; hữu &iacute;ch để theo d&otilde;i sức khỏe của bạn. Giờ đ&acirc;y,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\" title=\"Smartwatch Apple Watch chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">smartwatch Apple</a>&nbsp;c&oacute; thể đo được điện t&acirc;m đồ ngay tr&ecirc;n cổ tay bạn, t&iacute;nh năng mới n&agrave;y nhờ v&agrave;o cảm biến điện tử gi&uacute;p đo nhịp tim cực k&igrave; ch&iacute;nh x&aacute;c, gi&uacute;p bạn c&oacute; nhiều th&ocirc;ng tin hữu &iacute;ch hơn về sức khỏe của m&igrave;nh.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong>&nbsp;T&iacute;nh năng ECG hiện chưa được hỗ trợ sử dụng ở Việt Nam, sẽ sử dụng được ở những bản cập nhật sau.<a href=\"https://www.thegioididong.com/images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-7.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - nhịp tim\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-7.jpg\" title=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - nhịp tim\" /></a></p>\r\n\r\n<h3>Nhắc nhở</h3>\r\n\r\n<p>Chức năng nhắc nhở của Apple kh&ocirc;ng chỉ nằm ở đặt lịch, th&ocirc;ng b&aacute;o như điện thoại, n&oacute; c&ograve;n c&oacute; thể nhắc bạn nhớ thở đều mỗi ng&agrave;y, đứng nhiều, vận động nhiều hơn, tất cả l&agrave; để gi&uacute;p bạn cải thiện sức khỏe tốt hơn mỗi ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-8.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - tập luyện\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-8.jpg\" title=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - tập luyện\" /></a></p>\r\n\r\n<h3>Kết nối GPS</h3>\r\n\r\n<p>Kết nối GPS trong đồng hồ gi&uacute;p định vị qu&atilde;ng đường chạy, tốc độ, v&agrave; ph&aacute;c thảo qu&atilde;ng đường tập luyện. Nhờ đ&oacute; bạn c&oacute; thể theo d&otilde;i to&agrave;n bộ qu&atilde;ng đường tập luyện để đưa ra kế hoạch luyện tập ph&ugrave; hợp nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-9.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - gps\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-9.jpg\" title=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - gps\" /></a></p>\r\n\r\n<h3>Ph&aacute;t hiện t&eacute; ng&atilde;</h3>\r\n\r\n<p>Một trong những t&iacute;nh năng cực k&igrave; hữu &iacute;ch của Series 4 gi&uacute;p bảo vệ to&agrave;n diện cho bạn, đ&oacute; l&agrave; khi đồng hồ ph&aacute;t hiện bạn t&eacute; ng&atilde; sẽ lập tức rung phản hồi để b&aacute;o cho bạn biết liệu bạn c&oacute; cần sự trợ gi&uacute;p, nếu bạn kh&ocirc;ng phản hồi trong v&ograve;ng 15 gi&acirc;y, đồng hồ sẽ tự động gọi cấp cứu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-1-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - té ngã\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-1-1.jpg\" title=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - té ngã\" /></a></p>\r\n\r\n<h3>Hỗ trợ tập luyện</h3>\r\n\r\n<p>Nhiều chế độ tập luyện đ&atilde; được th&ecirc;m v&agrave;o chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Đồng hồ thông minh chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh</a>&nbsp;Apple Watch để gi&uacute;p bạn c&oacute; được th&ocirc;ng tin ch&iacute;nh x&aacute;c nhất về bộ m&ocirc;n m&agrave; m&igrave;nh tập h&agrave;ng ng&agrave;y, đưa ra chỉ số calories ch&iacute;nh x&aacute;c nhất, gi&uacute;p bạn dễ d&agrave;ng giảm c&acirc;n, tăng cường sức khỏe tốt hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-3-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - tập luyện\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-3-1.jpg\" title=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - tập luyện\" /></a></p>\r\n\r\n<h3>Hiển thị th&ocirc;ng b&aacute;o</h3>\r\n\r\n<p>Tất cả th&ocirc;ng b&aacute;o tr&ecirc;n iPhone đều sẽ được chuyển qua đồng hồ đầy đủ nhất, th&ocirc;ng b&aacute;o v&agrave; nội dung th&ocirc;ng b&aacute;o, cuộc gọi, trả lời cuộc gọi đều sẽ được hiển thị ngay tr&ecirc;n đồng hồ. Gi&uacute;p bạn kh&ocirc;ng bỏ lỡ bất k&igrave; một tin nhắn quan trọng n&agrave;o.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-4.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - thông báo\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194392/apple-watch-s4-gps-44mm-vien-nhom-xam-day-cao-su-m-4.jpg\" title=\"Apple Watch S4 GPS 44mm space Grey (MU6D2VN/A) - thông báo\" /></a></p>\r\n\r\n<h3>&nbsp;</h3>\r\n', 0, 0, 'apple-watch-s4-gps-44mm-vien-nhom-day-cao-su-den-nt-400x400.jpg', 12490000),
(30, 'Apple Watch S4 GPS 44mm viền nhôm dây vải', 5, 'Đồng hồ', '<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s4-gps-44mm-vien-nhom-day-vai-hong\" target=\"_blank\" title=\"Apple Watch S4 GPS 44mm viền nhôm dây vải màu hồng (MU6G2VN/A) chính hãng, giá tốt, bán tại Thế Giới Di Động\">Apple Watch S4 GPS 44mm viền nh&ocirc;m d&acirc;y vải</a>&nbsp;c&oacute; thiết kế kh&aacute; đơn giản v&agrave; nổi bật. Sử dụng d&acirc;y từ chất liệu vải, gi&uacute;p đồng hồ c&aacute; t&iacute;nh hơn, nhẹ nh&agrave;ng hơn khi đeo trong thời gian d&agrave;i. Ngo&agrave;i ra d&acirc;y vải c&ograve;n gi&uacute;p bạn thấy dễ chịu hơn khi tay ra mồ h&ocirc;i l&uacute;c vận động nhiều.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/194338/Slider/-k1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s4-gps-44mm-vien-nhom-day-vai-hong\" target=\"_blank\" title=\"Apple Watch S4 GPS 44mm viền nhôm dây vải màu hồng (MU6G2VN/A) chính hãng, giá tốt, bán tại Thế Giới Di Động\">Apple Watch S4 GPS 44mm viền nh&ocirc;m d&acirc;y vải</a>&nbsp;c&oacute; thiết kế kh&aacute; đơn giản v&agrave; nổi bật. Sử dụng d&acirc;y từ chất liệu vải, gi&uacute;p đồng hồ c&aacute; t&iacute;nh hơn, nhẹ nh&agrave;ng hơn khi đeo trong thời gian d&agrave;i. Ngo&agrave;i ra d&acirc;y vải c&ograve;n gi&uacute;p bạn thấy dễ chịu hơn khi tay ra mồ h&ocirc;i l&uacute;c vận động nhiều.</h2>\r\n\r\n<h3>Thiết kế ho&agrave;n to&agrave;n mới</h3>\r\n\r\n<p>Mang một thiết kế thanh tho&aacute;t hơn với m&agrave;n h&igrave;nh lớn, nhưng vẫn giữ được k&iacute;ch thước cũ, c&ugrave;ng với d&acirc;y đeo vải gi&uacute;p cho trọng lượng khi đeo Apple Watch v&ocirc; c&ugrave;ng nhẹ nh&agrave;ng. Th&acirc;n đồng hồ mỏng hơn cũng gi&uacute;p tăng phần thời trang khi đeo đồng hồ.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-1-1-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-1-1-1.jpg\" title=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - thiết kế\" /></a></p>\r\n\r\n<h3>Thay mặt đồng hồ đa dạng</h3>\r\n\r\n<p>Mặt đồng hồ kh&ocirc;ng những gi&uacute;p xem giờ, giờ đ&acirc;y bạn c&ograve;n c&oacute; thể th&ecirc;m được nhiều th&ocirc;ng tin hữu &iacute;ch, chuyển qua lại giữa c&aacute;c kiểu mặt đồng hồ kh&aacute;c nhau. C&aacute;c kiểu mặt mới lu&ocirc;n được Apple th&ecirc;m v&agrave;o sau mỗi bản cập nhật, gi&uacute;p bạn kh&ocirc;ng bị nh&agrave;m ch&aacute;n khi c&oacute; nhiều mặt đồng hồ kh&aacute;c nhau để lựa chọn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-1-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - mặt đồng hồ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-1-1.jpg\" title=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - mặt đồng hồ\" /></a></p>\r\n\r\n<h3>Điện t&acirc;m đồ</h3>\r\n\r\n<p>N&acirc;ng cấp lớn nhất của&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\" title=\"Đồng hồ thông minh Apple Watch chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh Apple Watch</a>&nbsp;Series 4 trong năm nay ch&iacute;nh l&agrave; khả năng đo điện t&acirc;m đồ, một biểu đồ ch&iacute;nh x&aacute;c về nhịp tim của bạn, từ c&aacute;c th&ocirc;ng tin n&agrave;y, c&oacute; thể gi&uacute;p &iacute;ch cho c&aacute;c b&aacute;c sĩ khi theo d&otilde;i, điều trị sức khỏe cho bạn. N&oacute; c&ograve;n ph&aacute;t hiện khi nhịp tim của bạn c&oacute; dấu hiệu nhanh hoặc chậm bất thường v&agrave; th&ocirc;ng b&aacute;o cho bạn biết.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong>&nbsp;T&iacute;nh năng ECG hiện chưa được hỗ trợ sử dụng ở Việt Nam, sẽ sử dụng được ở những bản cập nhật sau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-4.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - đo nhịp tim\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-4.jpg\" title=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - đo nhịp tim\" /></a></p>\r\n\r\n<h3>Nhắc nhở sức khỏe</h3>\r\n\r\n<p>Apple Watch theo d&otilde;i sức khỏe của bạn đến từng nhịp thở, cũng như vận động trong ng&agrave;y. Nếu n&oacute; ph&aacute;t hiện bạn đ&atilde; ngồi qu&aacute; l&acirc;u, đồng hồ sẽ rung th&ocirc;ng b&aacute;o nhắc nhở bạn đứng dậy v&agrave; đi lại, thư gi&atilde;n. N&oacute; c&ograve;n nhắc nhở bạn ngưng c&ocirc;ng việc hiện tại lại một t&iacute; v&agrave; h&iacute;t thở thật đều để gi&uacute;p l&agrave;m thư gi&atilde;n đầu &oacute;c, tinh thần.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-5.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - vận động\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-5.jpg\" title=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - vận động\" /></a></p>\r\n\r\n<h3>Kết nối GPS</h3>\r\n\r\n<p>Kết nối GPS tr&ecirc;n đồng hồ c&oacute; khả năng định vị được qu&atilde;ng đường chạy hoặc đi bộ, đạp xe của bạn. Bạn sẽ c&oacute; c&aacute;i nh&igrave;n tổng thể hơn về to&agrave;n bộ qu&atilde;ng đuờng đi được, lộ tr&igrave;nh đi, v&agrave; thời gian bao l&acirc;u m&agrave; kh&ocirc;ng cần c&oacute; kết nối internet hay kết nối với điện thoại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-6.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - gps\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-6.jpg\" title=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - gps\" /></a></p>\r\n\r\n<h3>Ph&aacute;t hiện t&eacute; ng&atilde;</h3>\r\n\r\n<p>Nhờ t&iacute;ch hợp v&ocirc; số cảm biến tr&ecirc;n đồng hồ, giờ đ&acirc;y Apple Watch c&oacute; thể ph&aacute;t hiện khi bạn t&eacute; ng&atilde;. Nếu n&oacute; ph&aacute;t hiện bạn bị ng&atilde; n&oacute; sẽ hỏi bạn c&oacute; cần gọi cấp cứu kh&ocirc;ng, nếu bạn kh&ocirc;ng c&oacute; phản hồi, Apple Watch sẽ tự động thực hiện cuộc gọi v&agrave; th&ocirc;ng b&aacute;o cho người th&acirc;n của bạn địa điểm hiện tại của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-2-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A)- té ngã\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-2-1.jpg\" title=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A)- té ngã\" /></a></p>\r\n\r\n<h3>Hỗ trợ nhiều m&ocirc;n thể thao</h3>\r\n\r\n<p>C&aacute;c m&ocirc;n thể thao được t&iacute;ch hợp v&agrave;o Series 4 được mở rộng hơn nữa gi&uacute;p đo lường ch&iacute;nh x&aacute;c được lượng calories ti&ecirc;u thụ đối với từng bộ m&ocirc;n, gi&uacute;p bạn c&oacute; th&ocirc;ng tin ch&iacute;nh x&aacute;c hơn khi tập luyện, qua đ&oacute; điều chỉnh cường độ tập luyện của m&igrave;nh cho ph&ugrave; hợp với mục ti&ecirc;u đề ra.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-3-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - tập luyện\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-3-1.jpg\" title=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - tập luyện\" /></a></p>\r\n\r\n<h3>Hiển thị nội dung th&ocirc;ng b&aacute;o</h3>\r\n\r\n<p>Hiển thị nội dung th&ocirc;ng b&aacute;o cũng l&agrave; một trong những điểm nổi bật tr&ecirc;n Apple Watch, với khả năng đồng bộ th&ocirc;ng tin từ iPhone, c&aacute;c th&ocirc;ng b&aacute;o sẽ được chuyển qua đồng hồ cực k&igrave; nhanh ch&oacute;ng. Ngo&agrave;i ra, Apple Watch c&ograve;n hỗ trợ đ&agrave;m thoại ngay tr&ecirc;n đồng hồ gi&uacute;p bạn thực hiện cuộc gọi nhanh, tiện lợi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-8.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - thông báo\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194338/apple-watch-s4-gps-44mm-vien-nhom-vang-dong-day-va-8.jpg\" title=\"Apple Watch S4 Gold Aluminium GPS 44mm (MU6G2VN/A) - thông báo\" /></a></p>\r\n\r\n<h3>&nbsp;</h3>\r\n', 0, 0, 'apple-watch-s4-gps-44mm-vien-nhom-day-vai-hong-nt-400x400.jpg', 12490000),
(31, 'Samsung Gear Fit2 Pro', 5, 'Đồng hồ', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/147042/Slider/-1.-jpg.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/147042/Slider/-1.-jpg.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Mở hộp Samsung Gear Fit 2 Pro - Thiết kế v&agrave; m&agrave;u sắc đẹp, song h&agrave;nh sự tiện dụng</h2>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/147042/samsung-gear-fit2-pro-11.jpg\" onclick=\"return false;\"><img alt=\"Samsung Gear Fit2 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/1882/147042/samsung-gear-fit2-pro-11.jpg\" title=\"Samsung Gear Fit2 Pro\" /></a></p>\r\n\r\n<p><em>Vẫn kiểu d&aacute;ng thon gọn như d&ograve;ng Gear đời đầu, mặt&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-gear-fit2-pro\" target=\"_blank\" title=\"Samsung Gear Fit2 Pro chính hãng, giá tốt, bán tại Thế Giới Di Động\">v&ograve;ng đeo tay th&ocirc;ng minh Samsung Gear Fit2 Pro</a>&nbsp;hơi cong &ocirc;m s&aacute;t cổ tay thoải m&aacute;i hơn.</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/147042/samsung-gear-fit2-pro-16.jpg\" onclick=\"return false;\"><img alt=\"Samsung Gear Fit2 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/1882/147042/samsung-gear-fit2-pro-16.jpg\" title=\"Samsung Gear Fit2 Pro\" /></a></p>\r\n\r\n<p><em>T&iacute;nh năng chăm s&oacute;c sức khỏe l&agrave; điều kh&ocirc;ng thể kh&ocirc;ng c&oacute;, cảm biến được đặt mặt sau v&ograve;ng đeo tay, với c&aacute;c ứng dụng như đếm bước ch&acirc;n, đo nhịp tim, oxi, ghi lại qu&atilde;ng đường chạy bằng GPS...</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/147042/samsung-gear-fit2-pro-12.jpg\" onclick=\"return false;\"><img alt=\"Samsung Gear Fit2 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/1882/147042/samsung-gear-fit2-pro-12.jpg\" title=\"Samsung Gear Fit2 Pro\" /></a></p>\r\n\r\n<p><em>Sử dụng c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/loai-man-hinh-tft-lcd-amoled-la-gi--592346#amoled\" target=\"_blank\" title=\"Tìm hiểu màn hình AMOLED là gì\">m&agrave;n h&igrave;nh AMOLED</a>&nbsp;gi&uacute;p bạn theo d&otilde;i th&ocirc;ng tin hiển thị khi ngo&agrave;i trời được tốt hơn</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/147042/samsung-gear-fit2-pro-14.jpg\" onclick=\"return false;\"><img alt=\"Samsung Gear Fit2 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/1882/147042/samsung-gear-fit2-pro-14.jpg\" title=\"Samsung Gear Fit2 Pro\" /></a></p>\r\n\r\n<p><em>M&agrave;u sắc v&agrave; chi tiết hiện ra rất sắc sảo v&agrave; r&otilde; n&eacute;t, rất dễ để đọc v&agrave; thao t&aacute;c tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=vong-deo-tay-thong-minh\" target=\"_blank\" title=\"Vòng đeo tay thông minh chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">v&ograve;ng đeo tay th&ocirc;ng minh</a>&nbsp;n&agrave;y.</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/147042/samsung-gear-fit2-pro-13.jpg\" onclick=\"return false;\"><img alt=\"Samsung Gear Fit2 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/1882/147042/samsung-gear-fit2-pro-13.jpg\" title=\"Samsung Gear Fit2 Pro\" /></a></p>\r\n\r\n<p><em>Được thiết kế để ph&ugrave; hợp với mọi hoạt động thể thao, với chuẩn chống nước 5 ATM cho khả năng lặn s&acirc;u 50 m m&agrave; vẫn an to&agrave;n</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/147042/samsung-gear-fit2-pro-17.jpg\" onclick=\"return false;\"><img alt=\"Samsung Gear Fit2 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/1882/147042/samsung-gear-fit2-pro-17.jpg\" title=\"Samsung Gear Fit2 Pro\" /></a></p>\r\n\r\n<p><em>Kh&ocirc;ng c&ograve;n những mối bận t&acirc;m d&iacute;nh nước g&acirc;y hư hỏng cho thiết bị</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/147042/samsung-gear-fit2-pro-15.jpg\" onclick=\"return false;\"><img alt=\"Samsung Gear Fit2 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/1882/147042/samsung-gear-fit2-pro-15.jpg\" title=\"Samsung Gear Fit2 Pro\" /></a></p>\r\n\r\n<p><em>Với thế hệ mới th&igrave; d&acirc;y đeo được thay đổi bằng m&oacute;c c&agrave;i chắc chắn hơn rất nhiều lần</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/1882/147042/samsung-gear-fit2-pro-18.jpg\" onclick=\"return false;\"><img alt=\"Samsung Gear Fit2 Pro\" src=\"https://cdn.tgdd.vn/Products/Images/1882/147042/samsung-gear-fit2-pro-18.jpg\" title=\"Samsung Gear Fit2 Pro\" /></a></p>\r\n\r\n<p><em>Kho t&agrave;ng khổng lồ giao diện mặt đồng hồ gi&uacute;p bạn c&aacute; nh&acirc;n h&oacute;a&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-samsung?g=vong-deo-tay-thong-minh\" target=\"_blank\" title=\"Vòng đeo tay thông minh Samsung chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">v&ograve;ng đeo tay th&ocirc;ng minh Samsung</a>&nbsp;Gear Fit2 Pro theo c&aacute; t&iacute;nh của ri&ecirc;ng bạn</em></p>\r\n', 0, 0, 'samsung-gear-fit2-pro-nt-1-400x400.jpg', 2990000),
(32, 'iPhone Xr 256GB', 1, 'Điện thoại', '<p>Ngo&agrave;i c&aacute;c phi&ecirc;n bản&nbsp;như thường lệ mọi năm, v&agrave;o đ&ecirc;m 12/9 theo giờ Việt Nam, Apple đ&atilde; cho ra mắt th&ecirc;m một phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại iPhone\" type=\"Tham khảo các dòng điện thoại iPhone\">iPhone</a>&nbsp;kh&aacute;c với t&ecirc;n gọi&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-xr-256gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone XR 256GB\" type=\"Chi tiết điện thoại iPhone XR 256GB\">iPhone Xr</a>, c&oacute; mức gi&aacute; dễ chịu hơn nhưng vẫn mang đầy đủ c&aacute;c t&iacute;nh năng cao cấp đầy hấp dẫn.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/190326/Slider/-iphone-xr-thietke-2.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Sạc, Tai nghe, S&aacute;ch hướng dẫn, C&aacute;p, C&acirc;y lấy sim</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Ngo&agrave;i c&aacute;c phi&ecirc;n bản&nbsp;như thường lệ mọi năm, v&agrave;o đ&ecirc;m 12/9 theo giờ Việt Nam, Apple đ&atilde; cho ra mắt th&ecirc;m một phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại iPhone\" type=\"Tham khảo các dòng điện thoại iPhone\">iPhone</a>&nbsp;kh&aacute;c với t&ecirc;n gọi&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-xr-256gb\" target=\"_blank\" title=\"Chi tiết điện thoại iPhone XR 256GB\" type=\"Chi tiết điện thoại iPhone XR 256GB\">iPhone Xr</a>, c&oacute; mức gi&aacute; dễ chịu hơn nhưng vẫn mang đầy đủ c&aacute;c t&iacute;nh năng cao cấp đầy hấp dẫn.</h2>\r\n\r\n<h3>Mượt m&agrave; mọi trải nghiệm nhờ&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-chip-apple-a12-bionic-ben-trong-iphone-xs-xs-max-1116982\" target=\"_blank\" title=\"Tìm hiểu chip Apple A12\" type=\"Tìm hiểu chip Apple A12\">chip Apple A12</a></h3>\r\n\r\n<p>Với mỗi lần ra mắt, Apple lại giới thiệu một con chip mới v&agrave; Apple A12 Bionic l&agrave; con chip đầu ti&ecirc;n sản xuất với tiến tr&igrave;nh 7nm được t&iacute;ch hợp tr&ecirc;n iPhone Xr.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-a12.jpg\" onclick=\"return false;\"><img alt=\"Chip A12 trên điện thoại iPhone Xr chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-a12.jpg\" title=\"Chip A12 trên điện thoại iPhone Xr chính hãng\" /></a></p>\r\n\r\n<p>Apple A12 được t&iacute;ch hợp tr&iacute; tuệ th&ocirc;ng minh nh&acirc;n tạo, mọi phản hồi tr&ecirc;n m&aacute;y đều nhanh ch&oacute;ng v&agrave; gần như l&agrave; ngay lập tức, kể cả khi bạn chơi game hay thao t&aacute;c b&igrave;nh thường.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-6.jpg\" onclick=\"return false;\"><img alt=\"iOS trên điện thoại iPhone Xr chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-6.jpg\" title=\"iOS trên điện thoại iPhone Xr chính hãng\" /></a></p>\r\n\r\n<p>Hơn nữa với AI tr&ecirc;n Apple A12,&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại tại Thegioididong.com\">smartphone</a>&nbsp;iPhone Xr c&oacute; thể ghi nhớ được thao t&aacute;c hằng ng&agrave;y của bạn để ho&agrave;n thiện v&agrave; hỗ trợ người d&ugrave;ng tốt hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-7.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm điện thoại iPhone Xr chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-7.jpg\" title=\"Trải nghiệm điện thoại iPhone Xr chính hãng\" /></a></p>\r\n\r\n<p>Thực tế khi chơi game tr&ecirc;n iPhone Xr, m&ocirc;i trường v&agrave; hiệu ứng trong game rất thật. Hơn nữa tr&ecirc;n chiếc iPhone c&ograve;n hỗ trợ&nbsp;thực tế tăng cường: Chơi game thực tế ảo với nhiều người c&ugrave;ng chơi, trải nghiệm c&ugrave;ng 1 m&ocirc;i trường với nhau.</p>\r\n\r\n<h3>D&agrave;n loa cho &acirc;m thanh đa chiều</h3>\r\n\r\n<p>Trước đ&acirc;y để trải nghiệm tối đa &acirc;m thanh th&igrave; tai nghe l&agrave; thiết bị kh&ocirc;ng thể thiếu. Nhưng với iPhone thế hệ mới, c&ocirc;ng nghệ &acirc;m thanh đa chiều từ loa sẽ gi&uacute;p bạn ho&agrave; v&agrave;o &acirc;m nhạc cũng như c&aacute;c bộ phim th&uacute; vị hay chơi game h&agrave;o hứng nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone Xr chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-3.jpg\" title=\"Thiết kế điện thoại iPhone Xr chính hãng\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh tr&agrave;n viền c&ocirc;ng nghệ LCD - True Tone</h3>\r\n\r\n<p>Kh&aacute;c với iPhone Xs hay Xs Max, chiếc&nbsp;smartphone&nbsp;n&agrave;y sở hữu m&agrave;n h&igrave;nh LCD.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại iPhone Xr chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-1.jpg\" title=\"Màn hình điện thoại iPhone Xr chính hãng\" /></a></p>\r\n\r\n<p>B&ugrave; lại với c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-man-hinh-true-tone-992705\" target=\"_blank\" title=\"Tìm hiểu công nghệ màn hình True Tone\" type=\"Tìm hiểu công nghệ màn hình True Tone\">True Tone</a>&nbsp;c&ugrave;ng m&agrave;n h&igrave;nh tr&agrave;n viền rộng tới 6.1 inch, mọi trải nghiệm tr&ecirc;n m&aacute;y vẫn đem lại sự th&iacute;ch th&uacute; v&agrave; ho&agrave;n hảo, như d&ograve;ng cao cấp kh&aacute;c của Apple.</p>\r\n\r\n<p>Ngo&agrave;i ra, Apple cũng giới thiệu rằng, iPhone Xr được trang bị một loại c&ocirc;ng nghệ mới c&oacute; t&ecirc;n&nbsp;Liquid Retina. M&aacute;y c&oacute; độ ph&acirc;n giải 1792 x 828 Pixels c&ugrave;ng 1.4 triệu điểm ảnh.</p>\r\n\r\n<h3>Camera với hiệu ứng bokeh cực đẹp</h3>\r\n\r\n<p>Nếu ai đ&oacute; khẳng định chỉ&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/camera-kep-la-gi-co-phai-la-xu-huong-tren-smartphone-2017-953888\" target=\"_blank\" title=\"Tìm hiểu về cẩm kép trên điện thoại\" type=\"Tìm hiểu về cẩm kép trên điện thoại\">camera k&eacute;p</a>&nbsp;mới xo&aacute; ph&ocirc;ng th&igrave; đ&oacute; l&agrave; một sai lầm. iPhone Xr c&oacute; khả năng tạo hiệu ứng bokeh tuyệt đỉnh, l&agrave;m nổi bật ch&acirc;n dung người chụp m&agrave; h&igrave;nh ảnh vẫn rất sắc n&eacute;t, chi tiết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-13.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-13.jpg\" title=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\" /></a></p>\r\n\r\n<p>iPhone Xr hỗ trợ người chụp ảnh từ cơ bản đến cả n&acirc;ng cao chuy&ecirc;n nghiệp, đem lại sự h&agrave;i l&ograve;ng tối đa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-9.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-9.jpg\" title=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\" /></a></p>\r\n\r\n<p>M&aacute;y c&ograve;n c&oacute; t&iacute;nh năng Smart HDR: D&ugrave; bạn chụp ảnh c&oacute; độ s&aacute;ng tối kh&aacute;c biệt nhiều nhưng vẫn giữ được chủ thể.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-12.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-12.jpg\" title=\"Ảnh chụp từ điện thoại iPhone Xr chính hãng\" /></a></p>\r\n\r\n<p>Camera selfie của m&aacute;y&nbsp;mang c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/mot-su-that-bat-ngo-ve-camera-truedepth-tren-iphone-x-1021510\" target=\"_blank\" title=\"Tìm hiểu về công nghệ TrueDepth\" type=\"Tìm hiểu về công nghệ TrueDepth\">TrueDepth</a>&nbsp;mới nhất c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/giai-thich-su-khac-nhau-giua-ar-emoji-cua-samsung-va-animoji-cua-apple-1070052\" target=\"_blank\" title=\"Tìm hiểu về Animoji trên điện thoại Apple\" type=\"Tìm hiểu về Animoji trên điện thoại Apple\">Animoji</a>&nbsp;th&ocirc;ng minh hơn, mọi cảm x&uacute;c v&agrave; biểu cảm của bạn trở n&ecirc;n ho&agrave;n hảo v&agrave; th&uacute; vị.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-animoji.jpg\" onclick=\"return false;\"><img alt=\"Animoji trên điện thoại iPhone XR 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-animoji.jpg\" title=\"Animoji trên điện thoại iPhone XR 256GB\" /></a></h3>\r\n\r\n<h3>Một số t&iacute;nh năng cao cấp kh&aacute;c</h3>\r\n\r\n<p>Như thường lệ, c&aacute;c d&ograve;ng si&ecirc;u phẩm mới sau n&agrave;y đều được hỗ trợ chống nước chống bụi v&agrave; iPhone Xr cũng thế.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/190326/iphone-xr-256gb-5.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone Xr chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190326/iphone-xr-256gb-5.jpg\" title=\"Thiết kế điện thoại iPhone Xr chính hãng\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra bảo mật khu&ocirc;n mặt Face ID c&agrave;ng ng&agrave;y c&agrave;ng ho&agrave;n thiện gi&uacute;p bạn mở m&aacute;y nhanh ch&oacute;ng trong mọi điều kiện sử dụng.</p>\r\n', 1, 0, 'iphone-xr-256gb-white-400x460.jpg', 23990000),
(33, 'OPPO Reno 10x Zoom Edition', 1, 'Điện thoại', '<p>Những chiếc flagship trong năm 2019 kh&ocirc;ng chỉ c&oacute; một camera chụp ảnh đẹp, chụp x&oacute;a ph&ocirc;ng ảo diệu m&agrave; c&ograve;n phải &quot;chụp thật xa&quot; v&agrave; với chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-reno-10x-zoom-edition\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO Reno 10x Zoom Edition chính hãng\">OPPO Reno 10x Zoom Edition</a>&nbsp;ch&iacute;nh thức gia nhập thị trường &quot;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>&nbsp;c&oacute; camera si&ecirc;u zoom&quot;.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/201235/Slider/vi-vn-oppo-reno-10x-zoom-edition-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Sạc, Tai nghe, S&aacute;ch hướng dẫn, C&aacute;p, C&acirc;y lấy sim, Nắp lưng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Những chiếc flagship trong năm 2019 kh&ocirc;ng chỉ c&oacute; một camera chụp ảnh đẹp, chụp x&oacute;a ph&ocirc;ng ảo diệu m&agrave; c&ograve;n phải &quot;chụp thật xa&quot; v&agrave; với chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-reno-10x-zoom-edition\" target=\"_blank\" title=\"Tham khảo điện thoại OPPO Reno 10x Zoom Edition chính hãng\">OPPO Reno 10x Zoom Edition</a>&nbsp;ch&iacute;nh thức gia nhập thị trường &quot;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại chính hãng\">smartphone</a>&nbsp;c&oacute; camera si&ecirc;u zoom&quot;.</h2>\r\n\r\n<h3>Khi chụp ảnh đẹp th&ocirc;i l&agrave; chưa đủ</h3>\r\n\r\n<p>B&ecirc;n cạnh cuộc chạy đua về cấu h&igrave;nh th&igrave; camera l&agrave; điểm n&oacute;ng của c&aacute;c h&atilde;ng khi li&ecirc;n tục c&oacute; những cải tiến mạnh mẽ về camera phone.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition9.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition9.jpg\" title=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" /></a></p>\r\n\r\n<p>Sau khi chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd/huawei-p30\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại Huawei P30 Pro chính hãng\">Huawei P30 Pro</a>&nbsp;ra mắt kh&ocirc;ng l&acirc;u với khả năng zoom l&ecirc;n tới 10X hay 50X th&igrave; thậm ch&iacute; chiếc&nbsp;OPPO Reno 10x Zoom Edition c&ograve;n l&agrave;m tốt hơn thế.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition6.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition6.jpg\" title=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" /></a></p>\r\n\r\n<p>M&aacute;y sở hữu cụm 3 camera với độ ph&acirc;n giải camera ch&iacute;nh l&agrave; 48 MP,&nbsp;một camera g&oacute;c rộng 8 MP F/2.2 v&agrave; một&nbsp;ống k&iacute;nh tiềm vọng 13 MP khẩu độ F/3.0 với khả năng zoom hybrid 10X.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition5.jpg\" onclick=\"return false;\"><img alt=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition5.jpg\" title=\"Camera sau của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" /></a></p>\r\n\r\n<p>Với&nbsp;OPPO Reno 10x Zoom Edition th&igrave; bạn c&oacute; thể sử dụng m&aacute;y như một chiếc &quot;k&iacute;nh viễn vọng&quot; khi c&oacute; thể quan s&aacute;t được những vật ở rất xa m&agrave; mắt thường cũng kh&oacute; c&oacute; thể thấy được.</p>\r\n\r\n<p>H&atilde;y thử tưởng tượng bạn đang đứng ở xa v&agrave; muốn chụp lấy phần ph&iacute;a tr&ecirc;n của một t&ograve;a nh&agrave; hay một ngọn n&uacute;i th&igrave; t&iacute;nh năng si&ecirc;u zoom tr&ecirc;n&nbsp;Reno 10x Zoom sẽ rất tuyệt vời.</p>\r\n\r\n<h3>Camera trước thoắt ẩn thoắt hiện</h3>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; camera sau m&agrave; camera trước tr&ecirc;n m&aacute;y cũng l&agrave; một bước s&aacute;ng tạo của OPPO so với những chiếc m&aacute;y kh&aacute;c đang c&oacute; tr&ecirc;n thị trường.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition4.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition4.jpg\" title=\"Camera trước selfie của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" /></a></p>\r\n\r\n<p>OPPO Reno 10x Zoom Edition sở hữu&nbsp;camera selfie ẩn trong khung v&agrave; mở l&ecirc;n bằng cơ chế pop-up sử dụng động cơ để n&acirc;ng l&ecirc;n.</p>\r\n\r\n<p>Cơ chế n&agrave;y sẽ kh&aacute;c so với việc n&acirc;ng to&agrave;n bộ cụm camera như tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-find-x\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\" type=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\">OPPO Find X</a>&nbsp;n&ecirc;n theo OPPO th&igrave; nhờ đ&oacute; n&oacute; sẽ c&oacute; độ bền cao hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition7.jpg\" onclick=\"return false;\"><img alt=\"Camera trước selfie của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition7.jpg\" title=\"Camera trước selfie của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" /></a></p>\r\n\r\n<p>Tốc độ n&acirc;ng l&ecirc;n hạ xuống của cụm camera n&agrave;y cũng rất nhanh gi&uacute;p bạn cảm thấy hầu như kh&ocirc;ng c&oacute; sự kh&aacute;c biệt so với việc c&oacute; một camera lu&ocirc;n xuất hiện ph&iacute;a trước.</p>\r\n\r\n<h3>Nhiều c&ocirc;ng nghệ ti&ecirc;n tiến kh&aacute;c</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại OPPO chính hãng\">điện thoại OPPO</a>&nbsp;n&agrave;y sở hữu cấu h&igrave;nh mạnh mẽ nhất thế giới Android trong năm 2019 với con chip&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/snapdragon-855-tong-hop-thong-tin-ve-chip-cao-cap-cua-qualcomm-1110568\" target=\"_blank\" title=\"Tìm hiểu chip Snapdragon 855 8 nhân 64-bit\">Snapdragon 855 8 nh&acirc;n 64-bit</a>&nbsp;kết hợp với 8 GB RAM v&agrave; 256 GB bộ nhớ trong.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition2.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition2.jpg\" title=\"Cấu hình của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" /></a></p>\r\n\r\n<p>Cảm biến v&acirc;n tay b&ecirc;n trong m&agrave;n h&igrave;nh cũng xuất hiện tr&ecirc;n m&aacute;y song song với khả năng mở kh&oacute;a bằng khu&ocirc;n mặt gi&uacute;p bạn dễ d&agrave;ng sử dụng mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition3.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay trên điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition3.jpg\" title=\"Cảm biến vân tay trên điện thoại OPPO Reno 10x Zoom Edition chính hãng\" /></a></p>\r\n\r\n<p>M&aacute;y c&oacute; pin dung lượng 4065 mAh, hỗ trợ sạc nhanh VOOC 3.0 50W cho tốc độ sạc đầy pin nhanh hơn khoảng 30% so với VOOC 2.0 trước đ&oacute;.</p>\r\n\r\n<h3>Kh&ocirc;ng chỉ l&agrave; smartphone m&agrave; c&ograve;n l&agrave; m&oacute;n đồ thời trang</h3>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; nhiều c&ocirc;ng nghệ ti&ecirc;n tiến m&agrave; thiết kế tr&ecirc;n&nbsp;OPPO Reno 10x Zoom Edition cũng sẽ khiến bạn &quot;y&ecirc;u từ c&aacute;i nh&igrave;n đầu ti&ecirc;n&quot;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition1.jpg\" title=\"Thiết kế của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" /></a></p>\r\n\r\n<p>Đầu ti&ecirc;n phải kể đến thiết kế khung viền kim loại c&ugrave;ng 2 mặt k&iacute;nh cường lực&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/corning-ra-mat-gorilla-glass-6-chiu-va-dap-o-do-cao-1-met-1102577\" target=\"_blank\" title=\"Tìm hiểu Corning Gorilla Glass 6\">Corning Gorilla Glass 6</a>&nbsp;mới nhất cho bạn một c&aacute;i nh&igrave;n rất h&agrave;o nho&aacute;ng v&agrave; sang trọng.</p>\r\n\r\n<p>Điểm nhấn tới từ cụm 3 camera ở mặt lưng đặt dọc c&ugrave;ng&nbsp;logo v&agrave; c&aacute;c th&ocirc;ng tin sản phẩm được đặt dọc theo một đường chạy xuy&ecirc;n suốt vị tr&iacute; ch&iacute;nh giữa tr&ocirc;ng thiết kế n&agrave;y rất hợp l&yacute; v&agrave; đẹp mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/201235/oppo-reno-10x-zoom-edition8.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/201235/oppo-reno-10x-zoom-edition8.jpg\" title=\"Thời lượng pin của điện thoại OPPO Reno 10x Zoom Edition chính hãng\" /></a></p>\r\n\r\n<p>Mặt lưng cũng được ho&agrave;n thiện cong 3D về c&aacute;c cạnh gi&uacute;p m&aacute;y &ocirc;m tay khi cầm nắm sử dụng trong thời gian d&agrave;i.</p>\r\n', 0, 0, 'oppo-reno-10x-zoom-edition-black-400x460.jpg', 20990000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(34, 'OPPO Find X', 1, 'Điện thoại', '<p><a href=\"https://www.thegioididong.com/dtdd/oppo-find-x\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\" type=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\">OPPO Find X</a>&nbsp;tạo n&ecirc;n một c&uacute; h&iacute;ch lớn trong l&agrave;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Các dòng smartphone đang kinh doanh tại Thegioididong.com\" type=\"Các dòng smartphone đang kinh doanh tại Thegioididong.com\">smartphone</a>&nbsp;hiện nay khi mang trong m&igrave;nh nhiều t&iacute;nh năng đột ph&aacute; m&agrave; nổi bật nhất đến từ thiết kế s&aacute;ng tạo v&agrave; một hiệu năng đỉnh cao.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/165189/Slider/-oppo-find-x-thietke.gif\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dtdd/oppo-find-x\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\" type=\"Chi tiết điện thoại OPPO Find X tại Thegioididong.com\">OPPO Find X</a>&nbsp;tạo n&ecirc;n một c&uacute; h&iacute;ch lớn trong l&agrave;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Các dòng smartphone đang kinh doanh tại Thegioididong.com\" type=\"Các dòng smartphone đang kinh doanh tại Thegioididong.com\">smartphone</a>&nbsp;hiện nay khi mang trong m&igrave;nh nhiều t&iacute;nh năng đột ph&aacute; m&agrave; nổi bật nhất đến từ thiết kế s&aacute;ng tạo v&agrave; một hiệu năng đỉnh cao.</h2>\r\n\r\n<h3>Thiết kế đến từ tương lai</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Các dòng điện thoại OPPO tại Thegioididong.com\" type=\"Các dòng điện thoại OPPO tại Thegioididong.com\">điện thoại OPPO</a>&nbsp;mới đ&atilde; tạo n&ecirc;n sự kh&aacute;c biệt cho ri&ecirc;ng m&igrave;nh nhờ lối thiết kế dạng trượt phần camera gi&uacute;p kh&ocirc;ng gian hiển thị mặt trước chiếm gần như l&agrave; trọn vẹn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-1-3.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-1-3.jpg\" title=\"Màn hình điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>Mặt trước&nbsp;OPPO Find X chiếm diện t&iacute;ch khoảng&nbsp;87.0%</em></p>\r\n\r\n<p>V&agrave; theo nhận định của nhiều chuy&ecirc;n gia cơ chế trượt của m&aacute;y hoạt động kh&aacute; trơn tru v&agrave; bền bỉ trong thời gian d&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-6.gif\" onclick=\"return false;\"><img alt=\"Cơ chế trượt trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-6.gif\" title=\"Cơ chế trượt trên điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>Cơ chế trượt của&nbsp;OPPO Find X&nbsp;được điều khiển th&ocirc;ng qua thao t&aacute;c tr&ecirc;n m&agrave;n h&igrave;nh&nbsp;</em></p>\r\n\r\n<p>Tuy nhi&ecirc;n, vấn đề bụi bẩn v&agrave; nước sẽ l&agrave; mối lo ngại đ&aacute;ng kể với hệ thống trượt n&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-9-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế trượt trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-9-1.jpg\" title=\"Thiết kế trượt trên điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>Cơ chế trượt OPPO Find X&nbsp;để lộ nhiều khoảng trống dễ bị v&agrave;o bụi</em></p>\r\n\r\n<p>Tổng thể th&acirc;n m&aacute;y được l&agrave;m sắc sảo đến từng chi tiết trong sự kết hợp h&agrave;i h&ograve;a giữa&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=kim-loai-va-kinh-cuong-luc\" target=\"_blank\" title=\"Điện thoại có thiết kế khung kim loại và mặt kính tại Thegioididong.com\" type=\"Điện thoại có thiết kế khung kim loại và mặt kính tại Thegioididong.com\">khung kim loại v&agrave; 2 mặt k&iacute;nh</a>&nbsp;cao cấp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-7-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-7-1.jpg\" title=\"Thiết kế điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>OPPO Find X sở hữu thiết kế sang trọng v&agrave; hiện đại</em></p>\r\n\r\n<p>D&ugrave; sở hữu k&iacute;ch thước m&agrave;n h&igrave;nh l&ecirc;n đến&nbsp;6.42 inch nhưng m&aacute;y vẫn kh&aacute; vừa vặn mỗi khi cầm nắm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-2-2.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-2-2.jpg\" title=\"Màn hình điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>OPPO Find X sở hữu tỷ lệ m&agrave;n h&igrave;nh 19.5:9</em></p>\r\n\r\n<p>Lấy m&agrave;u đen l&agrave;m t&ocirc;ng chủ đạo khi kết hợp với c&aacute;c m&agrave;u kh&aacute;c gi&uacute;p&nbsp;OPPO Find X trở n&ecirc;n lung linh mờ ảo nhưng kh&ocirc;ng k&eacute;m phần quyến rũ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-13-3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-13-3.jpg\" title=\"Thiết kế điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>M&agrave;u sắc lấp l&aacute;nh ở c&aacute;c g&oacute;c nghi&ecirc;ng kh&aacute;c nhau tr&ecirc;n&nbsp;OPPO Find X</em></p>\r\n\r\n<h3>Hiệu năng h&agrave;ng đầu hiện nay</h3>\r\n\r\n<p>Sức mạnh đến từ con chip&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/chip-snapdragon-845-manh-hon-25-tap-trung-ai-bao-mat-cao-1048166\" target=\"_blank\" title=\"Snapdragon 845\" type=\"Snapdragon 845\">Snapdragon 845</a>&nbsp;c&ugrave;ng 8 GB RAM sẽ l&agrave; th&ocirc;ng số đ&aacute;ng mơ ước tr&ecirc;n một chiếc smartphone v&agrave; nay đ&atilde; c&oacute; mặt tr&ecirc;n&nbsp;OPPO Find X.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Files/2018/08/06/1107210/hieu-nang-oppo-find-x-1_600x338.gif\" onclick=\"return false;\"><img alt=\"Chơi game Liên Quân trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Files/2018/08/06/1107210/hieu-nang-oppo-find-x-1_600x338.gif\" title=\"Chơi game Liên Quân trên điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>Trải nghiệm cấu h&igrave;nh cực khủng tr&ecirc;n OPPO Find X</em></p>\r\n\r\n<p>Với cấu h&igrave;nh mạnh mẽ như tr&ecirc;n ắt hẳn mọi tựa game nặng hiện nay sẽ bị khuất phục một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-5.jpg\" onclick=\"return false;\"><img alt=\"Giao diện điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-5.jpg\" title=\"Giao diện điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>Color OS tr&ecirc;n&nbsp;OPPO Find X th&ocirc;ng minh tiện dụng</em></p>\r\n\r\n<p>Hơn thế nữa, với sự t&ugrave;y biến cao đến từ hệ điều h&agrave;nh&nbsp;Color OS th&igrave; mọi thao t&aacute;c sử dụng hằng ng&agrave;y của bạn sẽ trở n&ecirc;n đơn giản hơn rất nhiều.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-10-1.jpg\" onclick=\"return false;\"><img alt=\"Chơi game trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-10-1.jpg\" title=\"Chơi game trên điện thoại OPPO Find X\" /></a></p>\r\n\r\n<h3>Hệ thống camera đỉnh cao</h3>\r\n\r\n<p>Cụm camera k&eacute;p ở ph&iacute;a sau của m&aacute;y bao gồm một ống k&iacute;nh ch&iacute;nh 16 MP v&agrave; một ống k&iacute;nh phụ 20 MP.</p>\r\n\r\n<p>Nhiều c&ocirc;ng nghệ chụp ảnh hiện đại nhất như:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chong-rung-quang-hoc-ois-chup-anh-tren-smar-906416\" target=\"_blank\" title=\"zoom quang học\" type=\"zoom quang học\">zoom quang học</a>,&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-xoa-phong-huong-dan-xoa-phong-tren-moi-smartphone-892585\" target=\"_blank\" title=\"chụp ảnh xóa phông\" type=\"chụp ảnh xóa phông\">chụp ảnh x&oacute;a ph&ocirc;ng</a>&nbsp;cũng được t&iacute;ch hợp tr&ecirc;n bộ đ&ocirc;i camera k&eacute;p của m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-4-2.jpg\" onclick=\"return false;\"><img alt=\"Khả năng chụp hình camera sau trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-4-2.jpg\" title=\"Khả năng chụp hình camera sau trên điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>Camera sau của&nbsp;OPPO Find X</em></p>\r\n\r\n<p>Với độ ph&acirc;n giải 25 MP tr&ecirc;n camera trước th&igrave;&nbsp;OPPO Find X sẽ l&agrave; c&aacute;i t&ecirc;n nằm trong danh s&aacute;ch những smartphone chụp ảnh selfie đẹp v&agrave; c&oacute; phần ảo diệu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-5-2.jpg\" onclick=\"return false;\"><img alt=\"Camera selfie trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-5-2.jpg\" title=\"Camera selfie trên điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>Cụm camera trước tr&ecirc;n&nbsp;OPPO Find X</em></p>\r\n\r\n<h3>C&ocirc;ng nghệ sạc nhanh VOOC độc quyền</h3>\r\n\r\n<p>OPPO Find X được trang bị c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"sạc nhanh VOOC\" type=\"sạc nhanh VOOC\">sạc nhanh VOOC</a>&nbsp;do h&atilde;ng ph&aacute;t triển đ&aacute;p ứng thời gian sạc nhanh ch&oacute;ng chỉ rơi v&agrave;o khoảng 30 ph&uacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-13-2.jpg\" onclick=\"return false;\"><img alt=\"Cổng kết nối trên điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-13-2.jpg\" title=\"Cổng kết nối trên điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p><em>Giao tiếp tr&ecirc;n&nbsp;OPPO Find X th&ocirc;ng qua cổng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-typec-chuan-muc-cong-ket-noi-moi-723760\" target=\"_blank\" title=\"USB Type C\" type=\"USB Type C\">USB Type C</a></em></p>\r\n\r\n<p>C&ocirc;ng nghệ n&agrave;y được xem như đối thủ của&nbsp;Qualcomm với c&ocirc;ng nghệ Quick charge.</p>\r\n\r\n<h3>Liệu&nbsp;OPPO Find X c&oacute; đ&aacute;ng để bạn m&oacute;c hầu bao?</h3>\r\n\r\n<p>Kh&ocirc;ng thể phủ nhận,&nbsp;Find X chứa đựng nhiều t&iacute;nh năng cao cấp đến từ phần cứng cũng như phần mềm, nhưng c&aacute;i gi&aacute; để bạn c&oacute; thể sở hữu sẽ kh&aacute; ch&aacute;t v&agrave; con số được t&iacute;nh bằng ng&agrave;n đ&ocirc;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/165189/oppo-find-x-12-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO Find X\" src=\"https://cdn.tgdd.vn/Products/Images/42/165189/oppo-find-x-12-1.jpg\" title=\"Thiết kế điện thoại OPPO Find X\" /></a></p>\r\n\r\n<p>Tuy nhi&ecirc;n, nếu bạn l&agrave; người kh&aacute; giả đam m&ecirc; c&aacute;i đẹp hay quan t&acirc;m đến hiệu năng v&agrave; th&iacute;ch th&uacute; với hệ thống trượt th&ocirc;ng minh th&igrave; Find X sẽ khiến bạn kh&ocirc;ng phải thất vọng.</p>\r\n', 0, 0, 'oppo-find-x-1-400x460-400x460.jpg', 14990000),
(35, 'BlackBerry KEY2', 1, 'Điện thoại', '<p><a href=\"https://www.thegioididong.com/dtdd/blackberry-key2\" target=\"_blank\" title=\"BlackBerry Key2\" type=\"BlackBerry Key2\">BlackBerry Key2</a>&nbsp;l&agrave; một bản n&acirc;ng cấp to&agrave;n diện cho chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd/blackberry-keyone\" target=\"_blank\" title=\"KeyOne\">KeyOne</a>&nbsp;với rất nhiều thay đổi v&agrave; điều đ&aacute;ng mừng l&agrave; yếu tố đặc trưng nhất của d&ograve;ng điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd-blackberry\" target=\"_blank\" title=\"điện thoại BlackBerry\" type=\"điện thoại BlackBerry\">BlackBerry</a>&nbsp;l&agrave; b&agrave;n ph&iacute;m vật l&yacute; cổ điển vẫn được giữ lại.</p>\r\n', '<h2>Đặc điểm nổi bật của BlackBerry KEY2</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/171028/Slider/vi-vn-blackberry-key2-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Sạc,Tai nghe,Hộp,C&aacute;p,C&acirc;y lấy sim,S&aacute;ch hướng dẫn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dtdd/blackberry-key2\" target=\"_blank\" title=\"BlackBerry Key2\" type=\"BlackBerry Key2\">BlackBerry Key2</a>&nbsp;l&agrave; một bản n&acirc;ng cấp to&agrave;n diện cho chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd/blackberry-keyone\" target=\"_blank\" title=\"KeyOne\">KeyOne</a>&nbsp;với rất nhiều thay đổi v&agrave; điều đ&aacute;ng mừng l&agrave; yếu tố đặc trưng nhất của d&ograve;ng điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd-blackberry\" target=\"_blank\" title=\"điện thoại BlackBerry\" type=\"điện thoại BlackBerry\">BlackBerry</a>&nbsp;l&agrave; b&agrave;n ph&iacute;m vật l&yacute; cổ điển vẫn được giữ lại.</h2>\r\n\r\n<h3>Thiết kế sang chảnh</h3>\r\n\r\n<p>Thiết kế của những chiếc m&aacute;y&nbsp;BlackBerry lu&ocirc;n được người d&ugrave;ng đ&aacute;nh gi&aacute; cao bởi chất doanh nh&acirc;n của m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key215.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng của BlackBerry KEY2\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key215.jpg\" title=\"Mặt lưng của BlackBerry KEY2\" /></a></p>\r\n\r\n<p><em>Mặt lưng của BlackBerry KEY2</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key23.jpg\" onclick=\"return false;\"><img alt=\"Các góc cạnh trên BlackBerry KEY2 được bo cong mềm mại\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key23.jpg\" title=\"Các góc cạnh trên BlackBerry KEY2 được bo cong mềm mại\" /></a></p>\r\n\r\n<p><em>C&aacute;c g&oacute;c cạnh tr&ecirc;n BlackBerry KEY2 được bo cong mềm mại</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key2-1.jpg\" onclick=\"return false;\"><img alt=\"Cổng kết nối trên BlackBerry KEY2 là chuẩn USB-C ở cạnh dưới\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key2-1.jpg\" title=\"Cổng kết nối trên BlackBerry KEY2 là chuẩn USB-C ở cạnh dưới\" /></a></p>\r\n\r\n<p><em>Cổng kết nối tr&ecirc;n BlackBerry KEY2 l&agrave; chuẩn USB-C</em></p>\r\n\r\n<h3>Bảo mật tối đa</h3>\r\n\r\n<p>DTEK l&agrave; phần mềm bảo mật được BlackBerry viết ra d&agrave;nh ri&ecirc;ng cho hệ điều h&agrave;nh Android.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key212.jpg\" onclick=\"return false;\"><img alt=\"Dữ liệu luôn an toàn với BlackBerry KEY2\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key212.jpg\" title=\"Dữ liệu luôn an toàn với BlackBerry KEY2\" /></a></p>\r\n\r\n<p><em>Dữ liệu lu&ocirc;n an to&agrave;n với BlackBerry KEY2</em></p>\r\n\r\n<p>DTEK&nbsp;c&oacute; thể m&atilde; h&oacute;a mọi dữ liệu của m&igrave;nh để tr&aacute;nh bị đ&aacute;nh cắp th&ocirc;ng tin, v&ocirc; hiệu h&oacute;a m&aacute;y từ xa v&agrave; x&oacute;a ho&agrave;n to&agrave;n những th&ocirc;ng tin c&aacute; nh&acirc;n được lưu tr&ecirc;n m&aacute;y.</p>\r\n\r\n<h3>B&agrave;n ph&iacute;m vật l&yacute;</h3>\r\n\r\n<p>Kh&ocirc;ng nhiều smartphone hiện nay c&ograve;n trang bị b&agrave;n ph&iacute;m vật l&yacute; như&nbsp;BlackBerry KEY2.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key26.jpg\" onclick=\"return false;\"><img alt=\"Bàn phím trên BlackBerry KEY2 cao hơn 20% giúp người dùng dễ bấm hơn so với chiếc KeyOne\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key26.jpg\" title=\"Bàn phím trên BlackBerry KEY2 cao hơn 20% giúp người dùng dễ bấm hơn so với chiếc KeyOne\" /></a></p>\r\n\r\n<p><em>B&agrave;n ph&iacute;m tr&ecirc;n BlackBerry KEY2 cao hơn 20% gi&uacute;p người d&ugrave;ng dễ bấm hơn so với chiếc KeyOne</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key24.jpg\" onclick=\"return false;\"><img alt=\"Bàn phím của BlackBerry KEY2 cũng hỗ trợ cảm ứng tương tự như phần màn hình\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key24.jpg\" title=\"Bàn phím của BlackBerry KEY2 cũng hỗ trợ cảm ứng tương tự như phần màn hình\" /></a></p>\r\n\r\n<p><em>B&agrave;n ph&iacute;m của BlackBerry KEY2 cũng hỗ trợ cảm ứng tương tự như phần m&agrave;n h&igrave;nh</em></p>\r\n\r\n<h3>Smartphone đầu ti&ecirc;n c&oacute; camera k&eacute;p của&nbsp;BlackBerry</h3>\r\n\r\n<p>BlackBerry n&oacute;i rằng thiết lập camera cải tiến tự động c&acirc;n bằng trắng, ổn định h&igrave;nh ảnh v&agrave; lấy n&eacute;t tự động nhanh hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key29.jpg\" onclick=\"return false;\"><img alt=\"Camera trên BlackBerry KEY2\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key29.jpg\" title=\"Camera trên BlackBerry KEY2\" /></a></p>\r\n\r\n<p><em>Camera tr&ecirc;n BlackBerry KEY2</em></p>\r\n\r\n<p>Key2 cung cấp c&aacute;c chế độ chụp: Ch&acirc;n dung v&agrave; Si&ecirc;u zoom quang học, gi&uacute;p m&aacute;y bắt được chủ thể từ xa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key210.jpg\" onclick=\"return false;\"><img alt=\"Chụp ảnh siêu zoom trên BlackBerry KEY2\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key210.jpg\" title=\"Chụp ảnh siêu zoom trên BlackBerry KEY2\" /></a></p>\r\n\r\n<p><em>Chụp ảnh si&ecirc;u zoom tr&ecirc;n BlackBerry KEY2</em></p>\r\n\r\n<h3>Trang bị cao cấp</h3>\r\n\r\n<p>BlackBerry Key2 c&ograve;n sở hữu m&agrave;n h&igrave;nh IPS LCD k&iacute;ch thước 4.5 inch độ ph&acirc;n giải 1080x1620 pixels v&agrave; bộ vi xử l&yacute;&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/snapdragon-660-vua-chip-tam-trung-lo-dien-ho-tro-ram-6-gb-963968\" target=\"_blank\" title=\"Qualcomm Snapdragon 660 8 nhân\">Qualcomm Snapdragon 660 8 nh&acirc;n</a>&nbsp;kết hợp với 6 GB RAM.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key213.jpg\" onclick=\"return false;\"><img alt=\"Màn hình trên BlackBerry KEY2 khá sắc nét\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key213.jpg\" title=\"Màn hình trên BlackBerry KEY2 khá sắc nét\" /></a></p>\r\n\r\n<p><em>M&agrave;n h&igrave;nh tr&ecirc;n BlackBerry KEY2 kh&aacute; sắc n&eacute;t</em></p>\r\n\r\n<p>Với những trang bị n&agrave;y gi&uacute;p m&aacute;y đ&aacute;p ứng tốt hầu hết c&aacute;c nhu cầu sử dụng hằng ng&agrave;y mượt m&agrave; v&agrave; ổn định.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/171028/blackberry-key214.jpg\" onclick=\"return false;\"><img alt=\"Pin trên BlackBerry KEY2 có dung lượng 3360 mAh\" src=\"https://cdn.tgdd.vn/Products/Images/42/171028/blackberry-key214.jpg\" title=\"Pin trên BlackBerry KEY2 có dung lượng 3360 mAh\" /></a></p>\r\n\r\n<p><em>Pin tr&ecirc;n BlackBerry KEY2 c&oacute; dung lượng 3360 mAh</em></p>\r\n\r\n<p>Nếu bạn th&iacute;ch một thiết bị Android c&oacute; thiết kế ho&agrave;n to&agrave;n kh&aacute;c biệt với những chiếc smartphone đang c&oacute; mặt tr&ecirc;n thị trường v&agrave; y&ecirc;u cầu cao về bảo mật th&ocirc;ng tin th&igrave;&nbsp;BlackBerry KEY2 ch&iacute;nh l&agrave; sự lựa chọn h&agrave;ng đầu d&agrave;nh cho bạn.</p>\r\n', 0, 0, 'blackberry-key2-3-400x460.jpg', 15990000),
(37, 'OPPO R17 Pro', 1, 'Điện thoại', '<p><a href=\"https://www.thegioididong.com/dtdd/oppo-r17-pro\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO R17 Pro\" type=\"Chi tiết điện thoại OPPO R17 Pro\">OPPO R17 Pro</a>&nbsp;được xem l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo smartphone tại Thegioididong.com\" type=\"Tham khảo smartphone tại Thegioididong.com\">smartphone</a>&nbsp;sự hiện th&acirc;n của c&aacute;i đẹp khi được kho&aacute;c l&ecirc;n m&igrave;nh một thiết kế trẻ trung v&agrave; hiện đại, c&ugrave;ng với đ&oacute; l&agrave; h&agrave;ng loạt c&aacute;c trang bị cao cấp đi từ cấu h&igrave;nh cho đến camera.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/186395/Slider/vi-vn-oppo-r17-pro-thietke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Tai nghe, S&aacute;ch hướng dẫn, Dock sạc, C&aacute;p, C&acirc;y lấy sim, Ốp lưng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dtdd/oppo-r17-pro\" target=\"_blank\" title=\"Chi tiết điện thoại OPPO R17 Pro\" type=\"Chi tiết điện thoại OPPO R17 Pro\">OPPO R17 Pro</a>&nbsp;được xem l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo smartphone tại Thegioididong.com\" type=\"Tham khảo smartphone tại Thegioididong.com\">smartphone</a>&nbsp;sự hiện th&acirc;n của c&aacute;i đẹp khi được kho&aacute;c l&ecirc;n m&igrave;nh một thiết kế trẻ trung v&agrave; hiện đại, c&ugrave;ng với đ&oacute; l&agrave; h&agrave;ng loạt c&aacute;c trang bị cao cấp đi từ cấu h&igrave;nh cho đến camera.</h2>\r\n\r\n<h3>Thiết kế thời trang, ph&aacute; c&aacute;ch</h3>\r\n\r\n<p>OPPO R17 Pro sở hữu cho m&igrave;nh lối thiết kế mới sẽ khiến bạn phải m&ecirc; mẩn đến từ chiếc tai thỏ h&igrave;nh giọt nước v&ocirc; c&ugrave;ng quyến rũ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-6-3.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-6-3.jpg\" title=\"Màn hình điện thoại OPPO R17 Pro chính hãng\" /></a></p>\r\n\r\n<p>Th&acirc;n h&igrave;nh uyển chuyển với c&aacute;c đường cong mềm mại v&agrave; cực k&igrave; thu h&uacute;t khi m&agrave;n h&igrave;nh của m&aacute;y gần như chiếm trọn mặt trước.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-9-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-9-1.jpg\" title=\"Thiết kế điện thoại OPPO R17 Pro chính hãng\" /></a></p>\r\n\r\n<p>Ấn tượng hơn với mặt lưng k&iacute;nh, trong sự kết hợp h&agrave;i h&ograve;a giữa m&agrave;u xanh v&agrave; t&iacute;m tạo n&ecirc;n hiệu ứng gradient đa sắc độ khi bạn ngắm nh&igrave;n ở c&aacute;c g&oacute;c nghi&ecirc;ng kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-2-3.jpg\" onclick=\"return false;\"><img alt=\"Đánh giá điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-2-3.jpg\" title=\"Đánh giá điện thoại OPPO R17 Pro chính hãng\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng gian trải nghiệm ph&iacute;a trước c&oacute; k&iacute;ch thước khủng l&ecirc;n đến 6.4 inch v&agrave; trở th&agrave;nh một trong những smartphone c&oacute; m&agrave;n h&igrave;nh lớn nhất hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-1-4.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-1-4.jpg\" title=\"Màn hình điện thoại OPPO R17 Pro chính hãng\" /></a></p>\r\n\r\n<p>Về khả năng hiển thị&nbsp;R17 Pro hứa hẹn sẽ mang lại cho bạn một trải nghiệm tuyệt vời nhất với tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-amoled-la-gi-905766\" target=\"_blank\" title=\"AMOLED\" type=\"AMOLED\">AMOLED</a>&nbsp;rực rỡ c&ugrave;ng độ ph&acirc;n giải&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178\" target=\"_blank\" title=\"Full HD+\" type=\"Full HD+\">Full HD+</a>&nbsp;si&ecirc;u n&eacute;t.</p>\r\n\r\n<h3>Cấu h&igrave;nh với chip&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/snapdragon-710-se-la-vi-xu-ly-dau-tien-thuoc-series-700-1079732\" target=\"_blank\" title=\"Snapdragon 710\" type=\"Snapdragon 710\">Snapdragon 710</a>&nbsp;mạnh mẽ</h3>\r\n\r\n<p>Sức mạnh của&nbsp;R17 Pro sẽ kh&ocirc;ng l&agrave;m bạn thất vọng khi mang trong m&igrave;nh con chip&nbsp;Snapdragon 710 kết hợp 8 GB RAM c&ugrave;ng 128 GB bộ nhớ trong.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với những th&ocirc;ng số tr&ecirc;n th&igrave; mọi t&aacute;c vụ hằng ng&agrave;y của bạn như: chơi game, xem phim, lướt web sẽ được xử l&yacute; một c&aacute;ch nhanh ch&oacute;ng v&agrave; đơn giản.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hơn nữa, giao diện&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/he-dieu-hanh-coloros-tren-oppo-la-ma-quen-1073718\" target=\"_blank\" title=\"ColorOS 5.2\" type=\"ColorOS 5.2\">ColorOS 5.2</a>&nbsp;được t&ugrave;y biến th&ocirc;ng minh sẽ gi&uacute;p c&aacute;c trải nghiệm của bạn trở n&ecirc;n th&uacute; vị hơn rất nhiều.</p>\r\n\r\n<h3>Điểm nhấn đặc biệt về camera</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Các dòng điện thoại OPPO đang kinh doanh tại thegioididong.com\" type=\"Các dòng điện thoại OPPO đang kinh doanh tại thegioididong.com\">OPPO</a>&nbsp;lu&ocirc;n cho thấy sự chịu chơi của m&igrave;nh khi trang bị cho R17 Pro cụm camera với 3 thấu k&iacute;nh c&oacute; độ ph&acirc;n giải lần lượt l&agrave;&nbsp;20 MP, 12 MP v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tren-tay-oppo-r17-pro-chip-snapdragon-710-camera-3d-1127832\" target=\"_blank\" title=\"Tìm hiểu công nghệ TOF 3D camera\">TOF 3D</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-3-3.jpg\" onclick=\"return false;\"><img alt=\"Camera sau điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-3-3.jpg\" title=\"Camera sau điện thoại OPPO R17 Pro chính hãng\" /></a></p>\r\n\r\n<p>Chi tiết hơn, camera ch&iacute;nh g&oacute;c rộng 12 MP được t&iacute;ch hợp&nbsp;ổn định h&igrave;nh ảnh quang học v&agrave; c&oacute; thể thay đổi khẩu độ&nbsp;giữa F/2.4 v&agrave; F/1.5 như tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-9\" target=\"_blank\" title=\"Samsung Galaxy Note 9\" type=\"Samsung Galaxy Note 9\">Samsung Galaxy Note 9</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-7-2.jpg\" onclick=\"return false;\"><img alt=\"Giao diện camera điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-7-2.jpg\" title=\"Giao diện camera điện thoại OPPO R17 Pro chính hãng\" /></a></p>\r\n\r\n<p>C&ograve;n với camera phụ 20 MP sẽ được sử dụng để ph&oacute;ng to ảnh cũng như tạo c&aacute;c hiệu ứng l&agrave;m mờ hỗ trợ cho t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-xoa-phong-huong-dan-xoa-phong-tren-moi-smartphone-892585\" target=\"_blank\" title=\"chụp ảnh xóa phông\" type=\"chụp ảnh xóa phông\">chụp ảnh x&oacute;a ph&ocirc;ng</a>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chưa dừng lại ở đ&oacute;,&nbsp;R17 Pro c&ograve;n được trang bị cảm biến TOF 3D camera chụp được ảnh 3D v&agrave; tương t&aacute;c ảo AR nhờ t&iacute;ch hợp th&ecirc;m&nbsp;m&ocirc;-đun cảm biến chiều s&acirc;u 3D.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/186395/dtdd-oppo-r17-pro-9.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera trước trên điện thoại OPPO R17 pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/dtdd-oppo-r17-pro-9.jpg\" title=\"Cụm camera trước trên điện thoại OPPO R17 pro chính hãng\" /></a></p>\r\n\r\n<p>Camera trước cũng để lại ấn tượng kh&ocirc;ng k&eacute;m với độ ph&acirc;n giải 25 MP cực khủng t&iacute;ch hợp&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/cac-tinh-nang-chup-anh-camera-truoc-769270#lamdep\" target=\"_blank\" title=\"chế độ làm đẹp\" type=\"chế độ làm đẹp\">chế độ l&agrave;m đẹp</a>&nbsp;nhiều cấp độ, hứa hẹn sẽ l&agrave; một lựa chọn tuyệt vời cho bạn mỗi khi chụp ảnh tự sướng.</p>\r\n\r\n<h3>C&ocirc;ng nghệ&nbsp;SuperVOOC sạc si&ecirc;u nhanh</h3>\r\n\r\n<p>OPPO R17 Pro được ưu &aacute;i t&iacute;ch hợp chuẩn&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cong-nghe-sac-nhanh-vooc-cua-oppo-918283\" target=\"_blank\" title=\"sạc nhanh VOOC\" type=\"sạc nhanh VOOC\">sạc nhanh SuperVOOC</a>&nbsp;với c&ocirc;ng suất l&ecirc;n đến 50W cho ph&eacute;p bạn sạc từ&nbsp;0% đến 40% chỉ trong 10 ph&uacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-4-3.jpg\" onclick=\"return false;\"><img alt=\"Cổng sạc điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-4-3.jpg\" title=\"Cổng sạc điện thoại OPPO R17 Pro chính hãng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, một c&ocirc;ng nghệ đặc biệt kh&aacute;c cũng phải kể đến đ&oacute; ch&iacute;nh l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/clear-id-la-gi-cam-bien-van-tay-duoi-man-hinh-co-gi-moi-1057643\" target=\"_blank\" title=\"cảm biến vân tay siêu âm\" type=\"cảm biến vân tay siêu âm\">cảm biến v&acirc;n tay si&ecirc;u &acirc;m</a>&nbsp;b&ecirc;n trong m&agrave;n h&igrave;nh v&ocirc; c&ugrave;ng hiện đại v&agrave; tương lai.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/186395/oppo-r17-pro-8-1.jpg\" onclick=\"return false;\"><img alt=\"Vân tay siêu âm trên điện thoại OPPO R17 Pro chính hãng\" src=\"https://cdn.tgdd.vn/Products/Images/42/186395/oppo-r17-pro-8-1.jpg\" title=\"Vân tay siêu âm trên điện thoại OPPO R17 Pro chính hãng\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, pin của m&aacute;y c&oacute; dung lượng cao l&ecirc;n đến 3700 mAh đ&aacute;p ứng cho bạn thời gian trải nghiệm dư giả v&agrave; thoải m&aacute;i trong 1 ng&agrave;y sử dụng.</p>\r\n', 0, 0, '1563431800oppo-r17-pro-2-400x460.jpg', 13990000),
(38, 'iPad Wifi Cellular 32GB (2018)', 2, 'Máy tính bảng', '<p><a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các loại\">M&aacute;y t&iacute;nh bảng</a>&nbsp;iPad 6th Wifi Cellular 32 GB mang trong m&igrave;nh cấu h&igrave;nh mạnh mẽ c&ugrave;ng gi&aacute; th&agrave;nh hợp l&yacute;.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/163793/Slider/-ipad-6th-wifi-cellular-32g-1-copy.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Adapter, S&aacute;ch hướng dẫn, C&aacute;p Lightning, C&acirc;y lấy sim, Hộp m&aacute;y</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\"><a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các loại\">M&aacute;y t&iacute;nh bảng</a>&nbsp;iPad 6th Wifi Cellular 32 GB mang trong m&igrave;nh cấu h&igrave;nh mạnh mẽ c&ugrave;ng gi&aacute; th&agrave;nh hợp l&yacute;.</h2>\r\n\r\n<h2 dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/522/163793/ipad-wifi-cellular-32g-2018-33397-thietke.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế nguyên khối, sang trọng trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/163793/ipad-wifi-cellular-32g-2018-33397-thietke.jpg\" title=\"Thiết kế nguyên khối, sang trọng trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" /></a></h2>\r\n\r\n<h3 dir=\"ltr\"><strong>Cấu h&igrave;nh mạnh mẽ thuộc top đầu</strong></h3>\r\n\r\n<p dir=\"ltr\">M&aacute;y t&iacute;nh bảng iPad 6th Wifi Cellular 32 GB c&oacute; những cải tiến mới vượt bậc trong vấn đề xử l&yacute; đồ họa, hỗ trợ tối đa cho ứng dụng AR (thực tế tăng cường).</p>\r\n\r\n<p dir=\"ltr\">Bộ nhớ trong của m&aacute;y c&oacute; dung lượng l&agrave; 32 GB cho ph&eacute;p người d&ugrave;ng c&oacute; thể lưu trữ dữ liệu nhiều hơn c&ugrave;ng với RAM 2 GB gi&uacute;p cho m&aacute;y thực hiện được đa nhiệm nhiều ứng dụng c&ugrave;ng l&uacute;c v&ocirc; c&ugrave;ng nhanh ch&oacute;ng v&agrave; mượt m&agrave;.</p>\r\n\r\n<p dir=\"ltr\">Apple đ&atilde; n&acirc;ng cấp chip xử l&yacute; trong&nbsp;iPad 6th Wifi Cellular 32GB&nbsp;l&ecirc;n th&agrave;nh chip Apple A10 Fusion, đ&acirc;y cũng l&agrave; con chip xử l&yacute; từng xuất hiện tr&ecirc;n bộ đ&ocirc;i&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Điện thoại Apple\">iPhone</a>&nbsp;7, 7 Plus c&oacute; 4 nh&acirc;n v&agrave; tốc độ xử l&yacute; l&ecirc;n đến 2.34 GHz.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/522/163793/ipad-wifi-cellular-32g-2018-33397-cauhinh.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình mạnh mẽ trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/163793/ipad-wifi-cellular-32g-2018-33397-cauhinh.jpg\" title=\"Cấu hình mạnh mẽ trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" /></a></p>\r\n\r\n<h3 dir=\"ltr\"><strong>M&agrave;n h&igrave;nh hiển thị sắc n&eacute;t</strong></h3>\r\n\r\n<p dir=\"ltr\">iPad 6th Wifi Cellular 32GB được trang bị tấm nền IPS với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-retina-la-gi-905780\" target=\"_blank\" title=\"Tìm hiểu màn hình Retina\">m&agrave;n h&igrave;nh Retina</a>&nbsp;k&iacute;ch thước 9.7 inch độ ph&acirc;n giải cao 1536 x 2048 pixels gi&uacute;p hiển thị h&igrave;nh ảnh trung thực, sắc n&eacute;t.</p>\r\n\r\n<p dir=\"ltr\">Ngo&agrave;i việc hỗ trợ rất tốt Apple Pencil th&igrave; m&agrave;n h&igrave;nh của m&aacute;y đ&atilde; bị lược bỏ một số chức năng cao cấp để giảm gi&aacute; th&agrave;nh như: True Tone, ProMotion, dải m&agrave;u rộng, khả năng chống ch&oacute;i&hellip;</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/522/163793/ipad-wifi-cellular-32g-2018-33397-manhinh.jpg\" onclick=\"return false;\"><img alt=\"Màn hình sắc nét trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/163793/ipad-wifi-cellular-32g-2018-33397-manhinh.jpg\" title=\"Màn hình sắc nét trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" /></a></p>\r\n\r\n<h3 dir=\"ltr\"><strong>Camera độ ph&acirc;n giải cao</strong></h3>\r\n\r\n<p dir=\"ltr\">Đối với người ti&ecirc;u d&ugrave;ng m&agrave; cụ thể hơn l&agrave; giới trẻ hiện nay, việc trang bị một camera chất lượng trở th&agrave;nh một trong những điều kiện quyết định trong việc lựa chọn thiết bị m&aacute;y t&iacute;nh bảng.</p>\r\n\r\n<p dir=\"ltr\">Ch&iacute;nh v&igrave; vậy, iPad 6th Wifi Cellular 32 GB đ&atilde; trang bị cho m&igrave;nh một camera sau c&oacute; độ ph&acirc;n giải 8 MP, camera trước 1.2 MP v&agrave; khả năng quay video chất lượng Full HD.</p>\r\n\r\n<h2 dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/522/163793/ipad-wifi-cellular-32g-2018-33397-camera.jpg\" onclick=\"return false;\"><img alt=\"Camera sắc nét trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/163793/ipad-wifi-cellular-32g-2018-33397-camera.jpg\" title=\"Camera sắc nét trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" /></a></h2>\r\n\r\n<p dir=\"ltr\"><em>Với độ ph&acirc;n giải cao như tr&ecirc;n th&igrave; đ&acirc;y l&agrave; 1 con số đ&aacute;ng mơ ước đối với c&aacute;c m&aacute;y t&iacute;nh bảng t&iacute;nh đến thời điểm hiện tại</em></p>\r\n\r\n<h3 dir=\"ltr\"><strong>Kết nối 4G tốc độ cao</strong></h3>\r\n\r\n<p dir=\"ltr\">Ngo&agrave;i khả năng kết nối 3G, m&aacute;y c&ograve;n hỗ trợ kết nối 4G&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/4g-la-gi-731757\" target=\"_blank\" title=\"Tìm hiểu 4G là gì?\">LTE Cat</a>&nbsp;6 với tốc độ l&ecirc;n đến 300 Mbps gi&uacute;p bạn t&igrave;m t&agrave;i liệu học tập hiệu quả, thoải m&aacute;i lướt Facebook hoặc xem YouTube mọi l&uacute;c mọi nơi m&agrave; kh&ocirc;ng phải ho&agrave;n to&agrave;n phụ thuộc v&agrave;o kết nối wifi.</p>\r\n\r\n<p dir=\"ltr\">C&ocirc;ng nghệ 4G sẽ gi&uacute;p bạn lướt web nhanh hơn, tốc độ tải một trang web hay một ứng dụng bất kỳ đều nhanh gấp 2 đến 3 lần so với 3G trước đ&acirc;y.</p>\r\n\r\n<h3 dir=\"ltr\"><strong>Phần mềm - ứng dụng tối ưu cho việc học tập</strong></h3>\r\n\r\n<p dir=\"ltr\">Với mục ti&ecirc;u cao cả l&agrave; hướng đến việc học tập v&agrave; nghi&ecirc;n cứu, iPad 6th Wifi Cellular 32 GB đ&atilde; trang bị cho m&igrave;nh hơn 200.000 ứng dụng d&agrave;nh ri&ecirc;ng cho việc nghi&ecirc;n cứu học tập.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/522/163793/ipad-wifi-cellular-32g-2018-33397-hoctap.jpg\" onclick=\"return false;\"><img alt=\"Tính năng học tập bổ ích trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/163793/ipad-wifi-cellular-32g-2018-33397-hoctap.jpg\" title=\"Tính năng học tập bổ ích trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" /></a></p>\r\n\r\n<p dir=\"ltr\">Để kết hợp thật tốt với Apple Pencil, iPad 6th Wifi Cellular 32 GB đ&atilde; cập nhật th&ecirc;m bộ ứng dụng iWork. Với sự kết hợp n&agrave;y, iPad sẽ cho ph&eacute;p gi&aacute;o vi&ecirc;n v&agrave; học sinh c&oacute; thể tận dụng tối đa c&aacute;c t&iacute;nh năng của m&aacute;y g&oacute;p phần biến iPad trở th&agrave;nh phương tiện hỗ trợ học tập hiện đại nhất.</p>\r\n\r\n<h3 dir=\"ltr\"><strong>Thời lượng pin lớn</strong></h3>\r\n\r\n<p dir=\"ltr\">Với dung lượng pin cao l&ecirc;n đến 32.4 Wh (Khoảng 8600 mAh), chiếc iPad mới của Apple dư sức hỗ trợ mọi hoạt động học tập, l&agrave;m việc v&agrave; giải tr&iacute; của bạn gần như trọn vẹn một ng&agrave;y.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/522/163793/ipad-wifi-cellular-32g-2018-33397-battery.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" src=\"https://cdn.tgdd.vn/Products/Images/522/163793/ipad-wifi-cellular-32g-2018-33397-battery.jpg\" title=\"Dung lượng pin trên Máy tính bảng iPad Wifi Cellular 32GB (2018)\" /></a></p>\r\n\r\n<p dir=\"ltr\"><em>Thời lượng pin l&agrave; một trong những điểm m&agrave; người d&ugrave;ng th&iacute;ch nhất khi sử dụng iPad</em></p>\r\n\r\n<h3 dir=\"ltr\"><strong>Mới mẻ hơn với&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/apple-pencil\" target=\"_blank\" title=\"Tham khảo giá tiền bút cảm ứng Apple Pencil\" type=\"Tham khảo giá tiền bút cảm ứng Apple Pencil\">Apple Pencil</a></strong></h3>\r\n\r\n<p dir=\"ltr\">iPad Wifi Cellular 32GB (2018) l&agrave; một trong những iPad 6th được hỗ trợ kết nối với b&uacute;t cảm ứng Apple Pencil&nbsp;<strong>(sản phẩm mua ri&ecirc;ng, kh&ocirc;ng đi k&egrave;m với bộ sản phẩm m&aacute;y).</strong></p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/522/163793/ipad-wifi-cellular-32g-2018-tgdd.jpg\" onclick=\"return false;\"><img alt=\"iPad Wifi Cellular 32GB 2018 kết nối được với Apple Pencil\" src=\"https://cdn.tgdd.vn/Products/Images/522/163793/ipad-wifi-cellular-32g-2018-tgdd.jpg\" title=\"iPad Wifi Cellular 32GB 2018 kết nối được với Apple Pencil\" /></a></p>\r\n\r\n<p dir=\"ltr\">Nếu bạn muốn kh&aacute;m ph&aacute; thế giới c&ocirc;ng nghệ ở nhiều phương diện hơn, hay th&iacute;ch vẽ, muốn ghi ch&uacute; nhanh, hay thử trải nghiệm với c&acirc;y b&uacute;t n&agrave;y, mọi thao t&aacute;c sẽ trở n&ecirc;n nhanh ch&oacute;ng v&agrave; tiện lợi hơn rất nhiều.</p>\r\n', 1, 0, 'ipad-6th-wifi-cellular-32g-1-400x460.jpg', 11990000),
(39, 'Samsung Galaxy Tab A 10.5', 2, 'Máy tính bảng', '<p>L&agrave; một phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.5 inch chính hãng, giá tốt | Thegioididong\">m&aacute;y t&iacute;nh bảng</a>&nbsp;gi&aacute; rẻ của d&ograve;ng Tab S4,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-a-105-inch\" target=\"_blank\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.5 inch\">Samsung Galaxy Tab A 10.5&nbsp;inch</a>&nbsp;c&oacute; gi&aacute; b&aacute;n phải chăng v&agrave; phục vụ tốt nhu cầu sử dụng của người d&ugrave;ng.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/184380/Slider/vi-vn-thiet-ke.jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Adapter, S&aacute;ch hướng dẫn</p>\r\n\r\n<p>-</p>\r\n\r\n<h2>L&agrave; một phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.5 inch chính hãng, giá tốt | Thegioididong\">m&aacute;y t&iacute;nh bảng</a>&nbsp;gi&aacute; rẻ của d&ograve;ng Tab S4,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-a-105-inch\" target=\"_blank\" title=\"Máy tính bảng Samsung Galaxy Tab A 10.5 inch\">Samsung Galaxy Tab A 10.5&nbsp;inch</a>&nbsp;c&oacute; gi&aacute; b&aacute;n phải chăng v&agrave; phục vụ tốt nhu cầu sử dụng của người d&ugrave;ng.</h2>\r\n\r\n<h3>M&agrave;n h&igrave;nh rộng, thoải m&aacute;i lướt web, xem phim</h3>\r\n\r\n<p>Samsung Galaxy Tab A 10.5 inch được trang bị m&agrave;n h&igrave;nh IPS LCD với độ ph&acirc;n giải&nbsp;1.920 x 1.200 pixels, viền bezels được l&agrave;m mỏng cho h&igrave;nh ảnh hiển thị chi tiết dễ d&agrave;ng đọc s&aacute;ch hay xem phim phụ đề m&agrave; kh&ocirc;ng sợ bị mỏi mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-manhinh.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Samsung Galaxy Tab A 10.5 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-manhinh.jpg\" title=\"Màn hình Samsung Galaxy Tab A 10.5 inch\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh đủ d&ugrave;ng</h3>\r\n\r\n<p>Được sở hữu bộ vi xử l&yacute; Snapdragon 450 với tốc độ 1.8GHz kết hợp với 3 GB RAM, 32 GB ROM cho hiệu năng kh&aacute; ổn định khi thực hiện c&aacute;c t&aacute;c vụ cơ bản. M&aacute;y hỗ trợ người d&ugrave;ng mở rộng bộ nhớ th&ocirc;ng qua thẻ&nbsp;microSD với dung lượng tối đa 512 GB.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-ch.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Tab A 10.5 inch - Cấu hình đủ dùng | Thegioididong\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-ch.jpg\" title=\"Samsung Galaxy Tab A 10.5 inch - Cấu hình đủ dùng | Thegioididong\" /></a></p>\r\n\r\n<h3>Camera chất lượng tốt</h3>\r\n\r\n<p>Camera trước 5 MP cho chất lượng vừa đủ để gọi video call hoặc&nbsp;selfie. Camera sau 8 MP hỗ trợ đ&egrave;n flash LED gi&uacute;p lưu trữ những khoảnh khắc đẹp của bạn một c&aacute;ch trọn vẹn nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-camera-1.jpg\" onclick=\"return false;\"><img alt=\"Camera trên Samsung Galaxy Tab A 10.5 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-camera-1.jpg\" title=\"Camera trên Samsung Galaxy Tab A 10.5 inch\" /></a></p>\r\n\r\n<h3>Dung lượng pin lớn</h3>\r\n\r\n<p>Galaxy Tab A 10.5 c&ograve;n được cung cấp năng lượng bởi vi&ecirc;n pin dung lượng 7.300 mAh cao hơn nhiều so với c&aacute;c thế hệ trước, gi&uacute;p cho thời gian sử dụng m&aacute;y được l&acirc;u hơn l&ecirc;n đến 13 giờ theo th&ocirc;ng tin được c&ocirc;ng bố từ h&atilde;ng Samsung.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-pin.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Tab A 10.5 inch - Dung lượng pin lớn | Thegioididong\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-pin.jpg\" title=\"Samsung Galaxy Tab A 10.5 inch - Dung lượng pin lớn | Thegioididong\" /></a></p>\r\n\r\n<h3>C&ocirc;ng nghệ &acirc;m thanh&nbsp;Dolby Atmos</h3>\r\n\r\n<p>Với hệ thống &acirc;m thanh gồm 4 loa, giống như tr&ecirc;n Samsung Tab S4. Cụm loa được hỗ trợ c&ocirc;ng nghệ &acirc;m thanh Dolby Atmos, cho chất lượng &acirc;m thanh tuyệt vời khi nghe nhạc, giải tr&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/184380/samsung-galaxy-tab-a-105-inch-at.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng  Samsung Galaxy Tab A 10.5 inch - Công nghệ âm thanh Dolby Atmos | Thegioididong\" src=\"https://cdn.tgdd.vn/Products/Images/522/184380/samsung-galaxy-tab-a-105-inch-at.jpg\" title=\"Máy tính bảng  Samsung Galaxy Tab A 10.5 inch - Công nghệ âm thanh Dolby Atmos | Thegioididong\" /></a></p>\r\n', 0, 0, 'samsung-galaxy-tab-a-105-inch-chitietblue-400x460.jpg', 9490000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(40, 'Samsung Galaxy Tab A6 10.1', 2, 'Máy tính bảng', '<p><strong>Tiếp nối sự th&agrave;nh c&ocirc;ng của chiếc Samsung Galaxy Tab A th&igrave; mới đ&acirc;y&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-samsung\" title=\"samsung\" type=\"samsung\">Samsung</a>&nbsp;đ&atilde; giới thiệu phi&ecirc;n bản cải tiến l&agrave; chiếc Galaxy Tab A6 10.1 với nhiều n&acirc;ng cấp đ&aacute;ng gi&aacute; về cấu h&igrave;nh v&agrave; thiết kế.</strong></p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/87306/Slider/-samsung-galaxy-tab-a6-101-spen-2.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Adapter, S&aacute;ch hướng dẫn, B&uacute;t cảm ứng (+ ng&ograve;i b&uacute;t), C&aacute;p Micro USB, Hộp m&aacute;y</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Tiếp nối sự th&agrave;nh c&ocirc;ng của chiếc Samsung Galaxy Tab A th&igrave; mới đ&acirc;y&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-samsung\" title=\"samsung\" type=\"samsung\">Samsung</a>&nbsp;đ&atilde; giới thiệu phi&ecirc;n bản cải tiến l&agrave; chiếc Galaxy Tab A6 10.1 với nhiều n&acirc;ng cấp đ&aacute;ng gi&aacute; về cấu h&igrave;nh v&agrave; thiết kế.</strong></h2>\r\n\r\n<h3><strong>Thiết kế chưa đổi mới, ấn tượng với vỏ nhựa nh&aacute;m</strong></h3>\r\n\r\n<p>L&agrave; d&ograve;ng m&aacute;y trong ph&acirc;n kh&uacute;c tầm trung, Samsung Galaxy Tab A6 10.1 Spen được thiết kế với vỏ nhựa nh&aacute;m nhẹ c&ugrave;ng c&aacute;c g&oacute;c bo tr&ograve;n cho cảm gi&aacute;c chắc chắn, kh&ocirc;ng b&aacute;m v&acirc;n tay khi cầm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/87306/samsung-galaxy-tab-a6-101-spen8.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế quen thuộc của Samsung\" src=\"https://cdn.tgdd.vn/Products/Images/522/87306/samsung-galaxy-tab-a6-101-spen8.jpg\" title=\"Thiết kế quen thuộc của Samsung\" /></a></p>\r\n\r\n<p><em>Thiết kế quen thuộc của Samsung</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/87306/samsung-galaxy-tab-a6-101-spen9.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng nhựa nhám mịn\" src=\"https://cdn.tgdd.vn/Products/Images/522/87306/samsung-galaxy-tab-a6-101-spen9.jpg\" title=\"Mặt lưng nhựa nhám mịn\" /></a></p>\r\n\r\n<p><em>Mặt lưng nhựa nh&aacute;m mịn</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/87306/samsung-galaxy-tab-a6-101-spen5.jpg\" onclick=\"return false;\"><img alt=\"Máy sở hữu độ mỏng khá ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/522/87306/samsung-galaxy-tab-a6-101-spen5.jpg\" title=\"Máy sở hữu độ mỏng khá ấn tượng\" /></a></p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh hiển thị đẹp, sử dụng tốt dưới &aacute;nh nắng</strong></h3>\r\n\r\n<p dir=\"ltr\">Sở hữu m&agrave;n h&igrave;nh k&iacute;ch thước 10.1 inch kh&ocirc;ng qu&aacute; lớn, c&oacute; thể nh&eacute;t vừa một chiếc balo dạng nhỏ, Samsung Galaxy Tab A6 10.1 Spen sử dụng tấm nền TFT LCD cho khả năng hiển thị tốt.</p>\r\n\r\n<p dir=\"ltr\">Độ ph&acirc;n giải &nbsp;WUXGA 1920 x 1200 pixels cho chất lượng h&igrave;nh ảnh sắc n&eacute;t, sử dụng tốt khi đi ngo&agrave;i trời.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/87306/samsung-galaxy-tab-a6-101-spen18.jpg\" onclick=\"return false;\"><img alt=\"Màn hình lớn hiển thị đẹp tối ưu cho giải trí và làm việc\" src=\"https://cdn.tgdd.vn/Products/Images/522/87306/samsung-galaxy-tab-a6-101-spen18.jpg\" title=\"Màn hình lớn hiển thị đẹp tối ưu cho giải trí và làm việc\" /></a></p>\r\n\r\n<p><em>M&agrave;n h&igrave;nh lớn hiển thị đẹp tối ưu cho giải tr&iacute; v&agrave; l&agrave;m việc</em></p>\r\n\r\n<h3 dir=\"ltr\"><strong>Vi xử l&yacute; mạnh, hiệu năng cao</strong></h3>\r\n\r\n<p>Được trang bị chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/samsung-exynos-7870-843409\" target=\"_blank\" title=\"Tìm hiểu chip Exynos 7870 8 nhân\">Exynos 7870 8 nh&acirc;n</a>&nbsp;3 GB RAM c&ugrave;ng 16 GB bộ nhớ trong, Samsung Galaxy Tab A6 10.1 Spen c&oacute; hiệu năng kh&aacute; ổn định so với mức gi&aacute;.</p>\r\n\r\n<p dir=\"ltr\">Thử nghiệm cho thấy m&aacute;y hoạt động tốt tr&ecirc;n c&aacute;c tựa game như Asphalt 8 hay N.O.V.A 3 kh&aacute; mượt m&agrave;, kh&ocirc;ng bị giật, lag ở mức độ đồ họa trung b&igrave;nh.</p>\r\n\r\n<p dir=\"ltr\">Khả năng đa nhiệm của m&aacute;y được tối ưu với RAM lớn v&agrave; m&agrave;n h&igrave;nh lớn thuận tiện cho c&aacute;c ứng dụng văn ph&ograve;ng, c&aacute;c c&ocirc;ng cụ giải tr&iacute;...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/87306/samsung-galaxy-tab-a6-101-spen6.jpg\" onclick=\"return false;\"><img alt=\"Galaxy Tab A6 10.1 Spen chơi game khá ổn\" src=\"https://cdn.tgdd.vn/Products/Images/522/87306/samsung-galaxy-tab-a6-101-spen6.jpg\" title=\"Galaxy Tab A6 10.1 Spen chơi game khá ổn\" /></a></p>\r\n\r\n<p><em>Galaxy Tab A6 10.1 Spen chơi game kh&aacute; ổn</em></p>\r\n\r\n<h3><strong>Hỗ trợ kết nối 3G/4G</strong></h3>\r\n\r\n<p>M&aacute;y cũng hỗ trợ khe cắm&nbsp;<a href=\"https://www.thegioididong.com/the-nho-dien-thoai\" title=\"thẻ nhớ\" type=\"thẻ nhớ\">thẻ nhớ</a>&nbsp;mở rộng tối đa l&ecirc;n tới 256 GB c&ugrave;ng 1 khe cắm&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-cac-loai-sim-thong-dung-sim-thuong-micro--590216#nanosim\" title=\"nano sim\" type=\"nano sim\">Nano Sim</a>&nbsp;v&agrave; kết nối 4G tốc độ cao gi&uacute;p bạn truy cập internet với tốc độ nhanh ch&oacute;ng.</p>\r\n\r\n<h3><strong>B&uacute;t Spen tiện dụng</strong></h3>\r\n\r\n<p>Chiếc b&uacute;t Spen quen thuộc đ&atilde; quay lại với thiết kế Samsung Galaxy Tab A6 10.1 Spen. Giờ đ&acirc;y ghi ch&uacute; đ&atilde; kh&ocirc;ng c&ograve;n kh&oacute; khi bạn c&oacute; thể nhanh ch&oacute;ng ghi l&ecirc;n m&agrave;n h&igrave;nh, chọn c&aacute;c đối tượng văn bản...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/87306/samsung-galaxy-tab-a6-101-spen17.jpg\" onclick=\"return false;\"><img alt=\"Bút Spen với nhiều tính năng thú vị\" src=\"https://cdn.tgdd.vn/Products/Images/522/87306/samsung-galaxy-tab-a6-101-spen17.jpg\" title=\"Bút Spen với nhiều tính năng thú vị\" /></a></p>\r\n\r\n<p><em>B&uacute;t Spen với nhiều t&iacute;nh năng th&uacute; vị</em></p>\r\n\r\n<p dir=\"ltr\">Thử nghiệm ban đầu với b&uacute;t Spen tr&ecirc;n Samsung Galaxy Tab A6 10.1 Spen cho cảm gi&aacute;c cảm ứng mượt, m&agrave;n h&igrave;nh ghi ch&uacute; rộng.</p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, b&uacute;t c&ograve;n hạn chế khi kh&ocirc;ng thể hiện được nhiều n&eacute;t kh&aacute;c nhau, chưa được tối ưu h&oacute;a như c&aacute;c d&ograve;ng Spen cao cấp. Đi k&egrave;m theo m&aacute;y l&agrave; bộ ng&ograve;i b&uacute;t thay thế đa dạng, c&oacute; thể khắc phục được khuyết điểm n&agrave;y.</p>\r\n\r\n<h3><strong>Camera chụp ảnh chất lượng cao</strong></h3>\r\n\r\n<p>Galaxy Tab A6 10.1 Spen được trang bị camera ch&iacute;nh độ ph&acirc;n giải 8 MP, khẩu độ F/1.9 k&egrave;m đ&egrave;n flash LED đơn. M&aacute;y cũng hỗ trợ quay phim với độ ph&acirc;n giải Full HD cho bạn những thước video đẹp mắt. Ảnh chụp ban ng&agrave;y của m&aacute;y c&oacute; chi tiết kh&aacute; cao, m&agrave;u sắc h&agrave;i h&ograve;a.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/87306/samsung-galaxy-tab-a6-101-spen7.jpg\" onclick=\"return false;\"><img alt=\"Máy sở hữu camera khá trong tầm giá\" src=\"https://cdn.tgdd.vn/Products/Images/522/87306/samsung-galaxy-tab-a6-101-spen7.jpg\" title=\"Máy sở hữu camera khá trong tầm giá\" /></a></p>\r\n\r\n<p><em>M&aacute;y sở hữu camera kh&aacute; trong tầm gi&aacute;</em></p>\r\n\r\n<p>Camera trước của m&aacute;y c&oacute; độ ph&acirc;n giải 2 MP khẩu độ F/2.2 gi&uacute;p bạn c&oacute; thể thực hiện c&aacute;c cuộc gọi video vui vẻ với bạn b&egrave; c&oacute; chất lượng tốt.</p>\r\n\r\n<h3><strong>Dung lượng pin kh&aacute;</strong></h3>\r\n\r\n<p>Galaxy Tab A6 10.1 Spen được trang bị&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/pin-smartphone-li-ion-va-phuong-thuc-su-dung-pin-595433\" title=\"pin lion\" type=\"pin lion\">pin Li-ion</a>&nbsp;c&oacute; dung lượng 7300 mAh cao hơn kh&aacute; nhiều so với c&aacute;c thế hệ trước. Bạn c&oacute; thể chơi game li&ecirc;n tục với m&aacute;y trong khoảng hơn 5 giờ v&agrave; c&oacute; khoảng hơn 10 giờ xem video li&ecirc;n tục.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/87306/samsung-galaxy-tab-a6-101-spen19.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin thoải mái cho cả ngày sử dụng\" src=\"https://cdn.tgdd.vn/Products/Images/522/87306/samsung-galaxy-tab-a6-101-spen19.jpg\" title=\"Thời lượng pin thoải mái cho cả ngày sử dụng\" /></a></p>\r\n\r\n<p><em>Thời lượng pin thoải m&aacute;i cho cả ng&agrave;y sử dụng</em></p>\r\n\r\n<p>Với gi&aacute; b&aacute;n hợp l&yacute; c&ugrave;ng thiết kế đẹp, pin tr&acirc;u v&agrave; hiệu năng kh&aacute; th&igrave;&nbsp;Galaxy Tab A6 10.1 Spen xứng đ&aacute;ng được s&aacute;t c&aacute;nh b&ecirc;n bạn cho c&aacute;c t&aacute;c vụ l&agrave;m việc v&agrave; giải tr&iacute; hằng ng&agrave;y.</p>\r\n', 1, 0, 'samsung-galaxy-tab-a6-101-spen-400x460.jpg', 7490000),
(41, 'Huawei Mediapad T5 10.1 inch', 2, 'Máy tính bảng', '<p><a href=\"https://www.thegioididong.com/may-tinh-bang/huawei-mediapad-t5\" target=\"_blank\" title=\"Máy tính bảng Huawei Mediapad T5 10.1 inch đang được kinh doanh tại Thegioididong.com\">M&aacute;y t&iacute;nh bảng Huawei Mediapad T5</a>&nbsp;vừa được ra mắt cuối năm 2018, mang trong m&igrave;nh thiết kế trẻ trung, m&agrave;n h&igrave;nh sắc n&eacute;t v&agrave; một cấu h&igrave;nh đủ khoẻ để chơi tốt c&aacute;c tựa game cơ bản hiện nay sẽ l&agrave; sự lựa chọn đ&aacute;ng c&acirc;n nhắc trong ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tablet chính hãng đang kinh doanh chính hãng tại Thegioididong.com\">tablet</a>&nbsp;phổ th&ocirc;ng.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/196071/Slider/-huawei-mediapad-t5-33397-thietke3.jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: S&aacute;ch hướng dẫn, Bao da, Miếng d&aacute;n m&agrave;n h&igrave;nh, C&aacute;p Micro USB, C&acirc;y lấy sim</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/may-tinh-bang/huawei-mediapad-t5\" target=\"_blank\" title=\"Máy tính bảng Huawei Mediapad T5 10.1 inch đang được kinh doanh tại Thegioididong.com\">M&aacute;y t&iacute;nh bảng Huawei Mediapad T5</a>&nbsp;vừa được ra mắt cuối năm 2018, mang trong m&igrave;nh thiết kế trẻ trung, m&agrave;n h&igrave;nh sắc n&eacute;t v&agrave; một cấu h&igrave;nh đủ khoẻ để chơi tốt c&aacute;c tựa game cơ bản hiện nay sẽ l&agrave; sự lựa chọn đ&aacute;ng c&acirc;n nhắc trong ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tablet chính hãng đang kinh doanh chính hãng tại Thegioididong.com\">tablet</a>&nbsp;phổ th&ocirc;ng.</h2>\r\n\r\n<h3>Thiết kế hiện đại, đẹp mắt</h3>\r\n\r\n<p>Chất liệu kim loại bao bọc th&acirc;n m&aacute;y tạo sự cứng c&aacute;p, mặt trước m&aacute;y l&agrave; lớp k&iacute;nh gần ra đến viền tạo sự hiện đại v&agrave; sang trọng cho sản phẩm. C&aacute;c cạnh được bo cong nhẹ, trọng lượng m&aacute;y chỉ 470 gam gi&uacute;p bạn cầm nắm bằng 1 tay chắc chắn.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/522/196071/huawei-mediapad-t5-thetke.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế hiện đại, đẹp mắt trên Máy tính bảng Huawei Mediapad T5\" src=\"https://cdn.tgdd.vn/Products/Images/522/196071/huawei-mediapad-t5-thetke.jpg\" title=\"Thiết kế hiện đại, đẹp mắt trên Máy tính bảng Huawei Mediapad T5\" /></a></h2>\r\n\r\n<h3>M&agrave;n h&igrave;nh sắc n&eacute;t, xem phim chất lượng cao</h3>\r\n\r\n<p>K&iacute;ch thước m&agrave;n h&igrave;nh&nbsp;10.1 inch, độ ph&acirc;n giải FullHD 1080p cho chất lượng h&igrave;nh ảnh tươi s&aacute;ng, xem c&aacute;c bộ phim&nbsp;bom tấn h&igrave;nh ảnh c&oacute; độ s&acirc;u cao v&agrave; sắc n&eacute;t.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/522/196071/huawei-mediapad-t5-content-33397-manhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Màn hình sắc nét trên Máy tính bảng Huawei Mediapad T5\" src=\"https://cdn.tgdd.vn/Products/Images/522/196071/huawei-mediapad-t5-content-33397-manhinhtgdd.jpg\" title=\"Cấu hình mạnh trên Máy tính bảng Huawei Mediapad T5\" /></a></h2>\r\n\r\n<h3>Camera tươi s&aacute;ng, lấy n&eacute;t nhanh</h3>\r\n\r\n<p>Camera trước 2 MP, kh&ocirc;ng qu&aacute; sắc n&eacute;t nhưng c&oacute; thể đ&aacute;p ứng kh&aacute; tốt nhu cầu gọi videocall với chất lượng ảnh tươi s&aacute;ng. Chế độ l&agrave;m đẹp l&agrave; một điểm cộng lớn tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-huawei\" target=\"_blank\" title=\"Máy tính bảng Huawei chính hãng đang kinh doanh chính hãng tại Thegioididong.com\">m&aacute;y t&iacute;nh bảng&nbsp;Huawei</a>&nbsp;tầm trung.</p>\r\n\r\n<p>Camera sau 5 MP chỉ mất 1 đến 2 gi&acirc;y để lấy n&eacute;t, h&igrave;nh ảnh c&oacute; độ n&eacute;t cao v&agrave; ch&acirc;n thật.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/522/196071/huawei-mediapad-t5-content-33397-cameratgdd.jpg\" onclick=\"return false;\"><img alt=\"Camera tươi sáng trên Máy tính bảng Huawei Mediapad T5\" src=\"https://cdn.tgdd.vn/Products/Images/522/196071/huawei-mediapad-t5-content-33397-cameratgdd.jpg\" title=\"Camera tươi sáng trên Máy tính bảng Huawei Mediapad T5\" /></a>Cấu h&igrave;nh ổn định</h3>\r\n\r\n<p>Chip xử l&yacute; Kirin 659 kết hợp c&ugrave;ng&nbsp;3GB RAM&nbsp;gi&uacute;p bạn chơi tốt c&aacute;c tựa game cơ bản hiện nay như temple run 2, super mario, asphalt 8,...</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/522/196071/huawei-mediapad-t5-content-33397-cauhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình mạnh trên Máy tính bảng Huawei Mediapad T5\" src=\"https://cdn.tgdd.vn/Products/Images/522/196071/huawei-mediapad-t5-content-33397-cauhinhtgdd.jpg\" title=\"Cấu hình mạnh trên Máy tính bảng Huawei Mediapad T5\" /></a></h2>\r\n\r\n<h3>&Acirc;m thanh sống động</h3>\r\n\r\n<p>C&ocirc;ng nghệ &acirc;m thanh Dual stereo speakers gi&uacute;p&nbsp;<strong>tablet Huawei Mediapad T5</strong>&nbsp;cho chất lượng &acirc;m to v&agrave; r&otilde;, bạn c&oacute; thể thư gi&atilde;n b&ecirc;n cạnh chiếc&nbsp;<strong>tablet</strong>&nbsp;với những b&agrave;i nhạc trầm ấm.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/522/196071/huawei-mediapad-t5-content-33397-amthanh.jpg\" onclick=\"return false;\"><img alt=\"Gải trí, thư giản cùng âm thanh trên Máy tính bảng Huawei Mediapad T5\" src=\"https://cdn.tgdd.vn/Products/Images/522/196071/huawei-mediapad-t5-content-33397-amthanh.jpg\" title=\"Gải trí, thư giản cùng âm thanh trên Máy tính bảng Huawei Mediapad T5\" /></a>Dung lượng Pin lớn gi&uacute;p bạn thoải m&aacute;i sử dụng trong 1 ng&agrave;y</h3>\r\n\r\n<p>Vi&ecirc;n pin 5100 mAh&nbsp;được trang bị cho&nbsp;<strong>Huawei Mediapad T5&nbsp;</strong>gi&uacute;p bạn c&oacute; thể xem phim li&ecirc;n tục khoảng 3 giờ, chơi game li&ecirc;n tục khoảng 2 giờ li&ecirc;n tục v&agrave; thực hiện những t&aacute;c vụ kh&aacute;c như&nbsp;nghe nhạc, lướt face, đọc b&aacute;o,... li&ecirc;n tục trong 24h, rất&nbsp;thuận tiện để bạn mang theo b&ecirc;n m&igrave;nh khi đi du lịch, d&atilde; ngoại.<a href=\"https://www.thegioididong.com/images/522/196071/huawei-mediapad-t5-content-33397-pintgdd.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin trên Máy tính bảng Huawei Mediapad T5\" src=\"https://cdn.tgdd.vn/Products/Images/522/196071/huawei-mediapad-t5-content-33397-pintgdd.jpg\" title=\"Dung lượng pin trên Máy tính bảng Huawei Mediapad T5\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 0, 'huawei-mediapad-t5-33397-hinhchitiet-400x460.jpg', 5690000),
(42, 'Samsung Galaxy Tab A 8', 2, 'Máy tính bảng', '<p><strong><a href=\"https://www.thegioididong.com/may-tinh-bang-samsung\" target=\"_blank\" title=\"Máy tính bảng hãng Samsung\">Samsung</a>&nbsp;Galaxy Tab A 8.0 (2017) mới c&oacute; m&agrave;n h&igrave;nh tỉ lệ 16:10 với kh&ocirc;ng gian hiển thị rộng th&ocirc;ng minh cho người d&ugrave;ng.</strong></p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/113278/Slider/vi-vn-samsung-galaxy-tab-a-8.0-(2017).jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Adapter, S&aacute;ch hướng dẫn, C&aacute;p chuyển, C&acirc;y lấy sim, Hộp m&aacute;y</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong><a href=\"https://www.thegioididong.com/may-tinh-bang-samsung\" target=\"_blank\" title=\"Máy tính bảng hãng Samsung\">Samsung</a>&nbsp;Galaxy Tab A 8.0 (2017) mới c&oacute; m&agrave;n h&igrave;nh tỉ lệ 16:10 với kh&ocirc;ng gian hiển thị rộng th&ocirc;ng minh cho người d&ugrave;ng.</strong></h2>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh rộng hiển thị th&ocirc;ng tin trọn vẹn</strong></h3>\r\n\r\n<p>Với c&aacute;ch bố tr&iacute; chiều ngang v&agrave; dọc theo tỉ lệ 16:10 th&ocirc;ng minh, những th&ocirc;ng tin bạn đọc tr&ecirc;n một trang web hay t&agrave;i liệu được hiển thị nhiều v&agrave; trọn vẹn trong một khung h&igrave;nh hơn.</p>\r\n\r\n<p>M&aacute;y sử dụng&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/loai-man-hinh-tft-lcd-amoled-la-gi--592346#tftlcd\" target=\"_blank\" title=\"Tìm hiểu công nghệ màn hình\">c&ocirc;ng nghệ hiển thị PLS TFT</a>, độ ph&acirc;n giải&nbsp;1280 x 800 pixels cho h&igrave;nh ảnh hiển thị chi tiết, gi&uacute;p bạn thoải m&aacute;i lướt web hay xem phim phụ đề m&agrave; kh&ocirc;ng mỏi mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/113278/samsung-galaxy-tab-a-80-2017-3.jpg\" onclick=\"return false;\"><img alt=\"Màn hình tỉ lệ 16:10 hiển thị hình ảnh tốt\" src=\"https://cdn.tgdd.vn/Products/Images/522/113278/samsung-galaxy-tab-a-80-2017-3.jpg\" title=\"Màn hình tỉ lệ 16:10 hiển thị hình ảnh tốt\" /></a></p>\r\n\r\n<h3><strong>Lướt web nhanh hơn với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/4g-la-gi-731757\" target=\"_blank\" title=\"Tìm hiểu công nghệ kết nối 4G\">c&ocirc;ng nghệ kết nối 4G</a></strong></h3>\r\n\r\n<p>Với sự ph&aacute;t triển v&agrave; phổ biến của mạng 4G hiện nay th&igrave; việc Galaxy A 8.0 (2017) được trang bị t&iacute;nh năng n&agrave;y cũng kh&ocirc;ng phải l&agrave; qu&aacute; bất ngờ. 4G sẽ gi&uacute;p bạn lướt web nhanh hơn, tốc độ tải một trang web hay một ứng dụng bất kỳ đều nhanh gấp 2 đến 3 lần so với 3G trước đ&acirc;y.</p>\r\n\r\n<p>=&gt;&nbsp;<a href=\"https://www.thegioididong.com/sim-so-dep/viettel?t=5&amp;l=11\" target=\"_blank\" title=\"Chọn mua sim 4G dung lượng cao\">Chọn mua sim 4G dung lượng cao</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/113278/samsung-galaxy-tab-a-80-2017-11-1.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Tab A 8.0 (2017) - Lướt web nhanh hơn với công nghệ kết nối 4G\" src=\"https://cdn.tgdd.vn/Products/Images/522/113278/samsung-galaxy-tab-a-80-2017-11-1.jpg\" title=\"Samsung Galaxy Tab A 8.0 (2017) - Lướt web nhanh hơn với công nghệ kết nối 4G\" /></a></p>\r\n\r\n<h3><strong>Camera 8 MP chất lượng tốt hơn</strong></h3>\r\n\r\n<p>Tuy l&agrave; d&ograve;ng m&aacute;y t&iacute;nh bảng nhưng Galaxy A 8.0 (2017) vẫn được trang bị camera ch&iacute;nh 8 MP c&oacute; khẩu độ f1.9 như những d&ograve;ng flagship cao cấp tr&ecirc;n thị trường hiện nay.</p>\r\n\r\n<p>Nhiều t&iacute;nh năng của smartphone được đưa v&agrave;o như chạm lấy n&eacute;t, lấy n&eacute;t tự động, chỉnh s&aacute;ng tốt, chụp HDR...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/113278/samsung-galaxy-tab-a-80-2017-12-1.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Tab A 8.0 (2017) - Camera 8 MP chất lượng tốt hơn\" src=\"https://cdn.tgdd.vn/Products/Images/522/113278/samsung-galaxy-tab-a-80-2017-12-1.jpg\" title=\"Samsung Galaxy Tab A 8.0 (2017) - Camera 8 MP chất lượng tốt hơn\" /></a></p>\r\n\r\n<p>Camera phụ ph&iacute;a trước cũng c&oacute; độ ph&acirc;n giải tốt 5 MP, t&iacute;nh năng chỉnh sửa khu&ocirc;n mặt, l&agrave;m mịn da... rất được nhiều bạn trẻ y&ecirc;u th&iacute;ch v&agrave; sử dụng.</p>\r\n\r\n<h3><strong>Dung lượng pin được cải thiện</strong></h3>\r\n\r\n<p>Dung lượng pin 5.000 mAh c&ugrave;ng những t&iacute;nh năng tiết kiệm pin như Power Saving and Ultra Power Saving Mode gi&uacute;p k&eacute;o d&agrave;i thời gian sử dụng l&ecirc;n đến 12 giờ, cho bạn những trải nghiệm tốt nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/113278/samsung-galaxy-tab-a-80-2017-13-1.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Tab A 8.0 (2017) - Dung lượng pin được cải thiện\" src=\"https://cdn.tgdd.vn/Products/Images/522/113278/samsung-galaxy-tab-a-80-2017-13-1.jpg\" title=\"Samsung Galaxy Tab A 8.0 (2017) - Dung lượng pin được cải thiện\" /></a></p>\r\n', 1, 0, 'samsung-galaxy-tab-a-80-2017-9-400x460.jpg', 5490000),
(43, 'Huawei MediaPad T3 10 (2017)', 2, 'Máy tính bảng', '<p><a href=\"https://www.thegioididong.com/may-tinh-bang-huawei\" target=\"_blank\" title=\"Máy tính bảng Huawei\">Huawei</a>&nbsp;MediaPad T3 10 (2017) l&agrave; chiếc m&aacute;y t&iacute;nh bảng với m&agrave;n h&igrave;nh k&iacute;ch thước lớn c&ugrave;ng khe cắm sim tiện lợi gi&uacute;p người d&ugrave;ng c&oacute; thể nghe gọi như một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại thông minh\">smartphone</a>.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/111223/Slider/vi-vn-huawei-mediapad-t3-10-2017-11.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Adapter,Hộp m&aacute;y,C&aacute;p Micro USB,S&aacute;ch hướng dẫn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/may-tinh-bang-huawei\" target=\"_blank\" title=\"Máy tính bảng Huawei\">Huawei</a>&nbsp;MediaPad T3 10 (2017) l&agrave; chiếc m&aacute;y t&iacute;nh bảng với m&agrave;n h&igrave;nh k&iacute;ch thước lớn c&ugrave;ng khe cắm sim tiện lợi gi&uacute;p người d&ugrave;ng c&oacute; thể nghe gọi như một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại thông minh\">smartphone</a>.</h2>\r\n\r\n<h3><strong>Thiết kế mỏng, đẹp</strong></h3>\r\n\r\n<p>Về tổng thể, m&aacute;y nổi bật với phần khung viền kim loại sang trọng, kết hợp m&agrave;u sắc hiện đại cho cảm gi&aacute;c kh&aacute; thanh lịch, nhẹ nh&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-103.png\" onclick=\"return false;\"><img alt=\"Thiết kế đơn giản nhưng vẫn thu hút được người dùng\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-103.png\" title=\"Thiết kế đơn giản nhưng vẫn thu hút được người dùng\" /></a></p>\r\n\r\n<p><em>Thiết kế đơn giản nhưng vẫn thu h&uacute;t được người d&ugrave;ng</em></p>\r\n\r\n<p>Ngo&agrave;i ra, với trọng lượng kh&aacute; nhẹ c&ugrave;ng với 4 g&oacute;c bo tr&ograve;n gi&uacute;p m&aacute;y c&oacute; trải nghiệm cầm nắm kh&aacute; thoải m&aacute;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-105.png\" onclick=\"return false;\"><img alt=\"Các phím bấm được hoàn thiện tốt, độ đàn hồi cao\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-105.png\" title=\"Các phím bấm được hoàn thiện tốt, độ đàn hồi cao\" /></a></p>\r\n\r\n<p><em>C&aacute;c ph&iacute;m bấm được ho&agrave;n thiện tốt, độ đ&agrave;n hồi cao</em></p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh hiển thị tốt</strong></h3>\r\n\r\n<p dir=\"ltr\">Sở hữu m&agrave;n h&igrave;nh k&iacute;ch thước 10 inch kh&ocirc;ng qu&aacute; lớn, c&oacute; thể nh&eacute;t vừa một chiếc balo dạng nhỏ, Huawei MediaPad T3 10 (2017) Spen sử dụng tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-man-hinh-pls-lcd-la-gi-901358\" target=\"_blank\" title=\"Tìm hiểu màn hình PLS LCD\">PLS LCD</a>&nbsp;cho khả năng hiển thị tốt.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-107.png\" onclick=\"return false;\"><img alt=\"Màn hình lớn cho trải nghiệm, sử dụng thoải mái\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-107.png\" title=\"Màn hình lớn cho trải nghiệm, sử dụng thoải mái\" /></a></p>\r\n\r\n<p dir=\"ltr\"><em>M&agrave;n h&igrave;nh lớn cho trải nghiệm, sử dụng thoải m&aacute;i</em></p>\r\n\r\n<p dir=\"ltr\">Độ ph&acirc;n giải 1280 x 800 pixels cho chất lượng h&igrave;nh ảnh sắc n&eacute;t, sử dụng tốt khi đi ngo&agrave;i trời.</p>\r\n\r\n<h3><strong>Cấu h&igrave;nh ổn định</strong></h3>\r\n\r\n<p>Cung cấp sức mạnh cho m&aacute;y l&agrave; con chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-qualcomm-snapdragon-425-1009665\" target=\"_blank\" title=\"Tìm hiểu chip Qualcomm MSM8917\">Qualcomm MSM8917</a>&nbsp;và RAM 2 GB&nbsp;ho&agrave;n to&agrave;n đ&aacute;p ứng tốt c&aacute;c nhu cầu thường ng&agrave;y của bạn, từ đọc b&aacute;o online, lướt facebook hay xem phim đến chơi game.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-108.png\" onclick=\"return false;\"><img alt=\"Hiệu năng không quá ấn tượng nhưng với tác vụ cơ bản máy vẫn hoạt động ổn định\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-108.png\" title=\"Hiệu năng không quá ấn tượng nhưng với tác vụ cơ bản máy vẫn hoạt động ổn định\" /></a></p>\r\n\r\n<p><em>Hiệu năng kh&ocirc;ng qu&aacute; ấn tượng nhưng với t&aacute;c vụ cơ bản m&aacute;y vẫn hoạt động ổn định</em></p>\r\n\r\n<p>Khả năng chơi game nặng của m&aacute;y sẽ c&oacute; phần hạn chế, d&ugrave; vậy so với tầm gi&aacute; th&igrave; trải nghiệm tr&ecirc;n MediaPad T3 10 (2017) vẫn kh&aacute; ấn tượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-106.png\" onclick=\"return false;\"><img alt=\"Huawei cũng không quên trang bị cho máy nhiều tiện ích đi kèm giúp bạn khai thác tối đa màn hình lớn của máy\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-106.png\" title=\"Huawei cũng không quên trang bị cho máy nhiều tiện ích đi kèm giúp bạn khai thác tối đa màn hình lớn của máy\" /></a></p>\r\n\r\n<p><em>Huawei cũng kh&ocirc;ng qu&ecirc;n trang bị cho m&aacute;y nhiều tiện &iacute;ch đi k&egrave;m gi&uacute;p bạn khai th&aacute;c tối đa m&agrave;n h&igrave;nh lớn của m&aacute;y</em></p>\r\n\r\n<p>MediaPad T3 10 (2017) hoạt đ&ocirc;̣ng tr&ecirc;n n&ecirc;̀n tảng Android 7 mới mẻ n&ecirc;n sở hữu cho m&igrave;nh khả năng chia đ&ocirc;i màn hình đ&ecirc;̉ sử dụng hai ứng dụng cùng m&ocirc;̣t lúc. K&ecirc;́t hợp với màn hình k&iacute;ch thước lớn thì đ&acirc;y quả là m&ocirc;̣t lợi th&ecirc;́ r&acirc;́t lớn cho những người thích làm vi&ecirc;̣c đa nhi&ecirc;̣m.</p>\r\n\r\n<h3><strong>Camera đủ d&ugrave;ng</strong></h3>\r\n\r\n<p>Việc chụp ảnh bằng một chiếc m&aacute;y t&iacute;nh bảng chưa bao giờ được người d&ugrave;ng đ&aacute;nh gi&aacute; cao v&agrave; Huawei cũng chỉ trang bị cho MediaPad T3 10 (2017) bộ đ&ocirc;i camera đủ đ&aacute;p ứng c&aacute;c nhu cầu cơ bản.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-1040.jpg\" onclick=\"return false;\"><img alt=\"Bộ đôi camera giúp bạn lưu lại những khoảnh khắc đáng nhớ\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-1040.jpg\" title=\"Bộ đôi camera giúp bạn lưu lại những khoảnh khắc đáng nhớ\" /></a></p>\r\n\r\n<p><em>Bộ đ&ocirc;i camera gi&uacute;p bạn lưu lại những khoảnh khắc đ&aacute;ng nhớ</em></p>\r\n\r\n<p>M&aacute;y sở hữu camera sau độ ph&acirc;n giải 5 MP, ảnh chụp ở điều kiện ban ng&agrave;y ở mức chấp nhận được mặc d&ugrave; m&agrave;u sắc c&oacute; phần hơi nhợt nhạt so với thực tế.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-102.png\" onclick=\"return false;\"><img alt=\"Camera trước giúp bạn có thể thực hiện các cuộc gọi video với bạn bè\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-102.png\" title=\"Camera trước giúp bạn có thể thực hiện các cuộc gọi video với bạn bè\" /></a></p>\r\n\r\n<p><em>Camera trước gi&uacute;p bạn c&oacute; thể thực hiện c&aacute;c cuộc gọi video với bạn b&egrave;</em></p>\r\n\r\n<p>Còn camera trước cũng có đ&ocirc;̣ ph&acirc;n giải chỉ dừng lại ở mức 2 MP nhưng cũng đủ đ&ecirc;̉ chúng ta thực hi&ecirc;̣n các cu&ocirc;̣c gọi video.</p>\r\n\r\n<h3><strong>Hỗ trợ kết nối 3G/4G</strong></h3>\r\n\r\n<p>M&aacute;y cũng hỗ trợ khe cắm&nbsp;<a href=\"https://www.thegioididong.com/the-nho-dien-thoai\" title=\"thẻ nhớ\" type=\"thẻ nhớ\">thẻ nhớ</a>&nbsp;mở rộng tối đa l&ecirc;n tới 32 GB c&ugrave;ng 1 khe cắm&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tim-hieu-cac-loai-sim-thong-dung-sim-thuong-micro--590216#nanosim\" title=\"nano sim\" type=\"nano sim\">Nano Sim</a>&nbsp;v&agrave; kết nối 4G tốc độ cao gi&uacute;p bạn truy cập internet với tốc độ nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/111223/huawei-mediapad-t3-1010.jpg\" onclick=\"return false;\"><img alt=\"Khe cắm SIM tiện lợi đảm bảo bạn luôn online mọi lúc mọi nơi\" src=\"https://cdn.tgdd.vn/Products/Images/522/111223/huawei-mediapad-t3-1010.jpg\" title=\"Khe cắm SIM tiện lợi đảm bảo bạn luôn online mọi lúc mọi nơi\" /></a></p>\r\n\r\n<p><em>Khe cắm SIM tiện lợi đảm bảo bạn lu&ocirc;n online mọi l&uacute;c mọi nơi</em></p>\r\n', 0, 0, 'huawei-mediapad-t3-10-1-33397-chitiet-400x460.jpg', 4490000),
(44, 'Masstel Tab10 Plus', 2, 'Máy tính bảng', '<p>Mang trong m&igrave;nh thiết kế hiện đại v&agrave; sang trọng -&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/masstel-tab10-plus\" title=\"Máy tính bảng Masstel Tab 10 plus\">M&aacute;y t&iacute;nh bảng Masstel Tab 10 plus</a>&nbsp;sẽ l&agrave; lựa chọn đ&aacute;ng c&acirc;n nhắc cho sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng muốn t&igrave;m một chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tablet chính hãng đang kinh doanh chính hãng tại Thegioididong.com\">tablet</a>&nbsp;để giải tr&iacute; hằng ng&agrave;y. Cấu h&igrave;nh đủ khoẻ để bạn c&oacute; thể xem phim, nghe nhạc,&nbsp;<strong>học tiếng anh, đọc s&aacute;ch</strong>&nbsp;hay chơi kh&aacute; tốt c&aacute;c tựa game cơ bản hiện nay.</p>\r\n', '<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/197193/Slider/-masstel-tab10-plus-33397-slider-thietke123.jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Adapter, Tai nghe, S&aacute;ch hướng dẫn, Hộp m&aacute;y</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Mang trong m&igrave;nh thiết kế hiện đại v&agrave; sang trọng -&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/masstel-tab10-plus\" title=\"Máy tính bảng Masstel Tab 10 plus\">M&aacute;y t&iacute;nh bảng Masstel Tab 10 plus</a>&nbsp;sẽ l&agrave; lựa chọn đ&aacute;ng c&acirc;n nhắc cho sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng muốn t&igrave;m một chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tablet chính hãng đang kinh doanh chính hãng tại Thegioididong.com\">tablet</a>&nbsp;để giải tr&iacute; hằng ng&agrave;y. Cấu h&igrave;nh đủ khoẻ để bạn c&oacute; thể xem phim, nghe nhạc,&nbsp;<strong>học tiếng anh, đọc s&aacute;ch</strong>&nbsp;hay chơi kh&aacute; tốt c&aacute;c tựa game cơ bản hiện nay.</h2>\r\n\r\n<h3>Thiết kế sang trọng</h3>\r\n\r\n<p>Được ho&agrave;n thiện chất liệu nhựa nguy&ecirc;n khối, c&aacute;c cạnh bo cong nhẹ tạo sự chắc chắn khi cầm nắm. Mặt k&iacute;nh ph&iacute;a trước ra gần s&aacute;t viền m&agrave;n h&igrave;nh, đường cắt kim loại s&aacute;ng b&oacute;ng chạy theo viền m&aacute;y to&aacute;t l&ecirc;n vẻ sang trọng cho sản phẩm tầm trung của&nbsp;<strong>Masstel</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/197193/masstel-tab10-plus-content-thietketgdd.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế hiện đại trên Máy tính bảng Masstel Tab10 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/522/197193/masstel-tab10-plus-content-thietketgdd.jpg\" title=\"Thiết kế hiện đại trên Máy tính bảng Masstel Tab10 Plus\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh tươi s&aacute;ng</h3>\r\n\r\n<p><strong>M&aacute;y t&iacute;nh bảng Masstel Tab 10 plus&nbsp;</strong>hỗ trợ m&agrave;n h&igrave;nh rộng&nbsp;<strong>10.1 inch độ ph&acirc;n giải HD</strong>&nbsp;chất lượng ảnh khi xem phim tươi s&aacute;ng.&nbsp;<strong>C&ocirc;ng nghệ m&agrave;n h&igrave;nh IPS LCD</strong>&nbsp;l&agrave; một lợi thế gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-masstel\" target=\"_top\" title=\"Máy tính bảng Masstel chính hãng đang được kinh doanh tại Thegioididong.com\">m&aacute;y t&iacute;nh bảng Masstel&nbsp;</a>mở rộng g&oacute;c h&igrave;nh, bạn c&oacute; thể xem phim c&ugrave;ng bạn b&egrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/197193/masstel-tab10-plus-content-manhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Màn hình tươi sáng trên Máy tính bảng Masstel Tab10 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/522/197193/masstel-tab10-plus-content-manhinhtgdd.jpg\" title=\"Màn hình tươi sáng trên Máy tính bảng Masstel Tab10 Plus\" /></a></p>\r\n\r\n<h3>Camera lấy n&eacute;t nhanh</h3>\r\n\r\n<p>Camer trước 2PM c&oacute;&nbsp;<strong>chế độ l&agrave;m đẹp</strong>, thuận tiện để bạn selfie v&agrave; gọi videocall c&ugrave;ng bạn b&egrave;,&nbsp;<strong>camera sau 8MP</strong>&nbsp;hỗ trợ đ&egrave;n flash lấy n&eacute;t kh&aacute; nhanh, h&igrave;nh ảnh s&aacute;ng bạn c&oacute; thể lưu lại những khoảnh khắc đẹp c&ugrave;ng bạn b&egrave;, người th&acirc;n.</p>\r\n\r\n<p>M&aacute;y hỗ trợ khe cắm sim (loại sim thường), c&oacute; hỗ trợ gọi điện thoại, bạn c&oacute; thể sử dụng chiếc tablet như một chiếc điện thoại th&ocirc;ng minh thuận tiện khi bạn sử dụng nhiều số điện thoại để li&ecirc;n lạc c&ugrave;ng gia đ&igrave;nh, bạn b&egrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/197193/masstel-tab10-plus-content-camera.jpg\" onclick=\"return false;\"><img alt=\"Camera tươi sáng trên Máy tính bảng Masstel Tab10 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/522/197193/masstel-tab10-plus-content-camera.jpg\" title=\"Camera tươi sáng trên Máy tính bảng Masstel Tab10 Plus\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Cấu h&igrave;nh ổn định</h3>\r\n\r\n<p>Ở ph&acirc;n kh&uacute;c phổ th&ocirc;ng n&ecirc;n chiếc&nbsp;<strong>tablet Masstel</strong>&nbsp;chỉ được hỗ trợ chip xử l&yacute; MTK 6580 c&ugrave;ng 1GB RAM, lướt web xem phim mượt m&agrave;. Chiến c&aacute;c game cơ bản hiện nay như Asphalt 8, Racing in car,... kh&aacute; tốt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/197193/masstel-tab10-plus-content-cauhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình mạnh mẽ trên Máy tính bảng Masstel Tab10 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/522/197193/masstel-tab10-plus-content-cauhinhtgdd.jpg\" title=\"Cấu hình mạnh mẽ trên Máy tính bảng Masstel Tab10 Plus\" /></a></p>\r\n\r\n<h3>Hỗ trợ ứng dụng học tiếng Anh v&agrave; đọc s&aacute;ch cho trẻ em</h3>\r\n\r\n<p>Masstel tab10 plus được t&iacute;ch hợp sẵn ứng dụng&nbsp;<strong>Monkey junio</strong>&nbsp;hỗ trợ tốt cho phụ huynh dạy tiếng Anh tại nh&agrave; cho con, ch&aacute;u của m&igrave;nh (lưu &yacute;: ứng dụng n&agrave;y kh&ocirc;ng miễn ph&iacute;, được học thử 2 b&agrave;i đầu).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/197193/masstel-tab10-plus-monkey-junio.jpg\" onclick=\"return false;\"><img alt=\"Ứng dụng monky junio bổ ích trên máy tính bảng masstel tab10 plus\" src=\"https://cdn.tgdd.vn/Products/Images/522/197193/masstel-tab10-plus-monkey-junio.jpg\" title=\"Ứng dụng monky junio bổ ích trên máy tính bảng masstel tab10 plus\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; ứng dụng&nbsp;<strong>Bibox</strong>&nbsp;gi&uacute;p trẻ c&oacute; thể kh&aacute;m ph&aacute; nhiều điều hay, kiến thức bổ &iacute;ch ngay ch&iacute;nh tr&ecirc;n chiếc&nbsp;<strong>m&aacute;y t&iacute;nh bảng Masstel.</strong></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/197193/masstel-tab10-plus-bibox.jpg\" onclick=\"return false;\"><img alt=\"Ứng dụng Bibox bổ ích trên máy tính bảng masstel tab10 plus\" src=\"https://cdn.tgdd.vn/Products/Images/522/197193/masstel-tab10-plus-bibox.jpg\" title=\"Ứng dụng Bibox bổ ích trên máy tính bảng masstel tab10 plus\" /></a></p>\r\n\r\n<h3>Dung lượng pin</h3>\r\n\r\n<p>Với&nbsp;<strong>dung lượng pin 5000mAh&nbsp;</strong>m&igrave;nh đ&atilde; thử đua Asphalt 8 với độ s&aacute;ng m&agrave;n h&igrave;nh cao nhất, trong 30 ph&uacute;t mất khoảng 35% pin (khoảng hơn 2 giờ chơi game li&ecirc;n tục), xem phim 45 ph&uacute;t mất khoảng 30% pin (khoảng hơn 3 giờ xem phim li&ecirc;n tục). Với khoảng thời gian tr&ecirc;n th&igrave; bạn c&oacute; thể y&ecirc;n t&acirc;m để mang theo b&ecirc;n m&igrave;nh đi du lịch, d&atilde; ngoại.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/197193/masstel-tab10-plus-content-pin.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin trên Máy tính bảng Masstel Tab10 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/522/197193/masstel-tab10-plus-content-pin.jpg\" title=\"Dung lượng pin trên Máy tính bảng Masstel Tab10 Plus\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 0, 'masstel-tab10-plus-33397-chitiet-400x460.jpg', 2490000),
(45, 'Laptop Asus 14 X407UA', 3, 'Laptop', '<p>Được thiết kế vẻ ngo&agrave;i trang nh&atilde;,&nbsp;<a href=\"https://www.thegioididong.com/laptop/asus-x407ua-bv351t\" target=\"_blank\" title=\"laptop Asus X407UA (BV351T) \">laptop Asus X407UA (BV351T)&nbsp;</a>c&oacute; cấu h&igrave;nh đủ khoẻ để chạy tốt c&aacute;c ứng dụng văn ph&ograve;ng, photoshop cơ bản ph&ugrave; hợp cho nh&acirc;n vi&ecirc;n văn ph&ograve;ng. L&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Laptop văn phòng chính hãng đang kinh doanh tại Thegioididong.com\">laptop văn ph&ograve;ng</a>&nbsp;-Asus X407UA (BV351T) c&oacute; trọng&nbsp;lượng nhẹ th&iacute;ch hợp cho việc mang v&aacute;c&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop chính hãng đang kinh doanh tại Thegioididong.com\">laptop</a>&nbsp;hằng ng&agrave;y đến trường của học sinh - sinh vi&ecirc;n</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/190014/Slider/vi-vn-asus-x407ua-bv351t-33397-thietke.jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: S&aacute;ch hướng dẫn,Adapter sạc,Th&ugrave;ng m&aacute;y,Chuột</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>Được thiết kế vẻ ngo&agrave;i trang nh&atilde;,&nbsp;<a href=\"https://www.thegioididong.com/laptop/asus-x407ua-bv351t\" target=\"_blank\" title=\"laptop Asus X407UA (BV351T) \">laptop Asus X407UA (BV351T)&nbsp;</a>c&oacute; cấu h&igrave;nh đủ khoẻ để chạy tốt c&aacute;c ứng dụng văn ph&ograve;ng, photoshop cơ bản ph&ugrave; hợp cho nh&acirc;n vi&ecirc;n văn ph&ograve;ng. L&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Laptop văn phòng chính hãng đang kinh doanh tại Thegioididong.com\">laptop văn ph&ograve;ng</a>&nbsp;-Asus X407UA (BV351T) c&oacute; trọng&nbsp;lượng nhẹ th&iacute;ch hợp cho việc mang v&aacute;c&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop chính hãng đang kinh doanh tại Thegioididong.com\">laptop</a>&nbsp;hằng ng&agrave;y đến trường của học sinh - sinh vi&ecirc;n.</h2>\r\n\r\n<p>Thiết kế h&agrave;i ho&agrave;, dễ di chuyển</p>\r\n\r\n<p>Laptop Asus X407UA (BV351T) được thiết kế đơn giản nhưng vẫn rất tinh tế với chất liệu vỏ nhựa giả kim loại ở mặt lưng. C&aacute;c cạnh được bo cong với độ d&agrave;y m&aacute;y chỉ 21.9 mm đi c&ugrave;ng trọng lượng 1.5 kg, đ&acirc;y l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=mong-nhe-thoi-trang\" target=\"_blank\" title=\"Xem thêm các mẫu laptop nhỏ gọn khác\" type=\"Xem thêm các mẫu laptop nhỏ gọn khác\">laptop nhỏ gọn</a>, tiện lợi bỏ v&agrave;o ba l&ocirc;, va li mang theo đến lớp, đi du lịch.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/190014/asus-x407ua-bv351t-thietke.jpg\" onclick=\"return false;\"><img alt=\"Thiết kết trang nhã trên laptop nhỏ gọn Asus X407UA i3 7020U (BV351T)\" src=\"https://cdn.tgdd.vn/Products/Images/44/190014/asus-x407ua-bv351t-thietke.jpg\" title=\"Thiết kết trang nhã trên laptop nhỏ gọn Asus X407UA i3 7020U (BV351T)\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh xử l&yacute; tốt ứng dụng văn ph&ograve;ng, đồ hoạ cơ bản</h3>\r\n\r\n<p>M&aacute;y được trang bị chip xử l&yacute; Core i3 thế hệ 7, kết hợp 4GB DDR4 đủ khoẻ để chạy mượt c&aacute;c ứng dụng văn ph&ograve;ng: word, excel, powepoint,... Thời gian phản hồi c&aacute;c thao t&aacute;c k&eacute;o thả trong photoshop nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/190014/asus-x407ua-bv351t-cauhinh.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình trên laptop nhỏ gọn Asus X407UA i3 7020U (BV351T)\" src=\"https://cdn.tgdd.vn/Products/Images/44/190014/asus-x407ua-bv351t-cauhinh.jpg\" title=\"Cấu hình trên laptop nhỏ gọn Asus X407UA i3 7020U (BV351T)\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh cấu h&igrave;nh khoẻ, ổ cứng 1 Terabyte (TB) tr&ecirc;n Asus X407UA (BV351T) hỗ trợ tốt cho nh&acirc;n vi&ecirc;n văn ph&ograve;ng, học sinh - sinh vi&ecirc;n khi phải thường xuy&ecirc;n lưu trữ nhiều file dữ liệu, t&agrave;i liệu học tập.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh 14 inch sắc n&eacute;t</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\" title=\"Laptop Asus\">Laptop Asus</a>&nbsp;X407UA (BV351T) trang bị m&agrave;n h&igrave;nh 14 inch, độ ph&acirc;n giải HD (1366 x 768) kết hợp c&ugrave;ng card đồ hoạ&nbsp;Intel&reg; HD Graphics 620 cho chất lượng ảnh tươi s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/190014/asus-x407ua-bv351t-manhinh14inch.jpg\" onclick=\"return false;\"><img alt=\"Màn hình hiển thị sắc nét trên laptop nhỏ gọn Asus X407UA i3 7020U (BV351T)\" src=\"https://cdn.tgdd.vn/Products/Images/44/190014/asus-x407ua-bv351t-manhinh14inch.jpg\" title=\"Màn hình hiển thị sắc nét trên laptop nhỏ gọn Asus X407UA i3 7020U (BV351T)\" /></a></p>\r\n\r\n<h3>Bảo mật v&acirc;n tay mở kho&aacute; m&agrave;n h&igrave;nh nhanh ch&oacute;ng</h3>\r\n\r\n<p>M&aacute;y được t&iacute;ch hợp<a href=\"https://www.thegioididong.com/hoi-dap/bao-mat-van-tay-tren-laptop-la-gi-cach-cai-dat-1018121\" target=\"_blank\" title=\"bảo mật vân tay\">&nbsp;c&ocirc;ng nghệ bảo mật v&acirc;n tay</a>&nbsp;tiện lợi tr&ecirc;n TouchPad, chỉ cần chạm nhẹ, mở kh&oacute;a nhanh ch&oacute;ng, tiết kiệm thời gian, dữ liệu tr&ecirc;n m&aacute;y cũng được bảo mật hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/190014/asus-x407ua-bv351t-vantay1cham-1.jpg\" onclick=\"return false;\"><img alt=\"Mở khoá vân tay trên laptop nhỏ gọn Ausu X407UA\" src=\"https://cdn.tgdd.vn/Products/Images/44/190014/asus-x407ua-bv351t-vantay1cham-1.jpg\" title=\"Mở khoá vân tay trên laptop nhỏ gọn Ausu X407UA\" /></a></p>\r\n\r\n<h3>Cổng USB 3.0 tiết kiệm thời gian truyền dữ liệu</h3>\r\n\r\n<p>Ngo&agrave;i c&aacute;c cổng kết nối cơ bản, m&aacute;y hỗ trợ 1&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-30-la-gi-926737\" target=\"_blank\" title=\"USB 3.0\">cổng USB 3.0</a>&nbsp;gi&uacute;p việc truyền dữ liệu sang thiết bị ngoại vi nhanh ch&oacute;ng với tốc độ tối đa l&ecirc;n đến 600MB/s.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/190014/asus-x407ua-bv351t-congketnoi.jpg\" onclick=\"return false;\"><img alt=\"Cổng kết nối trên laptop nhỏ gọn Asus X407UA i3 7020U (BV351T)\" src=\"https://cdn.tgdd.vn/Products/Images/44/190014/asus-x407ua-bv351t-congketnoi.jpg\" title=\"Cổng kết nối trên laptop nhỏ gọn Asus X407UA i3 7020U (BV351T)\" /></a></p>\r\n', 1, 0, 'asus-x407ua-i3-7020u-4gb-1tb-win10-bv309t-600x600.jpg', 9990000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(46, 'Laptop Acer Swift SF114 32 C9FV', 3, 'Laptop', '<h2>Laptop&nbsp;<a href=\"https://www.thegioididong.com/laptop/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win10\" target=\"_blank\" title=\"Laptop Acer Swift SF114-32 C9FV (NX.GXQSV.002)  chính hãng đang kinh doanh tại cửa hàng Thegioididong\" type=\"Laptop Acer Swift SF114-32 C9FV (NX.GXQSV.002)  chính hãng đang kinh doanh tại cửa hàng Thegioididong\">Acer Swift SF114-32 C9FV (NX.GXQSV.002)</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=mong-nhe-thoi-trang\" target=\"_blank\" title=\"Các loại laptop mỏng nhẹ chính hãng được bán tại Thế Giới Di Động\" type=\"Các loại laptop mỏng nhẹ chính hãng được bán tại Thế Giới Di Động\">laptop mỏng nhẹ</a>với thời lượng pin l&ecirc;n đến 20 giờ đồng hồ c&ugrave;ng nhiều t&iacute;nh năng hiện đại kh&aacute;c. Đ&acirc;y sẽ l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\" type=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">laptop</a>&nbsp;đ&aacute;ng c&acirc;n nhắc d&agrave;nh cho học sinh, sinh vi&ecirc;n hay d&acirc;n văn ph&ograve;ng.</h2>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/202320/Slider/acer-swift-sf114-32-c9fv-n40004gb64gb14fwin10(nx.gxqsv-(1).jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: S&aacute;ch hướng dẫn, Th&ugrave;ng m&aacute;y, Adapter sạc</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Laptop&nbsp;<a href=\"https://www.thegioididong.com/laptop/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win10\" target=\"_blank\" title=\"Laptop Acer Swift SF114-32 C9FV (NX.GXQSV.002)  chính hãng đang kinh doanh tại cửa hàng Thegioididong\" type=\"Laptop Acer Swift SF114-32 C9FV (NX.GXQSV.002)  chính hãng đang kinh doanh tại cửa hàng Thegioididong\">Acer Swift SF114-32 C9FV (NX.GXQSV.002)</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=mong-nhe-thoi-trang\" target=\"_blank\" title=\"Các loại laptop mỏng nhẹ chính hãng được bán tại Thế Giới Di Động\" type=\"Các loại laptop mỏng nhẹ chính hãng được bán tại Thế Giới Di Động\">laptop mỏng nhẹ</a>với thời lượng pin l&ecirc;n đến 20 giờ đồng hồ c&ugrave;ng nhiều t&iacute;nh năng hiện đại kh&aacute;c. Đ&acirc;y sẽ l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\" type=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">laptop</a>&nbsp;đ&aacute;ng c&acirc;n nhắc d&agrave;nh cho học sinh, sinh vi&ecirc;n hay d&acirc;n văn ph&ograve;ng.</h2>\r\n\r\n<h3>Thiết kế sang trọng c&ugrave;ng độ mỏng nhẹ ấn tượng</h3>\r\n\r\n<p>Thiết kế cao cấp với lớp<strong>&nbsp;vỏ kim loại&nbsp;</strong>nh&ocirc;m nguy&ecirc;n khối đem lại vẻ sang trọng v&agrave; thời thượng. Độ mỏng ấn tượng chỉ<strong>&nbsp;14.95 mm</strong>&nbsp;c&ugrave;ng trọng lượng nhẹ si&ecirc;u nhẹ&nbsp;<strong>1.3 kg</strong>&nbsp;đem lại cho bạn sự thuận tiện khi di chuyển. Việc mang&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer\" target=\"_blank\" title=\"Laptop Acer chính hãng, giá rẻ bán tại Thế Giới Di Động\" type=\"Laptop Acer chính hãng, giá rẻ bán tại Thế Giới Di Động\">laptop Acer</a>&nbsp;đi học, đi l&agrave;m sẽ chỉ đơn giản như mang th&ecirc;m 1 cuốn tập.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win103.jpg\" onclick=\"return false;\"><img alt=\"Acer Swift SF114 thiết kế sang trọng\" src=\"https://cdn.tgdd.vn/Products/Images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win103.jpg\" title=\"Acer Swift SF114 thiết kế sang trọng\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/laptop?g=khoang-14\" target=\"_blank\" title=\"Laptop chính hãng 14 inch bán tại Thế Giới Di Động\" type=\"Laptop chính hãng 14 inch bán tại Thế Giới Di Động\">Laptop 14 inch</a>&nbsp;Full HD chống ch&oacute;i, g&oacute;c nh&igrave;n rộng</h3>\r\n\r\n<p>K&iacute;ch thước m&agrave;n h&igrave;nh&nbsp;<strong>14 inch</strong>&nbsp;cũng gi&uacute;p m&aacute;y c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh vừa phải, dễ nh&igrave;n, h&igrave;nh ảnh kh&ocirc;ng bị qu&aacute; nhỏ g&acirc;y hại mắt khi l&agrave;m việc trong thời gian d&agrave;i. Độ ph&acirc;n giải&nbsp;<strong>Full HD</strong>&nbsp;c&ugrave;ng<strong>&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/ips-la-gi-807724\" target=\"_blank\" title=\"Tìm hiểu thêm về tấm nền IPS trên laptop\" type=\"Tìm hiểu thêm về tấm nền IPS trên laptop\">tấm nền IPS</a></strong>&nbsp;cho g&oacute;c nh&igrave;n rộng 178 độ gi&uacute;p bạn trải nghiệm h&igrave;nh ảnh đẹp, sắc n&eacute;t v&agrave; ch&acirc;n thực ở mọi ph&iacute;a.</p>\r\n\r\n<p>C&ocirc;ng nghệ&nbsp;<strong>chống ch&oacute;i</strong>&nbsp;Acer ComfyView cho bạn thoải m&aacute;i tương t&aacute;c với m&agrave;n h&igrave;nh ngay cả khi dưới trời nắng to.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win101.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Full HD trên Acer Swift SF114\" src=\"https://cdn.tgdd.vn/Products/Images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win101.jpg\" title=\"Màn hình Full HD trên Acer Swift SF114\" /></a></p>\r\n\r\n<p>Bản lề 180 độ linh hoạt</p>\r\n\r\n<p>Khi bạn phải l&agrave;m việc nh&oacute;m thường xuy&ecirc;n th&igrave; việc xoay laptop li&ecirc;n tục về người đối diện đ&ocirc;i khi cũng g&acirc;y kh&oacute; chịu. Với Acer Swift th&igrave; bạn c&oacute; thể mở thoải m&aacute;i m&agrave;n h&igrave;nh đến 180 độ m&agrave; chẳng sợ g&atilde;y bản lề, tiện lợi khi sử dụng đ&ocirc;ng người.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win10-16.jpg\" onclick=\"return false;\"><img alt=\"Acer Swift SF114 32 C9FV N4000 bản lề mở được 180 độ \" src=\"https://cdn.tgdd.vn/Products/Images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win10-16.jpg\" title=\"Acer Swift SF114 32 C9FV N4000 bản lề mở được 180 độ \" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh ổn cho d&acirc;n văn ph&ograve;ng</h3>\r\n\r\n<p>Bộ vi xử l&yacute;&nbsp;<strong>Chip</strong>&nbsp;<strong>Intel Celeron</strong>&nbsp;N4000 c&ugrave;ng&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Laptop chính hãng trang bị 4GB RAM đang kinh doanh tại Thegioididong.com\">4 GB RAM</a></strong>&nbsp;xử l&yacute; tốt c&aacute;c ứng dụng học tập, soạn thảo văn bản tr&ecirc;n Word hay t&iacute;nh to&aacute;n với Excel cũng như giải tr&iacute; nhẹ nh&agrave;ng h&agrave;ng ng&agrave;y như xem phim, chơi Bắn g&agrave;,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win10-17.jpg\" onclick=\"return false;\"><img alt=\"Acer Swift SF114 32 C9FV N4000 cấu hình đáp ứng nhu cầu văn phòng\" src=\"https://cdn.tgdd.vn/Products/Images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win10-17.jpg\" title=\"Acer Swift SF114 32 C9FV N4000 cấu hình đáp ứng nhu cầu văn phòng\" /></a></p>\r\n\r\n<h3>Thời lượng pin l&ecirc;n đến 20 tiếng</h3>\r\n\r\n<p>D&ugrave; mỏng nhẹ, nhỏ gọn như vậy nhưng&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer-swift\" target=\"_blank\" title=\"Xem thêm các Laptop Acer Swift đang bán tại Thegioididong.com\" type=\"Xem thêm các Laptop Acer Swift đang bán tại Thegioididong.com\">laptop Acer&nbsp;</a><a href=\"https://www.thegioididong.com/laptop-acer-swift\" target=\"_blank\" title=\"Xem thêm các Laptop Acer Swift đang bán tại Thegioididong.com\" type=\"Xem thêm các Laptop Acer Swift đang bán tại Thegioididong.com\">Swift</a>&nbsp;c&oacute; thời lượng pin v&ocirc; c&ugrave;ng ấn tượng. Đối với nhu cầu l&agrave;m việc văn ph&ograve;ng v&agrave; giải tr&iacute; th&ocirc;ng thường, m&aacute;y c&oacute; thể sử dụng được tới<strong>&nbsp;20 tiếng</strong>. C&ocirc;ng việc v&agrave; c&aacute;c bộ phim d&agrave;i tập của bạn sẽ được diễn ra li&ecirc;n tục m&agrave; kh&ocirc;ng ngại bị gi&aacute;n đoạn, mang đến cảm gi&aacute;c thoải m&aacute;i khi sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win107-1.jpg\" onclick=\"return false;\"><img alt=\"Acer Swift SF114 có thời lượng pin 20 giờ\" src=\"https://cdn.tgdd.vn/Products/Images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win107-1.jpg\" title=\"Acer Swift SF114 có thời lượng pin 20 giờ\" /></a></p>\r\n\r\n<h3>Tận hưởng &acirc;m thanh sống động sau thời gian l&agrave;m việc mệt mỏi</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-am-thanh-trueharmony-co-gi-dac-biet-1017616\" target=\"_blank\" title=\"Công nghệ âm thanh TrueHarmony có gì đặc biệt?\" type=\"Công nghệ âm thanh TrueHarmony có gì đặc biệt?\">C&ocirc;ng nghệ TrueHarmony</a>&nbsp;cho &acirc;m thanh sống động, ch&acirc;n thực. Sử dụng loa k&eacute;p stereo cho &acirc;m thanh hai chiều gi&uacute;p trải nghiệm &acirc;m thanh khi chơi game, xem phim hay hơn. C&ocirc;ng nghệ n&agrave;y c&ograve;n gi&uacute;p t&aacute;i tạo &acirc;m thanh v&ograve;m, tăng cường bass mang lại &acirc;m thanh mạnh mẽ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win105.jpg\" onclick=\"return false;\"><img alt=\"Acer Swift SF114 sử dụng công nghệ âm thanh Trueharmony\" src=\"https://cdn.tgdd.vn/Products/Images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win105.jpg\" title=\"Acer Swift SF114 sử dụng công nghệ âm thanh Trueharmony\" /></a></p>\r\n\r\n<h3>Bảo mật v&acirc;n tay th&ocirc;ng minh</h3>\r\n\r\n<p>Nằm ở ph&acirc;n kh&uacute;c laptop gi&aacute; rẻ nhưng m&aacute;y vẫn được trang bị t&iacute;nh năng bảo mật bằng v&acirc;n tay. Với t&iacute;nh năng n&agrave;y, bạn sẽ kh&ocirc;ng cần ghi nhớ v&agrave; nhập chuỗi mật khẩu d&agrave;i d&ograve;ng, chỉ cần 1 lần chạm l&agrave; đ&atilde; c&oacute; thể truy cập v&agrave; sử dụng laptop.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win104.jpg\" onclick=\"return false;\"><img alt=\"Acer Swift SF114 có bảo mật vân tay\" src=\"https://cdn.tgdd.vn/Products/Images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win104.jpg\" title=\"Acer Swift SF114 có bảo mật vân tay\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/hoi-dap/usb-typec-chuan-muc-cong-ket-noi-moi-723760\" target=\"_blank\" title=\"Cổng kết nối USB Type-C có những tiện lợi nào?\" type=\"Cổng kết nối USB Type-C có những tiện lợi nào?\">USB Type-C</a>&nbsp;hiện đại</h3>\r\n\r\n<p>Acer đ&atilde; trang bị cho laptop cổng kết nối&nbsp;<strong>USB Type C</strong>&nbsp;đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra cổng n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win106.jpg\" onclick=\"return false;\"><img alt=\"Cổng kết nối đa dạng trên Acer Swift SF114\" src=\"https://cdn.tgdd.vn/Products/Images/44/202320/acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win106.jpg\" title=\"Cổng kết nối đa dạng trên Acer Swift SF114\" /></a></p>\r\n', 0, 0, 'acer-swift-sf114-32-c9fv-n4000-4gb-64gb-14f-win10-600x600.jpg', 9990000),
(47, 'Laptop Lenovo Ideapad YOGA 530 14IKB i3', 3, 'Laptop', '<p>Được thiết kế thanh lịch đầy trang nh&atilde;,&nbsp;<a href=\"https://www.thegioididong.com/laptop/lenovo-ideapad-yoga-530-14ikb-7130u\" target=\"_blank\" title=\"Laptop Lenovo Ideapad YOGA 530 14IKB chính hãng đang được kinh doanh tại Thegioididong.com\">laptop Lenovo Ideapad Yoga 530 14IKB</a>mang đến cho sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng 1 sự lựa chọn tốt ph&ugrave; hợp với nhu cầu sử dụng. B&ecirc;n cạnh cấu h&igrave;nh khoẻ chạy kh&aacute; mượt c&aacute;c ứng dụng đồ hoạ cơ bản như photoshop,&nbsp;coreldraw.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/193392/Slider/vi-vn-lenovo-ideapad-yoga-530-14ikb-7130u-333997-1.jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: D&acirc;y nguồn,Hộp,B&uacute;t cảm ứng,S&aacute;ch hướng dẫn</p>\r\n\r\n<p>-</p>\r\n\r\n<h2>Được thiết kế thanh lịch đầy trang nh&atilde;,&nbsp;<a href=\"https://www.thegioididong.com/laptop/lenovo-ideapad-yoga-530-14ikb-7130u\" target=\"_blank\" title=\"Laptop Lenovo Ideapad YOGA 530 14IKB chính hãng đang được kinh doanh tại Thegioididong.com\">laptop Lenovo Ideapad Yoga 530 14IKB</a>mang đến cho sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng 1 sự lựa chọn tốt ph&ugrave; hợp với nhu cầu sử dụng. B&ecirc;n cạnh cấu h&igrave;nh khoẻ chạy kh&aacute; mượt c&aacute;c ứng dụng đồ hoạ cơ bản như photoshop,&nbsp;coreldraw.</h2>\r\n\r\n<h3>M&agrave;n h&igrave;nh 14 inch xem phim ch&acirc;n thực v&agrave; sắc n&eacute;t</h3>\r\n\r\n<p>M&aacute;y được trang bị&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=khoang-14\" target=\"_blank\" title=\"Laptop màn hình 14 inch đang được kinh doanh chính hãng tại Thegioididong.com\">m&agrave;n h&igrave;nh 14 inch</a>, độ ph&acirc;n giải HD (1366 x 768) hiển thị sắc n&eacute;t c&aacute;c chi tiết nhỏ gi&uacute;p bạn xem phim, giải tr&iacute; ch&acirc;n thật.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/193392/lenovo-ideapad-yoga-530-14ikb-7130u-33397-manhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Màn hình sắc nét trên Màn hinh sắc nét trên Laptop Lenovo Ideapad YOGA 530 14IKB i3 7130U\" src=\"https://cdn.tgdd.vn/Products/Images/44/193392/lenovo-ideapad-yoga-530-14ikb-7130u-33397-manhinhtgdd.jpg\" title=\"Màn hình sắc nét trên Màn hinh sắc nét trên Laptop Lenovo Ideapad YOGA 530 14IKB i3 7130U\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh xử l&yacute; đồ hoạ cơ bản</h3>\r\n\r\n<p>L&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Laptop sinh viên chính hãng đang được kinh doanh tại Thegioididong.com\">laptop sinh vi&ecirc;n</a>&nbsp;- Lenovo&nbsp;Ideapad Yoga 530 14IKB được trang bị sức mạnh đủ khoẻ để chạy c&aacute;c ứng dụng văn ph&ograve;ng, đồ hoạ cơ bản với&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=core-i3\" target=\"_top\" title=\"Laptop core i3 đang được kinh doanh chính hãng tại Thegioididong.com\">CPU core i3&nbsp;</a>7130U kết hợp c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Laptop 4GB RAM đang được kinh doanh chính hãng tại Thegioididong.com\">4GB RAM</a>&nbsp;DDR4 c&aacute;c phản hồi thao t&aacute;c k&eacute;o thả trong photoshop kh&ocirc;ng c&oacute; độ trễ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/193392/lenovo-ideapad-yoga-530-14ikb-7130u-33397-cauhinhtgdd.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng ổn định trên Màn hinh sắc nét trên Laptop Lenovo Ideapad YOGA 530 14IKB i3 7130U\" src=\"https://cdn.tgdd.vn/Products/Images/44/193392/lenovo-ideapad-yoga-530-14ikb-7130u-33397-cauhinhtgdd.jpg\" title=\"Hiệu năng ổn định trên Màn hinh sắc nét trên Laptop Lenovo Ideapad YOGA 530 14IKB i3 7130U\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/laptop?f=o-cung-ssd\" target=\"_blank\" title=\"Laptop trang bị ổ cứng SSD đang được kinh doanh tại Thegioididong.com\">Ổ cứng SSD</a>&nbsp;khởi động m&aacute;y, ứng dụng nặng nhanh ch&oacute;ng</h3>\r\n\r\n<p>Ổ cứng SSD 128GB tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/laptop-lenovo\" target=\"_blank\" title=\"Laptop Lenovo chính hãng đang được kinh doanh tại Thegioididong.com\">laptop Lenovo</a>&nbsp;Ideapad Yoga 530 14IKB&nbsp;&nbsp;ngo&agrave;i việc khởi động nhanh c&aacute;c ứng dụng nặng, c&ograve;n gi&uacute;p bạn khởi động m&aacute;y nhanh ch&oacute;ng gi&uacute;p n&acirc;ng cao hiệu quả l&agrave;m việc.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/44/193392/lenovo-ideapad-yoga-530-14ikb-7130u-33397-ssdtgdd.gif\" onclick=\"return false;\"><img alt=\"Ổ cứng SSD khỏi động nhanh chóng trên Laptop Lenovo Ideapad YOGA 530\" src=\"https://cdn.tgdd.vn/Products/Images/44/193392/lenovo-ideapad-yoga-530-14ikb-7130u-33397-ssdtgdd.gif\" title=\"Ổ cứng SSD khỏi động nhanh chóng trên Laptop Lenovo Ideapad YOGA 530\" /></a></h2>\r\n\r\n<h3>Cổng USB Type C tiết kiệm thời gian chuyển dữ liệu</h3>\r\n\r\n<p>Ngo&agrave;i c&aacute;c cổng kết nối cơ bản, m&aacute;y được trang bị cổng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-typec-chuan-muc-cong-ket-noi-moi-723760\" target=\"_blank\" title=\"USB type C là gì? | Thegioididong.com\">USB type C</a>&nbsp;v&agrave; 2 cổng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-30-la-gi-926737\" target=\"_blank\" title=\"Phân biệt cổng USB 3.0 và USB 2.0 ? | Thegioididong.com\">USB 3.0&nbsp;</a>cải thiện thời gian chờ khi chuyển file nặng với tốc độ tối đa l&ecirc;n đến 600MB/s.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/193392/lenovo-ideapad-yoga-530-14ikb-7130u-33397-conketnoi.jpg\" onclick=\"return false;\"><img alt=\"Cổng kết nối trên Laptop Lenovo Ideapad YOGA 530 14IKB i3 7130U\" src=\"https://cdn.tgdd.vn/Products/Images/44/193392/lenovo-ideapad-yoga-530-14ikb-7130u-33397-conketnoi.jpg\" title=\"Cổng kết nối trên Laptop Lenovo Ideapad YOGA 530 14IKB i3 7130U\" /></a></p>\r\n', 0, 0, 'laptop-lenovo-ideapad-yoga-530-14ikb-7130u-600x600.jpg', 13940000),
(48, 'Laptop Dell Vostro 14 3480 i5', 3, 'Laptop', '<p><a href=\"https://www.thegioididong.com/laptop/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377\" target=\"_blank\" title=\"Laptop Dell Vostro 3480 i5 8265U/4GB/1TB/Win10 (70183778) chính hãng đang kinh doanh tại cửa hàng Thegioididong\" type=\"Laptop Dell Vostro 3480 i5 8265U/4GB/1TB/Win10 (70183778) chính hãng đang kinh doanh tại cửa hàng Thegioididong\">Dell Vostro 3480 i5 8265U</a>&nbsp;mang trong m&igrave;nh một thiết kế kh&aacute; mỏng nhẹ thuận tiện để mang đi l&agrave;m việc h&agrave;ng ng&agrave;y.&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\" type=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">Laptop</a>&nbsp;được trang bị cấu h&igrave;nh gi&uacute;p chạy mượt m&agrave; c&aacute;c ứng dụng học tập, văn ph&ograve;ng cơ bản, đi k&egrave;m l&agrave; t&iacute;nh năng bảo mật v&acirc;n tay hiện đại. Chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-dell\" target=\"_blank\" title=\"Laptop Dell chính hãng, giá rẻ bán tại Thế Giới Di Động\" type=\"Laptop Dell chính hãng, giá rẻ bán tại Thế Giới Di Động\">laptop Dell</a>&nbsp;n&agrave;y sẽ l&agrave; một sự lựa chọn đ&aacute;ng để c&acirc;n nhắc d&agrave;nh cho nh&acirc;n vi&ecirc;n văn ph&ograve;ng hay học sinh, sinh vi&ecirc;n trong ph&acirc;n kh&uacute;c.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/204129/Slider/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377--(6).jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: S&aacute;ch hướng dẫn, Th&ugrave;ng m&aacute;y, Adapter sạc</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/laptop/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377\" target=\"_blank\" title=\"Laptop Dell Vostro 3480 i5 8265U/4GB/1TB/Win10 (70183778) chính hãng đang kinh doanh tại cửa hàng Thegioididong\" type=\"Laptop Dell Vostro 3480 i5 8265U/4GB/1TB/Win10 (70183778) chính hãng đang kinh doanh tại cửa hàng Thegioididong\">Dell Vostro 3480 i5 8265U</a>&nbsp;mang trong m&igrave;nh một thiết kế kh&aacute; mỏng nhẹ thuận tiện để mang đi l&agrave;m việc h&agrave;ng ng&agrave;y.&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\" type=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">Laptop</a>&nbsp;được trang bị cấu h&igrave;nh gi&uacute;p chạy mượt m&agrave; c&aacute;c ứng dụng học tập, văn ph&ograve;ng cơ bản, đi k&egrave;m l&agrave; t&iacute;nh năng bảo mật v&acirc;n tay hiện đại. Chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-dell\" target=\"_blank\" title=\"Laptop Dell chính hãng, giá rẻ bán tại Thế Giới Di Động\" type=\"Laptop Dell chính hãng, giá rẻ bán tại Thế Giới Di Động\">laptop Dell</a>&nbsp;n&agrave;y sẽ l&agrave; một sự lựa chọn đ&aacute;ng để c&acirc;n nhắc d&agrave;nh cho nh&acirc;n vi&ecirc;n văn ph&ograve;ng hay học sinh, sinh vi&ecirc;n trong ph&acirc;n kh&uacute;c.</h2>\r\n\r\n<h3>Thiết kế gọn nhẹ, thanh lịch</h3>\r\n\r\n<p>M&aacute;y t&iacute;nh sử dụng bộ khung được l&agrave;m từ chất liệu nhựa nhưng được ho&agrave;n thiện tốt n&ecirc;n vẫn đảm bảo sự bền bỉ, chắc chắn. Khối lượng tổng thể kh&aacute; nhẹ với khối lượng 1.79 kg, rất ph&ugrave; hợp để bỏ v&agrave;o t&uacute;i x&aacute;ch đi học, đi l&agrave;m h&agrave;ng ng&agrave;y.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-1-1.jpg\" onclick=\"return false;\"><img alt=\"Laptop Dell Vostro 3480 i5 khá nhẹ với khối lượng 1.79 kg\" src=\"https://cdn.tgdd.vn/Products/Images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-1-1.jpg\" title=\"Laptop Dell Vostro 3480 i5 khá nhẹ với khối lượng 1.79 kg\" /></a></h3>\r\n\r\n<h3>Hiệu năng mạnh mẽ, đa nhiệm ổn</h3>\r\n\r\n<p>Sức mạnh của m&aacute;y đến từ&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=core-i5\" target=\"_blank\" title=\"Laptop Core i5 chính hãng, giá rẻ bán tại Thế Giới Di Động\" type=\"Laptop Core i5 chính hãng, giá rẻ bán tại Thế Giới Di Động\">bộ vi xử l&yacute; Intel Core i5</a>&nbsp;Coffee Lake thế hệ mới c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Laptop chính hãng trang bị 4GB RAM đang kinh doanh tại Thegioididong.com\">4 GB RAM</a>&nbsp;bộ nhớ gi&uacute;p bạn xử l&yacute; tốt từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, bi&ecirc;n tập video, hay giải tr&iacute; cấu h&igrave;nh tầm trung. Khả năng lưu trữ c&aacute;c tệp tin cũng v&ocirc; c&ugrave;ng thoải m&aacute;i nhờ ổ cứng HDD dung lượng 1 TB. Ngo&agrave;i ra, laptop c&oacute; hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cac-chuan-toc-do-cua-o-cung-ssd-tren-1115453\" target=\"_blank\" title=\"Tìm hiểu về các chuẩn tốc độ của ổ cứng SSD trên laptop cùng Thegioididong.com\">khe cắm SSD</a>&nbsp;cho người d&ugrave;ng c&oacute; nhu cầu n&acirc;ng cấp tốc độ của m&aacute;y.</p>\r\n\r\n<p>Đối với kh&aacute;ch h&agrave;ng th&iacute;ch chơi game giải tr&iacute; nhẹ nh&agrave;ng th&igrave; laptop cũng c&oacute; thể đ&aacute;p ứng được nhờ card đồ họa t&iacute;ch hợp Intel UHD Graphics 620.&nbsp;</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-2-1.jpg\" onclick=\"return false;\"><img alt=\"Laptop Dell Vostro 3480 i5 giúp bạn xử lý tốt từ các việc đơn giản đến các công việc mang tính chất đòi hỏi cao\" src=\"https://cdn.tgdd.vn/Products/Images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-2-1.jpg\" title=\"Laptop Dell Vostro 3480 i5 giúp bạn xử lý tốt từ các việc đơn giản đến các công việc mang tính chất đòi hỏi cao\" /></a></h3>\r\n\r\n<h3>M&agrave;n h&igrave;nh laptop 14 inch</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop?g=khoang-14\" target=\"_blank\" title=\"Laptop chính hãng 14 inch bán tại Thế Giới Di Động\" type=\"Laptop chính hãng 14 inch bán tại Thế Giới Di Động\">M&agrave;n h&igrave;nh laptop 14 inch</a>&nbsp;c&oacute; độ ph&acirc;n giải HD, bạn sẽ thấy mọi h&igrave;nh ảnh hiển thị tr&ecirc;n laptop đều sắc n&eacute;t v&agrave; sống động, đồng thời tiết kiệm điện năng hơn so với m&agrave;n Full HD.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-3-1.jpg\" onclick=\"return false;\"><img alt=\"Laptop Dell Vostro 3480 i5  có độ phân giải HD\" src=\"https://cdn.tgdd.vn/Products/Images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-3-1.jpg\" title=\"Laptop Dell Vostro 3480 i5  có độ phân giải HD\" /></a></h3>\r\n\r\n<h3>B&agrave;n ph&iacute;m v&agrave; TouchPad</h3>\r\n\r\n<p>B&agrave;n ph&iacute;m của laptop c&oacute; thiết kế truyền thống với khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m hợp l&yacute;, vừa phải cho trải nghiệm tốt khi nhập dữ liệu. TouchPad c&oacute; diện t&iacute;ch kh&aacute; rộng v&agrave; hỗ trợ đa chạm, nhờ đ&oacute; m&aacute;y cho một kh&ocirc;ng gian thao t&aacute;c lớn để thực hiện c&aacute;c thao t&aacute;c.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-4-1.jpg\" onclick=\"return false;\"><img alt=\"Laptop Dell Vostro 3480 i5  có thiết kế truyền thống với khoảng cách giữa các phím hợp lý\" src=\"https://cdn.tgdd.vn/Products/Images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-4-1.jpg\" title=\"Laptop Dell Vostro 3480 i5  có thiết kế truyền thống với khoảng cách giữa các phím hợp lý\" /></a></h3>\r\n\r\n<h3>Mở kh&oacute;a nhanh ch&oacute;ng với cảm biến v&acirc;n tay</h3>\r\n\r\n<p>Với t&iacute;nh năng bảo mật v&acirc;n tay th&igrave; bạn sẽ kh&ocirc;ng cần ghi nhớ v&agrave; nhập chuỗi mật khẩu từ b&agrave;n ph&iacute;m để mở kh&oacute;a nữa. Tất cả những g&igrave; bạn cần l&agrave;m để mở kh&oacute;a l&agrave; 1 lần chạm tay, v&ocirc; c&ugrave;ng tiện lợi v&agrave; khả năng bảo mật cũng được n&acirc;ng cao.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-6-1.jpg\" onclick=\"return false;\"><img alt=\"Laptop Dell Vostro 3480 i5 không cần ghi nhớ và nhập chuỗi mật khẩu từ bàn phím để mở khóa\" src=\"https://cdn.tgdd.vn/Products/Images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-6-1.jpg\" title=\"Laptop Dell Vostro 3480 i5 không cần ghi nhớ và nhập chuỗi mật khẩu từ bàn phím để mở khóa\" /></a></h3>\r\n\r\n<h3>Cổng kết nối tiện lợi</h3>\r\n\r\n<p>Được trang bị c&aacute;c cổng kết nối cơ bản như:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-31-la-gi-926746\" target=\"_blank\" title=\"Giao thức truyền dữ liệu USB 3.1 là gì? \" type=\"Giao thức truyền dữ liệu USB 3.1 là gì? \">cổng USB 3.1</a>, USB 2.0, USB 3.0 gi&uacute;p sao ch&eacute;p dữ liệu nhanh ch&oacute;ng. Cổng&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/hdmi-la-gi--590061\" target=\"_blank\" title=\"Kết nối HDMI là gì?\" type=\"Kết nối HDMI là gì?\">HDMI</a>&nbsp;hỗ trợ kết nối với m&agrave;n h&igrave;nh lớn như tivi, m&aacute;y chiếu tr&igrave;nh chiếu h&igrave;nh ảnh, &acirc;m thanh trong c&aacute;c buổi họp, hội thảo. Đặc biệt, cổng LAN tiện dụng cho người d&ugrave;ng c&oacute; thể kết nối mạng trực tiếp khi t&iacute;n hiệu&nbsp;đường truyền wifi kh&ocirc;ng được ổn định.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-5-1.jpg\" onclick=\"return false;\"><img alt=\"Laptop Dell Vostro 3480 i5  trang bị các cổng kết nối cơ bản như: cổng USB 3.1, USB 2.0, USB 3.0 \" src=\"https://cdn.tgdd.vn/Products/Images/44/204129/dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-5-1.jpg\" title=\"Laptop Dell Vostro 3480 i5  trang bị các cổng kết nối cơ bản như: cổng USB 3.1, USB 2.0, USB 3.0 \" /></a></p>\r\n', 1, 0, 'dell-vostro-3480-i5-8265u-4gb-1tb-14-win10-7018377-6-1-1-600x600.jpg', 14990000),
(49, 'Laptop HP Pavilion 15 cs2031TU i3', 3, 'Laptop', '<p><a href=\"https://www.thegioididong.com/laptop/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10\" target=\"_blank\" title=\"Xem chi tiết Laptop HP Pavilion 15 cs2031TU\" type=\"Xem chi tiết Laptop HP Pavilion 15 cs2031TU\">Laptop HP Pavilion 15 cs2031TU</a>&nbsp;l&agrave; sản phẩm được h&atilde;ng&nbsp;HP đầu tư tỉ mỉ từ bề ngo&agrave;i cho đến cấu h&igrave;nh b&ecirc;n trong v&agrave; được b&aacute;n với mức gi&aacute; vừa phải ph&ugrave; hợp với nhiều kh&aacute;ch h&agrave;ng.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/44/204636/Slider/hp-pavilion-15-cs2031tu-i3-8145u4gb1tb15-(1).jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/laptop/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10\" target=\"_blank\" title=\"Xem chi tiết Laptop HP Pavilion 15 cs2031TU\" type=\"Xem chi tiết Laptop HP Pavilion 15 cs2031TU\">Laptop HP Pavilion 15 cs2031TU</a>&nbsp;l&agrave; sản phẩm được h&atilde;ng&nbsp;HP đầu tư tỉ mỉ từ bề ngo&agrave;i cho đến cấu h&igrave;nh b&ecirc;n trong v&agrave; được b&aacute;n với mức gi&aacute; vừa phải ph&ugrave; hợp với nhiều kh&aacute;ch h&agrave;ng.</h2>\r\n\r\n<h3>Thiết kế gọn nhẹ, thanh lịch</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\" type=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">Laptop</a>&nbsp;HP Pavilion 15 cs2031TU sở hữu tr&ecirc;n m&igrave;nh lớp vỏ nhựa cao cấp chỉ nặng&nbsp;<strong>1.75 kg</strong>&nbsp;v&agrave; phần nắp lưng bằng kim loại nhằm tăng th&ecirc;m vẻ sang trọng cho sản phẩm, tạo cảm gi&aacute;c m&aacute;t tay khi sờ v&agrave;o. Với trọng lượng kh&ocirc;ng qu&aacute; nặng như vậy th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/laptop-hp-compaq\" target=\"_blank\" title=\"Laptop HP chính hãng đang được kinh doanh tại Thegioididong.com\" type=\"Laptop HP chính hãng đang được kinh doanh tại Thegioididong.com\">laptop HP</a>&nbsp;Pavilion sẽ rất th&iacute;ch hợp với những ai phải thường xuy&ecirc;n di chuyển.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-2.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP Pavilion 15 cs2031TU  sở hữu trên mình lớp vỏ nhựa cao cấp\" src=\"https://cdn.tgdd.vn/Products/Images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-2.jpg\" title=\"Laptop HP Pavilion 15 cs2031TU  sở hữu trên mình lớp vỏ nhựa cao cấp\" /></a></h3>\r\n\r\n<h3>Trải nghiệm chất lượng h&igrave;nh ảnh đỉnh cao</h3>\r\n\r\n<p>Với k&iacute;ch thước rộng&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=tren-15\" target=\"_blank\" title=\"Laptop chính hãng 15 inch bán tại Thế Giới Di Động\" type=\"Laptop chính hãng 15 inch bán tại Thế Giới Di Động\">15.6 inch</a></strong>, độ ph&acirc;n giải&nbsp;<strong>Full HD</strong>&nbsp;(1920 x 1080 pixels) v&agrave; m&agrave;n h&igrave;nh&nbsp;<strong>chống ch&oacute;i</strong>, laptop HP Pavilion sẽ đem đến cho bạn những gi&acirc;y ph&uacute;t thư gi&atilde;n c&ugrave;ng bộ phim y&ecirc;u th&iacute;ch.&nbsp;<strong>Tấm nền IPS</strong>tăng cường độ tương phản m&agrave;u cho h&igrave;nh ảnh rực rỡ sẽ khiến bạn phải ngất ng&acirc;y.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-1.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP Pavilion 15 cs2031TU kích thước rộng 15,6 inch, độ phân giải Full HD \" src=\"https://cdn.tgdd.vn/Products/Images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-1.jpg\" title=\"Laptop HP Pavilion 15 cs2031TU kích thước rộng 15,6 inch, độ phân giải Full HD \" /></a></h3>\r\n\r\n<h3>Cấu h&igrave;nh ổn, sử dụng văn ph&ograve;ng</h3>\r\n\r\n<p>Chip&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=core-i3\" target=\"_blank\" title=\"Laptop Core i3 chính hãng, giá rẻ bán tại Thế Giới Di Động\" type=\"Laptop Core i3 chính hãng, giá rẻ bán tại Thế Giới Di Động\">Core i3</a>&nbsp;</strong>thế hệ thứ 8 với tốc độ 2.10 GHz c&ugrave;ng với bộ<strong>&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Laptop chính hãng trang bị 4GB RAM đang kinh doanh tại Thegioididong.com\">4 GB RAM</a>&nbsp;</strong>DDR4 gi&uacute;p chạy tốt c&aacute;c c&ocirc;ng cụ văn ph&ograve;ng như Word, Excel, Power Point v&agrave; c&aacute;c nhu cầu giải tr&iacute; cơ bản như nghe nhạc, lướt web, xem phim.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-3.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP Pavilion 15 cs2031TU  giúp chạy tốt các công cụ văn phòng\" src=\"https://cdn.tgdd.vn/Products/Images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-3.jpg\" title=\"Laptop HP Pavilion 15 cs2031TU  giúp chạy tốt các công cụ văn phòng\" /></a></p>\r\n\r\n<h3>&Acirc;m thanh to r&otilde; gi&agrave;u chi tiết</h3>\r\n\r\n<p>C&ocirc;ng nghệ Realtek High Definition Audio l&agrave; một trong những phần mềm về &acirc;m thanh phổ biến nhất, l&agrave; tr&igrave;nh quản l&yacute; &acirc;m thanh tuyệt vời tr&ecirc;n m&aacute;y t&iacute;nh. Sở hữu<a href=\"https://www.thegioididong.com/laptop-hp-compaq-pavilion\" target=\"_blank\" title=\"Xem thêm các sản phẩm Hp Pavilion đang bán tại Thegioididong.com\" type=\"Xem thêm các sản phẩm Hp Pavilion đang bán tại Thegioididong.com\">&nbsp;laptop HP Pavilion</a>&nbsp;c&ugrave;ng c&ocirc;ng nghệ &acirc;m thanh ti&ecirc;n tiến, bạn đ&atilde; kh&ocirc;ng bỏ lỡ chất lượng &acirc;m thanh tốt, sống động v&agrave; chi tiết nhất.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-4.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP Pavilion 15 cs2031TU chất lượng âm thanh tốt, sống động và chi tiết nhất.\" src=\"https://cdn.tgdd.vn/Products/Images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-4.jpg\" title=\"Laptop HP Pavilion 15 cs2031TU chất lượng âm thanh tốt, sống động và chi tiết nhất.\" /></a></h3>\r\n\r\n<h3>Ổ cứng dung lượng lớn</h3>\r\n\r\n<p>Bạn đ&atilde; từng đau đầu v&igrave; bộ nhớ m&aacute;y t&iacute;nh đầy? Đừng lo, với&nbsp;laptop HP n&agrave;y, kh&ocirc;ng gian lưu trữ cho bạn l&agrave; cực k&igrave; thoải m&aacute;i. Với dung lượng 1 TB bạn c&oacute; thể lưu trữ h&agrave;ng trăm bộ phim ngắn chất lượng cao, lưu trữ c&aacute;c file c&agrave;i đặt game, nhạc,...<a href=\"https://www.thegioididong.com/images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-5.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP Pavilion 15 cs2031TU có ổ cứng HDD 1 TB\" src=\"https://cdn.tgdd.vn/Products/Images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-5.jpg\" title=\"Laptop HP Pavilion 15 cs2031TU có ổ cứng HDD 1 TB\" /></a></p>\r\n\r\n<h3>Xuất h&igrave;nh ảnh chất lượng cao</h3>\r\n\r\n<p>Việc thuyết tr&igrave;nh trong c&aacute;c lớp học hay tr&igrave;nh chiếu dự thảo phương hướng với c&ocirc;ng ty sẽ trở n&ecirc;n thuận tiện hơn với cổng HDMI tiện lợi. Ngo&agrave;i ra, laptop c&ograve;n được trang bị 2 cổng&nbsp;<strong><a href=\"https://www.thegioididong.com/hoi-dap/usb-31-la-gi-926746\" target=\"_blank\" title=\"Giao thức truyền dữ liệu USB 3.1 là gì? \" type=\"Giao thức truyền dữ liệu USB 3.1 là gì? \">USB 3.1</a>, LAN (RJ45),&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/usb-typec-chuan-muc-cong-ket-noi-moi-723760\" target=\"_blank\" title=\"Cổng kết nối USB Type-C có những tiện lợi nào?\" type=\"Cổng kết nối USB Type-C có những tiện lợi nào?\">USB Type-C</a>,&nbsp;</strong>hỗ trợ tối đ&atilde; việc truyền tải th&ocirc;ng tin.</p>\r\n\r\n<p><strong><a href=\"https://www.thegioididong.com/images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-6-1.jpg\" onclick=\"return false;\"><img alt=\"Laptop HP Pavilion 15 hỗ trợ tối đã việc truyền tải thông tin.\" src=\"https://cdn.tgdd.vn/Products/Images/44/204636/hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-6-1.jpg\" title=\"Laptop HP Pavilion 15 hỗ trợ tối đã việc truyền tải thông tin.\" /></a></strong></p>\r\n', 0, 0, 'hp-pavilion-15-cs2031tu-i3-8145u-4gb-1tb-156-win10-7-600x600.jpg', 13290000),
(50, 'Dây cáp Micro USB 1 m eValu LTM-01', 4, 'Phụ kiện', '<ul>\r\n	<li>Th&acirc;n d&acirc;y được bảo vệ bằng sợi nilon gi&uacute;p hạn chế đứt g&atilde;y.</li>\r\n	<li>D&ugrave;ng được cho c&aacute;c thiết bị d&ugrave;ng cổng Micro USB.</li>\r\n	<li>Chất lượng tương đương với h&agrave;ng ch&iacute;nh h&atilde;ng.</li>\r\n	<li>D&ugrave;ng để ch&eacute;p dữ liệu hay sạc pin (d&ugrave;ng với adapter ri&ecirc;ng).</li>\r\n	<li>D&acirc;y 1 m thoải m&aacute;i vừa sạc vừa d&ugrave;ng điện thoại.</li>\r\n</ul>\r\n', '<h2><strong>D&acirc;y c&aacute;p Micro USB 1 m eValu LTM-01&nbsp;c&oacute; thiết kế đẹp mắt quen thuộc, m&agrave;u sắc bắt mắt, chất lượng tốt đảm bảo an to&agrave;n cho người d&ugrave;ng v&agrave; thiết bị khi sử dụng.</strong></h2>\r\n\r\n<h3><strong>Ph&ugrave; hợp với nhiều loại thiết bị</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\" title=\"Phụ kiện sạc, cáp các loại\">C&aacute;p</a>&nbsp;thiết kế với 1 đầu micro USB tương th&iacute;ch tốt với hầu hết smartphone như Samsung, HTC, LG, Lumia, Oppo, Sony...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/88357/cap-micro-1m-evalu-ltm-01-do-1-4.jpg\" onclick=\"return false;\"><img alt=\"Dây cáp Micro USB 1 m eValu LTM-01 - Phù hợp với nhiều loại thiết bị\" src=\"https://cdn.tgdd.vn/Products/Images/58/88357/cap-micro-1m-evalu-ltm-01-do-1-4.jpg\" title=\"Cáp Micro 1m eValu LTM-01 - Phù hợp với nhiều loại thiết bị\" /></a></p>\r\n\r\n<p>Đầu c&aacute;p USB tương th&iacute;ch với mọi adapter hay cổng tr&ecirc;n pin sạc,&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop các thương hiệu\">laptop</a>&hellip;</p>\r\n\r\n<h3><strong>Sạc pin, truyền tải dữ liệu cho thiết bị (<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại các thương hiệu\">điện thoại</a></strong><strong>,&nbsp;</strong><strong><a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các thương hiệu\">m&aacute;y t&iacute;nh bảng</a></strong><strong>&hellip;)</strong></h3>\r\n\r\n<p>Đầu c&aacute;p được gia c&ocirc;ng tỉ mỉ đảm bảo truyền tải dữ liệu ổn định khi kết nối với laptop, gi&uacute;p bạn sạc pin cho thiết bị từ nguồn điện hay từ&nbsp;<a href=\"https://www.thegioididong.com/pin-sac-du-phong\" target=\"_blank\" title=\"Phụ kiện pin sạc các loại\">pin sạc dự ph&ograve;ng</a>&hellip;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/88357/cap-micro-1m-evalu-ltm-01-do-1-5.jpg\" onclick=\"return false;\"><img alt=\"Dây cáp Micro USB 1 m eValu LTM-01 - Sạc pin cho điện thoại bằng pin sạc dự phòng\" src=\"https://cdn.tgdd.vn/Products/Images/58/88357/cap-micro-1m-evalu-ltm-01-do-1-5.jpg\" title=\"Cáp Micro 1m eValu LTM-01 - Sạc pin cho điện thoại bằng pin sạc dự phòng\" /></a></p>\r\n\r\n<p><em>Sạc pin cho điện thoại bằng pin sạc dự ph&ograve;ng</em></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/88357/cap-micro-1m-evalu-ltm-01-do-1-6.jpg\" onclick=\"return false;\"><img alt=\"Dây cáp Micro USB 1 m eValu LTM-01 - Kết nối với laptop truyền tải dữ liệu dễ dàng\" src=\"https://cdn.tgdd.vn/Products/Images/58/88357/cap-micro-1m-evalu-ltm-01-do-1-6.jpg\" title=\"Cáp Micro 1m eValu LTM-01 - Kết nối với laptop truyền tải dữ liệu dễ dàng\" /></a></p>\r\n\r\n<p><em>Kết nối với laptop truyền tải dữ liệu dễ d&agrave;ng</em></p>\r\n\r\n<h3><strong>Thiết kế dễ mang theo b&ecirc;n m&igrave;nh</strong></h3>\r\n\r\n<p>C&aacute;p c&oacute; chiều d&agrave;i d&acirc;y 1m gi&uacute;p việc kết nối c&aacute;c thiết bị với nhau dễ d&agrave;ng, đồng thời người d&ugrave;ng c&oacute; thể cuộn lại d&acirc;y gọn nhẹ thuận tiện hơn cho việc mang theo mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/88357/cap-micro-1m-evalu-ltm-01-do-1-1.jpg\" onclick=\"return false;\"><img alt=\"Dây cáp Micro USB 1 m eValu LTM-01 - Thiết kế dễ mang theo bên mình\" src=\"https://cdn.tgdd.vn/Products/Images/58/88357/cap-micro-1m-evalu-ltm-01-do-1-1.jpg\" title=\"Cáp Micro 1m eValu LTM-01 - Thiết kế dễ mang theo bên mình\" /></a></p>\r\n', 0, 0, 'cap-micro-1m-evalu-ltm-01-12-600x600.jpg', 48000),
(51, 'Cáp Lightning 1m Xmobile L-bending', 4, 'Phụ kiện', '<ul>\r\n	<li>Cổng lightning d&ugrave;ng cho d&ograve;ng iPhone 5, iPad 4 trở l&ecirc;n.</li>\r\n	<li>D&ugrave;ng để ch&eacute;p dữ liệu hay sạc pin (d&ugrave;ng với adapter ri&ecirc;ng).</li>\r\n	<li>D&acirc;y d&agrave;i 1 m thoải m&aacute;i để bạn vừa sạc vừa sử dụng khi cần thiết.</li>\r\n</ul>\r\n', '<h3>Thiết kế đẹp mắt, sang trọng</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/cap-dien-thoai/cap-lightning-1m-xmobile-l-bending\" target=\"_blank\" title=\"Cáp Lightning 1m Xmobile L-bending giá rẻ kinh doanh tại Thế Giới Di Động\" type=\"Cáp Lightning 1m Xmobile L-bending giá rẻ kinh doanh tại Thế Giới Di Động\">C&aacute;p sạc Lightning 1m Xmobile L-bending</a>&nbsp;c&oacute; m&agrave;u đỏ đẹp mắt v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai?g=tu-1-m-2-m#f\" target=\"_blank\" title=\"Cáp có độ dài 1 m giá rẻ kinh doanh tại Thế Giới Di Động\" type=\"Cáp có độ dài 1 m giá rẻ kinh doanh tại Thế Giới Di Động\">c&aacute;p c&oacute; độ d&agrave;i 1 m&eacute;t</a>&nbsp;gi&uacute;p người d&ugrave;ng tiện sử dụng v&agrave; mang theo b&ecirc;n m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/201505/cap-lightning-1m-xmobile-l-bending-den-2-org2.jpg\" onclick=\"return false;\"><img alt=\"Cáp Lightning 1m Xmobile L-bending Đen thiết kế sang trọng \" src=\"https://cdn.tgdd.vn/Products/Images/58/201505/cap-lightning-1m-xmobile-l-bending-den-2-org2.jpg\" title=\"Cáp Lightning 1m Xmobile L-bending Đen thiết kế sang trọng \" /></a></p>\r\n\r\n<h3>Thiết kế độc đ&aacute;o với&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai?g=cap-iphone#f\" target=\"_blank\" title=\"Cáp Lightning chính hãng, giá rẻ kinh doanh tại Thế Giới Di Động\" type=\"Cáp Lightning chính hãng, giá rẻ kinh doanh tại Thế Giới Di Động\">cổng kết nối Lightning</a>&nbsp;tiện lợi</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\" title=\"Dây cáp chính hãng, giá rẻ kinh doanh tại Thế Giới Di Động\" type=\"Dây cáp chính hãng, giá rẻ kinh doanh tại Thế Giới Di Động\">D&acirc;y c&aacute;p</a>&nbsp;c&oacute; cổng kết nối USB tiện để gắn v&agrave;o đầu Adapter, sạc dự ph&ograve;ng,... v&agrave; 1 đầu lightning tiện kết nối với iphone, ipad c&oacute; thiết kế bắt mắt tr&aacute;nh g&atilde;y đầu lightning.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/201505/cap-lightning-1m-xmobile-l-bending-den-2-org3.jpg\" onclick=\"return false;\"><img alt=\"Cáp Lightning 1m Xmobile L-bending Đen đầu USB kết nối adapter\" src=\"https://cdn.tgdd.vn/Products/Images/58/201505/cap-lightning-1m-xmobile-l-bending-den-2-org3.jpg\" title=\"Cáp Lightning 1m Xmobile L-bending Đen đầu USB kết nối adapter\" /></a><a href=\"https://www.thegioididong.com/images/58/201505/cap-lightning-1m-xmobile-l-bending-den-2-org4.jpg\" onclick=\"return false;\"><img alt=\"Cáp Lightning 1m Xmobile L-bending Đen đầu lightning kết nối với iphone\" src=\"https://cdn.tgdd.vn/Products/Images/58/201505/cap-lightning-1m-xmobile-l-bending-den-2-org4.jpg\" title=\"Cáp Lightning 1m Xmobile L-bending Đen đầu lightning kết nối với iphone\" /></a></p>\r\n\r\n<h3>Dễ d&agrave;ng sạc pin v&agrave; truyền dữ liệu</h3>\r\n\r\n<p>Sản phẩm tiện dụng gi&uacute;p người d&ugrave;ng sạc pin cho thiết bị từ nguồn điện, hay pin sạc dự ph&ograve;ng; kết nối thiết bị với laptop để truyền tải dữ liệu...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/201505/cap-lightning-1m-xmobile-l-bending-den-2-org1.jpg\" onclick=\"return false;\"><img alt=\"Cáp Lightning 1m Xmobile L-bending Đen truyền dữ liệu \" src=\"https://cdn.tgdd.vn/Products/Images/58/201505/cap-lightning-1m-xmobile-l-bending-den-2-org1.jpg\" title=\"Cáp Lightning 1m Xmobile L-bending Đen truyền dữ liệu \" /></a></p>\r\n', 0, 0, 'cap-lightning-1m-xmobile-l-bending-add-600x600.jpg', 66000),
(52, 'Tai nghe Bluetooth Roman R552N Xanh', 4, 'Phụ kiện', '<ul>\r\n	<li>Họa tiết kẻ ca-r&ocirc; bắt s&aacute;ng đẹp mắt tr&ecirc;n bề mặt tai nghe.</li>\r\n	<li>C&oacute; thể kết nối c&ugrave;ng 1 l&uacute;c 2 thiết bị.</li>\r\n	<li>C&ocirc;ng nghệ bluetooth 4.1 cho khoảng c&aacute;ch kết nối tốt l&ecirc;n đến 10 m.</li>\r\n	<li>C&ocirc;ng nghệ lọc tiếng ồn cho &acirc;m thanh chất lượng khi đ&agrave;m thoại.</li>\r\n	<li>Thời gian thoại 4.5 giờ, nghe nhạc 4.5 giờ, sạc chỉ trong 2 giờ.</li>\r\n	<li>D&ugrave;ng được khi xem video, nghe nhạc, chơi game...</li>\r\n	<li>C&oacute; n&uacute;t ấn nhận cuộc gọi, ngừng/chơi nhạc, tăng giảm &acirc;m lượng.</li>\r\n</ul>\r\n', '<h3>&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/89044/tai-nghe-bluetooth-roman-r552s-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Roman R552S - Thiết kế nhỏ gọn, tinh tế\" src=\"https://cdn.tgdd.vn/Products/Images/54/89044/tai-nghe-bluetooth-roman-r552s-1.jpg\" title=\"Tai nghe Bluetooth Roman R552S - Thiết kế nhỏ gọn, tinh tế\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/89044/tai-nghe-bluetooth-roman-r552s-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Roman R552S - Kích thước đệm tai nghe\" src=\"https://cdn.tgdd.vn/Products/Images/54/89044/tai-nghe-bluetooth-roman-r552s-2.jpg\" title=\"Tai nghe Bluetooth Roman R552S - Kích thước đệm tai nghe\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/89044/tai-nghe-bluetooth-roman-r552s-7.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Roman R552S - Lớp đệm cao su mềm tạo cảm giác êm ái khi đeo\" src=\"https://cdn.tgdd.vn/Products/Images/54/89044/tai-nghe-bluetooth-roman-r552s-7.jpg\" title=\"Tai nghe Bluetooth Roman R552S - Lớp đệm cao su mềm tạo cảm giác êm ái khi đeo\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/89044/tai-nghe-bluetooth-roman-r552s-3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Roman R552S - Khoảng cách đảm bảo kết nối\" src=\"https://cdn.tgdd.vn/Products/Images/54/89044/tai-nghe-bluetooth-roman-r552s-3.jpg\" title=\"Tai nghe Bluetooth Roman R552S - Khoảng cách đảm bảo kết nối\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/89044/tai-nghe-bluetooth-roman-r552s-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Roman R552S - Chức năng\" src=\"https://cdn.tgdd.vn/Products/Images/54/89044/tai-nghe-bluetooth-roman-r552s-4.jpg\" title=\"Tai nghe Bluetooth Roman R552S - Chức năng\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/89044/tai-nghe-bluetooth-roman-r552s-8.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Roman R552S - Công nghệ lọc tiếng ồn khi đàm thoại\" src=\"https://cdn.tgdd.vn/Products/Images/54/89044/tai-nghe-bluetooth-roman-r552s-8.jpg\" title=\"Tai nghe Bluetooth Roman R552S - Công nghệ lọc tiếng ồn khi đàm thoại\" /></a></p>\r\n\r\n<h3><strong>C&aacute;ch kết nối</strong></h3>\r\n\r\n<p>- Bạn&nbsp;<strong>gạt n&uacute;t nguồn</strong>&nbsp;để mở tai nghe, l&uacute;c n&agrave;y sẽ nghe tai nghe đọc&nbsp;<strong>Ready to pair</strong>&nbsp;v&agrave; đ&egrave;n sẽ&nbsp;<strong>chớp xanh đỏ</strong>.</p>\r\n\r\n<p>- Bật Bluetooth trong&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động\">điện thoại</a>&nbsp;v&agrave; d&ograve; t&igrave;m t&ecirc;n&nbsp;<strong>R552N</strong>&nbsp;để kết nối, nếu th&agrave;nh c&ocirc;ng, bạn sẽ nghe đọc&nbsp;<strong>Device connected</strong>.</p>\r\n\r\n<p>- Tai nghe sẽ tự động kết nối ở những lần kết nối sau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/89044/tai-nghe-bluetooth-roman-r552s--.gif\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Roman R552S - Cách kết nối\" src=\"https://cdn.tgdd.vn/Products/Images/54/89044/tai-nghe-bluetooth-roman-r552s--.gif\" title=\"Tai nghe Bluetooth Roman R552S - Cách kết nối\" /></a></p>\r\n\r\n<p><strong>* Để kết nối với điện thoại kh&aacute;c.</strong></p>\r\n\r\n<p>-&nbsp;<strong>Tắt</strong>&nbsp;Bluetooth tr&ecirc;n&nbsp;<strong>điện thoại</strong>&nbsp;<strong>đ&atilde; kết nối</strong>.</p>\r\n\r\n<p>-&nbsp;<strong>Bật Bluetooth tr&ecirc;n</strong>&nbsp;<strong>điện thoại mới</strong>&nbsp;v&agrave; lặp lại c&aacute;c bước kết nối ở tr&ecirc;n.</p>\r\n\r\n<h3><strong>C&aacute;ch sử dụng</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/89044/tai-nghe-bluetooth-roman-r552s-6.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Roman R552S - Cách sử dụng\" src=\"https://cdn.tgdd.vn/Products/Images/54/89044/tai-nghe-bluetooth-roman-r552s-6.jpg\" title=\"Tai nghe Bluetooth Roman R552S - Cách sử dụng\" /></a></p>\r\n\r\n<p>- Để gọi lại cuộc gọi trước đ&oacute;, bạn nhấn giữ n&uacute;t&nbsp;<strong>Nhận cuộc gọi&nbsp;</strong>trong&nbsp;<strong>2 gi&acirc;y</strong>&nbsp;v&agrave; bạn sẽ nghe được&nbsp;<strong>tiếng b&iacute;p.</strong></p>\r\n\r\n<p><strong>- Nhấn giữ &acirc;m lượng tăng:&nbsp;</strong>để chuyển b&agrave;i h&aacute;t.</p>\r\n\r\n<p><strong>- Nhấn giữ &acirc;m lượng giảm 2 lần:&nbsp;</strong>để l&ugrave;i b&agrave;i h&aacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/89044/tai-nghe-bluetooth-roman-r552s-9.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Roman R552S - Cách sử dụng\" src=\"https://cdn.tgdd.vn/Products/Images/54/89044/tai-nghe-bluetooth-roman-r552s-9.jpg\" title=\"Tai nghe Bluetooth Roman R552S - Cách sử dụng\" /></a></p>\r\n', 0, 0, 'tai-nghe-bluetooth-roman-r552n-xanh-avatar-2-600x600.jpg', 195000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(53, 'USB 2.0 8 GB Sandisk SDCZ50', 4, 'Phụ kiện', '<ul>\r\n	<li>Kiểu d&aacute;ng cực nhỏ nhắn chỉ 4.5 cm.</li>\r\n	<li>C&oacute; thể m&oacute;c d&acirc;y với lỗ nhỏ ph&iacute;a dưới c&ugrave;ng của USB.</li>\r\n	<li>Lưu trữ hơn 2600 b&agrave;i h&aacute;t (1 b&agrave;i ~3 MB).</li>\r\n	<li>Th&ecirc;m một sự lựa chọn của thương hiệu uy t&iacute;n SanDisk.</li>\r\n</ul>\r\n', '<h2>USB Sandisk SDCZ50 8GB 2.0 Xanh Dương</h2>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/75/83124/usb-sandisk-sdcz50-8gb-20-xanh-duong-1.jpg\" onclick=\"return false;\"><img alt=\"USB Sandisk SDCZ50 8GB - Thiết kế bắt mắt\" src=\"https://cdn.tgdd.vn/Products/Images/75/83124/usb-sandisk-sdcz50-8gb-20-xanh-duong-1.jpg\" title=\"USB Sandisk SDCZ50 8GB - Thiết kế bắt mắt\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/75/83124/usb-sandisk-sdcz50-8gb-20-xanh-duong-2.jpg\" onclick=\"return false;\"><img alt=\"USB Sandisk SDCZ50 8GB - Có thể gắn dây\" src=\"https://cdn.tgdd.vn/Products/Images/75/83124/usb-sandisk-sdcz50-8gb-20-xanh-duong-2.jpg\" title=\"USB Sandisk SDCZ50 8GB - Có thể gắn dây\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/75/83124/usb-sandisk-sdcz50-8gb-20-xanh-duong-3.jpg\" onclick=\"return false;\"><img alt=\"USB Sandisk SDCZ50 8GB - Dung lượng lớn 8 GB\" src=\"https://cdn.tgdd.vn/Products/Images/75/83124/usb-sandisk-sdcz50-8gb-20-xanh-duong-3.jpg\" title=\"USB Sandisk SDCZ50 8GB - Dung lượng lớn 8 GB\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>* Tốc độ đọc dữ liệu của&nbsp;<a href=\"https://www.thegioididong.com/usb\" target=\"_blank\" title=\"Phụ kiện USB\">USB</a>&nbsp;l&agrave;&nbsp;<strong>20 MB/s</strong>: mở xem 1 tấm h&igrave;nh nặng&nbsp;<strong>1 MB chưa tới&nbsp;1/20 gi&acirc;y</strong>.</p>\r\n\r\n<p>* Tốc độ ghi dữ liệu&nbsp;<strong>5 MB/s</strong>: ch&eacute;p một video dung lượng&nbsp;<strong>1 GB trong</strong>&nbsp;<strong>3 ph&uacute;t rưỡi</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/75/83124/usb-sandisk-sdcz50-8gb-20-xanh-duong-44.gif\" onclick=\"return false;\"><img alt=\"USB Sandisk SDCZ50 8GB - Tốc độ đọc ghi\" src=\"https://cdn.tgdd.vn/Products/Images/75/83124/usb-sandisk-sdcz50-8gb-20-xanh-duong-44.gif\" title=\"USB Sandisk SDCZ50 8GB - Tốc độ đọc ghi\" /></a></p>\r\n', 0, 0, 'usb-sandisk-sdcz50-8gb-20-xanh-duong-1-5-600x600.jpg', 105000),
(54, 'Loa Bluetooth Fenda W7', 4, 'Phụ kiện', '<p><strong>Loa Bluetooth Fenda W7 &nbsp;tương th&iacute;ch với hầu hết c&aacute;c d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các hãng\">m&aacute;y t&iacute;nh bảng</a>, &acirc;m thanh to v&agrave; đ&egrave;n LED hiển thị đẹp mắt.</strong></p>\r\n', '<h2><strong>Loa Bluetooth Fenda W7 &nbsp;tương th&iacute;ch với hầu hết c&aacute;c d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các hãng\">m&aacute;y t&iacute;nh bảng</a>, &acirc;m thanh to v&agrave; đ&egrave;n LED hiển thị đẹp mắt.</strong></h2>\r\n\r\n<h3><strong>Sử dụng dễ d&agrave;ng, thuận tiện khi mang theo</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/382/88234/loa-bluetooth-fenda-w7--1-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Fenda W7 - Thiết kế đẹp mắt, nhỏ gọn\" src=\"https://cdn.tgdd.vn/Products/Images/382/88234/loa-bluetooth-fenda-w7--1-1.jpg\" title=\"Loa Bluetooth Fenda W7 - Thiết kế đẹp mắt, nhỏ gọn\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/382/88234/loa-bluetooth-fenda-w7--3-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Fenda W7 - Đèn LED tiện dụng\" src=\"https://cdn.tgdd.vn/Products/Images/382/88234/loa-bluetooth-fenda-w7--3-1.jpg\" title=\"Loa Bluetooth Fenda W7 - Đèn LED tiện dụng\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/382/88234/loa-bluetooth-fenda-w7--2-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Fenda W7 - Âm thanh nghe tốt, chi tiết\" src=\"https://cdn.tgdd.vn/Products/Images/382/88234/loa-bluetooth-fenda-w7--2-1.jpg\" title=\"Loa Bluetooth Fenda W7 - Âm thanh nghe tốt, chi tiết\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/382/88234/loa-bluetooth-fenda-w7--5-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Fenda W7 - Thời gian chơi nhạc tốt\" src=\"https://cdn.tgdd.vn/Products/Images/382/88234/loa-bluetooth-fenda-w7--5-1.jpg\" title=\"Loa Bluetooth Fenda W7 - Thời gian chơi nhạc tốt\" /></a></p>\r\n\r\n<h3><strong>Cổng kết nối v&agrave; c&aacute;c n&uacute;t bấm tr&ecirc;n loa</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/382/88234/loa-bluetooth-fenda-w7--7-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Fenda W7 - Cổng kết nối và các nút bấm trên loa\" src=\"https://cdn.tgdd.vn/Products/Images/382/88234/loa-bluetooth-fenda-w7--7-1.jpg\" title=\"Loa Bluetooth Fenda W7 - Cổng kết nối và các nút bấm trên loa\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/382/88234/loa-bluetooth-fenda-w7--8-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Fenda W7 - Cổng kết nối và các nút bấm trên loa\" src=\"https://cdn.tgdd.vn/Products/Images/382/88234/loa-bluetooth-fenda-w7--8-1.jpg\" title=\"Loa Bluetooth Fenda W7 - Cổng kết nối và các nút bấm trên loa\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/382/88234/loa-bluetooth-fenda-w7--9-2.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Fenda W7 - Cổng kết nối và các nút bấm trên loa\" src=\"https://cdn.tgdd.vn/Products/Images/382/88234/loa-bluetooth-fenda-w7--9-2.jpg\" title=\"Loa Bluetooth Fenda W7 - Cổng kết nối và các nút bấm trên loa\" /></a></p>\r\n\r\n<h3><strong>Chức năng của loa</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/382/88234/loa-bluetooth-fenda-w7--6-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Fenda W7 - Chức năng của loa\" src=\"https://cdn.tgdd.vn/Products/Images/382/88234/loa-bluetooth-fenda-w7--6-1.jpg\" title=\"Loa Bluetooth Fenda W7 - Chức năng của loa\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/382/88234/loa-bluetooth-fenda-w7--4-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Fenda W7 - Chức năng của loa\" src=\"https://cdn.tgdd.vn/Products/Images/382/88234/loa-bluetooth-fenda-w7--4-1.jpg\" title=\"Loa Bluetooth Fenda W7 - Chức năng của loa\" /></a></p>\r\n\r\n<h3><strong>C&aacute;ch sử dụng</strong></h3>\r\n\r\n<p><em>-&nbsp;</em>Bạn kết nối loa với thiết bị c&oacute; Bluetooth như&nbsp;điện thoại,&nbsp;m&aacute;y t&iacute;nh bảng...&nbsp;</p>\r\n\r\n<p>- Khởi động loa bằng c&aacute;ch ấn n&uacute;t nguồn ở mặt đ&aacute;y của loa.</p>\r\n\r\n<p>- V&agrave;o thiết bị v&agrave; mở Bluetooth, d&ograve; t&igrave;m t&ecirc;n&nbsp;<strong>Fenda W7</strong><strong>&nbsp;</strong>v&agrave; nhấn để kết nối.</p>\r\n\r\n<p>- Loa&nbsp;sẽ tự động kết nối ở những lần sau.</p>\r\n', 0, 0, 'loa-bluetooth-fenda-w7-avatr-600x600.jpg', 350000),
(55, 'Tai nghe chụp tai Kanen IP-2090', 4, 'Phụ kiện', '<p>Thiết kế sang trọng, hợp thời trang,chất lượng &acirc;m thanh Stereo sống động.C&oacute; khả năng gập gọn khi bạn di chuyển.Lớp đệm tai mềm cho cảm gi&aacute;c thoải m&aacute;i khi đeo.T&iacute;ch hợp ph&iacute;m chức năng tiện lợi cho người d&ugrave;ng.</p>\r\n', '<p><a href=\"https://www.thegioididong.com/images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-1-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe chụp tai Kanen IP 2090\" src=\"https://cdn.tgdd.vn/Products/Images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-1-1.jpg\" title=\"Tai nghe chụp tai Kanen IP 2090\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-5copy.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe chụp tai Kanen IP 2090\" src=\"https://cdn.tgdd.vn/Products/Images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-5copy.jpg\" title=\"Tai nghe chụp tai Kanen IP 2090\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-6-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe chụp tai Kanen IP 2090\" src=\"https://cdn.tgdd.vn/Products/Images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-6-2.jpg\" title=\"Tai nghe chụp tai Kanen IP 2090\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe chụp tai Kanen IP 2090\" src=\"https://cdn.tgdd.vn/Products/Images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-4.jpg\" title=\"Tai nghe chụp tai Kanen IP 2090\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Sử dụng được cho với&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại\">điện thoại</a>, m&aacute;y t&iacute;nh bảng, m&aacute;y nghe nhạc....</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe chụp tai Kanen IP 2090\" src=\"https://cdn.tgdd.vn/Products/Images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-3.jpg\" title=\"Tai nghe chụp tai Kanen IP 2090\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>N&uacute;t ấn tương t&aacute;c tốt</strong></h3>\r\n\r\n<p>- Đối với ứng dụng nghe nhạc mặc định, bạn c&oacute; thể tăng giảm &acirc;m lượng, nhận cuộc gọi, ngừng/chơi nhạc, ấn n&uacute;t tr&ograve;n 2 lần để chuyển b&agrave;i.</p>\r\n\r\n<p>- Đối với ứng dụng nghe nhạc online, bạn kh&ocirc;ng thể ấn để chuyển b&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-2-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe chụp tai Kanen IP 2090\" src=\"https://cdn.tgdd.vn/Products/Images/54/74185/tai-nghe-chup-tai-kanen-ip-2090-2-1.jpg\" title=\"Tai nghe chụp tai Kanen IP 2090\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 0, 'tai-nghe-chup-tai-kanen-ip-2090-2-3-600x600.jpg', 227000),
(56, 'Gậy chụp ảnh Mini Cosano CW1', 4, 'Phụ kiện', '<p><strong>Gậy chụp ảnh Mini Cosano CW1 c&oacute; k&iacute;ch thước rất nhỏ gọn chỉ 16 cm gi&uacute;p bạn c&oacute; thể mang theo mọi l&uacute;c mọi nơi.</strong></p>\r\n', '<h2><strong>Gậy chụp ảnh Mini Cosano CW1 c&oacute; k&iacute;ch thước rất nhỏ gọn chỉ 16 cm gi&uacute;p bạn c&oacute; thể mang theo mọi l&uacute;c mọi nơi.</strong></h2>\r\n\r\n<h3><strong>Gậy chụp ảnh c&oacute; k&iacute;ch thước rất nhỏ gọn</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/3885/87311/gay-chup-anh-mini-cosano-cw1--1.jpg\" onclick=\"return false;\"><img alt=\"Gậy chụp ảnh Mini Cosano CW1 - kích thước rất nhỏ gọn\" src=\"https://cdn.tgdd.vn/Products/Images/3885/87311/gay-chup-anh-mini-cosano-cw1--1.jpg\" title=\"Gậy chụp ảnh Mini Cosano CW1 - kích thước rất nhỏ gọn\" /></a></p>\r\n\r\n<h3><strong>Độ d&agrave;i ch&ecirc;nh lệch khi mở phần để&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại các thương hiệu\">điện thoại</a></strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/3885/87311/gay-chup-anh-mini-cosano-cw1--2.jpg\" onclick=\"return false;\"><img alt=\"Gậy chụp ảnh Mini Cosano CW1 - Độ dài chênh lệch khi mở phần để điện thoại\" src=\"https://cdn.tgdd.vn/Products/Images/3885/87311/gay-chup-anh-mini-cosano-cw1--2.jpg\" title=\"Gậy chụp ảnh Mini Cosano CW1 - Độ dài chênh lệch khi mở phần để điện thoại\" /></a></p>\r\n\r\n<h3><strong>C&aacute;c chi tiết tr&ecirc;n th&acirc;n gậy chụp ảnh&nbsp;Mini Cosano CW1</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/3885/87311/gay-chup-anh-mini-cosano-cw1--3.jpg\" onclick=\"return false;\"><img alt=\"Gậy chụp ảnh Mini Cosano CW1 - Các chi tiết trên thân gậy chụp ảnh Mini Cosano CW1\" src=\"https://cdn.tgdd.vn/Products/Images/3885/87311/gay-chup-anh-mini-cosano-cw1--3.jpg\" title=\"Gậy chụp ảnh Mini Cosano CW1 - Các chi tiết trên thân gậy chụp ảnh Mini Cosano CW1\" /></a></p>\r\n\r\n<h3><strong>Đầu giữ điện thoại c&oacute; k&iacute;ch thước dưới 6 inch</strong></h3>\r\n\r\n<p>Lưu &yacute;: kh&ocirc;ng d&ugrave;ng được với&nbsp;<a href=\"https://www.thegioididong.com/dtdd/lenovo-k5-plus\" target=\"_blank\" title=\"Điện thoại K5 Plus\">Lenovo K5 Plus</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/3885/87311/gay-chup-anh-mini-cosano-cw1--4.jpg\" onclick=\"return false;\"><img alt=\"Gậy chụp ảnh Mini Cosano CW1 - Đầu giữ điện thoại có kích thước dưới 6 inch\" src=\"https://cdn.tgdd.vn/Products/Images/3885/87311/gay-chup-anh-mini-cosano-cw1--4.jpg\" title=\"Gậy chụp ảnh Mini Cosano CW1 - Đầu giữ điện thoại có kích thước dưới 6 inch\" /></a></p>\r\n\r\n<h3><strong>N&uacute;t ấn nhanh nhạy, d&ugrave;ng được cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=iphone-ios\" target=\"_blank\" title=\"Các mẫu điện thoại iPhone\">iOS</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Các mẫu điện thoại Android\">Android</a></strong></h3>\r\n\r\n<p>D&ugrave;ng để chụp ảnh tr&ecirc;n ứng dụng camera của điện thoại.</p>\r\n\r\n<p><strong>Lưu &yacute;:&nbsp;</strong>Windows Phone kh&ocirc;ng tương th&iacute;ch với gậy.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/3885/87311/gay-chup-anh-mini-cosano-cw1--5.jpg\" onclick=\"return false;\"><img alt=\"Gậy chụp ảnh Mini Cosano CW1 - Dùng được cho iOS, Android\" src=\"https://cdn.tgdd.vn/Products/Images/3885/87311/gay-chup-anh-mini-cosano-cw1--5.jpg\" title=\"Gậy chụp ảnh Mini Cosano CW1 - Dùng được cho iOS, Android\" /></a></p>\r\n\r\n<h3><strong>Chiều d&agrave;i gậy vừa phải 60 cm</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/3885/87311/gay-chup-anh-mini-cosano-cw1--6.jpg\" onclick=\"return false;\"><img alt=\"Gậy chụp ảnh Mini Cosano CW1 - Chiều dài gậy vừa phải 60 cm\" src=\"https://cdn.tgdd.vn/Products/Images/3885/87311/gay-chup-anh-mini-cosano-cw1--6.jpg\" title=\"Gậy chụp ảnh Mini Cosano CW1 - Chiều dài gậy vừa phải 60 cm\" /></a></p>\r\n', 0, 0, 'gay-chup-anh-mini-cosano-cw1-ava-600x600.jpg', 32000),
(58, 'Đồng hồ thông minh Fitbit Versa', 5, 'Đồng hồ', '<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/fitbit-versa-do\" target=\"_blank\" title=\"Đồng hồ thông minh Fitbit Versa Đỏ chính hãng, giá tốt, bán tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh Fitbit Versa</a>&nbsp;l&agrave; chiếc đồng hồ m&agrave; bạn sẽ kh&ocirc;ng thể lẫn v&agrave;o đ&acirc;u được v&agrave; sẽ bị n&oacute; hấp dẫn ngay từ &aacute;nh nh&igrave;n đầu ti&ecirc;n, kh&ocirc;ng chỉ bởi m&agrave;u sắc đặc biệt v&agrave; kiểu d&aacute;ng hiện đại, m&agrave; c&ograve;n bởi những t&iacute;nh năng c&ocirc;ng nghệ đột ph&aacute; m&agrave; n&oacute; mang theo</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/194520/Slider/-4.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/fitbit-versa-do\" target=\"_blank\" title=\"Đồng hồ thông minh Fitbit Versa Đỏ chính hãng, giá tốt, bán tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh Fitbit Versa</a>&nbsp;l&agrave; chiếc đồng hồ m&agrave; bạn sẽ kh&ocirc;ng thể lẫn v&agrave;o đ&acirc;u được v&agrave; sẽ bị n&oacute; hấp dẫn ngay từ &aacute;nh nh&igrave;n đầu ti&ecirc;n, kh&ocirc;ng chỉ bởi m&agrave;u sắc đặc biệt v&agrave; kiểu d&aacute;ng hiện đại, m&agrave; c&ograve;n bởi những t&iacute;nh năng c&ocirc;ng nghệ đột ph&aacute; m&agrave; n&oacute; mang theo.</h2>\r\n\r\n<h3>Thiết kế ấn tượng</h3>\r\n\r\n<p>Fitbit Versa nổi bật với thiết kế trẻ trung, thanh lịch v&agrave; hiện đại, ph&ugrave; hợp với cả nam v&agrave; nữ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194520/fitbit-versa-do-thietke.jpg\" onclick=\"return false;\"><img alt=\"Smartwatch Fitbit Versa Đỏ có thiết kế thanh lịch\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194520/fitbit-versa-do-thietke.jpg\" title=\"Smartwatch Fitbit Versa Đỏ có thiết kế thanh lịch\" /></a></p>\r\n\r\n<h3>Theo d&otilde;i nhịp tim</h3>\r\n\r\n<p>Nhờ v&agrave;o 3 cảm biến ở mặt lưng đồng hồ.&nbsp;Fitbit Versa c&oacute; khả năng đo được cả nhịp tim của người đeo.&nbsp;Gi&uacute;p bạn biết được ch&iacute;nh x&aacute;c t&igrave;nh trạng hoạt động, thể chất cũng như cường độ tập luyện của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-2.jpg\" onclick=\"return false;\"><img alt=\"Smartwatch Fitbit Versa Đỏ với tính năng theo dõi nhịp tim\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-2.jpg\" title=\"Smartwatch Fitbit Versa Đỏ với tính năng theo dõi nhịp tim\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh hiển thị sắc n&eacute;t</h3>\r\n\r\n<p>Ngo&agrave;i ra, bạn cũng c&oacute; thể y&ecirc;n t&acirc;m về khả năng hiển thị của chiếc đồng hồ n&agrave;y, m&agrave;n h&igrave;nh của&nbsp;Fitbit Versa cho ph&eacute;p người d&ugrave;ng quan s&aacute;t r&otilde; r&agrave;ng v&agrave; sắc n&eacute;t ngay cả ngo&agrave;i trời.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-3.jpg\" onclick=\"return false;\"><img alt=\"Smartwatch Fitbit Versa Đỏ được trang bị màn hình hiển thị sắc nét\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-3.jpg\" title=\"Smartwatch Fitbit Versa Đỏ được trang bị màn hình hiển thị sắc nét\" /></a></p>\r\n\r\n<h3>Theo d&otilde;i sức khỏe</h3>\r\n\r\n<p>Nếu bạn cần một c&aacute;i nh&igrave;n tổng quan v&agrave; đa chiều về t&igrave;nh trạng sức khoẻ của m&igrave;nh th&igrave;, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-fitbit\" target=\"_blank\" title=\"Đồng hồ thông minh Fitbit chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh Fitbit</a>&nbsp;n&agrave;y kh&ocirc;ng chỉ đo được số bước ch&acirc;n, mức độ vận động trong ng&agrave;y m&agrave; n&oacute; c&ograve;n c&oacute; thể theo d&otilde;i sức khỏe h&agrave;ng ng&agrave;y của bạn, gi&uacute;p bạn c&oacute; c&aacute;i nh&igrave;n tổng quan nhất về sức khỏe trong tuần, th&aacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-4.jpg\" onclick=\"return false;\"><img alt=\"Smartwatch Fitbit Versa Đỏ hỗ trợ theo dõi sức khỏe\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-4.jpg\" title=\"Smartwatch Fitbit Versa Đỏ hỗ trợ theo dõi sức khỏe\" /></a></p>\r\n\r\n<h3>Chống nước</h3>\r\n\r\n<p>Khả năng chống nước ở độ s&acirc;u đến 50 m&eacute;t cũng l&agrave; một điểm mạnh kh&ocirc;ng thể bỏ qua, bạn c&oacute; thể thoải m&aacute;i mang n&oacute; theo khi trời mưa, đi bơi... m&agrave; kh&ocirc;ng cần lo lắng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-5.jpg\" onclick=\"return false;\"><img alt=\"Smartwatch Fitbit Versa Đỏ cho khả năng chống nước lên đến 50m\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-5.jpg\" title=\"Smartwatch Fitbit Versa Đỏ cho khả năng chống nước lên đến 50m\" /></a></p>\r\n\r\n<h3>Thời gian sử dụng pin</h3>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Smartwatch chính hãng, giá tốt, bán tại Thế Giới Di Động\">smartwatch</a>&nbsp;Fitbit Versa c&ograve;n cho thời gian sử dụng pin l&ecirc;n đến 4 ng&agrave;y ở chế độ thường v&agrave; chỉ mất 2 tiếng để sạc đầy. Một con số rất ấn tượng m&agrave; kh&ocirc;ng nhiều mẫu đồng hồ th&ocirc;ng minh hiện nay c&oacute; thể đạt được.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-6.jpg\" onclick=\"return false;\"><img alt=\"Smartwatch Fitbit Versa Đỏ cho thời gian sử dụng pin lâu\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-6.jpg\" title=\"Smartwatch Fitbit Versa Đỏ cho thời gian sử dụng pin lâu\" /></a></p>\r\n\r\n<h3>Hướng dẫn sử dụng&nbsp;Fitbit Versa Fitbit Versa&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-1.jpg\" onclick=\"return false;\"><img alt=\"Hướng dẫn kết nối Smartwatch Fitbit Versa Đỏ với điện thoại\" src=\"https://cdn.tgdd.vn/Products/Images/7077/194520/dong-ho-thong-minh-dong-ho-thong-minh-fitbit-versa-ruby-4-1.jpg\" title=\"Hướng dẫn kết nối Smartwatch Fitbit Versa Đỏ với điện thoại\" /></a></p>\r\n', 0, 0, 'fitbit-versa-do-nt-600x600.jpg', 4941000),
(59, 'Vòng tay thông minh Fitbit Versa Lite', 5, 'Đồng hồ', '<p>Tuy kh&ocirc;ng được trang bị t&iacute;nh năng thanh to&aacute;n Fitbit Pay hay nghe nhạc tr&ecirc;n thiết bị,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/fitbit-versa-lite\" target=\"_blank\" title=\"Xem chi tiết Đồng hồ thông minh Fitbit Versa Lite\" type=\"Xem chi tiết Đồng hồ thông minh Fitbit Versa Lite\">Fitbit Versa Lite</a>&nbsp;vẫn l&agrave; một lựa chọn đồng hồ th&ocirc;ng minh gi&aacute; rẻ đ&aacute;ng được c&acirc;n nhắc với t&iacute;nh năng theo d&otilde;i sức khỏe cơ bản v&agrave; thiết kế cao cấp</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/200877/Slider/vi-vn-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\">Tuy kh&ocirc;ng được trang bị t&iacute;nh năng thanh to&aacute;n Fitbit Pay hay nghe nhạc tr&ecirc;n thiết bị,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/fitbit-versa-lite\" target=\"_blank\" title=\"Xem chi tiết Đồng hồ thông minh Fitbit Versa Lite\" type=\"Xem chi tiết Đồng hồ thông minh Fitbit Versa Lite\">Fitbit Versa Lite</a>&nbsp;vẫn l&agrave; một lựa chọn đồng hồ th&ocirc;ng minh gi&aacute; rẻ đ&aacute;ng được c&acirc;n nhắc với t&iacute;nh năng theo d&otilde;i sức khỏe cơ bản v&agrave; thiết kế cao cấp.</h2>\r\n\r\n<h3 dir=\"ltr\">Thiết kế cao cấp, trọng lượng nhẹ</h3>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-fitbit\" target=\"_blank\" title=\"Xem các mẫu Đồng hồ thông minh Fitbit \" type=\"Xem các mẫu Đồng hồ thông minh Fitbit \">Đồng hồ th&ocirc;ng minh Fitbit&nbsp;</a>Versa Lite c&oacute; thiết kế mỏng nhẹ v&agrave; bền bỉ nhờ v&agrave;o khung nh&ocirc;m anode cao cấp. M&agrave;n h&igrave;nh cảm ứng cho độ hiển thị cực s&aacute;ng v&agrave; m&agrave;u sắc sống động. Nhiều mặt đồng hồ v&agrave; d&acirc;y đeo thời trang được b&aacute;n ri&ecirc;ng để bạn c&oacute; thể thỏa sức s&aacute;ng tạo phong c&aacute;ch.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/200877/fitbit-versa-lite-6.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế Vòng tay thông minh Fitbit Versa Lite\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200877/fitbit-versa-lite-6.jpg\" title=\"Thiết kế Vòng tay thông minh Fitbit Versa Lite\" /></a></p>\r\n\r\n<h3 dir=\"ltr\">Thời lượng pin ấn tượng</h3>\r\n\r\n<p dir=\"ltr\">Sử dụng Fitbit Versa Lite li&ecirc;n tục m&agrave; kh&ocirc;ng cần sạc suốt 4 ng&agrave;y liền. Trong khoảng thời gian n&agrave;y, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem chi tiết đồng hồ thông minh Fitbit đang kinh doanh\" type=\"Xem chi tiết đồng hồ thông minh Fitbit đang kinh doanh\">đồng hồ th&ocirc;ng minh</a>&nbsp;Versa Lite sẽ tận t&acirc;m theo d&otilde;i mọi hoạt động sức khỏe của bạn.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/200877/fitbit-versa-lite-1-1.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin Vòng tay thông minh Fitbit Versa Lite\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200877/fitbit-versa-lite-1-1.jpg\" title=\"Thời lượng pin Vòng tay thông minh Fitbit Versa Lite\" /></a></p>\r\n\r\n<h3 dir=\"ltr\">Chống nước 5ATM</h3>\r\n\r\n<p dir=\"ltr\">Rửa xe, nước đổ hay đi dưới mưa kh&ocirc;ng l&agrave; vấn đề với khả năng chống nước 50m của Fitbit Versa Lite.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/200877/fitbit-versa-lite-2-1.jpg\" onclick=\"return false;\"><img alt=\"Chống nước trên Vòng tay thông minh Fitbit Versa Lite\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200877/fitbit-versa-lite-2-1.jpg\" title=\"Chống nước trên Vòng tay thông minh Fitbit Versa Lite\" /></a></p>\r\n\r\n<h3 dir=\"ltr\">T&iacute;nh năng theo d&otilde;i đặc biệt cho ph&aacute;i nữ</h3>\r\n\r\n<p dir=\"ltr\">Fitbit trang bị cho mẫu đồng hồ th&ocirc;ng minh Versa Lite t&iacute;nh năng theo d&otilde;i sức khỏe ph&aacute;i nữ cực kỳ hữu &iacute;ch. Giờ đ&acirc;y bạn c&oacute; thể theo d&otilde;i chu kỳ kinh nguyệt, ghi lại c&aacute;c triệu chứng v&agrave; so s&aacute;nh với chỉ số sức khỏe như t&igrave;nh trạng giấc ngủ, vận động v&agrave; c&acirc;n nặng.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/200877/fitbit-versa-lite-3-1.jpg\" onclick=\"return false;\"><img alt=\"Theo dõi kinh nguyệt Vòng tay thông minh Fitbit Versa Lite\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200877/fitbit-versa-lite-3-1.jpg\" title=\"Theo dõi kinh nguyệt Vòng tay thông minh Fitbit Versa Lite\" /></a></p>\r\n\r\n<h3 dir=\"ltr\">Cập nhật th&ocirc;ng b&aacute;o li&ecirc;n tục từ smartphone</h3>\r\n\r\n<p dir=\"ltr\">Khi kết nối với điện thoại, mọi th&ocirc;ng b&aacute;o, nhắc nhở, tin nhắn, cuộc gọi đều được cập nhật v&agrave; hiển thị tr&ecirc;n Versa Lite. Tuy nhi&ecirc;n, t&iacute;nh năng trả lời nhanh chỉ tương th&iacute;ch với c&aacute;c thiết bị Android.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/200877/fitbit-versa-lite-4.jpg\" onclick=\"return false;\"><img alt=\"Cập nhật thông báo Vòng tay thông minh Fitbit Versa Lite\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200877/fitbit-versa-lite-4.jpg\" title=\"Cập nhật thông báo Vòng tay thông minh Fitbit Versa Lite\" /></a></p>\r\n\r\n<h3 dir=\"ltr\">Huấn luyện vi&ecirc;n c&aacute; nh&acirc;n của bạn</h3>\r\n\r\n<p dir=\"ltr\">Versa Lite c&oacute; thể theo d&otilde;i hơn 15 hoạt động thể thao bao gồm chạy bộ, đạp xe v&agrave; thể h&igrave;nh. Cho ph&eacute;p đặt mục ti&ecirc;u r&egrave;n luyện sức khỏe v&agrave; nhắc nhở bạn ho&agrave;n th&agrave;nh mục ti&ecirc;u đ&oacute;.</p>\r\n\r\n<p dir=\"ltr\">Fitbit Versa Lite c&oacute; khả năng ph&aacute;t hiện thời gian nghỉ trong ng&agrave;y v&agrave; đưa ra c&aacute;c b&agrave;i tập thở d&agrave;nh ri&ecirc;ng cho bạn dựa tr&ecirc;n nhịp tim của bạn.</p>\r\n\r\n<p dir=\"ltr\">B&ecirc;n cạnh những t&iacute;nh năng đếm bước ch&acirc;n, x&aacute;c định khoảng c&aacute;ch, t&iacute;nh lượng calories ti&ecirc;u thụ, đồng hồ th&ocirc;ng minh Versa Lite c&ograve;n c&oacute; thể theo d&otilde;i giấc ngủ n&ocirc;ng, s&acirc;u của bạn.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/200877/fitbit-versa-lite-5.jpg\" onclick=\"return false;\"><img alt=\"Huấn luyện viên sức khỏe Vòng tay thông minh Fitbit Versa Lite\" src=\"https://cdn.tgdd.vn/Products/Images/7077/200877/fitbit-versa-lite-5.jpg\" title=\"Huấn luyện viên sức khỏe Vòng tay thông minh Fitbit Versa Lite\" /></a></p>\r\n', 1, 0, 'fitbit-versa-lite-7-600x600.jpg', 3861000),
(60, 'Đồng hồ thông minh Huawei Watch 2', 5, 'Đồng hồ', '<p>Sở hữu thiết kế với m&agrave;n h&igrave;nh AMOLED 1.2 inch tiện lợi d&ugrave;ng dưới &aacute;nh nắng, kết hợp c&ugrave;ng d&acirc;y đeo chất liệu cao su c&oacute; n&uacute;t c&agrave;i, dễ d&agrave;ng thay đổi độ rộng d&acirc;y để ph&ugrave; hợp với cổ tay người đeo.</p>\r\n\r\n<p>Giao diện m&agrave;n h&igrave;nh tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/huawei-watch-2\" target=\"_blank\" title=\"Đồng hồ thông minh Huawei Watch 2 chính hãng, giá tốt, bán tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh Huawei Watch 2</a>&nbsp;n&agrave;y c&oacute; thể dễ d&agrave;ng thay đổi&nbsp;bằng nhiều mặt đồng hồ kh&aacute;c nhau.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/Slider/-day-deo-co-the-thay-roi-tien-loi.gif\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/Slider/-3-thay-mat--slider.gif\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/Slider/-4--nghe-goi--slider.jpg\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/Slider/vi-vn-suc-khoe.jpg\" /></p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/Slider/-12-nghe-nhac-slider.jpg\" /></p>\r\n\r\n<p>Bộ sản phẩm chuẩn: Hộp, Đồng hồ, Sạc, S&aacute;ch hướng dẫn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thiết kế hiện đại</h3>\r\n\r\n<p>Sở hữu thiết kế với m&agrave;n h&igrave;nh AMOLED 1.2 inch tiện lợi d&ugrave;ng dưới &aacute;nh nắng, kết hợp c&ugrave;ng d&acirc;y đeo chất liệu cao su c&oacute; n&uacute;t c&agrave;i, dễ d&agrave;ng thay đổi độ rộng d&acirc;y để ph&ugrave; hợp với cổ tay người đeo.</p>\r\n\r\n<p>Giao diện m&agrave;n h&igrave;nh tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/huawei-watch-2\" target=\"_blank\" title=\"Đồng hồ thông minh Huawei Watch 2 chính hãng, giá tốt, bán tại Thế Giới Di Động\">đồng hồ th&ocirc;ng minh Huawei Watch 2</a>&nbsp;n&agrave;y c&oacute; thể dễ d&agrave;ng thay đổi&nbsp;bằng nhiều mặt đồng hồ kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-tk.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Huawei Watch 2 - Thiết kế hiện đại\" src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-tk.jpg\" title=\"Đồng hồ thông minh Huawei Watch 2 - Thiết kế hiện đại\" /></a></p>\r\n\r\n<h3>Tiện &iacute;ch sức khỏe</h3>\r\n\r\n<p>Với cảm biến được t&iacute;ch hợp, đồng hồ c&oacute; thể theo d&otilde;i v&agrave; cung cấp th&ocirc;ng tin cho người d&ugrave;ng ở c&aacute;c chế độ như:</p>\r\n\r\n<p>- Đo nhịp tim, theo d&otilde;i tim bạn c&oacute; chế độ hoạt động b&igrave;nh thường hay kh&ocirc;ng.</p>\r\n\r\n<p>- T&iacute;nh lượng calories ti&ecirc;u thụ trong từng chế độ luyện tập ri&ecirc;ng biệt.</p>\r\n\r\n<p>- Theo d&otilde;i giấc ngủ, ph&acirc;n t&iacute;ch bạn c&oacute; ngủ đủ giấc hay kh&ocirc;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-sk.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Huawei Watch 2 - Tiện ích sức khỏe\" src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-sk.jpg\" title=\"Đồng hồ thông minh Huawei Watch 2 - Tiện ích sức khỏe\" /></a></p>\r\n\r\n<h3>C&aacute;c chế độ luyện tập</h3>\r\n\r\n<p>Với c&aacute;c t&iacute;nh năng th&ocirc;ng minh được trang bị,&nbsp;Huawei Watch 2 theo d&otilde;i c&aacute;c chế độ tập luyện ri&ecirc;ng biệt như: Đi bộ, đạp xe, leo n&uacute;i... Từ đ&oacute; ph&acirc;n t&iacute;ch v&agrave; cung cấp những th&ocirc;ng tin cần thiết để người d&ugrave;ng c&acirc;n nhắc v&agrave; tự động điều chỉnh cường độ luyện tập ph&ugrave; hợp hơn cho bản th&acirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-lt.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Huawei Watch 2 - Các chế độ luyện tập\" src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-lt.jpg\" title=\"Đồng hồ thông minh Huawei Watch 2 - Các chế độ luyện tập\" /></a></p>\r\n\r\n<h3>T&iacute;nh năng GPS</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-huawei\" target=\"_blank\" title=\"Đồng hồ thông minh Huawei chính hãng, giá tốt, có bán trả góp tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh Huawei</a>&nbsp;Watch 2 với t&iacute;nh năng GPS được trang bị hỗ trợ định vị qu&atilde;ng đường đi khi bạn tập, từ đấy cung cấp th&ocirc;ng tin về lộ tr&igrave;nh trong qu&aacute; tr&igrave;nh luyện tập cho bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-map.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Huawei Watch 2 - Tính năng GPS\" src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-map.jpg\" title=\"Đồng hồ thông minh Huawei Watch 2 - Tính năng GPS\" /></a></p>\r\n\r\n<h3>Chế độ lu&ocirc;n lu&ocirc;n hiển thị</h3>\r\n\r\n<p>Với Huawei Watch 2 bạn sẽ kh&ocirc;ng bỏ lỡ những th&ocirc;ng b&aacute;o quan trọng như nội dung tin nhắn đến, cuộc gọi đến... Tất cả sẽ hiển thị r&otilde; r&agrave;ng tr&ecirc;n mặt đồng hồ một c&aacute;ch tiện lợi nhất.</p>\r\n\r\n<p>Thậm ch&iacute;, bạn chỉ cần kết nối với điện thoại, v&agrave; bạn đ&atilde; c&oacute; thể nhận/từ chối cuộc gọi trực tiếp ngay tr&ecirc;n mặt đồng hồ.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong>&nbsp;Trả lời cuộc gọi tr&ecirc;n đồng hồ (Chỉ tương th&iacute;ch với Android, iOS kh&ocirc;ng tương th&iacute;ch).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-ncg.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Huawei Watch 2 - Trả lời cuộc gọi trực tiếp trên đồng hồ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-ncg.jpg\" title=\"Đồng hồ thông minh Huawei Watch 2 - Trả lời cuộc gọi trực tiếp trên đồng hồ\" /></a></p>\r\n\r\n<h3>Kết nối đa dạng</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Đồng hồ thông minh chính hãng, giá tốt, bán tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh</a>&nbsp;Huawei Watch 2 dễ d&agrave;ng kết nối với điện thoại cho việc sử dụng trở n&ecirc;n tiện lợi hơn th&ocirc;ng qua c&aacute;c phương thức như: Wifi, Bluetooth.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/193447/huawei-watch-2-2.jpg\" onclick=\"return false;\"><img alt=\"Các kết nối trên đồng hồ thông minh Huawei Watch 2\" src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/huawei-watch-2-2.jpg\" title=\"Các kết nối trên đồng hồ thông minh Huawei Watch 2\" /></a></p>\r\n\r\n<p>Tải ứng dụng Wear OS tr&ecirc;n&nbsp;<a href=\"https://play.google.com/store/apps/details?id=com.google.android.wearable.app\" target=\"_blank\" title=\"Link tải ứng dụng Wear OS dành cho điện thoại Android\">Android</a>&nbsp;hoặc&nbsp;<a href=\"https://itunes.apple.com/us/app/wear-os-by-google-smartwatch/id986496028?mt=8\" target=\"_blank\" title=\"Link tải ứng dụng Wear OS dành cho điện thoại iOS\">iOS</a>. Sau đ&oacute; kết nối điện thoại với đồng hồ, bấm v&agrave;o ứng dụng tr&ecirc;n điện thoại để bắt đầu việc thiết lập c&agrave;i đặt.</p>\r\n\r\n<p>Xem chi tiết hướng dẫn sử dụng click&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cach-ket-noi-dong-ho-huawei-watch-2-voi-dien-thoai-1137772\" target=\"_blank\" title=\"Cách kết nối đồng hồ Huawei Watch 2 với điện thoại\">tại đ&acirc;y</a>.</p>\r\n\r\n<h3>Thời gian sử dụng</h3>\r\n\r\n<p>Với vi&ecirc;n pin dung lượng 420 mAh được Huawei t&iacute;ch hợp, đồng hồ cho khả năng sử dụng l&ecirc;n đến 2 ng&agrave;y, c&ugrave;ng thời gian sạc lại khoảng 2 giờ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2--pin.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Huawei Watch 2 - Thời gian sử dụng\" src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2--pin.jpg\" title=\"Đồng hồ thông minh Huawei Watch 2 - Thời gian sử dụng\" /></a></p>\r\n\r\n<h3>Chuẩn chống nước IP68</h3>\r\n\r\n<p>Đồng hồ được trang bị chuẩn chống nước IP68, bạn kh&ocirc;ng c&ograve;n phải lo lắng khi nước đổ v&agrave;o hoặc cả khi phải di chuyển dưới trời mưa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-cn.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Huawei Watch 2 - Chuẩn chống nước IP68\" src=\"https://cdn.tgdd.vn/Products/Images/7077/193447/dong-ho-thong-minh-dong-ho-thong-minh-huawei-watch-2-cn.jpg\" title=\"Đồng hồ thông minh Huawei Watch 2 - Chuẩn chống nước IP68\" /></a></p>\r\n\r\n<h3>&nbsp;</h3>\r\n', 0, 0, 'dong-ho-thong-minh-huawei-watch-2-chitiet-400x460.jpg', 3192000),
(61, 'Vòng tay thông minh Samsung Galaxy Fit R370', 5, 'Đồng hồ', '<p>Thời trang, c&aacute; t&iacute;nh, khỏe mạnh v&agrave; năng động 24/7 c&ugrave;ng v&ograve;ng đeo tay th&ocirc;ng minh&nbsp;<strong><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-fit-r370\" target=\"_blank\" title=\"Samsung Galaxy Fit\">Samsung Galaxy Fit</a></strong>, thiết bị theo d&otilde;i sức khỏe chuy&ecirc;n s&acirc;u đến từ Samsung.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/203470/Slider/-samsung-galaxy-fit-r370-sieu-nhe.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\">Thời trang, c&aacute; t&iacute;nh, khỏe mạnh v&agrave; năng động 24/7 c&ugrave;ng v&ograve;ng đeo tay th&ocirc;ng minh&nbsp;<strong><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/samsung-galaxy-fit-r370\" target=\"_blank\" title=\"Samsung Galaxy Fit\">Samsung Galaxy Fit</a></strong>, thiết bị theo d&otilde;i sức khỏe chuy&ecirc;n s&acirc;u đến từ Samsung.</h2>\r\n\r\n<h3 dir=\"ltr\">Thiết kế nhỏ gọn v&agrave; mỏng nhẹ</h3>\r\n\r\n<p dir=\"ltr\">Kh&ocirc;ng chỉ l&agrave; một chiếc v&ograve;ng tay theo d&otilde;i sức khỏe, Galaxy Fit c&ograve;n được biết đến như một phụ kiện thời trang với v&agrave;i lựa chọn m&agrave;u sắc v&agrave; d&acirc;y đeo. Ch&uacute; trọng v&agrave;o sự thoải m&aacute;i của người sử dụng, thiết kế mỏng nhẹ của Galaxy Fit sẽ mang đến cho bạn trải nghiệm tuyệt vời bất kể khi đi l&agrave;m hay tập thể thao.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/203470/samsung-galaxy-fit-r370-4.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Fit gọn nhẹ, năng động\" src=\"https://cdn.tgdd.vn/Products/Images/7077/203470/samsung-galaxy-fit-r370-4.jpg\" title=\"Samsung Galaxy Fit gọn nhẹ, năng động\" /></a></p>\r\n\r\n<h3 dir=\"ltr\">M&agrave;n h&igrave;nh AMOLED đầy sắc m&agrave;u</h3>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Xem thêm các sản phẩm đồng hồ thông minh, vòng tay thông minh đang kinh doanh\" type=\"Xem thêm các sản phẩm đồng hồ thông minh, vòng tay thông minh đang kinh doanh\">V&ograve;ng tay th&ocirc;ng minh</a>&nbsp;Galaxy Fit được trang bị một trong những c&ocirc;ng nghệ m&agrave;n h&igrave;nh xuất sắc nhất hiện nay, AMOLED. Kh&ocirc;ng chỉ gi&uacute;p hiển thị tươi s&aacute;ng v&agrave; rực rỡ, m&agrave;n h&igrave;nh cảm ứng AMOLED c&ograve;n tiết kiệm rất nhiều điện năng, cho ph&eacute;p bạn sử dụng li&ecirc;n tục, nhận th&ocirc;ng b&aacute;o từ điện thoại v&agrave; trả lời tin nhắn nhanh m&agrave; kh&ocirc;ng sợ hết pin.</p>\r\n\r\n<p dir=\"ltr\">&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/203470/samsung-galaxy-fit-r3706-2.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Fit hiển thị sắc nét\" src=\"https://cdn.tgdd.vn/Products/Images/7077/203470/samsung-galaxy-fit-r3706-2.jpg\" title=\"Samsung Galaxy Fit hiển thị sắc nét\" /></a></h2>\r\n\r\n<h3 dir=\"ltr\">Tập luyện chuy&ecirc;n s&acirc;u kh&ocirc;ng giới hạn</h3>\r\n\r\n<p dir=\"ltr\">Galaxy Fit sở hữu t&iacute;nh năng tự động ph&aacute;t hiện v&agrave; gợi &yacute; c&aacute;c b&agrave;i luyện tập cho bạn theo c&aacute;c khung giờ trong ng&agrave;y.</p>\r\n\r\n<p dir=\"ltr\">Ngo&agrave;i ra, chỉ cần nhấn giữ n&uacute;t vật l&yacute;, bạn c&oacute; thể bật nhanh t&iacute;nh năng theo d&otilde;i tiến tr&igrave;nh hơn 90 b&agrave;i tập thể dục kh&aacute;c nhau như c&aacute;c b&agrave;i tập căng cơ tay.</p>\r\n\r\n<p dir=\"ltr\">Kh&ocirc;ng dừng lại tại đ&oacute;, với chuẩn chống nước ở độ s&acirc;u l&ecirc;n đến 50m, bạn ho&agrave;n to&agrave;n c&oacute; thể thỏa sức bơi lội v&agrave; theo d&otilde;i qu&atilde;ng đường bơi c&ugrave;ng v&ograve;ng tay th&ocirc;ng minh Galaxy Fit.&nbsp;</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/203470/samsung-galaxy-fit-r370-6.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Fit nhắc nhở luyện tập\" src=\"https://cdn.tgdd.vn/Products/Images/7077/203470/samsung-galaxy-fit-r370-6.jpg\" title=\"Samsung Galaxy Fit nhắc nhở luyện tập\" /></a></p>\r\n\r\n<h3 dir=\"ltr\">Theo d&otilde;i v&agrave; cải thiện sức khỏe 24/24</h3>\r\n\r\n<p dir=\"ltr\">Cuộc sống bộn bề c&oacute; l&agrave;m bạn căng thẳng? H&atilde;y để chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-samsung\" target=\"_blank\" title=\"Xem thêm các sản phẩm đồng hồ thông minh, vòng tay thông minh Samsung đang kinh doanh\" type=\"Xem thêm các sản phẩm đồng hồ thông minh, vòng tay thông minh Samsung đang kinh doanh\">v&ograve;ng tay th&ocirc;ng minh Samsung</a>&nbsp;theo d&otilde;i mức độ stress của bạn v&agrave; đưa ra c&aacute;c b&agrave;i tập thở hợp l&yacute; v&agrave; hữu &iacute;ch mỗi khi bạn qu&aacute; căng thẳng.&nbsp;</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/203470/samsung-galaxy-fit-r370-7.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Fit theo dõi mức độ căng thẳng\" src=\"https://cdn.tgdd.vn/Products/Images/7077/203470/samsung-galaxy-fit-r370-7.jpg\" title=\"Samsung Galaxy Fit theo dõi mức độ căng thẳng\" /></a></p>\r\n\r\n<p dir=\"ltr\">N&oacute;i đến sức khỏe giấc ngủ, Galaxy Fit theo d&otilde;i v&agrave; ph&acirc;n t&iacute;ch chu kỳ ngủ của bạn để gi&uacute;p đưa ra giải ph&aacute;p tối ưu cho một giấc ngủ ngon.</p>\r\n\r\n<p dir=\"ltr\">V&ograve;ng tay th&ocirc;ng minh Galaxy Fit tự động hiển thị thời gian ở hai m&uacute;i giờ kh&aacute;c nhau mỗi khi bạn du lịch hay c&ocirc;ng t&aacute;c nước ngo&agrave;i, gi&uacute;p bạn c&oacute; thể th&iacute;ch nghi giữa thời gian ở qu&ecirc; nh&agrave; v&agrave; đất kh&aacute;ch để giữ g&igrave;n sức khỏe tốt hơn.</p>\r\n\r\n<h3 dir=\"ltr\">Pin sạc 1 lần, d&ugrave;ng được cả tuần</h3>\r\n\r\n<p dir=\"ltr\">Thời lượng pin của Galaxy Fit cực kỳ ấn tượng khi c&oacute; thể hoạt động đến 1 tuần li&ecirc;n tục, chăm s&oacute;c sức khỏe của bạn 24/7. C&ocirc;ng nghệ sạc kh&ocirc;ng d&acirc;y NFC cũng gi&uacute;p việc sạc pin cho Galaxy Fit đơn giản hơn rất nhiều.</p>\r\n\r\n<p dir=\"ltr\"><a href=\"https://www.thegioididong.com/images/7077/203470/samsung-galaxy-fit-r3706-1.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy Fit hoạt động 7 ngày liên tục\" src=\"https://cdn.tgdd.vn/Products/Images/7077/203470/samsung-galaxy-fit-r3706-1.jpg\" title=\"Samsung Galaxy Fit hoạt động 7 ngày liên tục\" /></a></p>\r\n', 1, 0, 'samsung-galaxy-fit-r370-3-600x600.jpg', 2490000);
INSERT INTO `tbl_product` (`id`, `name`, `category_id`, `category_name`, `description`, `content`, `hotproduct`, `new`, `img`, `price`) VALUES
(62, 'Đồng hồ thông minh Colmi S9 Plus', 5, 'Đồng hồ', '<p>Đồng hồ th&ocirc;ng minh Colmi S9 Plus&nbsp;c&oacute; thiết kế hiện đại. Kiểu d&aacute;ng m&agrave;n h&igrave;nh chữ nhật độc đ&aacute;o c&ugrave;ng với phối h&agrave;i h&ograve;a giữa m&agrave;u xanh nước v&agrave; đen tạo được n&eacute;t năng động, c&aacute; t&iacute;nh. Thiết kế c&aacute;c t&iacute;nh năng tiện &iacute;t gi&uacute;p theo d&otilde;i v&agrave; điều chỉnh chế độ luyện tập, r&egrave;n luyện sức khỏe, mang đến cho bạn sự h&agrave;i l&ograve;ng.</p>\r\n', '<h2>&nbsp;</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/202054/Slider/-thiet-ke.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/dong-ho-thong-minh-colmi-s9-plus\" target=\"_blank\" title=\"Đồng hồ thông minh Colmi S9 Plus chính hãng, giá rẻ bán tại Thế Giới Di Động\" type=\"Đồng hồ thông minh Colmi S9 Plus chính hãng, giá rẻ bán tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh Colmi S9 Plus&nbsp;</a>c&oacute; thiết kế hiện đại. Kiểu d&aacute;ng m&agrave;n h&igrave;nh chữ nhật độc đ&aacute;o c&ugrave;ng với phối h&agrave;i h&ograve;a giữa m&agrave;u xanh nước v&agrave; đen tạo được n&eacute;t năng động, c&aacute; t&iacute;nh. Thiết kế c&aacute;c t&iacute;nh năng tiện &iacute;t gi&uacute;p theo d&otilde;i v&agrave; điều chỉnh chế độ luyện tập, r&egrave;n luyện sức khỏe, mang đến cho bạn sự h&agrave;i l&ograve;ng.&nbsp;</h3>\r\n\r\n<h3>Thiết kế trẻ trung, năng động</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\" title=\"Đồng hồ thông mình chính hãng, giá rẻ bán tại Thế Giới Di Động\" type=\"Đồng hồ thông mình chính hãng, giá rẻ bán tại Thế Giới Di Động\">Đồng hồ th&ocirc;ng minh&nbsp;</a>được thiết kế với kiểu d&aacute;ng hiện đại, hợp thời trang. Thiết kế chất d&acirc;y silicon c&ugrave;ng với m&agrave;n h&igrave;nh hiển thị hiện đại ph&ugrave; hợp theo ch&acirc;n bạn đến mọi sự kiện.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-1-2.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế Đồng hồ thông minh Colmi S9 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-1-2.jpg\" title=\"Thiết kế Đồng hồ thông minh Colmi S9 Plus\" /></a></p>\r\n\r\n<h3>Thời lượng pin sử dụng khoảng 5 ng&agrave;y</h3>\r\n\r\n<p>Bạn c&oacute; thể mang theo thiết bị trong những chuyến đi chơi ngắn ng&agrave;y, m&agrave; kh&ocirc;ng cần lo lắng khi thời lượng sử dụng pin l&ecirc;n đến 5 ng&agrave;y.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-3-1.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin Đồng hồ thông minh Colmi S9 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-3-1.jpg\" title=\"Thời lượng pin Đồng hồ thông minh Colmi S9 Plus\" /></a></p>\r\n\r\n<h3>Hiển thị sắc n&eacute;t, chống vỡ hiệu quả&nbsp;</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh của đồng hồ được l&agrave;m từ k&iacute;nh cường lực gi&uacute;p chống vỡ do sơ &yacute; va chạm một c&aacute;ch hiệu quả. C&ocirc;ng nghệ m&agrave;n h&igrave;nh LCD cho hiển thị c&aacute;c th&ocirc;ng số hay dữ liệu sắc n&eacute;t. Đường k&iacute;nh mặt 19mm nhỏ nhắn vừa vặn với mọi cỡ tay.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-2-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Đồng hồ thông minh Colmi S9 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-2-1.jpg\" title=\"Màn hình Đồng hồ thông minh Colmi S9 Plus\" /></a></p>\r\n\r\n<h3>Kết nối v&agrave; nhận th&ocirc;ng b&aacute;o từ điện thoại&nbsp;</h3>\r\n\r\n<p>Được trang bị t&iacute;nh năng kết nối với cả Android hay IOS th&ocirc;ng qua Bluetooth, đồng hồ gi&uacute;p bạn nhận th&ocirc;ng b&aacute;o từ điện thoại. Mọi cuộc gọi, tin nhắn, Messenger đều được hiển thị tr&ecirc;n m&agrave;n h&igrave;nh gi&uacute;p bạn tiện lợi hơn khi c&oacute; thể nhận biết được th&ocirc;ng b&aacute;o d&ugrave; ở bất cứ đ&acirc;u. Ngo&agrave;i ra bạn c&ograve;n c&oacute; thể chủ động từ chối cuộc gọi th&ocirc;ng qua đồng hồ.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-6-1.jpg\" onclick=\"return false;\"><img alt=\"Kết nối Đồng hồ thông minh Colmi S9 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-6-1.jpg\" title=\"Kết nối Đồng hồ thông minh Colmi S9 Plus\" /></a></p>\r\n\r\n<h3>Chống nước ti&ecirc;u chuẩn IP68&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-zeblaze\" target=\"_blank\" title=\"Đồng hồ Zeblaze chính hãng, giá rẻ bán tại Thế Giới Di Động\" type=\"Đồng hồ Zeblaze chính hãng, giá rẻ bán tại Thế Giới Di Động\">Đồng hồ Zeblaze</a>&nbsp;được trang bị t&iacute;nh năng chống nước ở ti&ecirc;u chuẩn IP68. Bạn kh&ocirc;ng cần phải lo lắng bụi bẩn hay những cơn mưa r&agrave;o bất chợt l&agrave;m ảnh hưởng đến chất lượng đồng hồ v&agrave; hiển nhi&ecirc;n l&agrave; cũng kh&ocirc;ng cần phải lo ngại khi nước v&ocirc; t&igrave;nh bắn tr&uacute;ng.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-5-1.jpg\" onclick=\"return false;\"><img alt=\"Chuẩn chống nước Đồng hồ thông minh Colmi S9 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-5-1.jpg\" title=\"Chuẩn chống nước Đồng hồ thông minh Colmi S9 Plus\" /></a></p>\r\n\r\n<h3>D&acirc;y silicon mềm mại, tho&aacute;ng kh&iacute; với thiết kế đục lỗ</h3>\r\n\r\n<p>Điểm độc đ&aacute;o của chiếc đồng hồ th&ocirc;ng minh n&agrave;y l&agrave; th&acirc;n d&acirc;y hai m&agrave;u c&ugrave;ng với thiết kế nhiều lỗ tr&ograve;n. Chất d&acirc;y silicon mềm mại, dễ chịu khi mang. Nhiều lỗ đục tr&ecirc;n th&acirc;n d&acirc;y vừa gi&uacute;p điều chỉnh độ d&agrave;i d&acirc;y sao cho &ocirc;m cổ tay, vừa gi&uacute;p tho&aacute;ng kh&iacute;, thoải m&aacute;i mang cả ng&agrave;y d&agrave;i.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-7copy.jpg\" onclick=\"return false;\"><img alt=\"Dây đeo Đồng hồ thông minh Colmi S9 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-7copy.jpg\" title=\"Dây đeo Đồng hồ thông minh Colmi S9 Plus\" /></a></p>\r\n\r\n<h3>Theo d&otilde;i chỉ số sức khỏe&nbsp;</h3>\r\n\r\n<p>C&aacute;c chỉ số về nhịp tim, lượng calories ti&ecirc;u thụ hay số bước ch&acirc;n bạn đi được đều được hiển thị tr&ecirc;n m&agrave;n h&igrave;nh đồng hồ. Từ c&aacute;c chỉ số đồng hồ cung cấp, bạn c&oacute; thể nhận biết được t&igrave;nh trạng hay chế độ luyện tập của m&igrave;nh để c&oacute; thể điều chỉnh hợp l&iacute; v&agrave; cải thiện để c&oacute; sức khỏe tốt hơn. Ngo&agrave;i ra, đồng hồ c&ograve;n c&oacute; t&iacute;nh năng b&aacute;o thức v&agrave; bấm giờ cho bạn c&oacute; chế độ sống khỏe, điều chỉnh thời gian của m&igrave;nh tốt hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-4-1.jpg\" onclick=\"return false;\"><img alt=\"Chức năng Đồng hồ thông minh Colmi S9 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/7077/202054/dong-ho-thong-minh-colmi-s9-plus-4-1.jpg\" title=\"Chức năng Đồng hồ thông minh Colmi S9 Plus\" /></a></p>\r\n', 0, 0, 'dong-ho-thong-minh-colmi-s9-plus-1-600x600.jpg', 792000),
(63, 'TIVI LED HD PANASONIC TH-32C410V', 6, 'Ti vi', '<p>Tivi LCD Panasonic TH-32C410V trang bị m&agrave;n h&igrave;nh 32 inches độ ph&acirc;n giải HD c&ugrave;ng với những c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh hiện đại, mang đến khả năng thị sắc n&eacute;t, r&otilde; r&agrave;ng, đ&aacute;p ứng được nhu cầu giải tr&iacute; trong gia đ&igrave;nh của bạn. Về k&iacute;ch thước th&igrave; tivi LCD Panasonic TH-32C410V ph&ugrave; hợp với những kh&ocirc;ng gian ph&ograve;ng kh&aacute;ch nhỏ hay ph&ograve;ng ngủ. Tivi HD Panasonic TH-32C410V sở hữu hệ thống &acirc;m thanh v&ograve;m ảo V-audio , cho ra &acirc;m thanh v&ograve;m sống động để bạn...</p>\r\n', '<p>Tivi LCD Panasonic TH-32C410V trang bị m&agrave;n h&igrave;nh 32 inches độ ph&acirc;n giải HD c&ugrave;ng với những c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh hiện đại, mang đến khả năng thị sắc n&eacute;t, r&otilde; r&agrave;ng, đ&aacute;p ứng được nhu cầu giải tr&iacute; trong gia đ&igrave;nh của bạn. Về k&iacute;ch thước th&igrave; tivi LCD Panasonic TH-32C410V ph&ugrave; hợp với những kh&ocirc;ng gian ph&ograve;ng kh&aacute;ch nhỏ hay ph&ograve;ng ngủ. Tivi HD Panasonic TH-32C410V sở hữu hệ thống &acirc;m thanh v&ograve;m ảo V-audio , cho ra &acirc;m thanh v&ograve;m sống động để bạn c&oacute; thể cảm nhận được một bầu kh&ocirc;ng kh&iacute; giải tr&iacute; th&uacute; vị.</p>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/thumb/large/100/047/633/files/tivi-led-hd-panasonic-th-32c410v-1.jpg?v=1469337675678\" /></p>\r\n\r\n<p><strong>ĐẶC ĐIỂM NỔI BẬT</strong></p>\r\n\r\n<ul>\r\n	<li>Tivi 32 inches</li>\r\n	<li>Ph&ugrave; hợp với những kh&ocirc;ng gian diện t&iacute;ch nhỏ.</li>\r\n	<li>Độ ph&acirc;n giải HD</li>\r\n	<li>Hiển thị h&igrave;nh ảnh sắc n&eacute;t.</li>\r\n	<li>Thiết kế bắt mắt</li>\r\n	<li>Đem lại n&eacute;t nổi bật cho căn ph&ograve;ng bạn.</li>\r\n	<li>Đầu thu KTS DVB-T2</li>\r\n	<li>Mang đến những chương tr&igrave;nh chất lượng cao.</li>\r\n</ul>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/100/047/633/products/tivi-led-hd-panasonic-th-32c410v-3.jpg?v=1452221597677\" /></p>\r\n', 1, 0, 'tivi-led-hd-panasonic-th-32c410v-1.jpg', 5400000),
(64, 'TIVI LED SAMSUNG UA55J6200AKXXV', 6, 'Ti vi', '<p>Một sự lựa chọn m&agrave;n h&igrave;nh to hơn cho trải nghiệm sống động hơn từ d&ograve;ng sản phẩm Smart TV Led Samsung J6200. Tivi Led Samsung UA55J6200AKXXV m&agrave;n h&igrave;nh 55 inch độ ph&acirc;n giải Full HD gi&uacute;p bạn tận hưởng những khung h&igrave;nh rực rỡ, sắc n&eacute;t đến từng chi tiết. C&ocirc;ng nghệ tối ưu h&oacute;a cho chất lượng h&igrave;nh ảnh tuyệt đỉnh. Tận hưởng cuộc sống hiện đại với Tivi th&ocirc;ng minh Led Samsung UA55J6200AKXXV, đem cả thế giới giải tr&iacute; v&agrave;o gia đ&igrave;nh bạn. ...</p>\r\n', '<p>Một sự lựa chọn m&agrave;n h&igrave;nh to hơn cho trải nghiệm sống động hơn từ d&ograve;ng sản phẩm Smart TV Led Samsung J6200. Tivi Led Samsung UA55J6200AKXXV m&agrave;n h&igrave;nh 55 inch độ ph&acirc;n giải Full HD gi&uacute;p bạn tận hưởng những khung h&igrave;nh rực rỡ, sắc n&eacute;t đến từng chi tiết. C&ocirc;ng nghệ tối ưu h&oacute;a cho chất lượng h&igrave;nh ảnh tuyệt đỉnh. Tận hưởng cuộc sống hiện đại với Tivi th&ocirc;ng minh Led Samsung UA55J6200AKXXV, đem cả thế giới giải tr&iacute; v&agrave;o gia đ&igrave;nh bạn.</p>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/thumb/large/100/047/633/files/tivi-led-samsung-ua55j6200akxxv-2.jpg?v=1469337553235\" /></p>\r\n\r\n<p><strong>ĐẶC ĐIỂM NỔI BẬT</strong></p>\r\n\r\n<ul>\r\n	<li>Hệ điều h&agrave;nh Tizen OS</li>\r\n	<li>Giao diện dễ sử dụng với kho ứng dụng phong ph&uacute;</li>\r\n	<li>C&ocirc;ng nghệ h&igrave;nh ảnh chất lượng cao&nbsp;</li>\r\n	<li>Chất lượng h&igrave;nh ảnh tuyệt vời, sắc n&eacute;t.</li>\r\n	<li>Chip l&otilde;i tứ mạnh mẽ<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;<img src=\"https://bizweb.dktcdn.net/100/047/633/products/10021485-samsung-ua55j6200akxxv-2.jpg?v=1452221303230\" /></p>\r\n', 1, 0, 'tivi-led-samsung-ua55j6200akxxv-2.jpg', 25000000),
(65, 'TIVI LED SAMSUNG UA55JS9000KXXV', 6, 'Ti vi', '<p>Thị trường TV hiện nay hết sức đa dạng với nhiều c&ocirc;ng nghệ được cải tiến.. L&agrave; người ti&ecirc;u d&ugrave;ng bạn c&oacute; được nhiều sự lựa chọn hơn. Để chọn được chiếc tivi hợp t&uacute;i tiền, lại đ&aacute;p ứng được nhu cầu sử dụng v&agrave; ph&aacute;t huy được c&aacute;c thế mạnh c&ocirc;ng nghệ quả thật l&agrave; việc kh&ocirc;ng dễ. Sự ph&aacute;t triển c&ocirc;ng nghệ ch&oacute;ng mặt c&agrave;ng đưa ra nhiều sự lựa chọn phong ph&uacute; cho bạn. Nếu bạn l&agrave; người y&ecirc;u th&iacute;ch c&ocirc;ng nghệ v&agrave; c&oacute; t&iacute;nh...</p>\r\n', '<p>Thị trường TV hiện nay hết sức đa dạng với nhiều c&ocirc;ng nghệ được cải tiến.. L&agrave; người ti&ecirc;u d&ugrave;ng bạn c&oacute; được nhiều sự lựa chọn hơn. &nbsp;Để chọn được chiếc tivi hợp t&uacute;i tiền, lại đ&aacute;p ứng được nhu cầu sử dụng v&agrave; ph&aacute;t huy được c&aacute;c thế mạnh c&ocirc;ng nghệ quả thật l&agrave; việc kh&ocirc;ng dễ. Sự ph&aacute;t triển c&ocirc;ng nghệ ch&oacute;ng mặt c&agrave;ng đưa ra nhiều sự lựa chọn phong ph&uacute; cho bạn. Nếu bạn l&agrave; người y&ecirc;u th&iacute;ch c&ocirc;ng nghệ v&agrave; c&oacute; t&iacute;nh thẩm mỹ cao trong việc b&agrave;i tr&iacute; kh&ocirc;ng gian nội thất th&igrave; TIVI LED SAMSUNG UA55JS9000KXXV sẽ l&agrave; sự lựa chọn ho&agrave;n hảo cho bạn. Sản phẩm TV m&agrave;n h&igrave;nh cong t&iacute;ch hợp c&aacute;c c&ocirc;ng nghệ đỉnh cao. C&ugrave;ng ch&uacute;ng t&ocirc;i kh&aacute;m ph&aacute; TIVI LED SAMSUNG UA55JS9000KXXV 55 inch v&agrave; tận hưởng cảm gi&aacute;c xem phim như ở rạp.</p>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/100/047/633/products/tivi-led-samsung-ua55js9000kxxv.jpg?v=1452221103923\" /></p>\r\n\r\n<p><strong>ĐẶC ĐIỂM NỔI BẬT</strong></p>\r\n\r\n<ul>\r\n	<li>Đường cong ho&agrave;n mỹ</li>\r\n	<li>H&igrave;nh ảnh si&ecirc;u n&eacute;t&nbsp;</li>\r\n	<li>&Acirc;m thanh sống động v&agrave; trung thực&nbsp;</li>\r\n</ul>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/100/047/633/products/tivi-led-samsung-ua55js9000kxxv-1.jpg?v=1452221103923\" /></p>\r\n', 0, 0, 'tivi-led-samsung-ua55js9000kxxv.jpg', 64900000),
(66, 'TIVI LED SAMSUNG UA55JU6400KXXV', 6, 'Ti vi', '<p>Sang trọng, hiện đại, cho bạn những trải nghiệm cực th&iacute;ch th&uacute; khi xem tivi c&ugrave;ng gia đ&igrave;nh, bạn b&egrave;. Đ&acirc;y l&agrave; những nhận x&eacute;t của những người đang sử dụng tivi LED Samsung UA55JU6400. Quả thật vậy, bạn sẽ kh&ocirc;ng thể ch&ecirc; v&agrave;o đ&acirc;u được với chiếc tivi 55 inch n&agrave;y. Bạn c&oacute; thể tha hồ kh&aacute;m ph&aacute; thế giới giải tr&iacute; mu&ocirc;n m&agrave;u với smart tivi Samsung UA55JU6400. ĐẶC ĐIỂM NỔI BẬT M&agrave;n h&igrave;nh 55 inches Ultra HD hiển...</p>\r\n', '<p>Sang trọng, hiện đại, cho bạn những trải nghiệm cực th&iacute;ch th&uacute; khi xem tivi c&ugrave;ng gia đ&igrave;nh, bạn b&egrave;. Đ&acirc;y l&agrave; những nhận x&eacute;t của những người đang sử dụng tivi LED Samsung UA55JU6400. Quả thật vậy, bạn sẽ kh&ocirc;ng thể ch&ecirc; v&agrave;o đ&acirc;u được với chiếc tivi 55 inch n&agrave;y. Bạn c&oacute; thể tha hồ kh&aacute;m ph&aacute; thế giới giải tr&iacute; mu&ocirc;n m&agrave;u với smart tivi Samsung UA55JU6400.</p>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/100/047/633/products/10021487-samsung-ua55ju6400kxxv-1.jpg?v=1452220913847\" /></p>\r\n\r\n<p><strong>ĐẶC ĐIỂM NỔI BẬT</strong></p>\r\n\r\n<ul>\r\n	<li>M&agrave;n h&igrave;nh 55 inches Ultra HD hiển thị h&igrave;nh ảnh cực n&eacute;t.</li>\r\n	<li>Kết nối đa dạng, mang lại khả năng giải tr&iacute; phong ph&uacute;.</li>\r\n	<li>&Acirc;m thanh đầy mạnh mẽ, sống động.</li>\r\n	<li>Thiết kế si&ecirc;u mỏng, sang trọng.</li>\r\n</ul>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/100/047/633/products/10021487-samsung-ua55ju6400kxxv-3.jpg?v=1452220913847\" /></p>\r\n', 0, 0, '10021487-samsung-ua55ju6400kxxv-1.jpg', 24890000),
(67, 'TIVI LCD SONY KD-55X9000C VN3', 6, 'Ti vi', '<p>Tivi LED Sony 55X9000C l&agrave; một trong những sản phẩm chủ lực của h&atilde;ng điện tử Sony được sản xuất tr&ecirc;n d&acirc;y chuyền c&ocirc;ng nghệ hiện đại để đạt được chất lượng cao v&agrave; độ bền ổn định. Tivi LCD Sony 55X9000C mang tr&ecirc;n m&igrave;nh khả năng xử l&yacute; h&igrave;nh ảnh tuyệt vời v&agrave; hệ thống &acirc;m thanh chất lượng cao, chắc chắn sẽ mang đến cho bạn một bầu kh&ocirc;ng kh&iacute; giải tr&iacute; đầy th&uacute; vị. Tivi LED Sony 55X9000C sẽ l&agrave; một sự lựa chọn ho&agrave;n hảo...</p>\r\n', '<p>Tivi LED Sony 55X9000C l&agrave; một trong những sản phẩm chủ lực của h&atilde;ng điện tử Sony được sản xuất tr&ecirc;n d&acirc;y chuyền c&ocirc;ng nghệ hiện đại để đạt được chất lượng cao v&agrave; độ bền ổn định. Tivi LCD Sony 55X9000C mang tr&ecirc;n m&igrave;nh khả năng xử l&yacute; h&igrave;nh ảnh tuyệt vời v&agrave; hệ thống &acirc;m thanh chất lượng cao, chắc chắn sẽ mang đến cho bạn một bầu kh&ocirc;ng kh&iacute; giải tr&iacute; đầy th&uacute; vị. Tivi LED Sony 55X9000C sẽ l&agrave; một sự lựa chọn ho&agrave;n hảo t&ocirc; điểm th&ecirc;m cho ng&ocirc;i nh&agrave; hiện đại của bạn.</p>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/thumb/large/100/047/633/files/tivi-led-sony-55x9000c-1.jpg?v=1469338247340\" /></p>\r\n\r\n<p><strong>ĐẶC ĐIỂM NỔI BẬT</strong></p>\r\n\r\n<ul>\r\n	<li>Tivi m&agrave;n h&igrave;nh 55 inches</li>\r\n	<li>Cho khả năng hiển thị h&igrave;nh ảnh tuyệt vời.</li>\r\n	<li>Kiểu d&aacute;ng đầy tinh tế</li>\r\n	<li>L&agrave;m nổi bật l&ecirc;n căn ph&ograve;ng của bạn.</li>\r\n	<li>Tivi m&agrave;n h&igrave;nh độ ph&acirc;n giải UHD</li>\r\n	<li>Cho chất lượng h&igrave;nh ảnh 4K si&ecirc;u n&eacute;t.</li>\r\n	<li>Smart TV</li>\r\n	<li>Mang nhiều tiện &iacute;ch tiện lợi.</li>\r\n</ul>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/100/047/633/products/tivi-led-sony-55x9000c-4.jpg?v=1452220724187\" /></p>\r\n', 0, 0, 'tivi-led-sony-55x9000c-1.jpg', 57890